  [52] = {
    'Защита границы',
    {'Приветствую, $N. Чем дальше от Златоземья, тем агрессивнее становятся дикие звери: на Лесопилку Восточной долины постоянно нападают волки и медведи.$B$BХочешь нам помочь?', },
    {'Убейте 8 лесных хищников и 5 молодых лесных медведей. Затем вернитесь к стражнику Томасу у восточного моста Элвиннского леса.', },
  },
  [53] = {
    'Янтарное сладкое',
    {'Вот медовая брага готова вполне,$BСловно солнце играет в ее глубине!$BМы в дубовый бочонок ее разольем,$BА потом насладимся напитком вдвоем!', },
