  [55] = {
    'Морбент Скверн',
    {'Морбент Скверн прячется в своем доме на вершине холма на востоке, рядом с кладбищем Вороньего холма. Пора положить конец его бесчинствам в наших землях…$B$BПримени против него Погибель Морбента, она обратит в прах всю его защитную магию.$B$BУбей его. Убей негодяя и избавь нас от его гнусных козней. Стань орудием возмездия, герой Сумеречного леса.', },
    {'Примените Погибель Морбента против Морбента Скверна, убейте его и верните Погибель Морбента в лагерь Свена.', },
  },
  [56] = {
    'Ночной Дозор',
    {'Я – командир Ночного Дозора, и мой долг – охранять покой жителей Темнолесья. Солдаты Штормграда оставили свои посты, предоставив нас самим себе.$B$BЕсли ты $Gготов:готова; на подвиги, помоги нам! Отправляйся на Безмятежное кладбище на юге от Темнолесья и убей 8 скелетов-воинов и 6 магов-скелетов. Мы должны дать отпор нежити!', },
    {'Убейте 8 скелетов-воинов и 6 магов-скелетов по просьбе командира Алтеи Чернодрев.', },
  },
  [57] = {
    'Ночной Дозор',
    {'$N, я получила сведения о появлении нежити в наших лесах! Если ты не боишься, отправляйся на кладбище Вороньего холма и убей 15 скелетов-губителей и 15 ужасных скелетов. Также говорят, что скелеты появились и к югу от Темнолесья, на Безмятежном кладбище. Мы должны изгнать нежить из Сумеречного леса!', },
    {'Убейте 15 скелетов-губителей и 15 ужасных скелетов по просьбе командира Алтеи Чернодрев.', },
  },
  [58] = {
    'Ночной Дозор',
    {'Обстановка на Вороньем холме не улучшилась. Безопасность Темнолесья – в твоих руках, $N. У нас не хватает стражей для обеспечения безопасности города. Придется тебе снова отправиться на Вороний холм и уничтожить 20 распространителей чумы в восточном мавзолее.', },
    {'Убейте 20 распространителей чумы в восточном мавзолее Вороньего холма по просьбе командира Алтеи Чернодрев.', },
  },
  [59] = {
    'Броня из кожи и ткани',
    {'Твои хитроумие и доблесть должны быть вознаграждены. Я приготовил для тебя знак: он пригодится для какой-нибудь части доспехов. Отнеси его Саре Тимберлейн на Лесопилку Восточной долины: она изготовит для тебя броню, или что там тебе будет нужно. Мне хотелось бы надеяться, что новые доспехи помогут тебе, $N, в защите Элвиннского леса от врагов.$B$BЛесозаготовки находятся к востоку от сторожевого поста Томаса.', },
    {'Отдайте Саре Тимберлейн знак брони Штормграда.', },
  },
  [60] = {
    'Свечи кобольдов',
    {'Приветствую тебя, $Gдобрый путник:прекрасная госпожа;! Не найдется ли у тебя свободной минутки?$B$BМы с братом держим аптеку в Штормграде; так вот, мне тут понадобились большие свечи. Воск нужен, понимаешь? Я вот и думаю: может, ты мне поможешь?$B$BСвечи можно достать у кобольдов, а кобольдов, по слухам, в Элвиннских рудниках видимо-невидимо: и в руднике Подземных глубин к югу отсюда, и в Яшмовой шахте на востоке... Может, прогуляешься туда и раздобудешь для меня свечи?', },
    {'Принесите 8 больших свечей в Златоземье Вильяму Песту.', },
  },
  [61] = {
    'Посылка в Штормград',
    {'В Штормграде мой брат Морган ждет посылки со свечами. У меня нет времени съездить туда самому, но если ты окажешь нам эту услугу, Морган хорошо тебе заплатит.$B$BВот, я тут упаковал свечи. А Моргана найти несложно, он работает в Торговом квартале Штормграда, в аптеке Песта.', },
    {'Доставьте посылку Вильяма Моргану Песту в Торговый квартал Штормграда.', },
  },
  [62] = {
    'Рудник Подземных глубин',
    {'Рудник в Североземье – не единственная головная боль. До меня дошли известия о том, что и на элвиннском руднике Подземных глубин поселились кобольды.$B$BНужно исследовать рудник и подтвердить эти известия или опровергнуть их. Я буду ждать отчета. Рудник расположен к югу от Златоземья, между фермами Стоунфилд и Маклур.', },
    {'Исследуйте рудник Подземных глубин и вернитесь в Златоземье с докладом для маршала Дугхана.', },
  },
  [63] = {
    'Зов Воды',
    {'На побережье Серебряного Бора, к западу от Гробницы, находится святилище, оскверненное чумой. Духи перестали разговаривать с нашим народом, и тебе предстоит победить воплощение воды и очистить купель.$B$BВозьми браслеты элементалей и остатки зелья Брин и помести их в жаровню. Этот ритуал позволит призвать чистого духа, с которым можно будет поговорить.$B$BПринеси мне доказательство того, что мое задание выполнено, и в награду я дам тебе тотем.', },
    {'Одолейте оскверненное воплощение воды и поместите в жаровню Вечного источника в Серебряном бору его браслеты и оставшиеся капли чистейшей воды.', },
  },
  [64] = {
    'Забытая вещь',
    {'Это было ужасно! Верна разбудила меня, когда услышала гвалт в поле. Поля были полны хулиганья. Мы бежали в спешке, и я забыл взять с собой карманные часы. Папаша Верны подарил мне эти часы в день нашей с ней свадьбы, и мне прямо нехорошо от мысли, что ими могли завладеть те воры. Я оставил часы в доме, в платяном шкафу. Ищи тыквенную ферму к западу отсюда – ты ее не пропустишь. Если вернешь мне мои часы, я буду очень признателен!', },
    {'Верните фермеру Хмуроброву его карманные часы, которые находятся в платяном шкафу в его доме на тыквенной ферме на западе.', },
  },
  [65] = {
    'Братство Справедливости',
    {'Группа негодяев, ответственных за то, что честные люди покидают Западный Край, зовет себя Братством Справедливости. Мне нужно, чтобы ты $Gпроник:проникла; в эту бандитскую шайку. Нам нужно узнать, кто ее главарь, и где они прячутся. В деревне Приозерье, в таверне, обитает некий Вилли, который мне кое-чем обязан.$B$BОтправляйся в деревню Приозерье, что расположена в Красногорье, к востоку от Элвиннского леса, и все разузнай.', },
    {'Поговорите с Вилли в Приозерье по просьбе Гриана Камнегрива.', },
  },
  [66] = {
    'Легенда о Сталване',
    {'Вчера ночью меня охватил беспричинный ужас. Я почувствовала, что моя внучка Алисса оказалась в страшной опасности. Я разложила карты... и со стола на меня взглянула Смерть.$B$BПогрузившись в длительный и тяжкий транс, я сумела разгадать эту темную тайну. Мне открылось имя: Сталван.$B$BОтыщи клерка в городской Ратуше и постарайся побольше разузнать об этом Сталване. Я опасаюсь за всех нас.', },
    {'Найдите клерка Далтри в ратуше Темнолесья.', },
  },
  [67] = {
    'Легенда о Сталване',
    {'Сталван, значит? Дай-ка погляжу в журнал.$B$BСталван... Сталван... Ну-ка... А, вот он! У меня есть запись о некоем господине Сталване Мистмантле. Последний адрес – Лунореченская школа. Но это было так давно!$B$BОкажи любезность, дай знать, если выяснишь что-либо об этом типе. Записи должны содержаться в порядке.', },
