  [152] = {
    'Зачистка побережья',
    {'Ты, должно быть, $Gвидел:видела; все эти обломки кораблей вдоль побережья. Великое море и в самом деле очень сурово. Береговую линию Западного Края нужно держать чистой, чтобы моряки, если приплывут к нашим берегам, были в безопасности. На сегодняшний день мурлоки являются большой проблемой...$B$BРазберись с ними – убей 7 волноловов, 7 воинов, 7 оракулов и 7 береговиков, – и награда будет ждать тебя.', },
    {'Убейте 7 волноловов, 7 воинов, 7 оракулов и 7 береговиков-мурлоков и вернитесь к капитану Грейсону на Маяк в Западном Крае.', },
  },
  [153] = {
    'Красные кожаные банданы',
    {'В Братстве Справедливости идет постоянная кипучая деятельность. Я уже долго слежу за их передвижениями. Между прочим, я заметил, что многих бандитов можно опознать по красным кожаным банданам.$B$BПринесите мне 15 таких бандан, и я позабочусь о том, чтобы вы получили вознаграждение.', },
    {'Принесите разведчику Галиану на Сторожевой холм 15 красных кожаных бандан.', },
