  [160] = {
    'Записка для мэра',
    {'Отнеси эту записку Элло Чернодреву, мэру Темнолесья.$B$BМы с ним старые друзья... И когда он прочитает это и узнает, что ты для меня $Gсделал:сделала;... не сомневаюсь, он будет счастлив не меньше меня! Ха-ха-ха!!!', },
