  [167] = {
    'О, брат мой...',
    {'Мы находились в обширных шахтах Западного Края, скрытых глубоко под землями Луноречья. Понятия не имею, откуда явились эти мерзкие вороватые вонючки. Так или иначе – туннель обрушился на нас. Мне удалось выбраться, но остальные... о них я ничего не знаю.$B$BВдруг тебе повезет больше, чем мне? Я ищу своего брата. Он всегда носил значок Лиги Исследователей. Если ты не найдешь брата, постарайся найти хотя бы его значок.', },
    {'Принесите значок Лиги Исследователей, принадлежавший Вилдеру Крапивцу, в Штормград.', },
  },
  [168] = {
    'Сбор воспоминаний',
    {'Многие из моих друзей погибли в тот роковой день, когда обрушился штрек. Если тебя как-нибудь занесет в ту шахту в Луноречье, пожалуйста, постарайся добыть что-то, что поможет их опознать. А если найдешь их карточки Союза шахтеров, принеси их мне, я отправлю их семьям погибших.', },
    {'Добудьте 4 карточки Союза шахтеров и верните их Вилдеру Крапивцу в Штормград.', },
  },
  [169] = {
    'Разыскивается: Гат\'Илзогг',
    {'Розыск! Гат\'Илзогг, вождь клана Черной Горы, разыскивается за нападение на крепость Каменной Стражи и набеги на окрестности Приозерья. $B$BГат\'Илзогг крайне опасен. По последним сведениям, он находится в недавно захваченной крепости и замышляет новые преступления против Королевства. Нашествие орков должно быть остановлено любой ценой. Храбрец, доставивший голову Гат\'Илзогга мировому судье Соломону, будет щедро вознагражден.', },
    {'Убейте Гат\'Илзогга и принесите его голову мировому судье Соломону в Приозерье.', },
  },
  [170] = {
    'Новая угроза',
    {'Надеюсь, ты $Gоказался:оказалась; здесь не случайно, |3-6($C). Не буду лукавить: после недавнего нападения троггов для нас любая помощь – дар небес.$B$BЯ слышал, что эти мерзавцы теперь повсюду лезут – ну и до Холодной долины тоже добрались. Говорят, их полно в горах на юго-востоке и возле замерзшего озера.$B$BТак ведь и это еще не все! Недавно они напали на наш лагерь на западе и разгромили его!$B$BНам и так воинов не хватает, а тут еще эти гады... Послушай, $Gприятель:подруга;, может, ты нам поможешь? Боюсь, иначе нам троггов не одолеть...', },
    {'Убейте 6 троггов из племени Каменной Челюсти и 6 сильных троггов из племени Каменной Челюсти.', },
  },
  [171] = {
    'Опекун Альянса',
    {'Ты $Gбыл:была; очень $Gдобр:добра; ко мне, $N. Я и представить не мог, что Детская неделя пройдет так весело. Спасибо. Я не хочу печалится из-за собственной судьбы – предки всегда говорили мне, что я должен быть выше всех несчастий. Знаешь, когда я вырасту, я хочу стать героем... как ты.$B$BКак ни жаль, мне пора домой. Давай вернемся, и я расскажу нашей попечительнице, что ты лучше всех!', },
    {'Отведите своего подопечного в приют Штормграда, вернув детскую свистульку попечительнице сирот Найтингейл в Храмовый квартал.', },
  },
  [172] = {
    'Детская неделя',
    {'Ты хочешь помочь сиротам? Да благословят тебя боги! Что бы мы без тебя делали!$B$BВот, возьми этот свисток. Действовать он будет недолго – до конца Детской недели. Свистнув в него, ты сможешь поговорить с сиротой, за которым ты обещаешь присматривать. Свистни еще раз, и ты отпустишь ребенка восвояси.$B$BПрошу тебя, $N, пользуйся свистком почаще, не забывай своего подопечного! Для детей это так важно...', },
    {'Продудите в орочью детскую свистульку. Присматривайте за сиротой в течение всей Детской недели.', },
  },
  [173] = {
    'Воргены в лесу',
    {'На Сумеречный лес наступают силы тьмы, и цель мастера Карвина – борьба с этим злом. Благодаря нашим усилиям народ Темнолесья живет в безопасности.$B$BТы считаешь, что $Gдостоин:достойна; вступить в наши ряды?$B$BКогда-то я был таким же, как ты. Меня раздражало самодовольство Дозора, и я стал соратником мастера Карвина. Если ты хочешь показать, на что $Gспособен:способна;, то слова тебе не помощники.$B$BВыступи против воргенов-тенеткачей Ночной Погибели в Светлой роще – светлой, ха! – и Гниющем саду.', },
    {'Калор из Темнолесья просит вас убить 6 тенеткачей Ночной Погибели.', },
  },
  [174] = {
    'Взгляни на звезды',
    {'Один мой друг, гном по имени Ког, обещал мне помочь создать устройство, которое позволит изучать звезды. Уверен, что есть и иные объяснения творящемуся в лесу, чем магическое вмешательство.$B$BПринеси мне бронзовую трубку, изготовленную искусным инженером. Согласно чертежам Кога, это шаг номер один.', },
    {'Принесите Виктору Призм\'Антрасу бронзовую трубку.', },
  },
  [175] = {
    'Взгляни на звезды',
    {'Согласно чертежам Кога, мне нужно отыскать способ отражать свет. Помнится, есть у нас одна особа по имени Мэри, которая вечно таскает с собой зеркальце. Может быть, ты отыщешь ее и она одолжит нам свою отражающую поверхность? Кажется, она когда-то жила в южной части Сумеречного леса.', },
    {'Найдите Мэри и уговорите ее пожертвовать зеркало для проекта Виктора.', },
  },
  [176] = {
    'Разыскивается: "Дробитель"',
    {'Разыскивается: Дробитель$B$BВ юго-западной части Элвиннского леса поселился могучий гнолл по прозванию Дробитель. Несмотря на все усилия, поймать его так и не удалось.$B$BАрмия Штормграда назначила за этого гнолла щедрую награду. Чтобы получить ее, необходимо представить маршалу Дугхану доказательства смерти Дробителя.', },
    {'Убейте Дробителя и принесите маршалу Дугхану большой коготь гнолла.', },
  },
  [177] = {
    'Взгляни на звезды',
    {'Зеркальце было одним из моих главных сокровищ... но потом со мной случилось несчастье. После этого я зашвырнула стекляшку за амбар. Одно время там бродил Невменяемый Вурдалак. Может быть, он утащил мое зеркальце. Говорят, что теперь эта тварь живет на Безмятежном кладбище.', },
    {'Заберите зеркало Слепой Мэри у Невменяемого Вурдалака и отдайте его Виктору в Темнолесье.', },
  },
  [178] = {
    'Поручение Теокрита',
    {'*До вас доносится голос...*$B$BНе пугайся. Я Теокрит, верховный маг башни Азоры в Элвиннском лесу. При помощи этой подвески гноллы из стаи Темношкуров общаются со своим повелителем Моргантом.$B$BЯ потратил много времени на то, чтобы тоже научиться вещать через эти подвески. Если ты слышишь мое сообщение, значит, заклинание сработало.$B$BПринеси мне эту подвеску, и я щедро вознагражу тебя.', },
    {'Принесите выцветшую подвеску Темношкуров верховному магу Теокриту в Башню Азоры, что в Элвиннском лесу.', },
  },
  [179] = {
    'Дворфские экипировщики',
    {'Ну-ка, ну-ка, что тут у нас?.. Холодновато в наших краях, верно? Готов спорить, у тебя все время мерзнут руки. Ну, угадал?$B$BЯ скажу, что тебе нужно: пара хороших теплых перчаток. Ну да, душа у меня добрая, так что замерзнуть я тебе не дам. Только вот есть одно условие.$B$BПринеси мне волчьего мяса – и мы, считай, в расчете. Справедливо, как думаешь? Мне – мясо, а уж я-то позабочусь о том, чтобы у тебя пальцы больше не мерзли. Ну, что скажешь?', },
    {'Принесите Стену Крепкоруку 8 кусков жесткого волчьего мяса.', },
  },
  [180] = {
    'Разыскивается: лейтенант Фангор',
    {'Разыскивается: лейтенант Фангор$B$BГлава гнолльской стаи Темношкуров, соратник Морганта, врага Города и Королевства. По приказу чиновника Соломона подлый предатель Штормграда приговаривается к смерти. Тот, кто приведет приговор в исполнение, должен в доказательство принести лапу Фангора мировому судье Соломону.', },
    {'Убейте лейтенанта Фангора и принесите его лапу мировому судье Соломону в Приозерье.', },
  },
  [181] = {
    'Взгляни на звезды',
    {'Для завершения работы не хватает только одного предмета. Согласно чертежам Кога, нам нужна линза. Единственную подходящую линзу в наших краях будет нелегко достать.$B$BВ кургане на юге Сумеречного леса живет огр по имени Ззарк\'Вул. Если сможешь, принеси мне его монокль, и я использую его вместо линзы!', },
    {'Отыщите Ззарк\'Вула в кургане на юге Сумеречного леса и принесите его монокль Виктору в Темнолесье.', },
  },
  [182] = {
    'Пещера троллей',
    {'Нас с моим братом Сениром отправили в Дун Морог, чтобы разведать, действительно ли там объявились тролли, и насколько серьезна эта угроза. Совету хватает хлопот с троггами, лишние проблемы им ни к чему.$B$BНасколько я видел, тролли не слишком хорошо закрепились в Холодной долине; в основном они живут в южной пещере. Как по мне, тут и армия не нужна: несколько крепких бойцов вполне управятся.$B$BМожет, и ты решишь поучаствовать в этом деле? Не за так, конечно: у меня есть что тебе предложить.', },
    {'Убейте 10 троллей-подростков из племени Мерзлогривов по просьбе Грелина Белоборода.', },
  },
  [183] = {
    'Охотник на вепрей',
    {'Нет ничего лучше охоты на диких вепрей, верно?$B$BПравда, здесь, в Холодной долине, их расплодилось столько, что весь интерес пропадает. Их и загонять не надо – они злые, как демоны, и сами бросаются прямо на охотника. Я бы даже сказал, что этих злобных свиней так много, что и охотиться-то стало опасно...$B$BКороче говоря, я буду благодарен, если ты поможешь мне прикончить хотя бы нескольких кабанов.', },
    {'Убейте 8 малых скальных вепрей по просьбе Талина Остроглаза.', },
  },
  [184] = {
    'Документ Хмуроброва',
    {'Это закладная на ферму Хмуроброва в Западном Крае. Она подписана Теодором Хмуробровом и его женой, Верной Хмуробров. На обороте документа торопливо нацарапаны слова:$B$B"Мы нажали на Хмуроброва и получили от него нужную бумагу. Если тебе захочется заполучить один из участков, она тебе пригодится.$B$BС Хмуробровами проблем не предвидится: я видел, как они покидают Западный Край, у них как раз телега сломалась."$B$BНаверное, Хмуробровы были бы рады получить этот документ назад...', },
    {'Доставьте фермеру Хмуроброву документ на ферму.', },
  },
  [185] = {
    'Охота на тигров',
    {'Когда я была маленькой девочкой, отец научил меня искусству охоты на тигров.$B$BМолодых зверей гораздо проще выслеживать и убивать. Поэтому начнем с простого. Молодые тигры часто забредают к лагерю экспедиции.$B$BПостарайся выследить и убить несколько зверей.', },
    {'Ажек Руак из экспедиции Эрнестуэя попросила вас убить 10 молодых тигров Тернистой долины.', },
  },
  [186] = {
    'Охота на тигров',
    {'А ты учишься, |3-6($C). Посмотрим, как ты справишься со взрослыми кошками. На сей раз тебе предстоит убить 10 тигров Тернистой долины.', },
    {'Ажек Руак из экспедиции Эрнестуэя попросила вас убить 10 тигров Тернистой долины.', },
  },
  [187] = {
    'Охота на тигров',
    {'Поднимем ставки. Докажи, что сможешь убить 10 старых тигров Тернистой долины. Справишься – и я признаю, что ты $Gготов:готова; к последнему испытанию, после которого я объявлю тебя великим охотником на тигров в присутствии мастера Эрнестуэя.$B$BСтарые тигры Тернистой долины неуловимы для врага и безжалостны в схватке.', },
    {'Ажек Руак из экспедиции Эрнестуэя попросила вас убить 10 старых тигров Тернистой долины.', },
  },
  [188] = {
    'Охота на тигров',
    {'Вот твое последнее испытание. Мы уже много недель выслеживаем эту хитрую тигрицу по имени Син\'Далл. Интересно, сможешь ли ты сделать то, что не удавалось ни $Gодному:одной; |3-2($R) – выследить и убить Син\'Далл. Принеси мне ее лапу в доказательство своего подвига.$B$BХотя выследить ее – непростая задача.', },
    {'Ажек Руак из экспедиции Эрнестуэя просит вас убить Син\'Далл и принеси ее лапу.', },
  },
  [189] = {
    'Уши троллей Кровавого Скальпа',
    {'Проклятые тролли! Они постоянно убивают наших агентов! Я потерял в стычках с ними лучших своих бойцов. Цена операции все растет и растет! Как я объясню это барону Ревилгазу?$B$BНужно действовать решительно... Думай, Кебок... Тебя назначили на эту должность не за безделье... Придумал! Тебя... $N зовут, правда? Ты ведь мне поможешь? А уж я заплачу!$B$BПредлагаю сделку... ты принесешь мне уши пятнадцати троллей из племени Кровавого Скальпа, а я дам тебе денег.', },
    {'Добудьте 15 ушей троллей из племени Кровавого Скальпа и принесите их Кебоку в Пиратскую Бухту.', },
  },
  [190] = {
    'Охота на пантер',
    {'Если ты хочешь отправиться на охоту вместе с нами под предводительством Хеминга младшего, докажи, что умеешь выслеживать пантер. Не бойся, начнем с простого. Для примера убей 10 молодых пантер.$B$BВыследить их – только полдела...', },
    {'Сэр С. Дж. Эрлгедин из экспедиции Эрнестуэя попросил вас убить 10 молодых пантер.', },
  },
  [191] = {
    'Охота на пантер',
    {'А теперь задача потруднее. Если ты хочешь доказать, что $Gдостоин:достойна; общаться с настоящими охотниками, убей 10 пантер.$B$BС этими ребятами не так легко справиться, они посильнее молодых зверей, с которыми тебе приходилось сталкиваться прежде.', },
    {'Сэр С. Дж. Эрлгедин из экспедиции Эрнестуэя попросил вас убить 10 пантер.', },
  },
  [192] = {
    'Охота на пантер',
    {'Усложняем задание. Настоящий охотник сможет одолеть даже тенебрюхих пантер, самых опасных в Тернистой долине. Докажи, что сможешь прикончить 10 таких тварей.$B$BЕсли тебе покажется, что их трудно выслеживать, попробуй для начала убить одну...', },
    {'Сэр С. Дж. Эрлгедин из экспедиции Эрнестуэя попросил вас убить 10 тенебрюхих пантер.', },
  },
  [193] = {
    'Охота на пантер',
    {'Тебе почти удалось доказать, что ты $Gвеликий охотник:великая охотница; на пантер. И все же остается одна пантера по имени Баг\'тира.$B$BДобудь ее клык, и мое уважение тебе обеспечено!', },
    {'Сэр С. Дж. Эрлгедин из экспедиции Эрнестуэя попросил принести ему клык Баг\'тиры.', },
  },
  [194] = {
    'Охота на ящеров',
    {'Значит, ты считаешь себя $Gпревосходным охотником:превосходной охотницей;? Проверим. Ступай в джунгли и подстрели 10 ящеров Тернистой долины. Посмотрим, как ты справишься с крупной дичью.$B$BИ нет, я не скажу, где их искать! Первая половина испытания – поиски зверя.', },
    {'Хеминг Эрнестуэй младший просит вас убить 10 ящеров Тернистой долины.', },
  },
  [195] = {
    'Охота на ящеров',
    {'А теперь я задам тебе задачку, посмотрим, придется ли она тебе по вкусу. Отправляйся в джунгли и добудь 10 ящеров-кнутохвостов. Увидим, хватит ли тебе пороху!', },
    {'Хеминг Эрнестуэй младший попросил вас убить 10 ящеров-кнутохвостов.', },
  },
  [196] = {
    'Охота на ящеров',
    {'Посмотрим, как ты справишься со следующим испытанием. Здесь живут хитрые ящеры, мы зовем их тропическими ловцами. Их гораздо труднее выследить и убить, чем другие виды, с которыми тебе пришлось столкнуться. Убей 10 тропических ловцов, и я соглашусь, что из тебя еще выйдет $Gнастоящий охотник:настоящая охотница;.', },
    {'Хеминг Эрнестуэй младший попросил вас убить 10 тропических ловцов.', },
  },
  [197] = {
    'Охота на ящеров',
    {'Ну, раз ты ходишь в джунгли как к себе домой, дай-ка я расскажу тебе о свирепом ящере, которого даже мне не удалось убить. Ажек называет этого хитрого ящера Тетис.$B$BЕсли ты хочешь показать подлинное мастерство, убей Тетис и принеси мне ее коготь. Этот подвиг не удался еще ни одному охотнику!', },
    {'Хеминг Эрнестуэй младший попросил вас убить Тетис, хитрого, опасного ящера Тернистой долины.', },
  },
  [198] = {
    'Припасы для рядового Торсена',
    {'Хммм. У меня и правда есть для тебя дело. Лейтенант Дорен и его последователи там, на севере, регулярно получают от нас поставки еды и оборудования.$B$BПришло время доставить очередную партию, а, учитывая нападения троллей, никто не хочет за это браться. Лагерь Дорена располагается на северо-западе от дороги у входа в Тернистую долину из Сумеречного леса. Отнеси эту посылку рядовому Торсену, а если его нет в лагере, значит, он дежурит на юге от него. Он займется этими припасами.$B$BУчти, если ты провалишь задание, придется тебе возместить мои затраты.', },
    {'Доставьте различные припасы гоблинов рядовому Торсену.', },
  },
  [199] = {
    'Прямая и черная угроза',
    {'Вы берете образец странно пахнущего порошка.', },
    {'Вернитесь к главному инженеру Плотиннеру и покажите ему найденную улику.', },
  },
  [200] = {
    'Букмекер Ирод',
    {'Если Курцену известна тайна Торсена, значит, в наши ряды затесался шпион. Необходимо выяснить, откуда просочилась информация!$B$BСвязным Торсена в лагере Курцена был букмекер Ирод. Ирод работает снабженцем у Курцена, и знает все о его лагере. Если удастся добыть его записи, быть может, мы узнаем, как накрыли Торсена.$B$BКогда мы служили в войсках Курцена, кабинет букмекера располагался на верхнем этаже единственного двухэтажного дома в лагере. Скорее всего, его записи по-прежнему там.', },
    {'Найдите записи букмекера Ирода.', },
  },
  [201] = {
    'Поиск лагеря',
    {'Мне сообщили о группе охотников, разбивших лагерь вверх по реке от рудника Торговой Компании возле озера Назферити. Я бы не придал этому значения, но говорят, будто один из тамошних охотников – сам Хеминг Эрнестуэй младший. Мне не верится, но у него настолько высокие связи, если ты понимаешь о чем я, что лучше заранее обезопасить себя.$B$BПочему бы тебе не заглянуть в этот лагерь и не разузнать, что они замышляют?', },
    {'Найдите лагерь охотников и сообщите о его местонахождении Кразеку из Пиратской Бухты.', },
  },
  [202] = {
    'Полковник Курцен',
    {'И снова ты $Gпоказал:показала; нам, на что $Gспособен:способна;. Кажется, талантов у тебя не меньше, чем у самого Курцена! Это может спасти нас, горстку повстанцев... если ты сможешь выступить против Курцена и одержать над ним верх!$B$BОн управляет своими людьми из глубин Схрона. Ступай в эту пещеру, пробейся сквозь ряды ее защитников, и одолей Курцена и его ближайших приспешников.$B$BПрощай, $N. Ты – наша последняя надежда.', },
    {'Убейте 6 гвардейцев и 4 заместителей Курцена и принесите голову Курцена лейтенанту Дорену в Лагерь повстанцев.', },
  },
  [203] = {
    'Второе восстание',
    {'Мы решительно проигрываем полковнику Курцену. Он не только блестящий тактик – его войска превосходят нас в численности в десять раз! Чтобы выжить, нам придется прибегнуть к его же подлой тактике.$B$BПроберись в лагерь на востоке и убей как можно больше его бойцов. И скройся до прибытия подкрепления.', },
    {'Убейте 15 партизан Курцена.$B$BВернитесь к сержанту Йохве в Лагерь повстанцев.', },
  },
  [204] = {
    'Дурное лекарство',
    {'Чтобы получить шанс выстоять против Курцена, нам необходимо лишить его лекарей. Он собрал немало целителей, которые лечат его войска зельями из джунглей и травят наших бойцов экстрактами ядовитого папоротника.$B$BНам и самим нужны эти составы. Обыщи поселок Курцена – лекарства находятся у его целителей и партизанов, а экстракты скорее всего хранятся в одном из ящиков с припасами.', },
    {'Принесите сержанту Йохве из Лагеря повстанцев 7 порций зелья из джунглей и 1 порцию экстракта ядовитого папоротника.', },
  },
  [205] = {
    'Тролльское колдовство',
    {'Пока я изучаю принесенные тобой прорисовки, собери для меня принадлежности злой магии троллей. Тебе придется проникнуть в земли племени Дробителей Черепов, второго племени на далеком юге, и добыть фетиши у их знахарей и мистиков.', },
    {'Принесите 4 фетиша троллей из племени Дробителей Черепов брату Нимезу в лагерь Повстанцев.', },
  },
  [206] = {
    nil,
    {'Хотя Курцен и мертв, зло, соблазнившее его, живет! Вот что мне удалось выяснить: огрский маг – Май\'Зот – живет в огрском кургане в восточных горах Тернистой долины. Говорят, что он пользуется магией, овладевающей разумом, и я не сомневаюсь, что Май\'Зот виноват в падении Курцена.$B$BПока он жив, зло будет расползаться, и может даже появиться второй Курцен.$B$BУбей его и принеси мне его Око разума, артефакт, позволяющий ему творить темные дела.', },
    {'Принесите Око разума брату Нимезу в лагерь Повстанцев.', },
  },
  [207] = {
    'Тайна Курцена',
    {'Когда-то полковник Курцен был великим и благородным человеком – я хочу выяснить, что заставило его свернуть с пути истинного! Должно быть, он находится под воздействием магии троллей. Надо изучить древние легенды, чтобы убедиться в этом.$B$BВ руинах тролльских городов в Тернистой долине спрятаны 4 таблички. Мне необходимо их добыть и прочесть! Перерисуй с них все отметки и принеси рисунки мне.$B$BПервая табличка находится в Руинах Бал\'лал, вторая – на Коварном рифе, а третья и четвертая – в Руинах Зул\'Кунды.', },
    {'Найдите 4 таблички, перерисуйте их и принесите их брату Нимезу.', },
  },
  [208] = {
    'Охотник на крупную дичь',
    {'$N, ты $Gдостоин:достойна; занять место среди нас. И на полях битвы в Лордероне, и в глубоких джунглях, мне редко приходилось видеть столь умелое обращение с оружием.$B$BТебе удалось убить свирепую Син\'Далл, неуловимую Баг\'тиру и коварную Тетис. Осталось одно – добыть голову короля Бангалаша. Давненько я точу зуб на этого белого тигра и оказался здесь именно ради него. Убей короля Бангалаша, и мы признаем, что тебе нет равных в искусстве охоты.', },
    {'Хеминг Эрнестуэй младший попросил вас принести ему голову короля Бангалаша, огромного белого тигра.', },
  },
  [209] = {
    'Клыки троллей',
    {'Убивать тигров и добывать их клыки на экспорт в южные моря – трудно и дорого. К счастью, мы придумали способ обрабатывать простые рогоподобные предметы и изготавливать неотличимые подделки.$B$BЛучше всего, как ни смешно, подходят клыки троллей из племени Дробителей Черепов.$B$BШ-ш-ш, спокойно, чем меньше знает покупатель, тем крепче он спит, верно? Принеси мне побольше таких клыков, чтобы мы могли подготовить ежемесячную поставку!', },
    {'Добудьте 18 клыков троллей из племени Дробителей Черепов и принесите их Кебоку в Пиратскую бухту.', },
  },
  [210] = {
    'Горшок Кразека',
    {'Готовка по большей части лежит на мне. И она доставляла мне много хлопот, пока я не купил механический горшок у одного гоблина в Пиратской Бухте. Это чудо какое-то! Он сэкономил мне много часов... Пока не сломался!$B$BМожет, ты отнесешь его назад к продавцу, в ремонт? Продавца зовут Кразек.', },
