  [404] = {
    'Грязная работа',
    {'Плеть проникла в Тирисфальские леса и заполонила всю местность к западу от Брилла, возле старого моста.$B$BОтправляйся туда и загони этих гниющих мертвецов обратно в землю, откуда они вылезли. Разбросай их кости и принеси их гнилые когти – за них ты получишь награду от стражей смерти.', },
