  [412] = {
    'Операция "Перенаправление"',
    {'При дальнейшем анализе гномреганской ситуации оказалось, что нам не только не удалось извести троггов, но мы случайно превратили большую часть представителей нашей расы в ужасающих, безмозглых, злобных лепрогномов!$B$BМы с Оззи намерены направить в обратную сторону ужасный эффект лепры при помощи нашего последнего изобретения – атомарного перенаправлятора. Эта машина почти закончена, но нам чрезвычайно нужны зубцы рестабилизации и гиромеханические шестерни. Добудь их у лепрогномов, которые обитают около Гномрегана, и принеси нам!', },
    {'Принесите Раззлу Шустрецу в Караносе 8 зубцов рестабилизации и 8 гиромеханических шестерней.', },
  },
  [413] = {
    '"Мерцающий портер"',
    {'Хотелось бы мне, чтобы мой братец Велларт попробовал свежего "Мерцающего портера". Он горный пехотинец, их расквартировали в южной сторожевой башне, одной из двух на границе с озером Лок Модан.$B$BМожет, он и не такой пивовар, как остальные Ячменовары, но свое пиво он любит, и я знаю, что это тоже ему понравится.$B$BНе отнесешь ему вот этот бочонок "Мерцающего портера"?', },
    {'Отнесите бочонок "Мерцающего портера" горному пехотинцу Ячменовару.', },
  },
  [414] = {
    'Портер для Кэдрелла',
    {'Я бы и еще отхлебнул "Мерцающего портера" с пивоварни моего братца – забористое же пойло! Но я хочу, чтобы мой приятель-пехотинец попробовал его. Зовут парня Кэдрелл, и он обычно патрулирует дорогу через Телcамар в озере Лок Модан.$B$BЧтобы добраться до Телcамара, иди на юго-восток через туннель, затем на развилке возьми на северо-восток. И по той дороге иди прямо до Телcамара.$B$BОтдай горному пехотинцу Кэдреллу этот бочонок "Мерцающего портера", и посмотрим, что он скажет!', },
    {'Отнести бочонок "Мерцающего портера" горному пехотинцу Кэдреллу в Лок Модан.', },
  },
  [415] = {
    'Новое пиво Регольда',
    {'Регольд Ячменовар, что живет в Поселке Пивоваров, спрашивал про тебя.$B$BОн хочет поговорить с тобой про свое новое пиво... Сказал вроде, что ты ему $Gпомог:помогла;. Можешь самолично вернуться в Поселок Пивоваров и спросить, что ему надо.$B$BМожет, тебя дармовая выпивка ждет...', },
    {'Поговорите с Регольдом Ячменоваром.', },
  },
  [416] = {
    'Ловля крыс',
    {'К западу отсюда началось настоящее нашествие. Эти крысы, кобольды-туннельщики, обосновались в предгорьях, устроили там свои логовища и оскверняют нашу прекрасную землю!$B$BИх необходимо истребить. Перебей их как можно больше, принеси в доказательство уши и получишь награду.$B$BКобольды-туннельщиков поселились к западу от Телcамара вдоль горных склонов.', },
    {'Принесите 12 ушей кобольдов-туннельщиков горному пехотинцу Кэдреллу в Телcамар.', },
  },
  [417] = {
    'Месть пилота',
    {'Хилдельв пишет в своем журнале, что подвергся нападению Паршивого Когтя, "огромного медведя-ледолапа". Он отогнал тварь, но та успела страшно изранить его.$B$BСудя по состоянию трупа Хилдельва, Паршивый Коготь, видимо, вернулся и добил его.$B$BВ журнале Хилдельв записал свое последнее пожелание:$B$B"Отомстить Паршивому Когтю."', },
    {'Убейте Паршивого Когтя.$B$BПринесите его коготь и журнал Хилдельва пилоту Большеногу.', },
  },
  [418] = {
    'Телcамарские кровяные колбаски',
    {'Едоки в Телcамаре не переводятся – еще бы, детей полным-полно, после трудового дня работники ужинать хотят. Мы славимся кровяными колбасками... Но ты ведь их еще не $Gпробовал:пробовала;? Нет? Что же, у нас принято отрабатывать еду, и не думаю, что за красивые глазки тебе сделают исключение.$B$BМне понадобится медвежатина, кишки кабана на оболочки и паучья лимфа для остроты. Добудь все это, а уж Видра сготовит все в лучшем виде!', },
    {'Принесите Видре Жаркий Очаг в Телcамар 3 куска медвежатины, 3 связки кишок кабана и 3 порции паучьей лимфы.', },
  },
  [419] = {
    'Пропавший пилот',
    {'Мой друг и напарник, пилот осадной машины Мори Хилдельв, пропал в этих холмах. Мы искали там редкую руду, необходимую для высококачественного пороха, и во время поисков мы загнали его танк на крутой холм и он сломался!$B$BХилдельв, по-прежнему уверенный, что руда в тех горах есть, оставил меня охранять наши танки, а сам пошел дальше пешком.$B$BПрошло уже несколько дней, а я слышал ночью какой-то звериный вой в холмах. Мори – парень-кремень, но мне тревожно.$B$BПрошу тебя, $N, найди его.', },
    {'Найдите пилота Хилдельва.', },
  },
  [420] = {
    'Наблюдения Сенира',
    {'Трогги, поселившиеся в тоннелях, крайне враждебны: они без раздумий нападают на любого, кто только осмелится туда сунуться. С другой стороны, что-то мне подсказывает, что у тебя хватит сил и умения, чтобы с ними справиться.$B$BЕсли тебе хочется побеседовать с Сениром, то его можно отыскать в Караносе.$B$BА добраться туда и вовсе просто: пройдешь весь тоннель насквозь, а дальше шагай себе по дороге до самого до города Караноса.', },
    {'Доставьте донесение Грелина Сениру Белобороду в Караносе.', },
  },
  [421] = {
    'Проявить себя',
    {'Леди Сильвана поручила Вариматасу завоевать земли людей и дворфов к югу. $B$BНо этот идиот Аругал – даларанский шарлатан, а ныне проклятая тварь из крепости Темного Клыка – позволил своей бездумной магии нанести ущерб стратегической цитадели Серебряного бора. $B$BМне нужен кто-то, искушенный в воинском деле, чтобы разрубить узел, завязанный Аругалом. Убейте 5 белоголовов Лунной Ярости и докажите, что вы – тот, кого я ищу. Этих мерзких зверей легко можно встретить у самой дороги, внизу под холмом.', },
    {'Далар Ткач Рассвета из Гробницы хочет, чтобы вы убили 5 белоголовов Лунной Ярости.', },
  },
  [422] = {
    'Безрассудство Аругала',
    {'Для меня загадка, каким образом Аругал вообще был принят в Кирин-Тор. В искусстве магии он был совершеннейший невежда.$B$BСейчас важно точно выяснить, какую магию применил Аругал, чтобы я мог использовать ее против него и обеспечить владычество Темной Госпожи в Серебряном бору. $B$BВначале Аругал останавливался на пшеничной ферме, что к северу от моста. Один из стражей смерти сообщил о том, что видел какие-то книги с заклинаниями, но не сумел их заполучить. Добудь для меня ту из этих книг, что называется "Зелье Аругала".', },
    {'Раздобудьте книгу "Зелье Аругала" и принесите ее Далару Ткачу Рассвета в Гробницу.', },
  },
  [423] = {
    'Безрассудство Аругала',
    {'Изучив деятельность Аругала я понял, что мои наихудшие подозрения верны. Старый хрыч был недостоин даже ночные горшки выносить в Даларане, не говоря уж о том, чтобы представлять Кирин-Тор в самый трудный час. Идиоты! $B$BДля усиления своей никудышной магии Аругал использовал заколдованные предметы. В первую очередь я должен изучить эти предметы. Отправляйтесь в путь и убивайте обжор Лунной Ярости и чернозлобов Лунной Ярости, пока не наберете достаточного для моих исследований количества их заколдованных наручей. Эти злобные создания обитают к северу и к востоку отсюда.', },
    {'Принесите Далару Ткачу Рассвета в Гробницу 6 наручей обжор Лунной Ярости и 3 наруча чернозлобов Лунной Ярости.', },
  },
  [424] = {
    'Безрассудство Аругала',
    {'Мне потребуется больше времени на раскрытие темных тайн чар Аругала,чем я думал. $B$BНо тем временем мне нужно, чтобы вы помогли разрешить одну небольшую проблему, которую обнаружили наши разведчики. Похоже, Аругал распространил свою магию на Серебряный рудник, находящийся в холмах к юго-востоку отсюда. $B$BЭтот рудник стал бы ценным ресурсом для Вариматаса. $B$BЯ хочу, чтобы вы снесли голову с плеч пораженному скверной старшине рудника Мрачнону Белесому. После его смерти рудник будет нашим.', },
    {'Убейте Мрачнона Белесого и принесите его голову Далару Ткачу Рассвета в Гробницу.', },
  },
  [425] = {
    'Ивар Нечистый',
    {'Нам с моим братом Квинном было поручено провести рекогносцировку в Серебряном бору и отчитаться перед верховным палачом Хадриком. Но далеко продвинуться мы не сумели: когда мы проходили через эту ферму, нас атаковал вурдалак Ивар Нечистый. $B$BНам удалось уйти от Ивара, но он успел как следует приложить Квинна. Мой брат поправляется от зелья, что вы ему дали, но Ивар может вернуться раньше, чем Квинн совсем встанет на ноги. $B$BУбейте Ивара, чтобы Квинн мог спокойно поправляться. Обычно Ивар торчит в том амбаре – поджидает, когда я утрачу бдительность.', },
    {'Убейте Ивара Нечистого и принесите его голову Ран Йорик на делянку Ивара.', },
  },
  [426] = {
    'Нападение на мельницы',
    {'Плеть пытается основать базу на мельницах Агамондов. Если им это удастся, они продолжат свои нападения в Тирисфале.$B$BПоступил приказ уничтожить всю Плеть на мельницах.$B$BОтправляйся туда и собери зазубренные ребра костетрясов-солдат и костеголовых солдат, а также почерневшие черепа мрачнооких скелетов-заклинателей.$B$BЧтобы добраться до мельниц, иди по дороге на запад. После того как перейдешь мост, поверни на север, и скоро увидишь ветряные мельницы.', },
    {'Соберите 5 зазубренных ребер и 3 почерневших черепа для стража смерти Диллинджера в Брилле.', },
  },
  [427] = {
    'Война с Алым орденом',
    {'Документы, которые прислал палач Аррен, приблизят переломный момент в нашей битве с мерзким Алым орденом. Теперь мы знаем расположение всех их баз в Тирисфальских лесах.$B$BНо у стражей смерти есть заботы и поважнее: армия Короля-лича растет с каждой ночью. Нам нужен кто-то такой "инициативный", как ты, чтобы навсегда покончить с Алым орденом.$B$BДокажи мне, что $Gспособен:способна; служить Темной Госпоже: отправляйся на запад, к башне за усадьбой Соллиден, и убей 10 воинов из Алого ордена.', },
    {'Убейте 10 воинов из Алого ордена по просьбе палача Зуганда.', },
  },
  [428] = {
    'Пропавшие стражи смерти',
    {'Пару недель назад двое наших стражей смерти – Ран и Квинн Йорик, ушли на разведывательное задание в северный Серебряный бор. С тех пор от них не было никаких известий. $B$BОни могли стать жертвами Плети, но даже если это так, мне нужно подтверждение. Найдите этих стражей смерти. $B$BИх первой целью была разведка на полях в северной части Серебряного бора. Начните свои поиски там.', },
    {'Найдите стражей смерти Квинна Йорика и Ран Йорик.', },
  },
  [429] = {
    'Звериные сердца',
    {'Мой брат Квинн был тяжело ранен вурдалаком, Иваром Нечистым, и я не знаю, сможет ли он как следует поправиться без помощи магии. Хотя я сама и не фармацевт, но знаю, что наши аптекари могут изготавливать исцеляющие зелья из обесцвеченных сердец воргов. $B$BСобери такие сердца и отнеси их аптекарю Ренферрелу к Гробнице, на юг отсюда. А потом возвращайся ко мне с зельем. $B$BМножество воргов водится между этим местом и садом Мальдена к востоку отсюда.', },
    {'Соберите 6 обесцвеченных сердец ворга и принесите их аптекарю Ренферрелу в Гробницу.', },
  },
  [430] = {
    'Назад к Квинну',
    {'Вот зелье, которое должно помочь Квинну Йорику поправиться. $B$BА теперь, простите, мне нужно продолжить одно очень важное дело...', },
    {'Отнесите зелье Квинна Квинну Йорику на делянку Ивара, к северу от Гробницы.', },
  },
  [431] = {
    'Манящие свечи',
    nil,
    nil,
  },
  [432] = {
    'Проклятые трогги!',
    {'Эти проклятые Светом трогги перевернули все мое рабочее место! Ты только глянь, еще одну бочку пороха разворотили!$B$BОх, прямо руки чешутся спуститься к ним да головы поотрывать! Всех бы поубивал!$B$BНо здоровье не позволяет, да еще это потрясение и злость... Они ничего мне не оставили!$B$BЯ бы сам их перебил, да рука уже не так тверда! Иди в карьер и перебей грязных тварей! Я заплачу! Ты только задай им шороху, перебей их!', },
    {'Убейте по просьбе штейгера Камнелоба 6 головоломов из племени Каменной Челюсти в карьере Гол\'Болар.', },
  },
  [433] = {
    'Слуга народа',
    {'Будучи членом Сената и Лиги Исследователей, я взял на себя заботу об искоренении троггской заразы, наводнившей наши земли.$B$BОни ни за что ни про что разнесли карьер Гол\'Болар. Чем глубже мы копаем, тем больше их лезет наружу, уничтожая наше оборудование и выгоняя горняков из шахт. У нас нет выбора, кроме как истребить их как можно больше, отстроиться заново и вернуться к работе.$B$BЕсли вы поможете мне разобраться с троггами, я с радостью возмещу вам затраты времени.', },
    {'Убейте 10 костегрызов из племени Каменной Челюсти по просьбе сенатора Мера Каменная Реликвия из карьера Гол\'Болар.', },
  },
  [434] = {
    'Нападение!',
    {'Сейчас я переодену своего робота-шпиона, и он отправится на аудиенцию к Лесковару. Гвардия пропустит его без особых проблем.$B$BПока ты $Gсобирал:собирала; для меня материалы, я дал Марзону идейку, которую он непременно захочет обсудить с Лесковаром. Я даже подкинул ему пару ложных слухов.$B$BЛесковар отпустит свою охрану и будет дожидаться Марзона в саду. Начинай действовать, как только они заведут разговор о Братстве Справедливости. Убей обоих до возвращения гвардейцев! После этого поговори с Триасом.', },
    {'Спрячьтесь в саду и подслушайте разговор лорда Грегора Лесковара и Марзона Бесшумного Клинка. После этого убейте обоих и возвращайтесь к Иллингу Триасу в Штормград.', },
  },
  [435] = {
    'Сопровождая Эрланда',
    {'Волки там, снаружи, никуда не уйдут, и они злобно смотрят на меня каждый раз, когда я выхожу из двери. Должно быть, они невероятно голодны, раз смогли счесть меня пищей. $B$BЯ должен доложиться своей коллеге, стражу смерти Ран Йорик. Но чтобы добраться до Ран, мне нужна помощь против этих волков.$B$BПожалуйста, проводите меня! Волков здесь очень много, так что наши шансы будут выше, если у вас есть готовые помочь друзья...', },
    {'Проводите Эрланда, защищая его от волков, к Ран Йорик.', },
  },
  [436] = {
    'Раскопки Сталекрута',
    {'Геолог Сталекрут возглавляет раскопки в древних руинах к востоку от озера. В последнее время дело продвигается медленно – и это учитывая, сколько мы послали туда припасов!$B$BСталекрут – надежный, честный дворф, который прежде всего болеет за результат, потому-то я и думаю, что кто-то решил ему помешать.$B$BОтправляйся к раскопкам Сталекрута и обратись к Магмару Рудоколу. Он ведет отчетность по раскопкам и сможет объяснить, чем вызвана задержка.$B$BЧтобы отыскать раскопки, обогни озеро с юга и направляйся на восток.', },
    {'Обратитесь к исследователю Удару Топора.', },
  },
  [437] = {
    'Мертвое поле',
    {'Первые отчеты наших разведчиков в Серебряном бору говорят о том, что Гнилошкуры устроили базу на заброшенном поле к северу отсюда – на Мертвом поле. Мы посылали против них небольшие группы, но им каждый раз помогала банши Плеть Ночи, которая своей магией обращала наших воинов в бегство. $B$BМы хотим уничтожить Плеть Ночи. $B$BОтправляйтесь на Мертвое поле и атакуйте Гнилошкуров. Убивайте их до тех пор, пока не покажется Плеть Ночи, а затем убейте и ее. $B$BСущность банши обратится в прах. Принесите мне этот прах в доказательство ее смерти.', },
    {'Убивайте гноллов из стаи Гнилошкуров на Мертвом поле. $B$BКогда появится банши Плеть Ночи, убейте ее. $B$BСущность банши принесите верховному палачу Хадрику в Гробницу.', },
  },
  [438] = {
    'Старая переправа',
    {'$N, мы обнаружили еще одно место, где угнездилась Плеть: это старая переправа. Переправа находится к востоку от Гробницы, и ее патрулируют многочисленные гноллы-гнилошкуры и различная нежить. $B$BПолагаю, Плеть захватила это место неспроста. Отправляйся к старой переправе и выясни, в чем там дело.', },
    {'Отправляйтесь к старой переправе. $B$BПоищите причину присутствия там сил Плети.', },
  },
  [439] = {
    'Необычная находка',
    {'Из кучи трупов торчит обнаженная рука. Это рука женщины – тронутая чумой, но относительно свежая. $B$BНа пальце надето кольцо. Вы снимаете кольцо и, осмотрев его, обнаруживаете гравировку: $B$B"Делии".', },
    {'Принесите кольцо Делии верховному палачу Хадрику в Гробницу.', },
  },
  [440] = {
    'Кольцо с гравировкой',
    {'Если хотите найти мужа или возлюбленного Делии – того, кто подарил ей кольцо... поговорите с мировым судьей Севреном в Брилле. У него должны быть записи о недавних смертях и, если повезет, он сможет выяснить, кто такая Делия. $B$BНо я бы на это не рассчитывал. Тот, кто подарил ей кольцо, вероятно, уже мертв. Или, если он стал нежитью, то, возможно, ему больше нет дела до Делии.', },
    {'Отнесите кольцо Делии мировому судье Севрену в Брилл.', },
  },
  [441] = {
    'Роли, который живет в Подгороде',
    {'У Делии был муж по имени Роли, проживающий ныне в Подгороде. Если кольцо действительно принадлежало Делии, которая жила здесь, в Брилле, то Роли узнает его. $B$BНайти Роли вы можете в Торговом квартале Подгорода.', },
    {'Отнесите Роли кольцо Делии.', },
  },
  [442] = {
    'Атака на остров Фенриса',
    {'А теперь настало время для нападения на оплот силы Плети в Серебряном бору: остров Фенриса. $B$BТы снова $Gдолжен:должна; добраться до острова и уничтожить Тула Когтя Ворона. Мне нужна его голова!$B$BЯ посылаю тебя на смертельно опасное задание, но выполнить его – жизненно важная задача, $N. Ты $Gдолжен:должна; выйти $Gпобедителем:победительницей;!', },
    {'Принесите верховному палачу Хадрику в Гробницу голову Тула Когтя Ворона.', },
  },
  [443] = {
    'Лимфа Гнилошкуров',
    {'Прежде чем мы совершим нападение на остров Фенриса, мы должны понять, кто стоит за Гнилошкурами. Наши аптекари считают, что их особенности связаны со странной жидкостью, текущей по их жилам. $B$BОтправляйся на остров Фенриса, к востоку от старой переправы, и собери лимфу гноллов-гнилошкуров, которых вы там встретите. Образцы нужны аптекарю Ренферрелу, который стоит неподалеку от Гробницы.', },
    {'Принесите 8 бутылок лимфы Гнилошкуров аптекарю Ренферрелу рядом с Гробницей.', },
  },
  [444] = {
    'Происхождение Гнилошкуров',
    {'Я выпарил лимфу, которую ты $Gпринес:принесла; мне, и разложил ее на основные компоненты. Мои выводы еще не окончательные, но я определил, что для изготовления этой лимфы пользовались как чернокнижием, так и некромантией. $B$BПока я продолжу ее изучение, я прошу тебя отнести один образец Бетору Ледострелу в Подгород. Бетор – не фармацевт, но его знание магии может помочь нам раскрыть тайну Гнилошкуров.$B$BИди же. Бетора ты найдешь в Квартале Магов Подгорода.', },
    {'Отнесите образец лимфы Бетору Ледострелу в Подгород.', },
  },
  [445] = {
    'Доставка в Серебряный бор',
    {'Время – это непозволительная роскошь для тех, кто служит леди Сильване. Ты это, конечно, уже и $Gсам понял:сама поняла;.$B$BЯ состою в Королевском фармацевтическом обществе и в мои обязанности входит обмен знаниями с коллегами, чтобы однажды нашими совместными усилиями появилась новая чума, которую так желает получить Темная Госпожа.$B$BОтнеси эти находки аптекарю Ренферрелу – его можно найти в Гробнице, это поселение в Серебряном бору. Если идти из Брилла – то на юг, а если из Подгорода – на юго-запад.', },
    {'Отнесите находки аптекаря Юхана аптекарю Ренферрелу в Серебряном бору.', },
  },
  [446] = {
    'Тул Коготь Ворона',
    {'Эта лимфа была создана моим давним коллегой, Тулом Когтем Ворона. До великой войны он был молодым и многообещающим магом, но когда вошел в силу Король-лич, Тул предал своих братьев и перешел на сторону Плети. $B$BЯ и не думал, что он все еще жив, и уж тем более – что он возглавляет набеги Гнилошкуров на нас! $B$BЯ знаю многие из его заклинаний и могу помочь сварить зелье для того, чтобы им противостоять. Отнеси этот свиток обратно к аптекарю Ренферрелу. Там содержатся заклятья, необходимые для изготовления зелья.', },
    {'Отнесите свиток Бетора аптекарю Ренферрелу в Серебряный бор, в Гробницу.', },
  },
  [447] = {
    'Рецептура смерти',
    {'Силы Артаса неисчислимы. Но, создав новую чуму, мы могли бы раз и навсегда покончить как с армией Плети, так и с людьми. $B$BМои исследования показали, что паучья кровь в соединении с токсином, извлеченном из сердца бешеного медведя, дает смертельно опасный эликсир. Собери необходимые субстанции с пауков из Струящейся Тьмы к северо-западу отсюда и с медведей, бродящих по Серебряному бору. Принеси реагенты в Подгород аптекарю Фаранеллу из Королевского фармацевтического общества.', },
    {'Соберите 6 сердец медведя и 6 образцов Летящей крови и принесите их опытному аптекарю Фаранеллу в Подгород.', },
  },
  [448] = {
    'Доложиться Хадрику',
    {'Прежде чем вы нападете на остров Фенриса, доложитесь верховному палачу Хадрику. У него может быть дополнительная информация о том, что вам понадобится.', },
    {'Доложитесь верховному палачу Хадрику в Гробнице.', },
  },
  [449] = {
    'Отчет стражей смерти',
    {'Хотя раны Квинна заживают, я хотела бы доставить наш отчет верховному палачу Хадрику как можно скорее. В нем содержится информация, которая жизненно важна для захвата Серебряного бора Отрекшимися.', },
    {'Отнесите отчет стражей смерти верховному палачу Хадрику в Гробницу.', },
  },
  [450] = {
    'Рецептура смерти',
    {'Вы можете еще послужить Королевскому фармацевтическому обществу, $N. $B$BКогда Аругал навел на лес свою никчемную магию, он наложил проклятие на одного из наших самых многообещающих сотрудников, аптекаря Берарда. Ставший безвольным слугой Аругала, теперь Берард для нас бесполезен. Но его находки могут быть ценными. Он изучал древнюю чуму, давным-давно погубившую всякую жизнь в озере Лордамер. $B$BРаздобудьте записи Берарда из его кабинета в таверне деревни Погребальных Костров и принесите аптекарю Ренферрелу.', },
    {'Найдите в деревне Погребальных Костров записи Берарда и отнесите их в Гробницу аптекарю Ренферрелу.', },
  },
  [451] = {
    'Рецептура смерти',
    {'Согласно записям Берарда, мох озерных тихоходов и озерных ползунов может оказаться полезным для решения нашей задачи по изготовлению новой отравы. Эти твари обитают на островах на озере Лордамер на востоке. $B$BОн также упоминает об очень редкой субстанции – твердой опухоли, обнаруженной им у мурлока из племени Злобного Плавника. Очевидно, эта опухоль столь редка, что Берарду удалось раздобыть лишь одну. $B$BСоберите образцы моха, а также редкую твердую опухоль и отнесите их аптекарю Фаранеллу в Подгород.', },
    {'Принесите 6 образцов мха озерного ползуна, 6 образцов мха озерного тихохода и твердую опухоль опытному аптекарю Фаранеллу в Подгород.', },
  },
  [452] = {
    'Засада в деревне',
    {'Ты очень вовремя $Gдобрался:добралась; сюда, $N! $B$BСовет деревни Погребальных Костров вернется в любой момент. Аругалу же есть чем заняться в крепости. Он использует Совет как марионеток, чтобы держать лишенных разума и воли приспешников в деревне под своим полным контролем. Но я уверена, что без руководства Совета деревни Погребальных Костров сыны Аругала взбунтовались бы против своего предводителя. $B$BЯ не могу справиться со всем в этим в одиночку. Мне нужна твоя помощь. Вместе мы сможем погубить Cовет к вящей славе Леди Сильваны.', },
    {'Помогите стражу смерти Фарлии уничтожить Совет деревни Погребальных Костров.', },
  },
  [453] = {
    'Поиски таинственного незнакомца',
    {'Удачи, $N. Надеюсь, ты найдешь этого парня, причем живым.$B$BСбежав за пределы Темнолесья, он мог пасть жертвой диких зверей.', },
    {'Отыщите незнакомца. Его приметы:$B$BОн родом не из Темнолесья.$B$BОн – нервный тип.$B$BОн сбежал из Темнолесья и двинулся на запад.', },
  },
  [454] = {
    'После засады',
    {'До этого... случая мы все время таскали туда-сюда бочки с порохом. Теперь придется поделить работу: Миран понесет часть бочек, а я присмотрю за остальными.$B$BМне не хочется отпускать Мирана одного. Когда он соберется в путь, проводи, пожалуйста, его.', },
    {'Поговорите с Мираном.', },
  },
  [455] = {
    'Альгазская битва',
    {'Орки из клана Драконьей Пасти бесчинствуют по всей Болотине. А сейчас они двинулись на Дун Альгаз! Нельзя допустить, чтобы они здесь закрепились, иначе Лок Модан будет отрезан от Гавани Менетилов, нашего порта и крепости в Болотине.$B$BПопытайся прорваться сквозь Дун Альгаз в Болотине и убей столько орков, сколько сумеешь! Чем больше, тем лучше! Об исполнении доложишь Валстагу Сталезубу в Гавани Менетилов.$B$BНайти Менетилов несложно: пройдешь через Дун Альгаз, потом – по дороге на север, а на развилке свернешь на запад.$B$BМы очень надеемся на тебя, $N. Не подведи нас.', },
    {'Пройдите через Дун Альгаз.$B$BУбейте 8 разведчиков из клана Драконьей Пасти и 6 рубак из клана Драконьей Пасти.$B$BОб исполнении доложите Валстагу Сталезубу из Гавани Менетилов.', },
  },
  [456] = {
    'Природное равновесие',
    {'Мои приветствия, $N. Я – Опекун Илталайн. Здесь, в Тенистой долине, я слежу за поддержанием природного равновесия. $B$BВесенние дожди в этом году были очень сильными, и от этого одни лесные существа благоденствуют, а другие страдают.$B$BК несчастью, ночные саблезубы и щетинистые вепри слишком расплодились. Тенистая долина не может их прокормить. $GЮный:Юная:c; |3-6($C), помоги нам – сократи численность вепрей и саблезубов ради сохранения природного равновесия.', },
    {'Убейте 4 молодых ночных саблезубов и 4 молодых щетинистых вепрей и вернитесь к опекуну Илталайну.', },
  },
  [457] = {
    'Природное равновесие',
    {'Сокращение численности молодых особей, обитающих в Тенистой долине – это неплохо для начала, $N, однако нужно выполнить еще некоторую работу.$B$BЛесные ресурсы истощатся слишком быстро, если не решить эту проблему. Охота на диких зверей – это зло, необходимое для блага всех тех, кто обитает в этих землях. Отправляйся в лес и убивай облезлых ночных саблезубов и щетинистых вепрей ради равновесия.', },
    {'Опекун Илталайн хочет, чтобы вы убили 5 облезлых ночных саблезубов и 5 щетинистых вепрей.', },
  },
  [458] = {
    'Защитница леса',
    {'Какая отрада, что ты здесь, |3-6($C). Шепот лесных духов донес до моего слуха странные вести.$B$BТаинственная защитница леса, Тариндрелла, вернулась в Тенистую долину. Много лет эту дриаду не видели в лесах Калимдора. Что-то определенно не так, если она вновь появилась в этих землях. $B$BНайди Тариндреллу и узнай, что привело ее в наши рощи. Кто-то из Стражей сообщил о том, что ее видели к юго-западу от Альдрассила.', },
    {'Найдите дриаду по имени Тариндрелла.', },
  },
  [459] = {
    'Защитница леса',
    {'Непонятное зло затаилось в лесу Тельдрассила. Взгляни на холмы, где некогда обитали мирные фурболги. Они покинули свои дома и объединились в племя Кривой Сосны.$B$BЛишь порча, вызванная зловещим мхом Скверны, могла быть причиной подобного изменения. Грелли и греллиды кишат в округе, представляя собой угрозу для жителей Тенистой долины. $B$BНапади на этих бесов, $N, и узнай, действительно ли они подпали под чары зловещего мха Скверны.', },
    {'Соберите 8 порций мха Скверны и принесите их Тариндрелле.', },
  },
  [460] = {
    'В целости и сохранности',
    {'На вид ты не похож на гнилошкура. Это хорошо. А то я уже потерял надежду... $B$BМеня зовут Аларик, когда-то я был доверенным слугой и телохранителем Тула. Он использовал свою магию для "защиты" меня от Чумы Нежити, но вместо этого его заклинание повергло меня в нежитие – состояние, из которого я не могу уйти. А после создания этих проклятых гнилошкурых гноллов Туле обезглавил меня... и отдал гноллам мою голову в качестве игрушки! $B$BПожалуйста, воссоедини меня с телом. Я уверен, что гноллы закопали его в Крепости Фенриса, возле стойл, в ничем не примечательной могиле.', },
    {'Отнесите голову Аларика на его могилу.', },
  },
  [461] = {
    'Замаскированный тайник',
    {'Я знаю об одной тайной нише в крепости, где хранится дорогой сердцу Тула и его давних коллег предмет. В ней также лежит нечто, что вы можете счесть полезным для себя. Возьмите меня туда! $B$BУглубление в стене находится в юго-западной башне крепости, позади пыльной полки. $B$BВозьмите мое тело, а сапоги оставьте здесь. Они мне все равно не по ноге...', },
    {'Отнесите Аларика к замаскированной нише.', },
  },
  [463] = {
    'Страж Природы',
    {'Ты ведь $Gслышал:слышала; рассказы о Зеленом Страже? Я даже подозреваю, что ты и разыскать его хочешь. Если так, то скажу тебе, что это чистое безумие! Впрочем, не мне тебя отговаривать: хочешь найти верную смерть – пожалуйста, милости просим.$B$BОн живет на болотах, к востоку от развилки на дороге, ведущей в Дун Модр. Рыщет там на пару с кроколисками... и прочими тварями.$B$BОставь-ка свои денежки мне – покойникам они все равно не нужны. Да и Зеленый Дядя может обломать зуб об твои золотые, когда будет перекусывать тебя надвое... Ты ведь не хочешь, чтобы бедняга потом мучился?', },
    {'Найдите Зеленого Стража.', },
  },
  [464] = {
    'Боевые знамена',
    {'К концу Второй войны клан орков Драконьей Пасти потерял власть над красными драконами и был почти уничтожен. Драконы припомнили им все, гнали бывших хозяев аж до до самого Грим Батола. Затем орки скрылись в горах к востоку от раскопа Вельгара.$B$BОтсидевшись в горах, они вновь набрали силу и теперь разбили лагерь к северо-востоку от Менетила.$B$BНапади на этот лагерь, внеси панику в ряды орков. В доказательство содеянного принеси мне боевые знамена клана.', },
    {'Принесите 8 боевых знамен клана Драконьей Пасти капитану Крепкому Кулаку.', },
  },
  [465] = {
    'Гамбит Нек\'роша',
    {'Пока ты $Gсражался:сражалась; с кланом Драконьей Пасти, мы получили сведения об их планах.$B$BНек\'рош строит катапульты и планирует взять Менетил в осаду! А у нас тем временем вовсю идет битва при Дун Альгаз, и подкрепления ждать неоткуда!$B$BУничтожь катапульты, прошу тебя. Они стоят в лагере Драконьей Пасти.$B$BВозьми вот этот трут. Пробейся к катапультам и подожги их.$B$BПосле этого найди и убей Нек\'роша. Этот паршивец уже свое отжил...', },
    {'Уничтожьте катапульты Драконьей Пасти.', },
  },
  [466] = {
    'Поиски огневита',
    {'Я слышал о редком виде руды – огневите. Его добывают в Болотине, в пещере к северо-западу от Врат Альгаза. Огневит очень неустойчив – чуть что, сразу взрывается. Однако я уверен, что если правильно его выплавить, то можно делать из него мощнейшие снаряды для пушек!$B$BРаздобудь для меня огневитовую руду, а я пока поищу добровольца, который согласится принять участие в опытах.$B$BАх да, чтобы добыть руду, тебе нужны познания в горном деле. Если у тебя нет необходимых знаний, то... отправь в шахту кого-нибудь из знакомых рудокопов!', },
    {'Принесите 6 партий огневитовой руды пилоту Камнекожицу в Дун Морог.', },
  },
  [467] = {
    'Найдите Камнекожца',
    {'Пилот Камнекожиц любит свою осадную машину, как и все пилоты осадных машин! И он всегда рад найти новый способ повысить огневую мощь своих орудий.$B$BДо него дошли слухи о редкой руде, и он ищет кого-нибудь, кто добудет ее. И если тебе уже не терпится взяться за это дело, то найди Камнекожица в поселке Сталежара в Дун Мороге и спроси, чего он хочет.', },
    {'Поговорите с пилотом Камнекожицем.', },
  },
  [468] = {
    'Отчитайтесь перед горным пехотинцем Рокхаром',
    {'Горный пехотинец Рокхар ищет добровольцев для похода через Дун Альгаз в Болотину!$B$BЕсли желаете омыть сапоги в болотной жиже и снискать приключений на свою голову – поговорите со Скальным Панцирником в Дун Альгаз. Это к северу отсюда, по дороге за сторожевой башней.', },
    {'Поговорите с горным пехотинцем Рокхаром.', },
  },
  [469] = {
    'Ежедневная доставка',
    {'Привет тебе, $Gстранник:путешественница;!$B$BНе лежит ли твой путь в сторону Менетила? Если нет, то знай, что там можно передохнуть, сделать покупки и пропустить пару пинт. Прелестный городок!$B$BНу а если ты как раз туда и направляешься, то я бы хотел передать с оказией стопку кроколисковых шкур для кожевника Джеймса Халлорана.$B$BЯ бы и сам отнес их, но дома последнее время то свара, то скандал. Не хочу попадаться жене на глаза.', },
    {'Отнесите стопку кроколисковых шкур кожевнику Джеймсу Халлорану в Гавань Менетилов.', },
  },
  [470] = {
    'Прожорливый слизнюк',
    {'Каждую неделю я хожу к Гробнице Железноборода, и в этот раз все было как всегда, но вдруг на меня напали омерзительные хлюпающие слизнюки! Я так растерялась...$B$BЯ бросила в одного из них сумку, но он взял и засосал ее! Мне едва хватило времени, чтобы сбежать.$B$BА в сумке-то мои вещи!$B$BЯ даже не знаю, какой из этих гадов ее сожрал – они все одинаковые! Гробница Железноборода находится к северо-востоку отсюда, возле Дун Модра. Спасибо, что $Gсогласился:согласилась; помочь!', },
    {'Отнимите сумку Сиды у неизвестного слизнюка возле гробницы Железноборода и верните ее хозяйке в Гавань Менетилов.', },
  },
  [471] = {
    'Тяжкая судьба помощника',
    {'Недавно я отправил помощника на болото за шкурами гигантских болотных кроколисков. Он не вернулся обратно, и я отправился на поиски... Скажем так: зрелище было не для слабонервных.$B$BЛекарь говорит, что мальчик выживет, и слава богу, но у меня-то работа стоит!$B$BЕсли у тебя есть желание поохотиться на кроколисков, я препятствовать не стану – особенно после того, что они сделали с моим помощником. Кроколиски водятся на побережье к северу отсюда, за поселением мурлоков.', },
    {'Принесите 6 шкур гигантских кроколисков Джеймсу Халлорану в Гавань Менетилов.', },
  },
  [472] = {
    'Падение Дун Модра',
    {'Дворфы Черного Железа захватили Дун Модр!$B$BЯ тяжело ранен, |3-6($C). Почти все мои однополчане погибли! Подонки из клана Черного Железа набросились на нас, не успели мы оправиться после взрыва.$B$BНаш командир, Длиннокос, протрубил сигнал к отступлению. Во время бегства мне в спину попал топорик. Я потерял сознание...$B$BОчнулся уже в Менетиле, но меня волнует судьба остальных солдат. Надежда умирает последней... Может быть, Длиннокос тоже спасся! Иди в Дун Модр и отыщи его, |3-6($R)!', },
    {'Отыщите Грима Длиннокоса возле Дун Модра.', },
  },
  [473] = {
    'Доклад капитану Крепкому Кулаку',
    {'$N. Орки из клана Драконьей Пасти совсем озверели. Им уже мало одного Альгаза. Капитан Крепкий Кулак просил направлять к нему всех, кто готов оказать нам содействие в войне против орков. Он сейчас в крепости, в зале Войны.', },
    {'Найдите капитана Крепкого Кулака и поговорите с ним.', },
  },
  [474] = {
    'Убить Нек\'роша',
    {'Катапульты сожжены. Теперь найди и убей вождя Нек\'роша.', },
    {'Убейте вождя Нек\'роша.$B$BПринесите голову Нек\'роша капитану Крепкому Кулаку.', },
  },
  [475] = {
    'Ветер тревоги',
    {'Ветер тревоги веет над лесом.$B$BГэролас Талветрен служит Великим смотрителем за спящими друидами-воронами в обители Бен\'этиль. Его долг – обеспечивать их безопасность, дабы их уговор с Изерой оставался в силе.$B$BНо вести от Гэроласа что-то задерживаются, и я начинаю волноваться. Отправляйся на восток в Деревню Звездного Ветра – мне нужен отчет Гэроласа, дабы я был спокоен, узнав, что мои братья и сестры спят спокойно.', },
    {'Найдите Гароласа Талветрена в Деревне Звездного Ветра.', },
  },
  [476] = {
    'Порча Кривой Сосны',
    {'Племя Кривой Сосны поражено порчей!$B$BЭти некогда мирные фурболги обратились против покровителей леса. Они подстерегли меня в засаде, когда я выходил из обители Бен\'этиль и разграбили Деревню Звездного Ветра. Их вождь, Урсал Изверг, затмевает их разум при помощи мха Скверны.$B$BЯ слишком изранен, чтобы донести до Атридаса эти страшные вести. Теперь это придется сделать тебе, $Gюный:юная:c; |3-6($C). Остается только надеяться, что безумные фурболги Кривой Сосны еще не уничтожили Бен\'этиль!', },
    {'Вернитесь к Атридасу Медвежьей Шкуре в Доланаар.', },
  },
  [477] = {
    'Пересечение границ',
    {'Как вы, должно быть, знаете, в деревне Янтарная Мельница окопалась враждебная группа людей. Она держится там в основном за счет поддержки, которую они получают от волшебников Даларана. $B$BЯ не знаю, что они замышляют, но сам по себе интерес, проявленный ими к захолустной деревушке вроде Янтарной Мельницы, показывает, что она имеет немаленькое значение. $B$BОни отправляют повозкой из Хилсбрада сундуки. Многое из привезенного складируется в небольшом лагере к северу от деревни Погребальных Костров. Раздобудьте содержимое одного такого сундука и принесите мне.', },
    {'Раздобудьте содержимое одного из сундуков даларанских волшебников. Деревня Погребальных Костров находится на юге.', },
  },
  [478] = {
    'Карты и руны',
    {'*Вы быстро вытаскиваете подвеску из сундука и кладете крышку на место.*', },
    {'Отнесите странную подвеску темному жрецу Алистеру в Гробницу.', },
  },
  [479] = {
    'Расследование в Янтарной Мельнице',
    {'Далар пытается выяснить, каков источник магической силы этих волшебников. А пока что нам нужно замедлить их прогресс любым путем... $B$BМаги, окудники и заступники, несомненно, носят такие подвески. Добудьте их. $B$BЧтобы добраться до Янтарной Мельницы, идите на юг по главной дороге, а на развилке сверните на восток.', },
    {'Добудьте 8 подвесок Даларана и отнесите темному жрецу Алистеру в Гробницу.', },
  },
  [480] = {
    'Ткач',
    {'Верховный маг огромной силы отвечает за реактивацию энергетического узла. Они уже далеко зашли, так что единственный способ их остановить – это убить его. Это будет тяжело – безусловно, его защищают многочисленные маги и охранники. $B$BВам надо пойти и разобраться с ним, быстро. Когда он будет мертв, принесите мне его посох. Идите немедленно.', },
    {'Убейте даларанского верховного мага, заберите его посох и отнесите темному жрецу Алистеру в Гробницу.', },
  },
  [481] = {
    'Анализ Далара',
    {'Отнесите подвеску Далару Ткачу Рассвета – может быть, он сможет пролить какой-то свет на планы волшебников.', },
    {'Отнесите подвеску рунической надписи Далару Ткачу Рассвета в Гробницу.', },
  },
  [482] = {
    'Планы Даларана',
    {'Конечно! Как же я мог забыть?$B$BЯнтарная Мельница была одним из мест, отмеченных магами Кирин-Тора, и примечательна тем, что на территории деревни находится спящий энергетический узел. Волшебники, должно быть, планируют реактивировать узел и использовать его энергию для какой-то более серьезной цели. $B$BМы не можем позволить этому случиться... $B$BЧтобы активировать энергии этого места, понадобится много сил, и мы можем осложнить им задачу, стащив у волшебников подвески. $B$BПередайте эту информацию Алистеру, он спланирует дальнейшие действия.', },
    {'Поговорите с темным жрецом Алистером в Гробнице.', },
  },
  [483] = {
    'Реликвии Пробуждения',
    {'Захватчиков Кривой Сосны видели в обители Бен\'этиль на западе, они крушили там все подряд.$B$BСпящие друиды никогда не смогут выйти из Изумрудного Cна – они даже не понимают, в какую западню угодили, – если мы только им не поможем. Тонкий ритуал спячки нельзя нарушить без реликвий Пробуждения.$B$BОтправляйся в обитель и найди талисман Когтя Ворона, Черное перо, Небесный сапфир и руну Размещения. Друиды хранят их в священных ларцах. Принеси их мне, и я подготовлю ритуал пробуждения.', },
    {'Добудьте реликвии Пробуждения и принесите их Атридасу Медвежьей Шкуре в Доланаар.', },
  },
  [484] = {
    'Шкуры молодых кроколисков',
    {'Ты, наверное, $Gслышал:слышала; обо мне. Я Джеймс Халлоран, кожевник. Выделанные мною кроколисковые шкуры расходятся по всему континенту!$B$BУже $Gвстречался:встречалась; с Эйнаром? Он – один из охотников, приносящих мне шкуры.$B$BВ последнее время у меня осталось мало мягких шкур, а их можно снять только с молодых кроколисков.$B$BУчитывая обстоятельства, я готов заплатить высшую цену за стопку шкур. Молодые кроколиски водятся на болотах прямо за городом.', },
    {'Принесите 4 шкуры молодых кроколисков Джеймсу Халлорану в Гавань Менетилов.', },
  },
  [485] = {
    'Найти КПХ-9/HL!',
    {'Вы обнаружили странный металлический предмет яйцевидной формы. Когда вы нажали на какую-то кнопку, яйцо вдруг ожило, открылось и превратилось в непонятный механизм, который проскрежетал механическим голосом:$B$B"Меня зовут Карлс Пропеллерз, и мой автономный робот поврежден. Просьба найти робота, вставить в него этот маяк, а потом вернуть мне за вознаграждение".$B$B"Я вычислил координаты робота – он попал в аварию в устье реки близ Осклизлой скалы".', },
    {'Отнесите аварийный маяк к автономному роботу Карлса. Робот находится во Внутренних землях, в устье реки близ Осклизлой скалы.', },
  },
  [486] = {
    'Урсал Изверг',
    {'Настало время спасти друидов-воронов. Если сейчас мы потерпим неудачу, $N, их сон станет вечным.$B$BЯ подготовлю реликвии Пробуждения и совершу обряд. Но чтобы у меня все получилось, тварь, виновная в их злоключениях, должна быть уничтожена. Только тогда ритуал может быть выполнен.$B$BЭто Урсал Изверг разрушил дело наших братьев, и именно он должен теперь заплатить за свои злодеяния ради их освобождения. Отправляйся в Лагерь у Кривой Сосны на юго-западе и убей его.', },
    {'Убейте Урсала Изверга и возвращайтесь к Атридасу Медвежьей Шкуре в Доланаар.', },
  },
  [487] = {
    'Дорога в Дарнас',
    {'Дорогу в Дарнас нужно охранять. Путники, направляющиеся из Доланаара в Дарнас, рассказывают о варварских нападениях пораженных порчей фурболгов из племени Кривой Сосны.$B$BКаждый день из Дарнаса и в Дарнас поступают грузы и важные сообщения. Мы не можем допустить, чтобы шайка этих мерзостных тварей угрожала путникам.$B$BВозьмись же за оружие во имя священного леса, |3-6($C). Их логово находится где-то под этим наблюдательным пунктом. Убей 6 душителей из племени Кривой Сосны и возвращайся ко мне.', },
    {'Убейте 6 душителей из племени Кривой Сосны и найдите часового Амару Ночную Странницу возле Доланаара.', },
  },
  [488] = {
    'Просьба Зенна',
    {'Вижу, ты работенку ищешь? Хе-хе, ни дня не проходит без того, чтобы я не поручил какое-нибудь дельце $Gнаивному юнцу:наивной девчушке;... вроде тебя.$B$BВидишь ли, $N, я могу осчастливить тебя такими вещицами, что тебе и не снились. Но за это ты кое-что мне принесешь.$B$BМне часто бывают нужны определенные... реагенты. Принеси 3 клыка ночного саблезуба, 3 пера островной совы и 3 мотка паутины чащобного тенетника.$B$BИ пусть это все останется между нами, |3-6($R).', },
    {'Принесите Зенну Грязному Копыту, стоящему недалеко от Доланаара, 3 клыка ночного саблезуба, 3 пера островной совы и 3 мотка паутины чащобного тенетника.', },
  },
  [489] = {
    'Искупление вины',
    {'Совет Леса узнал, что ты $Gпомог:помогла; Зенну Грязному Копыту! Этот сатир – враг леса! И ты, как |3-6($R), $Gдолжен бы:должна бы; понимать, что оскверняешь лес, убивая созданий Природы!$B$BТы $Gдолжен:должна; искупить свою вину перед Советом, если хочешь оставаться другом Тельдрассилу! $B$BПреподай Грязному Копыту урок – и заслужишь прощение. Есть такие шишки Скверны – это семена, падающие с деревьев. Они испускают зеленый дым.$B$BСкорми несколько штук Грязному Копыту. Он подумает, что ему предлагают что-то вкусненькое.', },
    {'Соберите 3 шишки Скверны и отдайте их Зенну Грязному Копыту близ Доланаара.', },
  },
  [491] = {
    'Жезл для Бетора',
    {'Туле как-то рассказывал мне, что давным-давно он вместе с двумя товарищами создал магический жезл, сплетя его из трех принадлежащих им ученических жезлов. Он был символом их дружбы, и, хотя Тул презрел ее, с жезлом он расстаться не мог и не желал отдавать его другим. Поэтому он выкрал жезл и спрятал его в крепости Фенриса.$B$BЗдесь. Отнесите жезл Бетору Ледострелу, одному из прежних друзей Тула. Я уверен, он будет рад его получить.', },
    {'Отнесите Сплетенный жезл Бетору Ледострелу в Квартал Магов Подгорода.', },
  },
  [492] = {
    'Новая чума',
    {'Один из стражей смерти сообщил, что недавно был захвачен еще один дворф-горный пехотинец. Обычно стражи смерти сажают пленных в погреб таверны "Петля Висельника", где им оказывается "достойный прием".$B$BПочему бы тебе не пойти и не угостить пленника специальным напитком, который я приготовил? В нем есть небольшая примесь того, что Темная Госпожа готовит для всего Азерота.', },
    {'Отнесите особый напиток Юхана пленному горному пехотинцу.', },
  },
  [493] = {
    'Путешествие в предгорья Хилсбрада',
    {'Темная Госпожа давит на Королевское фармацевтическое общество, чтобы оно поскорее разработало Новую чуму. Мы упорно трудимся и добились значительного прогресса. Мы уверены, что успех был бы достигнут значительно быстрее, если бы мы могли распространять информацию среди членов общества. $B$BПоэтому я хотел бы, чтобы вы доставили мои последние находки аптекарю Лидону в Мельницу Таррен – небольшой городок, расположенный в предгорьях Хилсбрада. Путь туда неблизкий. $B$BНаправляйтесь на юг и держитесь поближе к дорогам. Строго следуйте дорожным указателям!', },
    {'Отнесите находки аптекаря Ренферрела аптекарю Лидону в деревню Мельница Таррен в предгорьях Хилсбрада.', },
  },
  [494] = {
    'Время нанести удар',
    {'Молчи и слушай, |3-6($C).$B$BМеня направили сюда для наблюдения за Хилсбрадом. У меня разведывательная миссия. А тебе необходимо срочно отнести вести верховному палачу Дарталии в Мельницу Таррен. Скажи, что страж смерти Леш передала следующие слова:$B$B"Крик ворона на западе манит."$B$BСтупай по дороге на восток и обращай внимание на знаки. Торопись! Ступай в Мельницу Таррен со всей возможной поспешностью!', },
    {'Отправляйтесь в Мельницу Таррен и доставьте послание стража смерти Леш верховному палачу Дарталии.', },
  },
  [495] = {
    'Корона Воли',
    {'Муг\'тола и его отряд огров отправили в долину Альтерака за короной Воли для леди Сильваны. К сожалению, вместо того чтобы принести ее королеве, этот идиот решил ее примерить... наверное, огры не могут устоять перед блестящими побрякушками.$B$BЕго необдуманный поступок привел к неприятным последствиям. Корона Воли позволила Муг\'толу воспротивиться заклинанию подчинения, наложенному на него леди Сильваной.$B$BТемная Госпожа хочет, чтобы это стало уроком для остальных. Отправляйся на мельницу Таррен в Хилсбраде и поговори с лейтенантом Мелизарой.', },
    {'Поговорите с Мелизарой в Мельнице Таррен в предгорьях Хилсбрада.', },
  },
  [496] = {
    'Эликсир страдания',
    {'Ах, еще один мерзкий день на мельнице Таррен. От этого чистого воздуха у меня сразу портится настроение, $N.$B$BЧем скорее мы заразим здешних людей, тем лучше. Я провожу исследования различных смертоносных веществ, чтобы использовать их в моих составах, но собрать все времени не хватило.$B$BЕсли ты хочешь принести пользу, добудь для меня следующее: 10 языков серых медведей и очень редкую и труднодоступную лимфу паука-охотника. Медведи и пауки-охотники живут рядом с мельницей Таррен.', },
    {'Аптекарь Лидон из Мельницы Таррен попросил принести ему 10 языков серых медведей и немного лимфы паука-охотника.', },
  },
  [498] = {
    'Спасение',
    {'Вождь послал нас с ребятами, чтобы добыть кое-что из Дарнхольда и разведать, что представляет собой так называемый Синдикат.$B$BМы пошли к Дарнхольду, поглядели, что к чему, тут на нас и напал большой отряд людей. Мы бились изо всех сил, но они сильно превосходили нас числом. В конце концов нескольких из нас взяли в плен, но мне удалось сбежать.$B$BДарнхольд находится на юго-востоке отсюда, по ту сторону реки. Там держат Друлла и Тог\'тара, освободи их.', },
    {'Освободите Друлла и Тог\'тара по поручению Круска из Мельницы Таррен.', },
  },
  [499] = {
    'Эликсир страдания',
    {'Мне понадобится совсем немного времени для создания того, что я хочу наречь эликсиром Страдания.$B$BВот, готово! Ах, ничто не доставляет столько радости в этих проклятых холмах, как принесение страдания в мир.$B$BПосмотрим, достигнут ли желанный эффект.$B$BДай попробовать моему помощнику Ампи то, что Темная Госпожа готовит людям и созданиям Плети.', },
    {'Дайте Ампи эликсир Страдания.', },
  },
  [500] = {
    'Награда Раздробленного Хребта',
    {'Огры из клана Раздробленного Хребта раскопали курган в Альтеракских горах, неподалеку от разрушенного города. Разведчики доносят, что они и сами руины захватили.$B$BМы не дадим им там вольготно расположиться; если они решат, что хорошо устроились, то следующим шагом спустятся с холмов и окажутся прямо рядом с нами!$B$BСтупай на север, в Альтеракские горы и убей побольше огров. Принеси мне грязные кости, которые они таскают с собой, и получишь неплохую награду.', },
    {'Соберите 9 грязных костей у огров из клана Раздробленного Хребта в Альтеракских горах. Принесите их маршалу Редпату в Южнобережье.', },
  },
  [501] = {
    'Эликсир Боли',
    {'О, $N... Луга Хилсбрада полны цветов, воздух свеж и приятен. Меня от этого просто тошнит! Я смотрю в мутное окно и мечтаю о наступлении дня, когда Новая чума принесет в мир заслуженную смерть!$B$BНо как бы мне ни хотелось сидеть и мечтать – прежде всего приказы Темной Госпожи. Помоги мне! В результате исследований я обнаружил, что из крови горного льва можно создать великолепный напиток смерти. Принеси мне немного крови, и мы повеселимся.', },
    {'Принесите 10 сосудов крови горного льва аптекарю Лидону из Мельницы Таррен.', },
  },
  [502] = {
    'Эликсир Боли',
    {'Ах, ничто так не радует, как запах свежей крови в предгорьях!$B$BСмешаем капелюшку этого и щепотку того, и пойдет веселье! Из этой крови я сделаю эликсир Боли. Если мои вычисления верны, то этот состав порадует леди Сильвану. Надо его испытать.$B$BОтнеси этот эликсир на самую северную ферму Хилсбрадских полей, что на юго-западе. Посмотрим, как мое "угощение" понравится собачке фермера Рея, Стэнли.', },
    {'Напоите Стэнли эликсиром Боли.', },
  },
  [503] = {
    'Гол\'дир',
    {'Судя по добытым тобой документам, Гол\'дира держат на большой базе Синдиката, расположенной на севере, вероятно, в Альтеракских горах. Он уж очень давно томится там в плену, пора освободить его.', },
    {'Освободите Гол\'дира и возвращайтесь к Круску в Мельницу Таррен.', },
  },
  [504] = {
    'Наемники клана Раздробленного Хребта',
    {'Ты уже $Gпомерился:померилась; силой с ограми Раздробленного Хребта, а теперь я хочу нанести им настоящий удар…$B$BСтупай в руины Альтерака и отыщи там наемников из клана Раздробленного Хребта. Убей побольше этих тварей – только так им можно показать, что лучше держаться подальше от земель Альянса.', },
    {'Убейте 10 солдат-наемников из клана Раздробленного Хребта и возвращайтесь к маршалу Редпату в Южнобережье.', },
  },
  [505] = {
    'Убийцы из Синдиката',
    {'Меня недавно назначили мировым судьей в Южнобережье. Моего предшественника убили, и виновников так и не нашли. Впрочем, расследование показало, что скорее всего их нанял Синдикат – группа воров под командованием злодеев-дворян, бывшей знати павшего королевства Альтерак.$B$BЛагеря Синдиката расположены на Возвышенности Соферы и на Кинжале Коррана, на севере и на западе от захваченной Ордой Мельницы Таррен. Убей всех союзников Синдиката, которых встретишь в этих лагерях.', },
    {'Убейте 12 преступников и 8 воров Синдиката и возвращайтесь к мировому судье Генри Малебу в Южнобережье.', },
  },
  [506] = {
    'Наследие Блэкмура',
    {'День или два назад ко мне пришел главарь этого Синдиката. Его звали Алиден Перенольд, и он нарек себя лордом этих земель. Он поверил мне свои планы о порабощении орков, выступивших против него, как и планировал его отец и учитель, ненавистный Блэкмур.$B$BКуда важнее то, что он привел с собой свою любовницу, девочку-тростинку по имени Элиза. У нее на шее висело ожерелье, который жаждет заполучить Вождь.$B$BСтупай, мне пора уходить, когда я закончу свои дела в лагере.', },
    {'Отнесите собранные Гол\'диром сведения Круску на мельницу Таррен.', },
  },
  [507] = {
    'Лорд Алиден Перенольд',
    {'Очевидно, что Перенольда необходимо убить, избавив мир от его гнусной сущности. Его поместье расположено к северу отсюда, на берегах озера Лордамер. Твой долг, $N, добыть медальон для Вождя и вернуться сюда.', },
    {'Убейте лорда Алидена Перенольда и допросите его любовницу Элизу насчет ожерелья Тареты.', },
  },
  [508] = {
    'Подарок Тареты',
    {'Ожерелье? Конечно же, можешь его забрать... Да, забирай! Мне не нужны воспоминания о нем... Просто оставь меня в покое...', },
    {'Верните ожерелье Тареты Круску в деревню Мельница Таррен, что в Предгорьях Хилсбрада.', },
  },
  [509] = {
    'Эликсир агонии',
    {'Новый день, новый эликсир. Если бы я тут командовал, я бы позволил Плети и жалким людишкам сцепиться между собой. Им и так нравится убивать друг друга.$B$BУвы, Темная Госпожа хочет ускорить процесс, так почему бы не скрасить унылые дни распространением смерти и болезней по стране.$B$BВот мой план: иди-ка ты во Владение Нетандера на юге от Мельницы Таррен и на востоке от реки и Южнобережья. Принеси мне 6 цветков Грязного Рыла для нового эликсира.', },
    {'Принесите 6 цветков Грязного Рыла аптекарю Лидону из Мельницы Таррен.', },
  },
  [510] = {
    'Зловещие планы',
    {'Среди документов вы находите список необходимых припасов. В их число входят самые простые вещи (еда, оружие, одежда) и несколько необычные (маски, масло, яд).$B$BВпрочем, более всего привлекает внимание срочность запроса – согласно документу, доставить все необходимое следует как можно скорее, чтобы "выполнить важную миссию, нацеленную против Южнобережья".', },
    {'Отнесите зловещие планы мировому судье Южнобережья Малебу.', },
  },
  [511] = {
    'Зашифрованное письмо',
    {'Одно из писем на столе зашифровано.', },
    {'Отнесите зашифрованное письмо хранителю мудрости Диббсу в Южнобережье.', },
  },
  [512] = {
    'Благородные смерти',
    {'Боюсь, что главари Синдиката жаждут гибели Южнобережья. Мы должны нанести удар первыми, пока они не успели воплотить свои планы.$B$BОтправляйся в руины Странбрада, в северной части Альтеракских гор. Мы полагаем, что именно там находится штаб-квартира Синдиката.$B$BНам известно, что эту шайку головорезов возглавляет бывшая знать Альтерака. Дворяне скрываются среди прочих посланников Синдиката, но отличить их можно по перстням-печаткам. Разумеется, прежде необходимо их убить.$B$BПринеси мне эти перстни.', },
    {'Принесите 7 альтеракских перстней-печаток мировому судье Малебу в Южнобережье.', },
  },
  [513] = {
    'Эликсир агонии',
    {'Я уже решил, что еще один день пройдет без капли радости, но тут ты $Gпринес:принесла; мне цветки Грязного Рыла.$B$BДля начала я приготовлю из них отличную смесь. Потом нам понадобится помощь мастера-аптекаря Фаранелла из Подгорода. Он должен добавить туда коллоид разложения и тогда-то самое веселье и начнется!$B$BВот возьми этот состав Грязного Рыла и немедленно отнеси его аптекарю Фаранеллу.', },
    {'Отнесите смесь из цветков Грязного Рыла опытному аптекарю Фаранеллу в Подгород.', },
  },
  [514] = {
    'Письмо Грозовой Вершине',
    {'У меня есть коллега из Стальгорна, который может расшифровать это письмо. Ступай в Библиотеку в Зале Исследователей Стальгорна и поговори с геологом Грозовой Вершиной. Скажи ему, что тебя прислал я и покажи письмо. Если кто и может прочитать это во всех землях к северу от пика Черной горы, то это он.', },
    {'Отнесите хитро зашифрованное письмо геологу Грозовой Вершине в Стальгорн.', },
  },
  [515] = {
    'Эликсир агонии',
    {'Смесь из цветков Грязного Рыла? Гениально! Почему мне не пришло это в голову?$B$BПожалуй, я добавлю свой коллоид разложения в эту хитрую смесь.$B$BЧтобы активировать отраву в смеси, Лидону понадобится крепкое зелье тролльей крови, немного чешуи наги из клана Пронзающего Гребня и глаза мурлоков из клана Рваный Плавник с южного берега Хилсбрада. Когда ты принесешь ему все составляющие, он мигом все сделает. Ох, как мне не терпится узнать о результатах его эксперимента!', },
    {'Отнесите микстуру из цветков Грязного Рыла, крепкое зелье тролльей крови, 5 чешуек нагов из клана Пронзающего Гребня и 5 глаз мурлоков из клана Рваный Плавник аптекарю Лидону в Мельницу Таррен.', },
  },
  [516] = {
    'Погибель Берена',
    {'Я получил сообщение, что группа нежити спряталась и готовит атаку против нас. Вооруженные этой информацией, мы можем повернуть все другой стороной и напасть первыми, подавив их планы в самом зародыше. $B$BК сожалению, моя информация весьма обрывочная. Говорят, они скрываются в месте, называемом Погибель Берена. Точное местонахождение неизвестно, но, похоже, это пещера где-то в холмах, недалеко от участка, занятого даларанскими волшебниками. $B$BЯ полагаюсь на твою изобретательность. Отыщи их и нанеси им сокрушительный удар.', },
    {'Найдите Погибель Берена, убейте 6 чернорабочих Когтя Ворона и 6 стражей Когтя Ворона, а затем вернитесь в Гробницу к темному жрецу Алистеру.', },
  },
  [517] = {
    'Эликсир агонии',
    {'Пришло время испытать новый эликсир. Но зачем же тратить хорошую, очень заразную болезнь на простых лягушек и собак? Давай устроим настоящее веселье во имя леди Сильваны.$B$BСтупай в Дун Гарок. Знаешь, это казармы, где вечно шныряют эти дворфишки. Укради у них бочонок "Землекопского портера". Получив его, я устрою веселую вечеринку!', },
    {'Принесите бочонок "Землекопского портера" аптекарю Лидону из Мельницы Таррен.', },
  },
  [518] = {
    'Корона Воли',
    {'Муг\'тол и огры Раздробленного Хребта захватили руины Альтерака. Наказать их всерьез будет нелегко, если не проредить сначала ряды огров.$B$BА уж потом мы продолжим.$B$BЯсно? Отлично. Выполняй.', },
    {'Убейте 10 извергов из клана Раздробленного Хребта по поручению Мелизары из Мельницы Таррен.', },
  },
  [519] = {
    'Корона Воли',
    {'Тарг, Навозер и Гломмус – лейтенанты Муг\'Тола. Они верны ему, хотя кто там разберет, что вынуждает огров быть верными.$B$BПринеси мне их головы, $N, и нам останется отрубить башку Муг\'Толу вместе с короной.', },
    {'Убейте Тарга, Навозера и Гломмуса и принесите их головы Мелизаре из Мельницы Таррен.', },
  },
  [520] = {
    'Корона Воли',
    {'Лейтенанты Муг\'Тола мертвы, и нам остается только убить его самого и забрать корону Воли. Нет сомнений, ему не захочется с ней расставаться. Скорее всего, он ждет тебя, учитывая... уменьшение числа его товарищей.$B$BОтыщи Муг\'Тола и принеси мне его голову вместе с короной. Положим конец сопротивлению Раздробленного Хребта.', },
    {'Убейте Муг\'Тола и принесите его голову и корону Воли Мелизаре на мельницу Таррен.', },
  },
  [521] = {
    'Корона Воли',
    {'Остается отнести корону Воли Шарлиндре в Подгород. Она уж передаст ее леди Сильване. Но даже не думай воспользоваться ей в личных целях, $N. Пусть тебе послужит уроком судьба Муг\'Тола.', },
    {'Отнесите корону Воли Шарлиндре из Подгорода.', },
  },
  [522] = {
    'Контракт убийцы',
    {'Это контракт на убийство мирового судьи Генри Малеба. Он был опечатан красным воском с характерным оттиском кольца.', },
    {'Отнесите контракт убийцы мировому судье Малебу в Южнобережье.', },
  },
  [523] = {
    'Смерть барона',
    {'Добытый тобой контракт был подписан бароном Вардусом, дворянином из бывшего королевства Альтерака, прославившегося холодным сердцем и необычайной жестокостью. Неудивительно, что за этим стоял барон.$B$BОтыщи его. Отыщи и сверши над ним суд. Должно быть, он прячется в одном из убежищ Синдиката – руинах Странбрада или долине на севере оттуда.', },
    {'Принесите голову барона Вардуса мировому судье Малебу в Южнобережье.', },
  },
  [524] = {
    'Эликсир агонии',
    {'За соседней дверью стражи смерти держат нескольких заложников-фермеров. Верховный палач Дарталия подозревает, что среди них скрывается один из военных-стратегов. Очевидно, его нашли мертвым в Чумных землях. Дарталия всегда заботится обо мне и решила передать пленников мне в руки.$B$BПора устроить этим фермерам вечеринку. Ох, повеселимся!$B$BПоставь гниющий бочонок на ковер у них в комнате. Чумной напиток для всех!', },
    {'Поставьте гниющий бочонок на ковер для плененных фермеров.', },
  },
  [525] = {
    'Все больше тайн',
    {'Я расшифровал письмо, но не знаю, что с ним дальше делать. Вы нашли его у воров в предгорьях Альтерака? Очень странно...$B$BНадо доставить это письмо мировому судье Южнобережья. Хотя письмо и зашифровано, это ближайший город к горам Альтерака, и если назревает какая-то опасность, то они должны об этом узнать.', },
    {'Доставьте расшифрованное письмо мировому судье Южнобережья Малебу.', },
  },
  [526] = {
    'Слитки светлостали',
    {'Ты $Gнашел:нашла; только один слиток светлостали – этого не хватит, чтобы выковать что-нибудь стоящее.$B$BВернись и поищи еще слитки. Думается, их растащили мурлоки, разграбившие корабль.', },
    {'Соберите 5 слитков светлостали и вернитесь к Глорину Стальному Лбу в Болотину.', },
  },
  [527] = {
    'Битва за Хилсбрад',
    {'Когда говоришь со мной, стой по стойке "смирно", |3-6($C).$B$BЯ нахожусь под прямым командованием Вариматаса. Наша задача – сдерживать распространение людей, покуда аптекари работают над составом новой чумы.$B$BПо нашим сведениям город Хилсбрад очень уязвим. Твое первое задание – прервать снабжение едой и прочим.$B$BОтправляйся на запад. Устрой налет на самые северные фермы Хилсбрадских полей. И казни землевладельцев, фермеров Рея и Гетца.', },
    {'Убейте 6 хилсбрадских батраков, 6 хилсбрадских фермеров, фермеров Рея и Гетца и возвращайтесь к Дарталии которая ждет вас на мельнице Таррен.', },
  },
  [528] = {
    'Битва за Хилсбрад',
    {'Слухи о нападении на северные фермы вызвали панику среди населения. Великолепно.$B$BНаши стражи смерти докладывают о крестьянах южного Хилсбрада, бегущих в поля. Эти глупцы надеются укрыться в садах, избежав гибели.$B$BПокажи им, как они заблуждаются, $N. Возвращайся в Хилсбрадские поля и отыщи там трусливых крестьян. Рази их направо и налево во имя Темной Госпожи.$B$B$GСвободен:Свободна;!', },
    {'Убейте 15 хилсбрадских крестьян и вернитесь к Дарталии на мельницу Таррен.', },
  },
  [529] = {
    'Битва за Хилсбрад',
    {'Люди Хилсбрада оказывают нам неожиданное сопротивление. Кажется, проблема в кузнеце, снабжающем горожан оружием. Необходимо прервать цепь снабжения.$B$BУбей кузнеца Верринтана вместе с учениками и возвращайся с партией железа. Нам оно пригодится, когда мы одержим победу над Хилсбрадом и нападем на Южнобережье.', },
    {'Убейте кузнеца Верринтана и 4 хилсбрадских учеников кузнеца.$B$BПринести партию железа Дарталии на мельницу Таррен.', },
  },
  [530] = {
    'Месть мужа',
    {'Когда-то Валдред был моим лучшим другом, а потом убил мою жену. Горевать я уже не способен, но до сих пор жажду мести. Если ты убьешь Валдреда и принесешь мне его проклятые руки, я тебя награжу.$B$BПо последним слухам, он был у Стены Седогрива в южном Серебряном бору. Небось, пытается сбежать в королевство Гильнеас.', },
    {'Убейте Валдреда Морая.$B$BПринесите его руки Роли Андреану в Подгород.', },
  },
  [531] = {
    'Месть Вайрины',
    {'Ради всего святого! Не приноси в Приют такое... это негигиенично, неприлично, не... не... негигиенично!$B$BСкорее убери ее отсюда! Никому здесь не нужны твои страхолюдные трофеи!', },
    {'Принесите голову Старины Черношкура Вайрине Быстрый Ветер в Приют странников.', },
  },
  [532] = {
    'Битва за Хилсбрад',
    {'В Хилсбраде царит паника. Думаю, он скоро падет. Но мы не должны ослаблять натиск, пока последние человеческие отродья не изжиты из этих холмов навеки.$B$BНевзирая на колоссальные потери, люди держатся вокруг своих вождей и не кладут оружия. Мы должны уничтожить их главу, мирового судью Бернсайда, и весь городской совет. Заодно уничтожь и их главный документ, Хилсбрадское официальное объявление. И раз уж на то пошло, укради и городскую книгу регистрации.', },
    {'Убейте мирового судью Бернсайда, и 4 членов совета Хилсбрада. Уничтожьте Хилсбрадское официальное объявление. Украдите регистрационную книгу Хилсбрада. Возвращайтесь к Дарталии на мельницу Таррен.', },
  },
  [533] = {
    'Шпионская работа',
    {'Наконец-то до нас дошли сведения о местопребывании Гол\'дира. Люди постоянно меняли места его заключения, но мы смогли сузить область поиска до большого лагеря Синдиката на севере отсюда.$B$BСтупай туда, $N, и заполучи все доступные сведения.', },
    {'Добудьте сведения о местонахождении Гол\'дира для Круска из Мельницы Таррен.', },
  },
  [535] = {
    'Валлик',
    {'Гр-рр... Раненый, умирающий, но не опускающий оружия.$B$BНе сомневаюсь, ты все равно убьешь меня, но если ты принесешь мне немного южнобережского портера – вы ведь такое тоже пьете? – я расскажу тебе то, что ты хочешь знать.', },
    {'Принесите кружку южнобережского портера Валлику в лагерь Синдиката.', },
  },
  [536] = {
    'На побережье',
    {'Меня перевели в Южнобережье из городской стражи Штормграда. Непонятно, как тут вообще люди живут... А как воняет со стороны пристани и днем и ночью!$B$BДа еще старый Редпат на мою голову... Он хочет, чтобы я занялся мурлоками. Ха!$B$BЯ едва могу заставить себя подойти к этим рыбам-переросткам на Западном побережье. Если ты разберешься с ними вместо меня, я буду очень признателен.', },
    {'Убейте 10 волноловов из племени Порванного Плавника и 10 оракулов из племени Порванного Плавника по поручению лейтенанта Фаррена Оринеля из Южнобережья.', },
  },
  [537] = {
    'Темный совет',
    {'Это письмо от существа, называющего себя Нагазом, членом сообщества Недремлющего Аргуса. Не знаю, что это за Аргус, но если он в союзе с Синдикатом, то представляет собой угрозу.$B$BОтыщи Нагаза и упомянутых в письме темных магов Недремлющего Аргуса. Найди их и убей.$B$BИщи их в Высокогорье, на севере от Странбрада, где позиции Синдиката сильнее всего.$B$BЕсли повезет, ты найдешь там послов Недремлющего Аргуса.', },
    {'Убейте 4 темных магов Недремлющего Аргуса.$B$BПринесите голову Нагаза мировому судье Южнобережья Малебу.', },
  },
  [538] = {
    'Южнобережье',
    {'Доброго дня вам, $N. Если вы не против долгого путешествия, то у меня для вас найдется задание, которое поможет пополнить библиотеку Штормграда.$B$BВместе со старым королевством Альтерака во время войны погибла и огромнейшая библиотека. Жаль, что город разрушен, но вдвойне жаль, что пропал бесценный кладезь знаний! Мы должны восстановить все, что можем.$B$BЮжнобережье – ближайший к руинам Альтерака город. Отправляйтесь туда и поговорите с Хранителем мудрости Диббсом. Он направит вас к руинам, может быть, там вы сумеете найти книги...', },
    {'Поговорите с Хранителем мудрости Диббсом в Южнобережье.', },
  },
  [539] = {
    'Битва за Хилсбрад',
    {'Согласно регистрационной книге Хилсбрада, Лазуритовый рудник находится в руках Альянса. Более того, львиную долю руды, идущей на вооружение Альянса, добывают именно там.$B$BХотя мы нанесли решительный удар по городу Хилсбраду, Альянс изо всех сил защищает свой рудник.$B$BРудник расположен на юге от Хилсбрада. Ступай туда и убей штейгера, человека по имени Бондс. Убей и его горняков. Альянс надолго запомнит этот урок.', },
    {'Убейте штейгера Бондса и 10 хилсбрадских шахтеров и доложите о проделанной работе Дарталии, которая ждет вас на мельнице Таррен.', },
  },
  [540] = {
    'Сохранение знаний',
    {'Мерзкие огры поселились в руинах Альтерака, и мне дурно при мысли, что они делают с драгоценными книгами из библиотеки. Спаси все, что можно!$B$BСтупай в руины Альтерака и поищи там фолианты, украденные ограми. Собери все, что сможешь и принеси мне. Больше всего меня интересует одна книга – "Рука Гри\'лека". В ней содержится древняя мудрость троллей, о которой я жажду узнать, а последняя копия этой книги находилась именно в руинах Альтерака.', },
    {'Найдите 5 фолиантов и потертую кожаную книгу, называемую "Рука Гри\'лека", и принесите их хранителю мудрости Диббсу в Южнобережье.', },
  },
  [541] = {
    'Битва за Хилсбрад',
    {'Дворфы пришли на помощь людям? Проклятые мелкие твари!$B$BЧто ж, испытаем силу так называемого Альянса. Посмотрим, решатся ли эти недорослики помогать людям, когда почувствуют наш гнев. Мы отлично знаем, где скрывается их цитадель в этих краях.$B$BОни построили свои казармы в юго-восточной части Хилсбрада, рядом со стеной Торадина. Это местечко зовется Дун Гарок. Последняя цель этой битвы – осадить Дун Гарок, |3-6($C). Глава дворфов, капитан Железная Гора, должен умереть.', },
    {'Отправляйтесь в Дун Гарок и убейте 8 горных пехотинцев, 4 стрелков, 2 жрецов и капитана Железную Гору. После возвращайтесь к Дарталии на мельницу Таррен.', },
  },
  [542] = {
    'Возвращение к Милтону',
    {'Мне понадобится время на изучение "Руки Гри\'лека", хотя я не сомневаюсь, что Милтону не терпится вернуть книги в коллекцию Штормграда.$B$BВот книги, добытые тобой в руинах Альтерака. Они уже упакованы и каталогизированы. Отнеси их Милтону Снопу в Штормград, он несомненно будет тебе признателен.', },
    {'Отнесите фолианты Альтерака Милтону Снопу в Штормград.', },
  },
  [543] = {
    'Тиара Перенольда',
    {'Тиара Перенольда – старая семейная реликвия одного из древнейших семейств Альтерака, входившего в правящие круги до падения королевства. Говорят, что ее изготовили мастера Стальгорна, украсив лучшими изумрудами.$B$BКогда Альтерак пал, тиара была утеряна, хотя поговаривали, что огрский маг Грел\'борг Скряга нашел ее и теперь пытается разыскать на руинах Альтерака другие сокровища.$B$BМы надеемся, что вы отыщете тиару.', },
    {'Доставьте тиару Перенольда Ремингтону Риджвеллу в Штормград.', },
  },
  [544] = {
    'Взлом тюрьмы',
    {'Я прибыл в Мельницу Таррен с исследовательскими целями, а пришлось разрешать кризис. Видишь ли, четверо Отрекшихся несколько месяцев назад бежали из Подгорода. Они предали своих братьев, но что еще хуже... они украли кое-что у Темной Госпожи.$B$BЭти воры вломились в надежное хранилище и похитили четыре артефакта, необходимых нашим аптекарям для исследований. Сильвана жаждет вернуть эти артефакты.$B$BВоры бежали из Подгорода в Даларан, и волшебники спрятали их в Лордамерских выселках.', },
    {'Найдите предателей и отберите у них артефакты, а потом возвращайтесь к волхву Сиянью Бездны в Мельницу Таррен.', },
  },
  [545] = {
    'Даларанские патрули',
    {'Волшебники Даларана создали вокруг сердца своего города огромный магический купол. Некоторые говорят, что он призван защитить город от опасностей, которыми полнятся наши дни, другие же уверяют, будто волшебники захотели работать и строить планы в тишине и покое.$B$BМне лично наплевать, зачем соорудили этот купол. Меня интересуют развалины на окраинах города, где могут быть спрятаны ценные магические предметы.$B$BК сожалению, все окрестности патрулируют волшебники и порабощенные ими элементали.$B$BИтак, напади на них и возвращайся, когда существенно сократишь их число.', },
    {'Убейте 6 даларанских призывателей и 12 порабощенных элементалей. Затем возвращайтесь к волхву Сиянью Бездны на мельницу Таррен.', },
  },
  [546] = {
    'Сувениры смерти',
    {'Я слышал, что Дарталия призвала тебя в строй, чтобы выступить против людей Хилсбрада. Как я завидую…$B$BПока ты там развлекаешься – убиваешь людей, грабишь города, запугиваешь невинных – я торчу здесь, на страже мельницы Таррен.$B$BМожет, ты смилостивишься над старым стражем смерти? Видишь, я собираю коллекцию человеческих черепов, а тебе предстоит убить немало смертных. Набери в ходе битвы 30 черепов, принеси мне, и я тебе хорошо заплачу.', },
    {'Страж смерти Самса из Мельницы Таррен попросил принести 30 человеческих черепов Хилсбрада.', },
  },
  [547] = {
    'Меч Гумберта',
    {'Верховный палач Дарталия послала меня наблюдать за Дун Гароком издалека. Я разбил лагерь подальше от дворфов. Но пришло утро, мне нужно было вскипятить воду, и я пошел за дровами.$B$BВернувшись, я обнаружил, что мои вещи пропали.$B$BОчевидно, ко мне заглянули дворфийские разведчики из Дун Гарока и украли их. Мне нужен мой меч! Если ты сможешь вернуть его, я буду очень признателен. Там так много дворфов... будет нелегко понять, кто же из них вор.', },
    {'Найдите меч стража смерти Гумберта в Дун Гароке и верните его владельцу на мельнице Таррен.', },
  },
  [549] = {
    'РАЗЫСКИВАЮТСЯ: сторонники Синдиката',
    {'По приказу леди Сильваны все члены организации, именуемой Синдикат, приговариваются к смертной казни. Всех пленных казнят. В настоящий момент организация собирает силы в руинах крепости Дарнхольд на юго-востоке от мельницы Таррен.$B$BВсем, предоставившим доказательства своих подвигов верховному палачу Дарталии, будет дарована щедрая награда.', },
    {'Убейте 10 разбойников и 10 сторожей Синдиката и возвращайтесь за наградой к верховному палачу Дарталии в Мельницу Таррен.', },
  },
  [550] = {
    'Битва за Хилсбрад',
    {'Поскольку ты $Gпроявил:проявила; немалое мужество и стойкость в битве за Хилсбрад, я написала это рекомендательное письмо, описывающее твой героизм. Пусть его прочитает высшее командование.$B$BОтнеси мое письмо Брагору Кровопалу в Подгород. Можешь гордиться собой, |3-6($C).', },
    {'Отнесите запечатанное рекомендательное письмо Дарталии Брагору Кровопалу в Подгород.', },
  },
  [551] = {
    'Заколдованный пергамент',
    {'На этот пергамент наложено заклинание, из-за чего надписи на нем невозможно расшифровать. Вы можете разобрать только одно слово вверху страницы: $B$BНагаз$B$B', },
    {'Отнесите заколдованный пергамент Хранителю мудрости Диббсу в Южнобережье, что в Предгорьях Хилсбрада.', },
  },
  [552] = {
    'Месть Гелькулара',
    {'Мастер Гелькулар почти превратился в Короля Мертвых, когда его ритуальное логово в холмах обнаружила разъяренная толпа людей и зверски убила его. Хуже того, они похоронили моего учителя на кладбище Южнобережья.$B$BНемыслимое неуважение к великому волшебнику. Я его ученик, и хочу отомстить ненавистному городу.$B$BПервым делом нам понадобится жезл Гелькулара. Его ритуальное логово захватили йети. Жезл несомненно находится у одного из них.', },
    {'Отберите у йети жезл Гелькулара и отнесите его новобранцу Тайлванду на мельницу Таррен.', },
  },
  [553] = {
    'Месть Гелькулара',
    {'Вот, возьми Гелькуларов жезл. Он тебе понадобится.$B$BГелькулар создал для своего заклятья 3 церемониальных погребальных костра. Пламя Азеля и пламя Вераза находятся в Пещерах предгорий. Пламя Узеля находится в пещере выше по склону, в северной части гор.$B$BГелькуларов жезл необходимо зарядить в каждом из этих костров. Завершив ритуал, вонзи жезл в гробницу Гелькулара в Южнобережье, надежно охраняемом человеческом городе на юге. Мы отомстим!', },
    {'Зарядите Гелькуларов жезл силой пламени Азеля, пламени Вераза и пламени Узеля.$B$BВонзите заряженный жезл в гробницу Гелькулара в Южнобережье.', },
  },
  [554] = {
    'Расшифровщик Грозовая Вершина',
    {'Геолог Грозовая Вершина – мастер дешифрования и декодирования. Если ты отнесешь найденный пергамент ему, то при удачном стечении обстоятельств он сможет его прочитать. В настоящий момент Грозовая Вершина работает в штаб-квартире Геологов в Стальгорне.$B$BЕсли увидишь его, сообщи о нашем разговоре – в последнее время я много работы на него свалил.', },
    {'Отнесите заколдованный пергамент геологу Грозовой Вершине в Стальгорн.', },
  },
  [555] = {
    'Нежный черепаховый суп',
    {'Ты скоро поймешь, что в Южнобережье готовят лучше, чем во всех землях к северу от Штормграда!$B$BСкажем, возьмем мой секретный рецепт черепахового супа. Иногда ко мне захаживают из самого Темнолесья, чтобы насладиться миской-другой. Правда, в последнее время мне не хватает ингредиентов. Я бы сам пошел к озеру Лордамер у Даларана и поохотился на тамошних хрустогрызов, но это слишком опасно. Если ты принесешь мне черепашьего мяса, я мигом сварю супчик.', },
    {'Принесите 10 кусков черепашьего мяса шеф-повару Джессену в Южнобережье.', },
  },
  [556] = {
    'Каменные талисманы',
    {'Волшебники Даларана славятся своей связью с элементалями, могущественными созданиями из других измерений, которыми они управляют при помощи магических талисманов. Волхв Сиянье Бездны хотел бы изучить эти талисманы.$B$BСтупай на земли даларанских волшебников на западе от Хилсбрада и собери несколько талисманов.$B$BТакие талисманы носят многие жители Даларана, но я советую поискать их в выселках. Это гораздо безопаснее.', },
    {'Отнесите 10 истертых каменных талисманов хранителю Бел\'варилу из Мельницы Таррен.', },
  },
  [557] = {
    'Сковывающие браслеты',
    {'Пока что колдун изучает каменные талисманы, но вскоре ему понадобится и другая часть – браслеты, связующие Стихии. Элементали носят их на запястье, что заставляет их выполнять волю мага.$B$BУбей побольше духов в руинах Даларана и собери с них браслеты Земли, и колдун будет доволен.', },
    {'Принесите 4 браслета Земли хранителю Бел\'варилу из Мельницы Таррен.', },
  },
  [558] = {
    'Автограф Джайны',
    {'Говорят, что леди Джайна Праудмур из Терамора – одна из величайших героинь Альянса. Когда я вырасту, я тоже хочу стать героем Альянса!$B$BТы $Gзнаком:знакома; с Джайной? Окажи мне услугу! Возьми у нее автограф! Я не знаю, как ее попросить, у нее нет времени для таких, как я... другое дело ты! Ты $Gтакой же искатель:такая же искательница; приключений, как и она! Готов поспорить, что тебе получить у нее автограф – раз плюнуть!$B$BПожалуйста!', },
    {'Отправляйтесь в Терамор и постарайтесь получить автограф Джайны Праудмур для своего подопечного.', },
  },
  [559] = {
    'Доказательство Фаррена',
    {'Ты не поверишь, Редпата не устроили все убитые нами мурлоки – не скромничай, ты тоже $Gприложил:приложила; к этому руку. Теперь он хочет от меня доказательств их смерти. Чтобы я отрубил холодные склизские мурлочьи головы и принес пред его очи!', },
    {'Соберите 10 голов мурлоков на Западном побережье по просьбе лейтенанта Фаррена Оринеля из Южнобережья.', },
  },
  [560] = {
    'Доказательство Фаррена',
    {'Знаешь, мне даже прикасаться к этим головам не хочется... Раз уж они у тебя, отнеси их Редпату от моего имени, а?', },
    {'Отнесите мешок с головами мурлоков маршалу Редпату в Южнобережье.', },
  },
  [561] = {
    'Доказательство Фаррена',
    {'Пожалуйста, сообщи лейтенанту Оринелю, что я хотел бы, чтобы он занялся нагами, заселившими Восточное побережье. Когда он добьется результатов, пусть лично доложит о них мне.', },
    {'Поговорите с лейтенантом Фарреном Оринелем из Южнобережья.', },
  },
  [562] = {
    'Эй, Штормград!',
    {'Мой бывший наставник из городской стражи Штормграда послал мне сообщение, что верховное командование Альянса обеспокоено появлением наг. По слухам, тот, кто хорошо проявит себя в этой области, получит повышение – или, по крайней мере, новое назначение.$B$BЕсть одна проблема... я не могу справиться с нагами. Разумеется, прекратить пьяную драку или скрутить воинственного вора я могу, но наги мне не по зубам. Помоги мне, $N!', },
    {'Убейте 10 береговых охотников и 10 сирен из клана Пронзающего Гребня по просьбе лейтенанта Фаррена Оринеля из Южнобережья.', },
  },
  [563] = {
    'Новое назначение',
    {'Мне будет легко добиться, чтобы мое донесение прочитали. Новый капитан стражи, майор Самуэльсон, был моим учителем еще в Штормграде. Ему не составит труда перевести меня обратно в столицу. Но придется попросить тебя отнести мое донесение ему... Главное, чтобы старина Редпат ничего не узнал.', },
    {'Отнесите донесение Фаррена майору Самуэльсону в Крепость Штормграда.', },
  },
  [564] = {
    'Серьезная угроза',
    {'Ничто не злит меня так, как потеря отличных коней. И поверь, эти назойливые мертвяки, синдикатские бандюги и прочие ни на что не годные негодяи лишили нас множества лошадок.$B$BНо я готов поклясться могилой моего прадедушки, что главный враг коней – горные львы Альтерака. Вечно осложняют мою жизнь!$B$BТы считаешь себя героем? Тогда пойди разберись с ними!', },
    {'Убейте 8 горных львов и 10 крупных горных львов по поручению Даррена Малвея из Южнобережья.', },
  },
  [565] = {
    'Плащ из меха йети',
    {'Я, великий Бартоло, сделаю для тебя знаменитый во всем Альянсе, да и во всем мире, плащ из меха йети!$B$BОбыщи весь белый свет, и нигде прекрасней нет! Что, что я слышу, $Gмаленький:маленькая:r; |3-6($R)? "Бартоло, твои плащи лучше всех!" Да, да! Мне не раз это говорили.$B$BБартоло нужен рулон шерсти, плащ жителя холмов от искусного кожевника, тонкая нить и мех йети, конечно! Принеси мне все составляющие, и ты станешь $Gсчастливым обладателем:счастливой обладательницей; одного из моих плащей!', },
    {'Принесите Бартоло Джинсетти из Южнобережья рулон шерсти, плащ жителя холмов, тонкую нить и 10 клоков меха йети.', },
  },
  [566] = {
    'РАЗЫСКИВАЕТСЯ: Барон Вардус',
    {'По приказу верховного палача Дарталии, человек по имени барон Вардус разыскивается по обвинению в многочисленных преступлениях против Отрекшихся. Вардус является высокопоставленным членом преступной организации Синдикат.$B$BВ последний раз его видели в Высокогорье Альтеракских гор, к северу от мельницы Таррен.$B$BОн приговаривается к смертной казни. Для получения награды необходимо предоставить доказательства его гибели верховному палачу Дарталии.', },
    {'Убейте барона Вардуса и принесите его голову верховному палачу Дарталии на мельницу Таррен.', },
  },
  [567] = {
    'ОПАСНО!',
    {'Внимание: вооружены и очень опасны!$B$BСледующие люди из Хилсбрада объявлены вне закона, и верховный палач Дарталия предлагает награду за их головы:$B$BКлерк Гораций Белоконь. Разыскивается за убийство стража смерти Томма.$B$BГорожанин Вилкс. Разыскивается за убийство аптекаря Эли.$B$BШахтер Хакетт. Разыскивается за убийство стража смерти Фрая.$B$BФермерша Калаба. Разыскивается за нападение из засады на караван с припасами из Подгорода.$B$BВсе перечисленные преступники скрываются, и найти их нелегко. Награда будет выдана при предъявлении доказательств их смерти.', },
    {'Верховный палач Дарталия из Мельницы Таррен назначила награду за убийство клерка Горация Белоконя, гражданина Уилкса, шахтера Хакетта и фермерши Калабы.', },
  },
  [568] = {
    'Защита Гром\'гола',
    {'Тром\'ка, |3-6($C)!$B$BТы на удивление вовремя $Gоказался:оказалась; в Гром\'голе. Как командир лагеря Вождя, я обязан защищать всех сторонников Орды в этих джунглях. Наша цель – обеспечить бесперебойное снабжение Каменора, а некоторые обитатели здешних мест нам мешают.$B$BЯ поручаю тебе сократить численность ящеров рядом с Гром\'голом. Добившись заметных успехов, возвращайся, и я дам тебе новое задание.', },
    {'Командир Агро\'гош из лагеря Гром\'гол поручил вам убить 15 ящеров-кнутохвостов.', },
  },
  [569] = {
    'Защита Гром\'гола',
    {'Отправляясь в джунгли, мы не собирались воевать с местными племенами. Главное – снабжение Каменора. Увы, возник ряд сложностей. Местные огры заняли руины Мизжа на юго-востоке от Гром\'гола. Невзирая на многочисленные предупреждения, они продолжают нападать на наши караваны.$B$BЯ приказываю тебе разобраться с этими упертыми ограми.$B$BОтправляйся в развалины и попытайся выбить их оттуда. По крайней мере, преподай им хороший урок.', },
    {'Командир Агро\'гош из лагеря Гром\'гол поручил вам убить 10 огров-громил и 5 огров-знахарей.', },
  },
  [570] = {
    'Чары Мок\'тардина',
    {'Орда должна узнать о силе магии джунглей и подчинить ее себе. Мне уже удалось добиться успеха, и я могу пользоваться могучими заклинаниями. Я сделаю для тебя особую вещь, $Gотважный:отважная:c; |3-6($C).$B$BПринеси мне когти тенебрюхих пантер и клык тигрицы. Учти, что клык должен быть безупречно ровным.$B$BЭти твари разгуливают по всем джунглям, но тебе не придется далеко ходить – просто перейди реку рядом с руинами Мизжа на юго-востоке.', },
    {'Провозвестник Мок\'тардин из лагеря Гром\'гол попросил вас принести ему 8 когтей тенебрюхих пантер и безупречный тигриный клык.', },
  },
  [571] = {
    'Чары Мок\'тардина',
    {'Следующий компонент будет нелегко найти.$B$BЧтобы связать перья, когти и чистый клык, мне понадобится волшебный шнурок. Нам подойдет лишь сухожилие старой гориллы.$B$BЕго можно добыть только у старых горилл Мглистой долины. Они кормятся к востоку от Пиратской Бухты.$B$BТолько сухожилие должно быть в идеальном состоянии. Быть может, тебе понадобится убить нескольких зверей, чтобы понять, что именно мне нужно, но результат того стоит.', },
    {'Принесите сухожилие старой гориллы провозвестнику Мок\'тардину в лагерь Гром\'гола.', },
  },
  [572] = {
    'Чары Мок\'тардина',
    {'То, что тебе удалось добыть у зверей из семейства кошачьих – лишь первое, что требуется для создания могущественного зачарованного предмета. Меж тем украшения джунглей, усиленные при помощи моих ритуалов, неплохо послужат тебе в деле помощи Орде и Вождю.$B$BТеперь же собери для меня перья тропических ловцов. Эти твари как правило прикрепляют перья на лапы. На юге от Гром\'гола, недалеко от большой арены, есть такое племя ящеров.', },
    {'Принесите 10 перьев тропических ловцов провозвестнику Мок\'тардину в лагерь Гром\'гол.', },
  },
  [573] = {
    'Чары Мок\'тардина',
    {'Остается лишь одно составляющее, и я смогу наложить чары на колье.$B$BК югу отсюда, близко от западного побережья, высоко над утесами, располагается Святой источник. Он вызывает множество конфликтов среди религиозных ученых. Все расы Азерота утверждают, что воды освящены именно для их народа.$B$BВот мое последнее поручение: принеси мне воду Святого источника. Соблюдай осторожность. Сейчас его окрестности захватили наги. С ними тоже следует разобраться.', },
    {'Провозвестник Мок\'тардин из лагеря Гром\'гол попросил вас принести ему воду Святого источника. Кроме того, он поручил вам убить 10 наг-исследователей.', },
  },
  [574] = {
    'Особые войска',
    {'После удара по обычным партизанам Курцена тебе придется вступить в схватку с его особыми отрядами. Они либо выбираются в лес и убивают наших солдат, либо отсиживаются позади поселка Курцена, в пещере, называемой Схрон.$B$BЯ знакома со многими воинами Курцена, и все они отличаются прекрасной подготовкой, верностью и жестокостью. Задание непростое, $N.$B$BЕсли ты сможешь их одолеть, доложи об этом лейтенанту Дорену, моему командиру. Он захочет услышать об этом из первых уст.', },
    {'Убейте 10 коммандос и 6 охотников за головами Курцена.$B$BДоложите об этом лейтенанту Дорену из Лагеря повстанцев.', },
  },
  [575] = {
    'Спрос и предложение',
    {'"Лучший мастер-кожевник Южных морей и старого и нового мира." Вот как меня называют! Меня! Дриззлика! Лучшим коже... Кхе-кхе. Короче.$B$BМои товары славятся по всему свету как наилучшие по мнению всех экспертов!$B$BЯ только что получил заказ от директора Ридлвокса из Союза ремонтников на дюжину его любимых превосходных сапог. Не скрою, это самая популярная модель.$B$BЧтобы приступить к раскройке, мне нужны шкуры кроколисков. Их можно добыть у реки в северной части Тернистой долины.', },
    {'Принесите 2 большие шкуры речного кроколиска Дриззлику из Пиратской Бухты.', },
  },
  [576] = {
    'Смотри в оба',
    {'Стой! Кто идет?$B$BАррр, кому я нужен без волшебного глаза. Мало того, что я лишился зрения в бою! Но тогда мне свезло, и один волшебник зачаровал для меня особый стеклянный глаз, и я снова мог видеть.$B$BА вчера на меня по дороге накинулись пираты Кровавого Паруса и отобрали у меня глаз! Кажется, эти типы чувствуют себя в Пиратской Бухте как дома.$B$BНе поможешь старому морскому волку вернуть зрение?', },
    {'Диззи Одноглазый из Пиратской Бухты попросил вас отобрать его глаз у пиратов Кровавого Паруса.', },
  },
  [577] = {
    'Добыча шкур',
    {'Что ж, я раскроил основу сапог, получается превосходно, но на подбой шкур не хватит.$B$B"Что же делать, Дриззлик?" – спросишь ты. К счастью, ты говоришь со мной, и я скажу тебе, как мы поступим!$B$BПодбой все равно никто не видит, поэтому мы используем более толстую и дешевую шкуру кроков с озера Назферити.', },
    {'Отнесите 5 шкур кроколисков-хрустогрызов Дриззлику в Пиратскую Бухту.', },
  },
  [578] = {
    'Камень приливов',
    {'Если правду говорят о гоблине-маге и элементалях воды, живущих на острове, значит я могу заполучить Камень Приливов!$B$BНайди этот остров... И выясни, что происходит. И если ты хочешь узнать подробнее о Камне Приливов, отнеси эту записку в библиотеку Штормграда, они найдут тебе книгу, в которой я впервые прочитал эту легенду.$B$BНеужели Газбан в самом деле нашел Камень?..', },
    {'По поручению барона Ревилгаза из Пиратской Бухты найдите остров, населенный духами Воды.', },
  },
  [579] = {
    'Библиотека Штормграда',
    nil,
    nil,
  },
  [580] = {
    'Потерянный грог Алкача Виски',
    {'На подходе к Отвесным скалам, как раз в тот момент, когда старые добрые Внутренние земли показались на горизонте, нас настигла жуткая буря. Мы думали – все, нам каюк, море нас проглотит.$B$BНо старый Алкач знает, что такое жидкая храбрость! У кэпа был ящик портвейна "Пупелливербос", который мы взяли трофеем у одного пирата по пути в Штормград.$B$BВ общем, когда с Внутренних земель налетел шторм, я налег на грог. И тут шхуну захлестнула большая волна и смыла бутылки за борт. Кэп всыплет мне по первое число, если ты не поможешь их найти.', },
    {'Алкач Виски из Пиратской Бухты попросил вас принести ему потерянные бутылки портвейна "Пупелливербос".', },
  },
  [581] = {
    'Поиски Йеннику',
    {'Нас постиг злой рок. И коснулось это младшего сына моего вождя. Как у нас и заведено, мы отдали его на воспитание племени Гурубаши в Зул\'Гурубе, но после того, как племя Черного Копья последовало за орком Траллом, бедняга Йеннику оказался потерян для нас. Меня прислали в Тернистую долину из далекого Оргриммара на поиски сына вождя.$B$BЯ думаю, он в руках племени Кровавого Скальпа.$B$BЕсли ты хочешь помочь мне, напади на них. Они живут к северу отсюда. Собери их клыки и принеси мне.', },
    {'Принесите 9 клыков тролля из племени Кровавого Скальпа Нимбойе в лагерь Гром\'гол.', },
  },
  [582] = {
    'Охота за головами',
    {'Мы должны узнать, мертв ли Йеннику.$B$BУбей как можно больше охотников за головами из племени Кровавого Скальпа и забери у них сушеные головы. Набери как можно больше голов, и я осмотрю их, чтобы убедиться, что среди них нет головы Йеннику.$B$BЕсли племя Кровавого Скальпа убило Йеннику, да падет 600-летнее проклятье на душу их предводителя, Ган\'зулаха!$B$BСтупай, $N. Охотников за головами можно найти в Руинах Зуулдая и Зул\'Кунды, в северной части побережья.', },
    {'Принесите Нимбойе из лагеря Гром\'гол 20 сушеных голов.', },
  },
  [583] = {
    'Добро пожаловать в джунгли',
    {'Добро пожаловать в Тернистую долину!$B$BБыть может, ты не знаешь об этом, но этот дворф – сын единственного и неповторимого Хеминга Эрнестуэя, прославленного героя Альянса и великого охотника на крупную дичь. Подобно отцу, он не слишком жалует гостей, но на твою долю выпало немало приключений, |3-6($C).$B$BСтупай, поговори с ним. Яблочко от яблоньки, как говорится, далеко не падает... наверное, он сможет научить тебя охотиться.', },
    {'Поговорите с Хемингом Эрнестуэем младшим.', },
  },
  [584] = {
    'Головы племени Кровавого Скальпа',
    {'Я не нашел Йеннику среди голов, хвала Великому Змею за это. Но мы не сильно продвинулись в наших поисках.$B$BНеобходимо выяснить правду, и, боюсь, она известна лишь вождю племени Кровавого Скальпа и его знахарю. Они враги племени Черного Копья, и просто так говорить не будут, поэтому мы должны вырвать правду из их мертвых губ!$B$BПринеси мне головы Ган\'зулаха и Неззилока Зловещего и помести их в этот котел.$B$BЭти тролли живут в глубине Руин Зул\'Кунды. Принеси их головы... а уж говорить мы их заставим.', },
    {'Принесите головы Ган\'зулаха и Неззилока и положите их в бурлящий котел в лагере Гром\'гол.', },
  },
  [585] = {
    'Разговор с Неззилоком',
    {'*Голова Неззилока начинает говорить.*$B$BЭто правда. Я знаю, куда увели дитя Черного Копья. Ты $Gодолел:одолела; меня и $Gсвязал:связала; мой дух, но я все равно не буду говорить даром!$B$BПлемя Кровавого Скальпа враждует с племенем Дробителей Черепов. Чтобы добиться от меня ответа, укради 3 трофейных черепа у племени Дробителей Черепов и положи их к нам, чтобы мой дух обрел рабов в Пустоте. Один череп возьми в Балиа\'ма, другой – в Зиата\'джаи, а последний – в Зул\'Мамве.', },
    {'Принесите в лагерь Гром\'гол трофей из Балиа\'ма, трофей из Зиата\'джаи и трофей из Зул\'Мамве и положите их в бурлящий котел.', },
  },
  [586] = {
    'Разговор с Ган\'зулахом',
    {'*Голова Ган\'зулаха начинает говорить.*$B$BБудь ты $Gпроклят:проклята;! И будь прокляты твои предки!!!$B$BЛишь кровь и месть остудят мою ярость, поэтому если ты этого добиваешься, то... сделай вот что:$B$BОтправляйся в сердце владений Дробителей Черепов на востоке. Убей самых лютых воинов, и если выживешь... брось вызов их вождю, Анатеку Жестокому. Ха! Принеси мне его разбитую броню, если сможешь!$B$BДа оторвет он у тебя руки и ноги и бросит медленно гнить. Да станешь ты добычей падальщиков!', },
    {'Убейте 8 охотников, 6 охотников за головами и 4 берсерков из племени Дробителей Черепов.$B$BПринесите разбитую броню Анатека к бурлящему котлу в лагере Гром\'гол.', },
  },
  [587] = {
    'Понюшка табака',
    {'Ах, жизнь моряцкая! Не скажу, что она хороша, но это моя жизнь.$B$BПришвартовавшись в порту, я люблю выпить бутылочку грога и нюхнуть хорошего табачку. Вредные привычки, но что за пират без пороков, а?$B$BТолько представь, как я был разочарован, узнав, что местный травник распродал весь товар. Говорит, мол, новую поставку перехватили эти проклятые пираты Кровавого Паруса.$B$BГотов поспорить, что $Gбесстрашный:бесстрашная:c; |3-6($C) вроде тебя сможет убедить их поделиться товаром, а?', },
    {'Диг из Пиратской Бухты попросил принести ему 15 понюшек табаку.', },
  },
  [588] = {
    'Судьба Йеннику',
    {'<Неззилок говорит.>$B$BТы $Gсослужил:сослужила; мне хорошую службу, и я скажу тебе все, что ты хочешь знать... $B$BЮный Йеннику потерян для своего племени.$B$BЕго забрал к себе знахарь Занзил Отверженный. Теперь Йеннику – полностью в его власти... и душой, и телом... и его может освободить только очень сильная магия.$B$BТы такой магией не владеешь, |3-6($C).$B$BНадежды практически нет. Хотя, может быть, твои союзники из Черного Копья в Бамбале на востоке все-таки сумеют спасти Йеннику. Но я сомневаюсь...', },
    {'Поговорите с Кин\'вилаем.', },
  },
  [589] = {
    'Поющие кристаллы',
    {'Легенды повествуют о драгоценном камне, сокрытом в самом сердце Тернистой долины. Мы зовем его Оком разума, и он хранит в себе великую мощь. Если добыть этот камень, быть может, удастся вернуть Йеннику из тьмы.$B$BЯ попрошу духов джунглей помочь нам в поисках Ока разума. Тем временем отправляйся в рудник Хрустальной шахты на юго-востоке отсюда и добудь пульсирующие синие осколки из железозубых василисков. Осколки встречаются очень редко, но без их магии мы не сможем подчинить себе Око разума.', },
    {'Принесите 3 пульсирующих синих осколка Кин\'вилаю в лагерь Гром\'гол.', },
  },
  [590] = {
    'Выгодная сделка',
    {'Что?$B$BЧТО?!$B$BДенег хочешь? Не должен я тебе никаких денег. $B$BПисьмо? Какое письмо? Может, еще и подеремся? Ну, $Gприятель:подружка;, давай! Вот еще, деньги я должен...', },
    {'Одержите победу в схватке с Кальвином Монтеччи в Могильнике.', },
  },
  [591] = {
    'Око разума',
    {'Око разума находится у огра-мага на востоке отсюда, в глубине холма Мош\'Огг. Этого мага зовут Май\'Зот, и я помню его с тех пор, как клан Черного Копья обитал в Тернистой долине. Он жесток, а как маг – силен.$B$BИ если у него есть Око разума, его магия еще опасней.$B$BВступи с бой с Май\'Зотом и отними у него Око разума. Задание опасное, но другой надежды спасти Йеннику у нас нет.', },
    {'Отнесите Око разума Кинвилаю в лагерь Гром\'гол.', },
  },
  [592] = {
    'Спасение Йеннику',
    {'Я превратил Око Разума в самоцвет Души. Он послужит двоякой цели: оглуши при его помощи Йеннику, а затем помести самоцвет ему на голову, чтобы поймать его душу.$B$BПотом отнеси камень Нимбойе.$B$BЙеннику живет у Занзила Отверженного, в руинах Абораза на Хрустальном взморье далекого мыса Тернистой долины.', },
    {'Отнесите Нимбойе наполненный самоцвет Души.', },
  },
  [593] = {
    'Наполнение самоцвета Души',
    nil,
    nil,
  },
  [594] = {
    'Послание в бутылке',
    {'Записка написана красивым почерком. В ней значится:$B$BЛюбому, кто может мне помочь.$B$BПрошу тебя, не оставь меня в нужде. Великий Небогрив держит меня в плену на острове в Южных морях.$B$BПо ночам я смотрю на чужие звезды, полускрытые листвой деревьев, но в самую хорошую погоду на северо-западе я вижу Южное взморье и большие корабли на юго-западе.$B$BМолю тебя, мой будущий спаситель, помоги мне.', },
    {'Отыщите автора послания в бутылке.', },
  },
  [595] = {
    'Пираты Кровавого паруса',
    {'Ты не прочь подзаработать, а, $Gприятель:подружка;? Понять не могу, зачем сухопутной крысе вроде тебя понадобилось помогать Разбойникам Черноводья, но не гнать же тебя в шею!$B$BМы только что бросили якорь в Пиратской Бухте, и уже прослышали, что наши соперники – гнусные, подлые пираты Кровавого Паруса – пустили корни в южной части Тернистой долины.$B$BВсе мои ребята слишком увлеклись грогом и девками! А мне нужен разведчик.$B$BОсмотри залив на севере от Пиратской Бухты рядом с Гибельным берегом и расскажи мне, что там творится.', },
    {'Первый помощник Кразз попросил вас осмотреть залив на севере от Пиратской Бухты.', },
  },
  [596] = {
    'Ожерелье кровавых костей',
    {'Меня послали в Гром\'гол на помощь Нимбойе, но... У меня здесь свои дела.$B$BПлемя Кровавого Скальпа – наш заклятый враг. Трофеи, собранные с врагов, дают власть над ними. Ты можешь помочь нашему племени, открыв охоту на врага.$B$BПлемя Кровавого Скальпа живет на севере отсюда. Убей побольше троллей этого племени и забери у них ожерелья Кровавых костей. Собери как можно больше, и я награжу тебя.', },
    {'Принесите Кин\'вилаю 25 ожерелий Кровавых костей в лагерь Гром\'гол.', },
  },
  [597] = {
    'Пираты Кровавого паруса',
    {'На карте Пиратской Бухты нарисован красный крестик именно там, где вы стоите.$B$BНиже имеется подпись:$B$B"Разбейте здесь лагерь и сидите тихо. Не нападайте, пока не получите сигнал с южного командного поста. Если заметите кого постороннего, сразу убивайте."', },
    {'Отнесите находку первому помощнику Краззу в Пиратскую Бухту.', },
  },
  [598] = {
    'Ожерелье Расколотых костей',
    {'Раз ты $Gпринес:принесла; трофеи от Кровавого Скальпа, у меня есть для тебя другое задание. На юго-востоке живет племя Дробителей Черепов, и мы воевали с ними много лет. Хотя племя Черного Копья давно не живет в Тернистой долине, мы не забыли раздоров с Дробителями Черепов.$B$BТак что начни на них охоту. Собери побольше ожерельев Расколотых костей и принеси их мне. Помоги нам, и заслужишь признательность клана Черного Копья.', },
    {'Принесите 25 ожерелий Расколотых костей Кин\'вилаю в лагерь Гром\'гол.', },
  },
  [599] = {
    'Пираты Кровавого паруса',
    {'Лопни моя селезенка! Значит, пираты Кровавого Паруса собираются напасть на Пиратскую Бухту? Вот наглость-то! Этот город испокон века был в руках Пиратов Черноводья!$B$BЗначит, дела обстоят хуже, чем я думал. Немедленно расскажи об этом командиру флота Морскому Рогу! Этот старый морской волк шатается где-то по городу...', },
    {'Сообщите о запланированном нападении Кровавого Паруса командиру флота Морскому Рогу.', },
  },
  [600] = {
    'Рудник Торговой Компании',
    {'Торговая Компания ведет операции в Тернистой долине, которые не дают трудолюбивым гоблинам вроде меня честно зарабатывать деньги! Пожалуйста, помоги мне!$B$BТорговая Компания обосновалась возле Хрустальной шахты к северу отсюда. Внутрь они проникнуть не могут из-за злобных василисков, но все равно умудряются добывать Поющие кристаллы в ближайших холмах.$B$BОтбери у них эти кристаллы, показав тем самым, что джунгли им не принадлежат.$B$BИ.. э... принеси в доказательство сами кристаллы!', },
    {'Принесите Шиззи Пуззыриксу из Пиратской Бухты 10 синих Поющих кристаллов.', },
  },
  [601] = {
    'Элементали воды',
    {'Элементалей воды, несомненно, призвал Газбан, гоблин-волшебник, который нашел Камень Приливов, если верить слухам. Мы знаем, что на владельца камня накладывается проклятие, которое то стирает его из реальности, то возвращает обратно в наш мир...$B$BБыть может, нам удастся снять проклятье. Один даларанский маг может исследовать следы магии на браслетах элементалей. Отправляйся на остров и принеси несколько штук.', },
    {'Принесите барону Ревилгазу в Пиратскую Бухту 6 браслетов элементалей воды.', },
  },
  [602] = {
    'Магический анализ',
    {'Насколько я знаю, магам Даларана удалось восстановить свой разрушенный город и перенести его в небо над Нордсколом, оставив позади гигантский кратер. Но некоторые маги остались здесь, и один из них обязательно найдет время посмотреть на браслеты. Насколько я знаю магов, они не устоят перед возможностью разгадать запутанную магическую головоломку.$B$BВозьми сумку с браслетами элементалей воды и узнай, не прольют ли они свет на проклятие Камня. Лучше всего обратиться к верховному магу Ансарему Руноплету. Я уже имел с ним дело, и его знания велики.', },
    {'Отнесите браслеты элементалей воды верховному магу Ансарему Руноплету в Даларан.', },
  },
  [603] = {
    'Ключ Ансарема',
    {'Маг, который владеет Камнем, может умножить его силу при помощи некой точки фокуса... вроде алтаря. Чтобы не допустить этого, необходим предмет равной силы.$B$BКак ни странно, я знаю одну очень подходящую вещь: древний кинжал, еще одну из тролльских реликвий. Только есть... небольшая проблема.$B$BЯ нашел этот кинжал, странствуя по Тернистой долине, и отдал его своей своевольной дочери, которая сбежала в Пиратскую Бухту и стала пираткой.', },
    {'Поговорите с Кателиной в Пиратской Бухте.', },
  },
  [604] = {
    'Пираты Кровавого паруса',
    {'Кразз высоко ценит твою преданность Разбойникам Черноводья. Я доверяю тебе задание, очень важное для нашего флота.$B$BПроникни на так называемый южный командный пост, который пираты Кровавого Паруса основали на Диком взморье. Разграбь весь лагерь и принеси мне их карты и записи приказов. Так мы выясним, что замышляют эти ублюдки.', },
    {'Командир флота Морской Рог из Пиратской Бухты попросил вас убить 10 сорвиголов из шайки Кровавого Паруса и принести ему карты и приказы Кровавого Паруса.', },
  },
  [605] = {
    'Осколки Поющих кристаллов',
    {'Поющие кристаллы добывают только в Тернистой долине, и некоторые покупатели готовы немало за них отсыпать. Кому их толкнуть, мне ясно, но проклятая Торговая Компания не дает собирать кристаллы честным предпринимателям вроде меня.$B$BЯ хочу тебя нанять.$B$BВасилиски Тернистой долины питаются этими кристаллами. Благодаря им у них прочная шкура, и иногда из нее можно добыть качественные кристаллы.$B$BГодится шкура любого василиска, но на северном побережье, южнее Зул\'Кунды, они не такие свирепые.', },
    {'Принесите Шиззи Пуззыриксу 10 осколков Поющих кристаллов.', },
  },
  [606] = {
    'Напугать Трусишку',
    {'Я вообще-то очень терпимый, но вот кто меня бесит, так это те, кто не платит игорные долги! А мне уже столькие задолжали... Кстати, не поможешь взыскать пару-тройку должков? $B$BВо-первых, там в доках ошивается некто Филипп по прозвищу "Трусишка". На прошлой неделе он проиграл мне изрядную сумму – и до сих пор не расплатился! Я так понимаю, чтобы забрать мои денежки, нам надо его припугнуть.$B$BСходи за город и убей пяток старых горилл Мглистой долины. Выпусти им потроха, а потом покажи "Трусишке". Только не говори, что они горилльи. Пусть подумает чего похуже.', },
    {'Отнесите 5 потрохов горилл Мглистой долины Филиппу "Трусишке" в Пиратскую Бухту.', },
  },
  [607] = {
    'Возвращение к Маккинли',
    {'Вот, возьмите этот кошель и отнесите его Морскому Волку. И передайте, что я собирался ему заплатить! Клянусь!!!$B$BЯ всего лишь немного перебрал и заблудился по пути, и... знаете, как бывает. Он поймет – не нужно применять силу!', },
    {'Отнесите плату Трусишки "Морскому волку" Маккинли в Пиратской бухте.', },
  },
  [608] = {
    'Пираты Кровавого паруса',
    {'Ну и наглец этот Фираллон! Он бы и Озеро Хрустальное на лодке не переплыл, куда ему вести против нас весь свой флот!$B$BПираты Кровавого Паруса поплатятся за свою беспечность. Теперь мы знаем все их планы и нападем первыми.$B$BХотя зачем посылать целый флот, если хватит отряда отважных искателей приключений? $N, возьми с собой самых опытных бойцов, ступай на место высадки Фираллона и лиши пиратов Кровавого Паруса всех командиров. Весь флот отправится на дно следом за ними...', },
    {'Командир флота Морской Рог из Пиратской Бухты попросил вас убить капитана Штиллеца, капитана Кильвателя и командира флота Фираллона.', },
  },
  [609] = {
    'Должники вуду',
    {'Список сокращается, но должники все еще остались.$B$BДалее идут Моури "Деревянная Нога" Вилкинс, Джон-Джон Ворон и Чаки "Недотепа".$B$BЭти паршивые собаки просрочили платежи на много месяцев, я-то думал, они поэтому из города сбежали! Потом, правда, мне сказали, что их прокляли и заколдовали, и они бродят по развалинам в джунглях. Но меня их судьба не волнует – мне бы свое вернуть!$B$BИх видели на северо-востоке отсюда, в руинах Абораза и руинах Жубуваля. Отыщи их и забери у них мои вещи.', },
    {'Принесите деревянную ногу Моури, золотую подзорную трубу Джон-Джона и огромное кольцо Чаки "Морскому волку" Маккинли в Пиратскую Бухту.', },
  },
  [610] = {
    '"Красавчик" Дункан',
    {'Так тебя прислал мой ОТЕЦ? Я думала, старик давно махнул на меня рукой.$B$BЭто он виноват, что я сбежала. С чего он взял, что я только и мечтаю о скучной жизни среди заплесневелых книг и седых, горбатых и надутых магов!$B$BЕсли тебе нужен мой кинжал, можешь отобрать его у "Красавчика" Дункана, одного из пиратов Кровавого Паруса. Я думаю, что почуствовав металл между лопаток, Дункан охотно расстанется с кинжалом.', },
    {'Отберите кинжал Кателины у "Красавчика" Дункана.', },
  },
  [611] = {
    'Проклятье приливов',
    {'Значит, ты ищешь какой-то алтарь? Я не так хорошо разбираюсь в развалинах, как мой отец, но мне приходилось слышать о затонувшем городе троллей рядом с островом, про который ты $Gспрашивал:спрашивала;. Посмотри там.$B$BТы $Gвернул:вернула; мне нож, поэтому можешь пока им воспользоваться... но не забудь принести его обратно, когда закончишь дела!', },
    {'Уничтожьте Алтарь Приливов при помощи меча Кателины, убейте Газбана и принесите Камень Приливов и меч Кателины барону Ревилгазу.', },
  },
  [613] = {
    'Вскрыть ногу Моури',
    {'Проклятье! Этот Моури поставил на свою ногу замок, и я не могу ее открыть! Его сокровища внутри, и именно их он мне должен!$B$BМне необходим ключ, чтобы вскрыть этот замок. Моури нередко играл в кости с ограми Мош\'Огг у них на холме на северо-востоке. Он успел рассказать мне, что ему пришлось спасаться бегством после прошлой игры, потому что больно много выиграл. Может быть, его ключ у огров.$B$BНо если так, придется убить целую толпу, чтобы его отыскать...', },
    {'Принесите "Морскому Волку" Маккинли из Пиратской Бухты ключ Моури.', },
  },
  [614] = {
    'Сундук капитана',
    {'Когда-то у меня был корабль, на котором я ходил до самого Кабестана в Калимдоре, и репутация у меня была безупречная.$B$BПока…$B$BПока не появились проклятые морские великаны! Они разбили мой корабль, поубивали всю команду, а меня отправили на шлюпке в Пиратскую Бухту. А один из гигантов, Горлаш, украл мой капитанский сундук, мол табакерка из него выйдет неплохая. Наглец!$B$BГоворят, Горлаш бродит по берегу к востоку отсюда. Найди его и верни мой сундук! Благодарность моя не будет знать предела.', },
    {'Верните Гекельберри Смоттсу из Пиратской Бухты его сундук.', },
  },
  [615] = {
    'Капитанская сабля',
    {'После того как эти проклятые великаны уничтожили мой корабль, я купил еще один. Назвал его "Месть Смоттса", запасся продовольствием, нанял команду и отправился в поисках этих злодеев. Найти-то я их нашел, но... они снова мне надавали по шее. "Месть Смоттса" разбили на части, поубивали вторую команду, а меня снова посадили на шлюпку.$B$BУ другого великана, Неголаша, моя сабля.$B$BСразись с Неголашем и принеси мне мою саблю!$B$BМожешь поговорить со Салагой, поваром с "Мести Смоттса", который выжил в последней битве. Он поможет тебе найти великана.', },
    {'Поговорите с Салагой.', },
  },
  [616] = {
    'Остров духов',
    {'Барон Ревилгаз велел мне не упускать слухов о странных событиях и местах в округе Пиратской Бухты и всей Тернистой долины. Обычно я не придаю большого значения историям про призраков и прочую чушь, но моряки в таверне говорили об острове элементалей недалеко от побережья. Мол, там духи Воды и сумасшедший призрак гоблина.$B$BЕсли тебе встретится Ревилгаз, перескажешь ему эту историю?', },
    {'Поговорите с бароном Ревилгазом в Пиратской Бухте.', },
  },
  [617] = {
    'Вязанка акириса',
    {'Ищешь работу, $N? Если ты не боишься трудностей, могу тебе предложить одно дельце.$B$BУ меня есть дружбан, Гроу, который зашибает бешеные деньги, продавая то, что наги называют стеблями акириса. Это очень ценная морская водоросль. Он за нее отлично платит.$B$BПритащи мне 10 стеблей этой травки, и мы сторгуемся. Наги живут на берегу к северу отсюда.', },
    {'Принесите каперу Блоудсу из Пиратской Бухты 10 свертков со стеблями акириса.', },
  },
  [618] = {
    'Бой с Неголашем',
    {'Неголаш – великан-обжора.$B$BКогда они уничтожили наш корабль, Неголаш бросился прямиком на кухню. В отличие от его товарищей, команда его не очень интересовала. Ему хотелось вина... и жареных крылышек канюка. Это мое коронное блюдо, рецепт я привез из самих Бесплодных земель!$B$BЕсли ты хочешь приманить великана, добудь вина и кучу крылышек канюка. Сложи их в нашу старую шлюпку, которая причалена на юго-востоке отсюда. Когда Неголаш почует запах еды, он непременно явится!', },
    {'Отнесите 10 жареных крылышек канюка и 5 бутылок экзотического вина в спасательную шлюпку капитана Смоттса.$B$BУбейте Неголаша и отнесите абордажную саблю Смоттса хозяину в Тернистую долину.', },
  },
  [619] = {
    'Приманка для Неголаша',
    nil,
    nil,
  },
  [621] = {
    'Тайна Занзила',
    {'Занзил Отверженный и его приспешники живут на руинах Жубуваля и Абораза на северо-востоке от Пиратской Бухты. Его изгнали из племени. Почему? Ну…$B$BЗанзил научился подчинять себе разум других, и его страшатся собственные командиры. Он заставляет своих приспешников принимать смесь из тропических растений, и она подавляет их волю и укрепляет тела.$B$BЭта смесь пригодилась бы любому, кто желает повелевать чужим разумом.$B$BПринеси мне образцы этой смеси, и уж я-то найду им применение.', },
    {'Принесите Шиззи Пуззыриксу из Пиратской Бухты 12 образцов смеси Занзила.', },
  },
  [622] = {
    'Возвращение к капралу Калебу',
    {'Пока тебя не было, я починил твой горшок и даже приделал к нему свисток. Больше того, смотри! На дне появился новый нож, который запросто рубит кости!$B$BДа-да, теперь этот горшок – настоящая радость повара! Не сомневаюсь, владелец будет доволен.', },
    {'Отнесите целый горшок Крейвела капралу Калебу в Лагерь повстанцев.', },
  },
  [623] = {
    'Вязанка акириса',
    {'$N, раз уж ты здесь, я тебе вот что скажу. Пока тебя не было, я получил послание от Гроу. Его очень порадовала последняя поставка, и клиент немедленно захотел получить еще акириса.$B$BЯ собираюсь в Терамор не раньше, чем через неделю, но если ты не против двинуть в путь прямо сейчас, почему бы тебе не отвезти товар?$B$BХочешь поприключаться? Ты знаешь, где находятся Пылевые топи? Только будь $Gосторожен:осторожна;, в Тераморе может быть опасно.', },
    {'Принесите сверток со стеблями акириса каперу Грою в Терамор.', },
  },
  [624] = {
    'Загадка Кортелло',
    {'Есть мост, изукрашен резьбою, красивый на нем узор$BК востоку от Мертвого Ветра и к югу от Красногорья$BПод ним тебя ждет подсказка ль, улика ль – как назовешь.$BИ будет на Болоте Печали лежать, пока не найдешь.', },
    {'Разгадай загадку!', },
  },
  [625] = {
    'Загадка Кортелло',
    {'Калимдор далекий ждет тебя давно,$BГде надеждам к сроку сбыться не дано.$BТы в Степях почуешь вкус пыли на губах,$BНу а в топях пылевых – лишь печаль и страх.$BНо на юго-запад путь тебя манит,$BМрак пещеры тайну для тебя хранит.', },
    {'Разгадай загадку!', },
  },
  [626] = {
    'Загадка Кортелло',
    {'Спеши же в славный Лордерон! Давно тебя он ждет.$BТам с мором, гибелью и тьмой сражается народ. $BПусть не страшит тебя война и битвы шум и гам – $BЗато задание свое исполнить сможешь там. $BВот берег Внутренних земель, где шумный водопад, $BТам, под водами его, тебя ждет дивный клад!', },
    {'Разгадай загадку!', },
  },
  [627] = {
    'Просьба Кразека',
    {'В Теснине Сухоусов – далеко на севере, на нагорье Арати – есть пещера. Там добывают редкие полезные ископаемые – кровавые камни. Да, они там не лучшего качества, но даже самые плохие кровавые камни высоко ценятся... теми, кто знает что с ними делать.$B$BЯ – знаю.$B$BЯ починю твой разбитый горшок, но пока я работаю над ним, принеси мне образцы кровавого камня. Если ты принесешь их достаточно, я награжу тебя.$B$BАх, да, если ты не умеешь добывать полезные ископаемые... попроси кого-нибудь тебе помочь!', },
    {'Принесите 4 партии бедной кровавой руды Кразеку в Пиратскую Бухту.', },
  },
  [628] = {
    'Эксельзиор',
    {'Подбой готов, осталось сделать верхний слой. На него идут только наилучшие шкуры взрослых кроколисков. Они крепче и представляют собой лучшую основу для узоров, которыми славится линия превосходных сапог.$B$BХотя найти эту ценную шкуру нелегко, мне подходит лишь шкура старого солоноводного кроколиска. Эти твари выходят лишь на защиту молоди, поэтому может понадобиться убить несколько молодых зверей, чтобы выманить нужного.', },
    {'Принесите Дриззлику из Пиратской Бухты шкуру старого солоноводного кроколиска.', },
  },
  [629] = {
    'Коварный риф',
    {'Давным-давно вода затопила древний город троллей рядом с Гибельным берегом, что на северо-западе. Мы зовем это место Коварным рифом, потому что в развалинах поселились мурлоки, которые нападают на всех, кто осмелится приблизиться.$B$BСреди руин есть древняя табличка, повествующая о древнем герое троллей, Гри\'леке. Это сказание священно в племени Черного Копья, и хотя сама табличка слишком велика и ее не унесешь, я хочу получить хотя бы осколок, чтобы поместить его в святилище в нашем новом городе, Оргриммаре.', },
    {'Принесите осколок таблички Кин\'вилаю в лагерь Гром\'гол.', },
  },
  [630] = {
    'Послание в бутылке',
    {'$N, ты и те, кого ты сможешь позвать мне на помощь – моя последняя надежда. Меня держит в плену великий Король Мукла. Он повелевает зверями на этом острове, и они держат меня здесь помимо моей воли на этой цепи. Если ты сможешь убить моего мучителя и отыскать ключ к кандалам, я буду навеки у тебя в долгу.$B$BЯ часто слышу шаги Короля Муклы на юго-восточной стороне острова. Соблюдай осторожность, справиться с ним нелегко.', },
    {'Убейте Короля Муклу, возвращайтесь с ключом к принцессе Пубе и освободите ее.', },
  },
  [631] = {
    'Мост Тандола',
    {'Я помню, как горы содрогнулись от ужаснейшего взрыва. Неслабо рвануло, скажу я тебе!$B$BНа мосту Тандола погибли лучшие из наших солдат. Длиннокос говорит, что дворфы Черного Железа еще заплатят нам за все до конца этой войны.$B$BНо сейчас у нас отчаянное положение, подкрепления все нет и нет. Длиннокос отправил Ржавого Засова на разведку к восточному мосту – посмотреть, можно ли защитить мост теми силами, которые сейчас у нас есть.$B$BПрошло уже несколько дней. Мы не можем отправить солдат на поиски – Дун Модр осажден...$B$BБыть может, ты согласишься поискать нашего разведчика?', },
    {'Найдите Ржавого Засова для Рага Гармасона.', },
  },
  [632] = {
    'Мост Тандола',
    {'Кажется, бедняга Ржавый Засов попал в засаду дворфов Черного Железа. Возле его остывшего тела лежит клочок пергамента.$B$BРасскажите Рагу Гармасону о случившемся и передайте ему пергамент.', },
    {'Принесите печальное известие Рагу Гармасону в Дун Модр в Болотину и отдайте ему пергамент, найденный возле трупа Ржавого Засова.', },
  },
  [633] = {
    'Мост Тандола',
    {'Во имя Света, только не это! Бедняга Ржавый Засов. Ты меня $Gопечалил:опечалила;, $N.$B$BНо его жертва будет не напрасной! В пергаменте сказано, что Кам Гневливый – тот, кто организовал нападение, – приказал заложить взрывчатку под мостом Тандола на границе с нагорьем Арати.$B$BЕсли ему удастся очередная диверсия, то мы лишимся поставок с севера! Взрыв нужно предотвратить. Сделай все возможное, чтобы спасти мост Тандола, и возвращайся ко мне.$B$BТы – наша единственная надежда.', },
    {'Уничтожьте взрывчатку.', },
  },
  [634] = {
    'Просьба о помощи',
    {'Я попрошу тебя о еще одном одолжении, |3-6($C). Мы ожидаем подкрепление из Стальгорна. Рогго принес сведения о лагере воинов Альянса к северу отсюда. Они расположились на нагорье Арати, возле Опорного пункта.$B$BОтправься туда и отыщи капитана Ниалс. Спроси у очаровательного капитана, не выделит ли Альянс нам отряд солдат, чтобы помочь отбить Дун Модр и мост Тандола.', },
    {'Найдите капитана Ниалс в Опорном пункте на нагорье Арати.', },
  },
  [635] = {
    'Горный кристалл',
    {'Осколок пульсирует у вас в руках…$B$BВам кажется, что он молит о чем-то, пытается передать послание того, кто нуждается в вашей помощи.$B$BВам является видение – в северных горах Арати из земли торчит сияющий кристалл.$B$BВас так и тянет к этому месту.', },
    {'Найдите кристалл из видения.', },
  },
  [636] = {
    'Легенды Земли',
    {'В книге идет речь о принцессе элементалей, Мизраэль, и ее заточении глубоко под нагорьем Арати.$B$BТам же говорится о тайной россыпи кристаллов на нагорье. Эти кристаллы Мизраэль – окна в темницу принцессы.$B$BЕсли отважная душа отыщет эти осколки, быть может, $Gон:она; сможет поговорить с Мизраэль...', },
    {'Найдите кристаллы Мизраэль.', },
  },
  [637] = {
    'Письмо Салли Баллу',
    {'Промокший и потрепанный конверт разваливается в руках, и в ладонях остается письмо на нескольких страницах. Каждая буковка выведена с удивительной аккуратностью.$B$BПисьмо адресовано некой миссис Саре Баллу из Стальгорна. Оно начинается: "Дорогая Сара..."', },
    {'Отнесите письмо Салли Баллу Саре Баллу.', },
  },
  [638] = {
    'Троллебой',
    {'Торас Троллебой мертв, а Стромгард разрушен. Вол\'джин велел мне приложить все усилия, чтобы вырвать легендарный меч Трол\'калар из рук людей. Тогда мы сможем воспользоваться им в грядущих битвах с троллями Зул\'Гуруба.$B$BЗенгу, один из наших опытных охотников, отправился в Павший Молот в северо-восточной части нагорья Арати, чтобы проследить за этим.', },
    {'Отправляйтесь в Павший Молот и поговорите с Зенгу.', },
  },
  [639] = {
    'Печать Строма',
    {'В древние времена тролли воевали с Араторской империей и нашим самым могучим противником, лордом Игнеусом Стромским по прозвищу Троллебой. Он бросался в бой с мечом Трол\'каларом в руке – "убийцей троллей", на их древнем наречии.$B$BНо теперь сыновья Троллебоя ослабели, а цитадель их разрушена.$B$BМеч защищен несколькими печатями, которые были распределены между выжившими защитниками Стромгарда, и мы должны их отыскать.$B$BПервую из печатей захватил Синдикат, должно быть, она находится у одного из их людей в Стромгарде.', },
    {'Добудьте печать Строма для Зенгу в Павшем Молоте.', },
  },
  [640] = {
    'Сломанная печать',
    {'Могила Тораса Троллебоя защищена тремя печатями, первую из которых вы уже раздобыли у Синдиката. Остальные две достать будет сложнее, поскольку ими владеет гораздо более сильное ополчение Стромгарда.$B$BПечать Торадина была разделена на одиннадцать частей сыном Тораса Галеном, а потом он распределил эти фрагменты между стромгардскими защитниками Соберите фрагменты и принесите их Тор\'гану, троллю-шаману, – он сможет восстановить целостность печати.', },
    {'Добудьте у защитников Стромгарда 5 фрагментов печати, принесите их Тор\'гану в Павший Молот.', },
  },
  [641] = {
    'Печать Торадина',
    {'Восстановление прошло без сучка и задоринки. Теперь печать можно использовать, чтобы отпереть могилу Троллебоя. Верните печать Зенгу. Несомненно, у него найдутся еще для вас задания.', },
    {'Принесите восстановленную печать Торадина Зенгу в Павший Молот.', },
  },
  [642] = {
    'Пойманная принцесса',
    {'Меня зовут Мизраэль. Я принцесса духов земли, и мои пленители – великаны – поймали меня в ловушку глубоко под нагорьем Арати. С миром на поверхности я могу говорить только при помощи этих кристаллов.$B$BПожалуйста, помоги мне. У союзников гигантов – кобольдов из племени Сухоусов – есть такие же кристаллы, они находятся в Теснине Сухоусов, к востоку отсюда. Чтобы объединить их силу, нужно отобрать у кобольдов частицы Мизраэль.$B$BПрошу тебя, $N, помоги мне!', },
    {'Соберите 12 частиц Мизраэль и принесите их к радужным осколкам в Теснине Сухоусов.', },
  },
  [643] = {
    'Печать Аратора',
    {'Хотя принц едва ли может похвастаться властью над Стромгардом, не говоря уж обо всем нагорье Арати, тем не менее он разъезжает с остатками своей кавалерии по всей области. Я не могу сказать, зачем он это делает, но кто поймет, как мыслят эти люди?$B$BКакова бы ни была причина, более важно то, что он отдал одну из своих печатей лейтенанту Храброзову, командиру кавалерии. Найдите его и принесите мне эту печать.', },
    {'Добудьте Печать Аратора и принесите ее Зенгу в Павший Молот.', },
  },
  [644] = {
    'Печать Троллебоя',
    {'Последняя из печатей, необходимых, чтобы отпереть могилу Тораса Троллебоя, хранится у его сына. Он обитает в Стромгарде, в часовне, в том районе, что контролируется ополчением. У принца Галена хорошая охрана, да и сам он боец не промах, так что это задание может оказаться наиболее сложным.', },
    {'Убейте принца Галена Троллебоя и принесите печать Троллебоя Зенгу в Павший Молот.', },
  },
  [645] = {
    'Трол\'калар',
    {'Что это? Когда четыре печати оказались вместе, они соединились, образовав единую печать. Нет сомнений, что она сможет разбить охранные заклинания, наложенные на могилу Троллебоя.$B$BОстанки Троллебоя захоронены в святилище позади часовни в Стромгарде. Принесите же Трол\'калар.', },
    {'Достаньте Трол\'калар из могилы Троллебоя в Стромгарде.', },
  },
  [646] = {
    'Трол\'калар',
    {'Медленно угасает свет. Меч Трол\'калар, упрятанный в каменные ножны, освобожден.', },
    {'Отнесите меч Трол\'калар Зенгу в Павший Молот.', },
  },
  [647] = {
    'Самогон Маккрила',
    {'Ох, и хлебнул я вчера самогона! Узнай Длиннокос, что я отрубился на посту, он оторвал бы мне голову.$B$BБашка раскалывается, жуть. И в ушах гремит, будто осадные машины вокруг грохочут.$B$BОй-ой, а времени сколько! Я обещал мастеру-пивовару Бочкопузу из Южнобережья вернуть должок своим самогоном.$B$BА осталось до истечения срока действия долга всего 15 минут! Отнеси ему самогон, будь другом! И поскорее!', },
    {'Отнесите самогон Маккрила мастеру-пивовару Бочкопузу в Южнобережье.', },
  },
  [648] = {
    'Спасти КПХ-17/TN!',
    {'Из динамика снова слышится искаженный помехами голос гнома:$B$B"Мне необходимо перегнать КПХ-17/TN в просторное и безопасное место, чтобы провести длительную процедуру наладки. Проводите робота из того места, где вы его обнаружили, в... ну, скажем, к Пещерам Времени! Это самое подходящее место, и там достаточно просторно, чтобы подготовить аппарат к новому полету.$B$BПроводите робота в порт, а затем сообщите об этом мне. Я живу в Пиратской Бухте, меня зовут Карлс Пропеллерз. Конец связи!"', },
    {'Сопроводите КПХ-17/TN в безопасное место, затем сообщите об этом Карлсу Пропеллерзу в Пиратской Бухте.', },
  },
  [649] = {
    'За медовухой',
    {'Хочу я поручить тебе одно дельце. Если ты любишь добрую медовуху, как и я, оно тебе понравится. Медовуха!$B$BЭто напиток, балда!$B$BНа заброшенной заставе Орды хранится запас "Медовой зыби" Внутренних земель. Такого славного пойла нынче больше не делают! Один из этих балбесов, дружков Мальтона, знает туда дорогу. Сходи к нему, принеси бочонок медовухи и еще пару бутылок мне лично. Поговори с Мальтоном, он тебе все расскажет.', },
    {'Поговорите с Мальтоном Дрофферсом в Оргриммаре.', },
  },
  [650] = {
    'За медовухой',
    {'Отправляйся к эльфу по имени Гилверадин Ловчий Солнца. Он так разбирается в винах, что умеет по запаху определить не только сорт вина, но и какого оно урожая! Он может прислать медовуху. Шесть бутылок на продажу, две отцу... а может, и больше – он не откажется! В общем, забери медовуху и возвращайся сюда.$B$BГилверадин живет неподалеку от пристанища высших эльфов во Внутренних землях. С эльфами этими лучше не связываться, уж поверь мне!', },
    {'Поговорите с Гилверадином Ловчим Солнца во Внутренних землях.', },
  },
  [651] = {
    'Связывающие камни',
    {'Четыре связывающих камня разбросаны по нагорью Арати. Эти камни пригвоздили меня к земле.$B$BЧтобы оковы исчезли, нужно снять замок с самого мощного из них – с Внутреннего связывающего камня. А чтобы это сделать, придется собрать ключи с остальных трех камней.$B$BЭти камни охраняют стражи, но если у тебя хватит ума – или удачи, – тебе удастся найти возможность их нейтрализовать.', },
    {'Добудьте огненный ключ, резной ключ и громовой ключ с западного связывающего камня, восточного связывающего камня и внешнего связывающего камня.$B$BОтнесите ключи к внутреннему связывающему камню.', },
  },
  [652] = {
    'Разбить краеугольный камень',
    {'Открыв замки связывающих камней, ты $Gосвободил:освободила; меня от большей части моих оков, но кое-что еще удерживает меня. Это краеугольный камень, который ты видишь среди других камней круга. А ключ от него держит при себе один из моих пленителей, каменный страж Фозрук.$B$BВы должны заполучить у Фозрука этот ключ, который каменные стражи называют Жезлом Порядка, и использовать его для открытия краеугольного камня.$B$B$N, вы так много сделали, чтобы мне помочь... Еще немножко... и моя благодарность потрясет все земные устои!', },
    {'Найдите и убейте Фозрука. Принесите Жезл Порядка к краеугольному камню на нагорье Арати.', },
  },
  [653] = {
    'Союзники Мизраэль',
    {'<Говорит Мизраэль.>$B$BСвободна! Я СВОБОДНА! Я могу теперь набирать силу, скрывшись от моих пленителей. Потому что, если бы я предстала перед ними сейчас, они бы наверняка пересилили меня и вновь заключили бы в оковы.$B$BНо в должный час я пойду против великанов, и они еще пожалеют о том, что отняли у меня свободу!$B$BВы $Gблагородный союзник:благородная союзница;, $N. Через некоторое время мне вновь понадобится ваша помощь. Когда я буду готова, нужно, чтобы вы призвали меня на поверхность.$B$BПоговорите с Герриком Костохватом. Он обитает в Стальгорне, в Заброшенном гроте. Он знает, как меня можно призвать.', },
    {'Поговорите с Герриком Костохватом в Заброшенном гроте в Стальгорне.', },
  },
  [654] = {
    'Полевые испытания в Танарисе',
    {'После подключения источника энергии набор для полевых испытаний начинает светиться и вибрировать. Похоже, он работает как надо.$B$BЯ позабочусь о доставке.$B$BВсе необходимые образцы ты можешь найти в пустыне Танарис.$B$Bсобрав их, доложи о выполнении главному инженеру Чепухастеру.', },
    {'Добудьте приемлемые образцы тканей 8 василисков, 8 гиен и 8 скорпионов. Отнесите набор для проведения испытаний главному инженеру Чепухастеру в Прибамбасск до окончания действия источника энергии.', },
  },
  [655] = {
    'Павший Молот',
    {'Воспоминания по-прежнему ярки, как будто все было вчера... Так много лет без надежды, прикованный к этой проклятой дыре деревом и сталью. И вот наконец появился вождь: Молот Рока блещет полуночными доспехами, высоко вздымает боевой молот...$B$BКак же это подло, как непереносимо, что удар труса мог сразить нашего величайшего героя. Враг наскочил сзади и пронзил его копьем... Я упал на колени. Какая могла быть надежда? Какая еще может быть надежда...', },
    {'Поговорите с Тор\'ганом в Павшем Молоте.', },
  },
  [656] = {
    'Призыв принцессы',
    {'Вы должны нанести поражение Мизраэль прежде, чем она обретет полную силу. Если вы этого не сделаете, то, как только она наберет достаточно сил, она кинется на своих пленителей! Есть такие, кто считает, что может ее контролировать, но если ее можно контролировать, с чего было великанам с самого начала заковывать ее в цепи?$B$BПотрясающая логика, да?$B$BЧтобы сразить Мизраэль, призовите ее у ее кристаллов, где вы в первый раз говорили с ней. Нанесите ей поражение и заберите ее мистические оковы, а потом подсоедините их к кристаллам Мизраэль.$B$BСделайте так – и она вновь будет поймана в ловушку.', },
    {'Отправляйтесь к кристаллам Мизраэль, вызовите принцессу и победите ее.$B$BЗаберите ее мистические оковы и присоедините их к кристаллам.', },
  },
  [657] = {
    'Новая чума зарождается?',
    {'*Кви начинает просматривать папку.*$B$BЭто в самом деле интересно! Похоже, аптекарь Йорель проводил здесь, на ферме Го-Шека, какие-то смертельно опасные эксперименты. Я не знаю точно, какое это имеет отношение к Хилсбраду, но Фину не понравится, если мы проигнорируем такое дело.$B$BМы тут столкнулись с орками в ваше отсутствие, и теперь я не настолько хорошо себя чувствую, чтобы отправиться на помощь к Кинелори. Но если вы не против, проводите ее к ферме, где она собирается выкрасть результаты исследований, которые Йорель так и не отослал пока на Мельницу Таррен.', },
    {'Поговорите с Кинелори на Ферме Го\'Шека, что на нагорье Арати.', },
  },
  [658] = {
    'Новая чума зарождается?',
    {'Так вас Фин послал узнать, как мы тут? Неудивительно – он слишком много беспокоится.$B$BМы уже много дней наблюдаем за этой фермой, и Кин уверяет, что недавно видела гонца, выходящего из вон того маленького домика с южной стороны.$B$BОна любительница поболтать, но я всегда доверяю ее ощущениям, когда мы бываем в опасных местах.$B$BА вы часом не следопыт, а? Мы думаем, что гонец может относить записки в деревню Мельница Таррен. Если бы мы могли заполучить эти документы, это пролило бы свет на события в Хилсбраде.', },
    {'Найдите Отрекшегося-курьера и принесите обнаруженную у него запечатанную папку Кви близ фермы Го\'Шека.', },
  },
  [659] = {
    'Новая чума зарождается?',
    {'Приветствую, $N, приятно познакомиться. Я руковожу группой смельчаков, расследующих странные происшествия в этом районе.$B$BМоим родственникам в Хилсбраде недавно пришлось избавиться от своей собаки Стэнли, потому что животное таинственным образом выросло до невообразимых размеров и стало кидаться на каждого встречного.$B$BЯ уверен, что тут замешана нежить, и мы ищем доказательства.$B$BДве моих спутницы, Кви и Кинелори, отправились к ферме Го\'Шека, что находится на нагорье Арати, и давно не подавали о себе вестей. Найдешь их? А то я начинаю беспокоиться.', },
    {'Найдите Кви и Кинелори у фермы Го\'Шека на нагорье Арати.', },
  },
  [660] = {
    'Новая чума зарождается?',
    {'Ладно, $N. Если вы готовы, то и я тоже.$B$BПопробуем?', },
    {'Защищайте Кинелори, пока она не вернется к Кви.', },
  },
  [661] = {
    'Новая чума зарождается?',
    {'После ночной гулянки в таверне Кинелори выглядела похуже, чем сейчас. Видно, вы неплохо ее защищали.$B$BТо, что она принесла с фермы, должно как минимум дать Фину представление о том, что происходит здесь, в Арати. Для того чтобы удоствоериться, что после вашей атаки не последует ответного удара, мы здесь ненадолго задержимся. Вы не откажетесь отправиться обратно в Южнобережье и поговорить с Фином?', },
    {'Найдите Фина Оделика в Южнобережье, что в Предгорье Хилсбрада.', },
  },
  [662] = {
    'Поиски на глубине',
    {'О\'Брин повел корабли в бухту. Мы были уверены, что именно здесь будет найдено сокровище. "Вестник" причалил нормально, но когда "Девичья Блажь" и "Дух Серебряного бора" приблизились к берегу, вода начала быстро понижаться. Страннейшая вещь, я такого в жизни не видал!$B$BТе два корабля налетели на что-то... нечто вроде храма в рифе. Оба быстро затонули – это была настоящая трагедия.$B$BНужно найти судовые журналы и карты обоих кораблей, прежде чем мы отчалим. Мы не можем оставить эту информацию доступной для любопытных глаз!', },
    {'Первый помощник Нилзликс хочет, чтобы вы достали карты и судовые журналы кораблей "Девичья Блажь" и "Дух Серебряного бора".', },
  },
  [663] = {
    'Эй, там!',
    {'Ой, кого видит Лоло! Это ж крошка- |3-6($C)! Лоло всегда начеку!$B$BДобро пожаловать в Бухту Фальдира. Капитан О\'Брин сказал, что мы задержимся тут лишь на несколько часов. Ровно настолько, сколько потребуется, чтобы добыть сокровище и отчалить обратно в Пиратскую Бухту.$B$BНо Лоло думает, мы тут надолго засели. Из нашей эскадры мы потеряли два корабля. Беднягу "Дух Серебряного бора" и несчастную "Девичью Блажь". На дне морском они теперь!$B$BЛоло думает, вам бы переговорить с капитаном О\'Брином, если вы тут и дальше слоняться собираетесь.', },
    {'Поговорите с капитаном О\'Брином в бухте Фальдира, что на нагорье Арати.', },
  },
  [664] = {
    'Затонувшие печали',
    {'Ох, жизнь – ужасная штука. Я должен бы покоиться в водяной могиле, а не сидеть здесь у огня, наслаждаясь свежим воздухом.$B$BКоманда не жалела сил, чтобы спасти мою прекрасную "Девичью Блажь". Да упокоятся ребята в мире, благословенны их сердца.$B$BО\'Брину удалось выбраться на берег, а нам не повезло. Море просто опрокинулось на нас, клянусь вам.$B$BВот мы плывем – и вот следующая волна обрушивается на нас. А эти твари! Они убили всех, кто выжил. Подлое нагово отродье. Я вырубился и пришел в себя уже здесь.$B$BЯ жажду их мерзкой холодной крови!', },
    {'Капитан Стальные Нервы из бухты Фальдира хочет, чтобы вы убили 10 налетчиков и 3 колдуний из клана Пронзающего Гребня.', },
  },
  [665] = {
    'Утонувшее сокровище',
    {'Теперь, когда мы настоящие полноценные разбойники Черноводья, мы должны помочь мистеру О\'Брину разыскать пропавшее эльфийское сокровище.$B$BПочти невозможно найти драгоценные камни в море без посторонней помощи. Доктор сконструировал специальные очки, которые должны помочь. Ему нужно зарядить эти очки энергией, извлеченной из заколдованного камня в пещере, расположенной выше по холму.$B$BНо эта пещера проклята! Когда мы подошли к ней, на нас напали. Защитите меня, и я смогу вытянуть энергию из камня и направить ее в очки.', },
    {'Проводите профессора Физзлторпа в пещеру и обратно.', },
  },
  [666] = {
    'Утонувшее сокровище',
    {'Сокровища так долго лежали на дне морском, что покрылись известковым налетом, превратившись в большой камень. Но сила, влитая в эти очки, позволит вам легко определить их местонахождение.$B$BМаленькая гномская хитрость делает большие дела!$B$BВозьмите эти очки Ловца сокровищ, $N, посмотрите, может, вам удастся собрать какие-то из утерянных сокровищ для капитана О\'Брина.$B$BЯ бы и сам поплыл туда, но... эм... ну, у меня есть серьезные научные задачи, требующие срочного решения на надежной, сухой земле... да... вот.', },
    {'Доктор Драксельмер в бухте Фальдира хочет, чтобы вы собрали 10 эльфийских самоцветов и вернули ему очки Ловца сокровищ, когда закончите.', },
  },
  [667] = {
    'Смерть со дна морского',
    {'Это плохо. Похоже, клан Пронзающего Гребня готовится к нападению.$B$BНаги известны своей жестокостью в бою. Они не остановятся, пока я не умру.$B$BНе то чтобы я ценил свою жизнь выше, чем жизни членов команды, но вы должны помочь защитить меня. Я единственный, кто знает морские пути, – команда просто не сможет вернуться в Пиратскую Бухту без меня.$B$BНаги придут с моря. Мне нужно, чтобы вы оставались здесь, со мной, чтобы отразить их атаку. Орудия к бою! Надо оттеснить их.$B$BГотовы, $N?', },
    {'Защитите Мураша О\'Брина во время атаки наг.', },
  },
  [668] = {
    'Утонувшее сокровище',
    {'Не будем заставлять капитана О\'Брина ждать. Он захочет первым увидеть эти драгоценности. В конце концов, для того мы и здесь!$B$BА через несколько недель сотрудничества с этими пиратами мы с профессором поняли, что на нашем здоровье самым пагубным образом может сказаться, если нас застанут придерживающими их сокровища.$B$BВот, $N, отнесите все О\'Брину.', },
    {'Отнесите эльфийские самоцветы капитану О\'Брину в бухту Фальдира, что на нагорье Арати.', },
  },
  [669] = {
    'Утонувшее сокровище',
    {'Командир флота Морской Рог захочет сразу же услышать о нашей находке.$B$BНо, как видите, мы пока не в том положении, чтобы сняться отсюда. Не только прилив очень низок, но еще и эти проклятые твари, которым мы помешали, бдительно следят за нашими передвижениями.$B$BВы оказали бы большую услугу Пиратам Черноводья, если бы смогли сообщить флотовладельцу Морскому Рогу в Пиратскую Бухту, что мы отыскали сокровища и работаем над тем, чтобы достать еще. А этот образчик отвезите ему в качестве доказательства.', },
    {'Доставьте образец эльфийского самоцвета командиру флота Морскому Рогу в Пиратскую Бухту, что в Тернистой долине.', },
  },
  [670] = {
    'Утонувшее сокровище',
    {'Слушайте, $N, у вас лицо искателя приключений. Мой флот застрял в Пиратской Бухте, пока мы не загрузимся.$B$BТам, да и не только там, творятся темные делишки, а мне нужно доставить кое-какую секретную корреспонденцию Мурашу О\'Брину, пришвартовавшемуся к побережью Арати.$B$BМогу ли я доверить вам доставку этого сообщения в целости и сохранности и надеяться на вашу готовность хранить это задание в секрете?', },
    {'Отвезите письмо Морского Рога Мурашу О\'Брину в Бухту Фальдира, что на нагорье Арати.', },
  },
  [671] = {
    'Зловещая магия',
    {'Я почувствовал что-то странное в воздухе... ощущение, будоражащее мою кровь. У меня возникли подозрения. Я твердо решил найти источник беспокойства и обнаружил на западе, у Северного поместья, остаточные следы призыва демона. Когда я приблизился, кровь буквально вскипела, и ярость разгорелась во мне.$B$BНа земле я заметил амулет из кровавого камня, какими пользуются, вызывая демона. Однако магическая сила найденного мной камня была уже полностью исчерпана. Мне нужен для изучения другой образец, но я не рискую возвращаться в поместье. Можете ли вы принести мне оттуда несколько амулетов?', },
    {'Принесите Тор\'гану в Павший Молот 10 амулетов из кровавого камня.', },
  },
  [672] = {
    'Вызов духов',
    {'Мы пытались успокоить его, развеселить, рассказывать ему истории о нашем огромном городе Оргриммаре в Калимдоре, но его ничто не трогало.$B$BИстории о нашем молодом вожде, Тралле, который носит доспехи и молот Оргрима... но, быть может... Может быть! Демонстрация нашей новообретенной шаманской магии могла бы дать ему луч надежды. Попробовать стоит!$B$BМы могли бы сделать для него маленькую вещицу, да... Принесите мне несколько глаз раптора, и я посмотрю, что можно сделать.', },
    {'Соберите 10 глаз ящеров нагорья с долгоногов и твердозубов нагорья, принесите их Тор\'гану в Павший Молот.', },
  },
  [673] = {
    'Зловещая магия',
    {'У меня нет сомнений, что в стенах Стромгарда засел могущественный чернокнижник. Время от времени, используя обладающую магической энергией реликвию, этот чернокнижник призывал в больших количествах ужасных демонов. Мы должны отнять у него этот источник энергии.$B$BНайдите чернокнижника и убейте его. Принесите мне магический объект, который найдете при нем, чем бы он ни оказался. Я предприму шаги по разрушению реликвии и прослежу, чтобы ее мощь больше не использовалась для осквернения этой земли.', },
    {'Добудьте сферу кровавого камня у чернокнижницы Марез Клобук и принесите ее Тор\'гану в Павший Молот.', },
  },
  [674] = {
    'Вызов духов',
    {'Вот, отнесите этот амулет Гор\'мулю и посмотрите, пробудит ли сила ящера в нем тот боевой огонь, который некогда пылал в его крови.', },
    {'Отдайте амулет из когтя ящера Гор\'мулю. Его можно найти в Павшем Молоте, что на нагорье Арати.', },
  },
  [675] = {
    'Вызов духов',
    {'*Гор\'муль бросает амулет на землю.*$B$BВас Тор\'ган послал, да? Тьфу... Никак не могу понять, почему он жалеет меня. В сострадании мне было отказано, когда мне не позволили умереть вместе с моим вождем во время боя. Жестокий удар судьбы настиг меня...', },
    {'Поговорите с Тор\'ганом в Павшем Молоте, что на нагорье Арати.', },
  },
  [676] = {
    'Да падет молот',
    {'Слабаки... Все они слабаки. А ты, |3-6($C), куда смотришь? Думаешь, $Gособенный:особенная;? Проверим?$B$BНа форт Павший Молот напали враги. Если не помочь нашим, им не устоять.$B$BВпрочем, не спеши. Прежде надо испытать твое искусство. На северо-востоке от Стромгарда в Арати есть курган, который заселило огрское племя Тяжелого Кулака. Когда одолеешь тамошних огров и мародеров, доложи об этом Драму Желчному в Павшем Молоте.', },
    {'Убейте 8 огров из клана Тяжелого Кулака и 10 головорезов из клана Тяжелого Кулака, затем найдите Друма Скверного в Павший Молот на нагорье Арати.', },
  },
  [677] = {
    'К оружию!',
    {'Тролли племени Сухокожих и огры клана Тяжелого Кулака с нагорья Арати объединились, чтобы выжить нас с этой земли, но мы не собираемся уступать ни им, ни кому угодно другому. Мы убьем их и покажем, что и мы здесь – у себя дома.$B$BТролли позорно бежали от нас; огры сожрали бы нас и спали бы на наших шкурах, если бы мы им позволили, – этим тварям нельзя жить.$B$BМы начнем атаковать троллей Сухокожих к югу отсюда. Убивай их пачками и возвращайся только тогда, когда их поселения будут залиты кровью.', },
    {'Убейте 10 метателей топоров из племени Сухокожих, 10 охотников за головами из племени Сухокожих и 8 знахарей племени Сухокожих, а затем возвращайтесь к Драму Желчному на заставу Павшего Молота.', },
  },
  [678] = {
    'К оружию!',
    {'Твоя сила очевидна, $N. Теперь посмотрим, как ты справишься с настоящим врагом.$B$BУ клана Тяжелого Кулака есть логово к юго-западу отсюда по направлению к дворфийскому мосту, и, если я не ошибаюсь, их молотобойцы и маги должны стать для тебя более серьезным испытанием – по крайней мере, для нас они таковым являются по сию пору.$B$BУбей столько огров, сколько сможешь, и возвращайся ко мне во плоти, если добьешься успеха, или в виде призрака, если потерпишь неудачу. В любом случае я воздам хвалу твоей стойкости в бою.', },
    {'Убейте 10 громил и 4 магов из клана Тяжелого Кулака, затем возвращайтесь к Драму Желчному на заставу Павшего Молота.', },
  },
  [679] = {
    'К оружию!',
    {'Зверю вот-вот отрубят голову... и все же для решающего удара требуется огромная сила, $N. Уверен, именно ты можешь помочь нам нанести этот последний мощный удар. Руководящие этими атаками устроили свое логово прямо в руинах Стромгарда – вот яркий пример человеческой слабости...$B$BНайди и убей вождей клана Тяжелого Кулака и их прислужников-шаманов, пытающихся выжить нас с этих земель.', },
    {'Убейте 7 шаманов клана Тяжелого Кулака и 3 вождей клана Тяжелого Кулака и возвращайтесь к Драму Желчному на заставу Павшего Молота.', },
  },
  [680] = {
    'Настоящая угроза',
    {'Вы отлично служите моему мужу, |3-6($C), но он не знает, почему Павший Молот подвергается атакам.$B$BОр\'Калар – пешка в руках Муг\'Тола в Стромгарде – яро ненавидит моего мужа и ничего так не желает, как его смерти – за то, что тот нанес ему поражение много лет назад.$B$BМои гадатели сообщают, что Ор\'Калар устроил для мужа ловушку, поэтому я скрыла от него, что огр объявился в этих землях.$B$BМой муж ринется сломя голову, если узнает о местонахождении огра, а я хотела бы попросить вашей помощи в убийстве этой твари.', },
    {'Убейте Ор\'Калара и принесите его голову Корин Желчной на заставу Павшего Молота.', },
  },
  [681] = {
    'Северное поместье',
    {'Мы ведем войну со Стромгардом и терпим в ней поражение. Мы делим наш некогда великий город с ограми и Синдикатом, каждая группировка бьется с другой, и конца этому не видно.$B$BНо наши силы иссякают, в то время как силы наших врагов – растут.$B$BНам нужна помощь из Южнобережья, но есть проблемы с доставкой припасов, поскольку Синдикат контролирует территорию между нами.$B$BПойдите на базу Синдиката в Северном поместье и убейте их. Тогда, может быть, давление на наши транспортные пути ослабеет, и доставлять припасы станет проще.', },
    {'Убейте 10 проходимцев из Синдиката и 6 наемников Синдиката.$B$BЗатем вернитесь к капитану Ниалс в Опорном пункте.', },
  },
  [682] = {
    'Знаки Стромгарда',
    {'Синдикат в Стромгарде воюет с силами нашего ополчения. Чтобы побудить толпу биться с нашими более дисциплинированными войсками, руководство Синдиката объявило о награде за убийство наших солдат, предлагая золото за каждый знак Стромгарда, снятый с убитых ополченцев.$B$BМы хотим вернуть себе эти знаки – не можем же мы позволить головорезам брать трофеи с тел наших героически погибших ребят!$B$BОтправляйтесь в район Стромгарда, контролируемый Синдикатом, и отберите у их бойцов наши знаки.', },
    {'Принесите 7 знаков Стромгарда Капитану Ниалс в Опорный пункт.', },
  },
  [683] = {
    'Мольба Сары Баллу',
    {'$N, у меня есть к вам маленькая просьба.$B$BВозьмите прошение, которое я написала на имя нашего великого государя стальгорнского, короля Магни Бронзоборода. Это небольшой труд для $Gтакого:такой;, как ты, $Gславного героя:славной девы;.$B$BМне в моем горе сейчас трудно будет предстать перед королем. Ты понимаешь меня.', },
    {'Отнесите прошение Сары Баллу королю Магни Бронзобороду в Стальгорн.', },
  },
  [684] = {
    'Разыскивается: Марез Клобук',
    {'Назначена награда за голову Марез Клобук, высокопоставленной советницы и деятеля Синдиката. В последний раз ее видели на контролируемой Синдикатом территории Стромгарда.$B$BНаграду можно будет получить у капитана Ниалс.$B$BПредупреждение: Марез – опытная и коварная чернокнижница, замеченная в связях с демонами. Вооружена и очень опасна!', },
    {'Принесите капитану Ниалс в Опорный пункт голову Марез.', },
  },
  [685] = {
    'Разыскивается: Отто и лорд Соколиный Шлем',
    {'Ополчение Стромгарда уведомляет о назначении награды за головы лорда Соколиного Шлема и его телохранителя Отто. Соколиный Шлем руководит деятельностью Синдиката на нагорье Арати, и его смерть внесла бы большую сумятицу.$B$BЕго телохранитель Отто, хотя и не является стратегической целью, представляет собой опасного противника, на счету которого – смерть десятков наших воинов.$B$BНаграды за их головы можно получить у капитана Ниалс.', },
    {'Принесите головы Отто и лорда Соколиного Шлема капитану Ниалс в Опорный пункт.', },
  },
  [686] = {
    'Королевские почести',
    {'Вряд ли добрые дворфские вдовы вроде госпожи Баллу понимают, что нам приходится терпеть тяготы войны ради высшего блага нашего народа. Гибель ее мужа, как и гибель всех дворфов, что сражаются за дело Альянса, тяжким бременем лежит на моей душе.$B$BМне не под силу вернуть ее супруга из Круговерти Пустоты, но я могу хотя бы почтить его память.$B$BЯ намерен заказать главному каменотесу Стальгорна Мраморщику памятник в честь Салли Баллу.$B$BОтправляйтесь к нему сейчас же и передайте мои слова, |3-6($C). Вы будете моим вестником.', },
    {'Поговорите с главным каменотесом Мраморщиком.', },
  },
  [687] = {
    'Тельдарин Заблудший',
    {'Тельдарин Заблудший некогда был членом нашего ордена Сумеречного Молота. Однако он сошел с ума и отверг наше учение. Перед побегом он украл cвиток Мизраэль! Мы не смогли поймать его, но ходят слухи, что он совсем спятил и прячется в Бесплодных землях.$B$BЧтобы переманить на нашу сторону Госпожу, необходимо найти свиток. А для этого – найти Тельдарина.', },
    {'Найдите Тельдарина Заблудшего.', },
  },
  [688] = {
    'Союзники Мизраэль',
    {'<Говорит Мизраэль.>$B$BСвободна! Я СВОБОДНА! Я могу теперь набирать силу, скрывшись от моих пленителей. Потому что, если бы я предстала перед ними сейчас, они бы наверняка пересилили меня и вновь заключили бы в оковы.$B$BНо в должный час я пойду против великанов, и они еще пожалеют о том, что отняли у меня свободу!$B$BВы $Gблагородный союзник:благородная союзница;, $N. Через некоторое время мне вновь понадобится ваша помощь. Когда я буду готова, нужно, чтобы вы призвали меня на поверхность.$B$BПоговорите с Заруком в Павшем Молоте. Он знает, как меня можно призвать.', },
    {'Поговорите с Заруком в Павшем Молоте, что на нагорье Арати.', },
  },
  [689] = {
    'Королевские почести',
    {'Значит, наш добрый король решил построить памятник в честь солдата по имени Салли Баллу? И вы говорите, что бедный парень погиб на мосту Тандола?$B$BДа будет когда-нибудь мир в Азероте?$B$BКак бы то ни было, любое поручение нашего государя – дело первостепенной важности. Чтобы возвести действительно впечатляющий памятник на века, мне нужен самый лучший камень. Отправляйся на Холм Дарроу в Хилсбрад и привези мне альтеракского гранита.', },
    {'Принесите главному каменотесу Стальгорна Мраморщику 5 кусков альтеракского гранита.', },
  },
  [690] = {
    'Просьба Малина',
    {'Приветствую вас, $N. Вы похожи на искателя приключений, а у меня как раз есть задание, требующее склонности к авантюрам.$B$BВерховный маг Трелан, мой первый наставник, не так давно бежал из Стромгарда, что на нагорье Арати, и просил меня помочь ему вернуть несколько предметов, которые он там оставил.$B$BГород был осажден, и Трелан попытался защитить Башню Аратора, но потерпел неудачу. У меня на нагорье есть агенты, но они просили дополнительной помощи. Вы согласитесь мне помочь?', },
    {'Найдите Скуэрто в Опорном пункте на нагорье Арати.', },
  },
  [691] = {
    'На вес золота',
    {'Верховный маг Малин послал нас со Скуэрто собирать для него реагенты. Я был в полном замешательстве и не знал, как приступить к делу, но теперь, когда вы здесь, думаю, моя проблема решена.$B$BВот что мне нужно: клыки троллей из клана Сухокожих, лекарские сумки клана Сухокожих и нож темного охотника.$B$BНе могли бы вы добыть это все для меня? Племя Сухокожих обитает к юго-востоку от Арати.', },
    {'Найдите 10 клыков троллей племени Сухокожих, 4 лекарских сумки племени Сухокожих и нож Темного охотника и принесите их ученику Кретьену в Опорном пункте.', },
  },
  [692] = {
    'Утраченные фрагменты',
    {'Свиток Мизраэль... Свиток Мизраэль?! Нет, уверяю вас, не стоит даже думать о нем! Его магия может вытащить Мизраэль на поверхность, и если вы это сделаете, нам грозит беда!$B$BИли... может быть, уже слишком поздно. Может быть, она уже свободна. Может быть, она уже собирается с силами, прежде чем обрушиться на нас. Если так, свиток вам НЕПРЕМЕННО ПОНАДОБИТСЯ!$B$B...как ужасно, что он уничтожен. Спятившие элементали похитили его и разорвали на клочки, а теперь дерутся за них.$B$BНо если бы вам удалось собрать эти фрагменты, может быть, нам удалось бы восстановить свиток...', },
    {'Принесите рваный, мятый и обожженный фрагменты свитка Тельдарину Заблудшему.', },
  },
  [693] = {
    'Оружие получше кулаков',
    {'Здорово, $N.$B$BЯ выяснил, где оказалась первая вещь Трелана: Коргреш держит ее в Крепости Тяжелого Кулака. И судя по тому, как он кидался в своих потоками льда, чтобы заставить их держать строй, он, должно быть, уже близко подошел к пониманию того, как использовать мощь жезла на полную катушку.$B$BЧтобы добраться до Крепости Тяжелого Кулака, нужно идти на юг отсюда, а затем на восток от моста Тандола. Найдите этого огра и отберите у него жезл.$B$BА за время вашего отсутствия я соображу, что нам делать со Стромгардом.', },
    {'Найдите заклинательный жезл Трелана и верните его Скуэрто в Опорный пункт.', },
  },
  [694] = {
    'Оборона Трелана',
    {'Все отдохнули?$B$BВот наш следующий шаг: огры так и кишат вокруг башни Аратора в Стромгарде. Необходимо активировать защитные возможности башни, если вы хотите иметь хоть малейший шанс выстоять против них. Чтобы это сделать, нам понадобится несколько самоцветов, на которые наложил лапы шаман клана Тяжелого Кулака. Он стянул их из башни, но радость его преждевременна – камни недолго будут в его власти.$B$BДобудьте один из самоцветов и принесите его Кретьену – он знает заклинание, которое нужно наложить на эти камни, чтобы они работали как ключи.', },
    {'Найдите лазурный агат и принесите его ученику Кретьену в Опорный пункт.', },
  },
  [695] = {
    'Чары ученика',
    {'Вот, $N. Самоцвет нужно только прижать к одному из пьедесталов у основания башни, чтобы активировать ее защитные силы. Используйте его, когда будете готовы войти в башню и искать остальные принадлежащие Трелану предметы.$B$BУдачи вам. Я буду ждать вашего возвращения в лагере. Ах да, перед уходом поговорите со Скуэрто: он расскажет вам обо всем, что нужно.', },
    {'Поговорите со Скуэрто в Опорном пункте, что на нагорье Арати.', },
  },
  [696] = {
    'Атака на башню',
    {'Вот что тебе нужно найти: талисман Трелана, сферу Трелана да угольный агат Трелана.$B$BВсе эти три предмета должны быть в сундуках в башне, и ты спокойненько откроешь эти сундуки; главное – берегись огров.$B$BЗащитники башни не будут ими заниматься; придется тебе свое оружие держать наготове. Эти огры сообразят, что ты там и пытаешься обокрасть их – ясное дело, они решат задать тебе взбучку.$B$BБудь $Gосторожен:осторожна; – и удачи!', },
    {'Найдите талисман, сферу и угольный агат Трелана и принесите их Скуэрто в Опорный пункт.', },
  },
  [697] = {
    'Просьба Малина',
    {'Отвезите это письмо обратно в Штормград и скажите Малину, что мы с Кретьеном скоро будем дома. Он хорошо вознаградит вас за помощь нам, $N, в этом-то я не сомневаюсь.$B$BДоброго пути!', },
    {'Отвезите запечатанное письмо верховному магу Малину в Квартал Магов Штормграда.', },
