  [886] = {
    'Оазисы Степей',
    {'Друиды Громового Утеса ощущают странную силу, просачивающуюся в Степи к, востоку от Мулгора.$B$BМы отправили найти источник этой силы Тонгу Рунического Тотема, но, похоже, ему нужна помощь.$B$BОтправляйся в Степи и поговори с Тонгой. Он будет в Перекрестке. Иди туда по дороге на восток от Деревни Кровавого Копыта. Как попадешь в Степи, сверни на развилке на север. И продолжай идти, пока не упрешься в Перекресток.$B$BС дороги не сходи, $N, ибо Степи – край суровый.', },
    {'Поговорите с Тонгой Руническим Тотемом на Перекрестке в Степях.', },
  },
  [887] = {
    'Флибустьеры Южных морей',
    {'Жаль, что вице-адмиралу Греззлику не удалось добиться безопасности в наших водах для торговых кораблей. В торговый флот вложено столько денег, что я просто зверею, когда пираты как нечего делать нападают на мои корабли и похищают товары! $B$BГоворят, что флибустьеры Южных морей разбили лагерь к югу отсюда. Они мне как бельмо на глазу, и если ими не хочет заняться Греззлик, может быть, ты поможешь избавиться от них?', },
    {'Убейте 12 грабителей Южных морей и 6 канониров Южных морей по просьбе Газлоу из Кабестана.', },
  },
  [888] = {
    'Украденное добро',
    {'Хоть я и хотел бы вернуть все товары, думаю, это невозможно. Но некоторые из них мне очень нужны!$B$BКак видишь, моя обсерватория почти готова, но ей не хватает двух малых линз. Первая из них прибыла с караваном из Дуротара, но вторую везли на корабле, который ограбили пираты. Кроме того, до меня так и не дошла партия сапог от Дриззлика из Пиратской Бухты!$B$BСтупай в пиратский лагерь и поищи их.', },
    {'Верните Газлоу из Кабестана посылку с сапогами и линзу от телескопа.', },
  },
  [889] = {
    'Дух Ветра',
    nil,
    nil,
  },
  [890] = {
    'Пропавший груз',
    {'Я уже сто лет жду последнего заказа из Пиратской Бухты! Наверняка его перехватили флибустьеры, но на всякий случай сходи в порт и спроси у Головокружилкинса, вдруг товары без моего ведома отнесли на склад?$B$BВот, отнеси мою учетную книжку Головокружилкинсу, пусть сверит со своими записями.', },
    {'Отнесите учетную книжку Газлоу управляющему пристанью Головокружилкинсу.', },
  },
  [891] = {
    'Пушки крепости Северной Стражи',
    {'Вот и еще один мой корабль захватили рьяные людишки из крепости Северной стражи!$B$BПора разобраться с этими мерзавцами. Их паранойя перешла все мыслимые границы. Эти безрассудные любители спускать курок стреляют во все, что движется.$B$BОтправляйся на юг вдоль Торгового побережья, найди их крепость и брось вызов их силе. Принести в доказательство собственных подвигов медали их солдат. И во имя моего флота, убей капитана Гордостанную и ее рьяных канониров!', },
    {'Капитан Тало\'тас Сияющий поручил вам собрать 10 тераморских медалей и убить капитана Гордостанную, канонира Вессана и канонира Смайта.', },
  },
  [892] = {
    'Пропавший груз',
    {'У меня в записях все в порядке, $N. Ступай к Газлоу и скажи, что ему не повезло. Не удивлюсь, если его товары прибрали к рукам пираты. $B$BЯ ничем не могу ему помочь. А, да... Скажи, что у меня есть для него кое-что из Нижней шахты, пусть приходит и забирает.', },
    {'Верните Газлоу из Кабестана его регистр.', },
  },
  [893] = {
    'Предпочитаемое оружие',
    {'Хотя говорят, что у свинобразов племени Иглогривых за Полями Великанов нет искусных кузнецов, они научились делать серьезное оружие. Мне хотелось бы получить несколько образцов, чтобы изучить их технику работы.$B$BЕсли ты стремишься к подвигам во славу Орды, добудь для меня их оружие. Мне понадобятся кинжал, жезл и щит. Этого вполне хватит!', },
    {'Принесите стилет Иглогривых, обгоревший жезл Иглогривых и боевой щит Иглогривых Таттернаку Стальному Горну в Лагерь Таурахо в Степях.', },
  },
  [894] = {
    'Самофланж',
    {'Торговая Компания устроила к северу отсюда, в юго-западной части Нефтяного болота, небольшой исследовательский центр. Я не очень хорошо представляю, чем они занимаются, но до меня дошли сведения, что они проводят эксперименты со штукой под названием "самофланж".$B$BЧто еще за самофланж? Как бы то ни было, я хочу исследовать его, а значит, мне надо его заполучить.$B$BМне удалось достать копию их руководства пользователя. Почитай его, там наверняка написано, как вытащить самофланж из терминала.', },
    {'Получите доступ к терминалу в исследовательском центре Торговой Компании.', },
  },
  [895] = {
    'РАЗЫСКИВАЕТСЯ: Барон Дольноберег',
    {'РОЗЫСК! $B$BРазыскивается Барон Дольноберег, капитан "Безрассудной".$B$BКапитан Дольноберег из Гилнеаса водит корабли корсаров Южного моря. Он разыскивается по обвинению в пиратских нападениях. Предоставленные останки должны быть пригодны для опознания!$B$B- Газлоу.', },
    {'Принесите Газлоу в Кабестан голову барона Дольноберега.', },
  },
  [896] = {
    'Удача шахтера',
    {'Управляющий пристанью в таком оживленном порту, как Кабестан, держит руку на пульсе происходящего. Я в курсе всего товарообмена между нашим городом и Пиратской Бухтой.$B$BПоследние слухи касаются Каменного карьера, принадлежащего Торговой Компании, что на северо-востоке от Нефтяного болота. Один из шахтеров нашел изумруд размером с твой кулак. Я знаю несколько покупателей на такой товар и готов поделиться с тобой комиссией.', },
    {'Заберите изумруд "Кошачий глаз" у одного из надзирателей или головорезов Торговой Компании и отдайте его управляющему пристанью Головокружилкинсу в Кабестане.', },
  },
  [897] = {
    'Жнец',
    {'Это странное, похожее на насекомое существо выглядит так, будто попало в Степи случайно.', },
    {'Принесите голову Жнеца Джорну Заклинателю Небес.', },
  },
  [898] = {
    'Свобода!',
    {'Наконец-то! Кто-то пришел мне на помощь!$B$BПоверить не могу, что тебе удалось пройти мимо стражей. Фанатики из Терамора совсем спятили. Они потопили наш корабль и меня, единственного выжившего, взяли в плен. Меня назвали врагом и угрозой безопасности Альянса.$B$BЯ! Угроза великому и могучему Альянсу? Я был матросом на грузовом судне, возившем самогон из Кабестана в Пиратскую Бухту.$B$BДовольно болтать. Ты ведь поможешь мне вернуться в Кабестан? Скажи, когда решишь тронуться в путь, и мы совершим побег.', },
    {'Проводите Гилтареса Огненный Цветок к капитану Сияющему в Кабестан.', },
  },
  [899] = {
    'Огонь ненависти',
    {'Быть может, тебе знакомы муки неопределенности, а может, и нет. Но знай: каждый день я стою здесь от восхода до заката и выискиваю взглядом этих чудовищ. Я убил всех людей-свиней, которые попались мне на пути, но так и не смог утолить ненависть. Быть может, мне стоило идти не на север к Перекрестку, а в Таурахо.$B$BТы, |3-6($C), $Gмог:могла; бы мне помочь.$B$BУбей их. Убей как можно больше этих тварей. Принеси мне их клыки, и мы вместе попируем на их могилах.', },
    {'Принесите Манкрику с Перекрестка 60 клыков свинобразов из племени Дыбогривов.', },
  },
  [900] = {
    'Самофланж',
    {'В настоящий момент открыты три контрольных клапана. Суда по надписям на панели управления, их надо закрыть, прежде чем выключать прибор.', },
    {'Закройте топливный распределительный клапан, перепускной клапан и главный клапан управления, а потом снова попытайтесь воспользоваться панелью управления.', },
  },
  [901] = {
    'Самофланж',
    {'Прибор выключен, и вы можете открыть панель управления и достать самофланж из терминала. Все бы хорошо, но чтобы отпереть терминал, нужен ключ.', },
    {'Возьмите у ремонтника Угрелова ключ от терминала и отоприте им панель управления.', },
  },
  [902] = {
    'Самофланж',
    {'Под панелью управления оказывается мешанина проводов, ламп и прочих загадочных механических объектов – кажется, большая часть там вообще не нужна. Отодвинув их в сторону и сунув руку поглубже, вы находите самофланж. Одного усилия хватает, чтобы вытащить его из паза.', },
    {'Отнесите самофланж Пароклапану в Кабестан.', },
  },
  [903] = {
    'Хищники Степей',
    {'Пришло время поохотиться на тех, кто может постоять за себя. Хищники саванны, живущие в Степях – сильные звери. Сильные, могучие, хитрые и опасные. Их можно найти в высокой траве на юго-западе от Перекрестка.$B$BСоблюдай осторожность, иначе можешь неожиданно превратиться из охотника в дичь.', },
    {'Принесите 7 когтей хищников саванны Сергре Черный Шип на Перекресток.', },
  },
  [905] = {
    'Разъяренные смертехваты',
    {'Что ж, $N, теперь твоя задача усложняется. Сначала тебе надо победить свою добычу, а потом отыскать ее логово.$B$BЯ предлагаю поохотиться на яркочешуйчатых ящеров, живущих на юге. Убей их и забери перья. Отнеси перья в их гнезда на юго-западе от Застывшего оазиса. Покажи их сородичам, что ты не боишься!', },
    {'Поохотьтесь на яркочешуйчатых ящеров и соберите с них перья. Отнесите перья в 3 гнезда когтедеров. Возвращайтесь к Сергре Черный Шип в Перекресток.', },
  },
  [906] = {
    'Предатель в наших рядах',
    {'Отнеси голову Лока Торку на Перекресток, |3-6($R). *фыркает* Он-то без слов должен знать, что случилось с моим племенем; ха, он ни за что не поверит, что Кривозуб помог в *фыркает* таких вещах. Но я не сомневаюсь, что он наградит тебя за совершенный подвиг. *фыркает*$B$BПока я сижу в этой клетке, я и дальше буду благословлять тебя силой Агамаггана. Пока же прощай.', },
    {'Отнесите череп Лока Торку на Перекресток.', },
  },
  [907] = {
    'Разъяренные рокочущие ящерицы',
    {'Пришло время выступить против рокочущих ящериц. Они столь огромны, что земля дрожит под их шагами. Сражаясь с ними, соблюдай осторожность: один неверный шаг – и они стопчут тебя.$B$BЯщерицы живут в южной части Степей. Их называют грозорылами, грозошкурами и громоголовами. Возвращайся ко мне с победой.', },
    {'Отнесите 3 порции крови рокочущей ящерицы Джорну Искателю Неба в Лагерь Таурахо.', },
  },
  [910] = {
    'В порту',
    {'Я слышал, что между Кабестаном и Пиратской Бухтой плавают огромные корабли! Вот бы мне поплавать по океану и принять участие в морских боях. Может быть, когда я вырасту, я стану великим моряком Орды! Да!$B$BПожалуйста, своди меня в Кабестан, посмотреть на порт. Обещаю, я не свалюсь в воду и буду хорошо себя вести – мне бы посмотреть на корабли и вдохнуть запах соленой морской воды! Да!', },
    {'Отведите своего подопечного в порт Кабестана в Степях.', },
  },
  [911] = {
    'Врата пограничья',
    {'Говорят, что Ясеневый лес – это новое пограничье, а врата к нему – Застава Мор\'шан. Здорово быть первооткрывателем, добывать все необходимое из первозданного леса! Говорят, что именно там будут греметь битвы между Ордой и Альянсом. Вот бы мне попасть туда!$B$BОтведи меня к заставе Мор\'шан, посмотреть на новое пограничье! Я хочу посмотреть, как Орда выгонит оттуда вонючих ночных эльфов!', },
    {'Отведите своего подопечного к Заставе Мор\'шан в северной части Степей на границе с Ясеневым лесом.', },
  },
  [913] = {
    'Крик грозового змея',
    {'Грозовой змей – лютый зверь, $N. И все же ты уже $Gготов:готова; бросить ему вызов. Ступай, отыщи его и принеси мне крылья в доказательство успешной охоты.$B$BВыполни мое задание, и твое обучение подойдет к концу.', },
    {'Отыщите и убейте грозового змея. Принесите его крылья Джорну Заклинателю Небес в Лагерь Таурахо.', },
  },
  [914] = {
    'Повелители Змей',
    {'Друиды Клыка в Пещерах Стенаний поражены порчей. Когда-то они были частью ордена, давшего обет исцелить Степи, но теперь хотят перекроить эти земли в соответствии со своими извращенными представлениями о порядке.$B$BУ друидов Клыка четыре предводителя и каждый обладает Камнем Сновидения. Даже теперь их лица преследуют меня в кошмарах. Убей предводителей друидов, принеси мне их камни, и в Степях снова воцарится мир.$B$BИди, $N, и разыщи их укрытия в Пещерах Стенаний.', },
    {'Принесите камни Кобрана, Анакондры, Пифаса и Серпентиса Наре Буйногривой, на Громовой Утес.', },
  },
  [915] = {
    'Женщинам – цветы, детям – ...',
    {'МОРОЖЕНОЕ! Пожалуйста, пожалуйста, пожалуйста, добудь для меня немного мороженого!!! Я больше всего люблю клубничное, а самое лучше клубничное мороженое делают Тигуль и Форор! Это мое самое любимое мороженое на свете!$B$BЯ пробовал его только один раз, когда меня водили на гонки на Мерцающей равнине, но говорят, его сейчас продают у нас в городе! Пожалуйста! Пожалуйста-препожалуйста! Преогромное пожалуйста!!!!', },
    {'Купите своему подопечному клубничное мороженое. Кажется, мальчик предпочитает мороженое Тигуля и Форора.', },
  },
  [916] = {
    'Яд чащобного паука',
    {'Я пришел в Тенистую долину, чтобы наблюдать за чащобными тенетниками, обитающими в Паучьем логове. Они – родичи более мелких разновидностей пауков. Полагаю, что Древо Жизни оказало на них сильное воздействие... Я хотел бы получить несколько вещей, чтобы изучить их и подтвердить свои догадки.$B$BДля начала мне понадобится немного их яда. Добудь ядовитые железы у пауков-тенетников в Паучьем логове и вокруг него, к северу отсюда. После этого я смогу изучить эти образцы и понять, есть ли у этого яда сходство с ядом их более мелких сородичей.', },
    {'Принесите 10 ядовитых желез чащобных тенетников Гилшалану Буйному Ветру, стоящему у Альдрассила.', },
  },
  [917] = {
    'Яйцо чащобного тенетника',
    {'Теперь, когда у меня есть паучий яд, мне хотелось бы получить для изучения живой образец. Не волнуйся, я не могу просить тебя поймать гигантского паука живьем. Да и мне самому вряд ли удалось бы справиться с таким "образцом"!$B$BНо если ты принесешь яйцо, из которого еще не вылупился паучок, то получить образец будет куда легче, не говоря уж о том, чтобы содержать.$B$BВ глубинах Паучьего логова должно быть гнездо. Прошу тебя, найди его, возьми оттуда яйцо паука и принеси мне.', },
    {'Принесите яйцо чащобного тенетника Гилшалану, стоящему у Альдрассила.', },
  },
  [918] = {
    'Саженцы древесника',
    {'Древесники Тельдрассила – духи Природы. Они в какой-то мере отражают естественный порядок жизни растений и животных на нашем великом древе.$B$BПотому меня очень тревожит, что древесники становятся такими озлобленными.$B$BМне кажется, что дело в почве. Я разными методами выращивал растения, и мне хотелось бы испытать эти способы на саженцах древесников. Не $Gмог:могла; бы ты собрать для меня этих саженцев вокруг озера Аль\'Амет?', },
    {'Принесите 8 саженцев древесников Деналану к озеру Аль\'Амет.', },
  },
  [919] = {
    'Ростки древесника',
    {'Вокруг вод озера Аль\'Амет прорастают маленькие древесники. Боюсь, им уже не помочь, нам придется выдернуть их из земли прежде, чем они станут слишком большими и начнут приносить беды.$B$BКогда будешь бродить вокруг озера, выдергивай каждый росток древесника, что попадется тебе по дороге. Помоги очистить нашу землю!', },
    {'Принесите 12 ростков древесника Деналану к озеру Аль\'Амет.', },
  },
  [920] = {
    'Зов Тенарона',
    {'Ты $N, не так ли? До меня доходили слухи, что тебя искал Тенарон. Ты отыщешь его в самой верхней комнате на вершине Альдрассила.$B$BХоть мы и живем долго, не стоит заставлять Тенарона ждать.', },
    {'Поговорите с Тенароном Хваткой Бури. Он ждет вас на вершине Альдрассила в Тенистой долине.', },
  },
  [921] = {
    'Венец Земли',
    {'Пришло время тебе отправиться на поиски своей судьбы, $N. Но чтобы подготовиться к встрече с миром за пределами наших зачарованных лесов, тебе нужно больше узнать о нашей недавней истории.$B$BМногое изменилось для нашего народа со времен битвы за гору Хиджал. Нордрассил ныне – лишь бледная тень того, чем он некогда был, его сила ушла на то, чтобы одолеть Архимонда и отбросить прочь Пылающий Легион...$B$BТебе нужно исполнить важное поручение. Принеси мне воды из лунного колодца к северу от Альдрассила.', },
    {'Наполните хрустальный фиал и отнесите его Тенарону Хватке Бури, обитающему на вершине Альдрассила.', },
  },
  [922] = {
    'Релиан Зеленый Костер',
    {'$N, не $Gмог бы ты:могла бы ты; доставить один из тех ростков, что ты $Gпринес:принесла; мне, моему другу, Релиану Зеленому Костру? Он друид из Дарнаса. Когда мы в последний раз общались, он проявил интерес к моей работе с древесниками. У него самого кое-какие идеи есть, и он будет очень рад поработать с таким образцом.$B$BБлагодарю тебя, $N. Твоя помощь просто неоценима. Надеюсь, что однажды ты увидишь плоды моих трудов.$B$BРелиана обычно можно найти на какой-нибудь из дорожек Анклава Кенария в Дарнасе, он любит там гулять.', },
    {'Принесите росток древесника Релиану Зеленому Костру в Дарнас.', },
  },
  [923] = {
    'Опухоли',
    {'Древесники становятся все более злобными. Мы пытаемся найти источник этой беды, но пока... чтобы уберечь Тельдрассил, нам приходится уничтожать древесников, которым уже не поможешь. Те, что бродят вокруг Родникового озера на севере, поражены порчей сильнее всего. Их надо уничтожить!$B$BУничтожь всех древесников, которых там найдешь, и собери растущие на них мшистые опухоли. Принеси их мне, чтобы я мог их сжечь.', },
    {'Принесите 5 мшистых опухолей Релиану Зеленому Костру в Дарнас.', },
  },
  [924] = {
    'Демоново семя',
    {'Демоново семя – страшное оружие Пылающего Легиона. Оно хранится в пещере на Вершине Багрового Тумана, над демоническим алтарем Огня.$B$BЯ подготовил силовые камни, которые разорвут связь Семени с Легионом.$B$BВозьми камень со стола и возложи его на алтарь Огня на Вершине Багрового Тумана. Отправляйся на запад, а на развилке сверни на север. Вершина окажется по левую руку от тебя. Безопаснее всего подниматься по северному склону. $B$BТоропись: сила камня постепенно иссякает.', },
    {'Возьмите треснувший камень силы, возложите его на алтарь Огня до окончания его действия и возвращайтесь к Ак\'Зелоту.', },
  },
  [925] = {
    'Отпечаток копыта Кэрна',
    {'Говорят, что Кэрн Кровавое Копыто – один из величайших героев Орды. Когда я вырасту, я тоже хочу стать героем Орды!$B$BТы $Gзнаком:знакома; с Кэрном? Окажи мне услугу! Возьми у него отпечаток копыта! Я не знаю, как его попросить, у него нет времени на таких, как я... другое дело ты! Ты $Gтакой же искатель:такая же искательница; приключений, как и он! Готов поспорить, что тебе получить у него отпечаток копыта – раз плюнуть!$B$BПожалуйста!', },
    {'Отправляйтесь в Громовой Утес и постарайтесь получить отпечаток копыта Кэрна Кровавого Копыта для своего подопечного.', },
  },
  [926] = {
    'Треснувший камень силы',
    nil,
    nil,
  },
  [927] = {
    'Сердце, поросшее мхом',
    {'Сердце Черномшеца Злосмрадного поросло темным маслянистым мхом. Понять, что это сердце, можно лишь по тому, что оно медленно и ритмично пульсирует... Даже сейчас. $B$BВозможно, это заинтересует Деналана.', },
    {'Принесите поросшее мхом сердце Деналану к озеру Аль\'Амет.', },
  },
  [928] = {
    'Венец Земли',
    {'Я мог бы поведать тебе еще многое о лунных колодцах и о Тельдрассиле, однако тебе пора в путь. Коритрас Лунный Гнев ждет тебя. Я перелил воду из фиала, принесенного тобой, в этот сосуд, чтобы ты $Gотнес:отнесла; его Коритрасу.$B$BТы найдешь Коритраса у лунного колодца в Доланааре. Иди из Тенистой долины по дороге, что на юге от Альдрассила, а когда она свернет на запад, следуй по пути, мощенному булыжниками.$B$BИ советую тебе не сходить с дороги, $N, – в последнее время в лесу появились опасные существа.', },
    {'Отнесите частично наполненный сосуд Коритрасу Лунному Гневу в Доланаар.', },
  },
  [929] = {
    'Венец Земли',
    {'Сначала позволь поведать тебе, какая стоит перед тобой задача. Друиды Дарнаса пользуются водой из лунных колодцев Тельдрассила, и временами их колодцы надо пополнять. При помощи этих специально изготовленных фиалов ты сможешь набирать воду из лунных колодцев.$B$BОтнеси этот сосуд к лунному колодцу у Деревни Звездного Ветра на востоке и набери из него немного воды, а затем возвращайся ко мне. Когда ты выполнишь это задание, я продолжу свой рассказ с момента, на котором его прервал Тенарон...', },
    {'Наполните нефритовый фиал и принесите его Коритрасу Лунному Гневу в Доланаар.', },
  },
  [930] = {
    'Светящийся плод',
    {'Под ветками этого светящегося растения висит большой круглый плод.$B$BВы уверены, что Деналану захочется его изучить.', },
    {'Принесите светящийся плод Деналану к озеру Аль\'Амет.', },
  },
  [931] = {
    'Мерцающий росток',
    {'Росток этого дерева мерцает в сумраке леса, распространяя вокруг себя изменчивое, пульсирующее свечение.$B$BНаверное. Деналана интересует именно такой образец.', },
    {'Принесите мерцающий росток Деналану к озеру Аль\'Амет.', },
  },
  [932] = {
    'Извращенная ненависть',
    {'Должен предупредить тебя, $N, что это должно остаться только между нами. Сатиры и так уже причиняют нам немало неприятностей, а этот уж слишком близко к нашему дому рыщет.$B$BЕго зовут лорд Меленас. Он живет по соседству, в Пещере Бесов, где собрал шайку греллей-воинов. Его сердце чернее ночи, а замыслы – омерзительны.$B$BНайди его в пещере, прямо к северу отсюда, и принеси мне его голову!', },
    {'Убейте лорда Меленаса и принесите его голову Таллонкаю Гибкому Корню в Доланаар.', },
  },
  [933] = {
    'Венец Земли',
    {'К юго-востоку от входа в Дарнас, на берегу Арлитириэнских прудов есть еще один лунный колодец. Стражам трудно наблюдать за этими местами из-за все учащающихся нападений и все большей злобности фурболгов Кривой Сосны. $B$BБудь начеку во время поисков колодца и держи оружие наготове.', },
    {'Наполните турмалиновый фиал и вернитесь к Коритрасу Лунному Гневу в Доланаар.', },
  },
  [934] = {
    'Венец Земли',
    {'Но не все было ладно с Тельдрассилом. Олений Шлем долго лелеял надежду вырастить новое Древо жизни и даже тщательно спланировал это мероприятие. Но была одна небольшая проблема, с которой можно связать большинство бед Тельдрассила.$B$BНо пока я об этом говорить не буду. Тебе нужно посетить последний лунный колодец, к северо-востоку от Врат Дарнаса. Под ветвями Древа-оракула находится первый и самый могучий из наших колодцев. Набери воды из него и возвращайся ко мне.', },
    {'Наполните аметистовый фиал и принесит его Коритрасу Лунному Гневу в Доланаар.', },
  },
  [935] = {
    'Венец Земли',
    {'Без благословения Алекстразы Хранительницы Жизни и Ноздорму Хозяина Времени Тельдрассил вырос с изъяном. Странные твари лезут из-под его корней, и бешеные фурболги нападают на проходящих мимо путников.$B$BМогу только надеяться, что верховный друид быстро найдет то, что ищет. Я наполню все принесенные тобой фиалы, чтобы ты $Gдоставил:доставила; их в Дарнас.$B$BОтнеси их к Фэндралу Оленьему Шлему, ты найдешь его в роще друидов.', },
    {'Отнесите наполненный сосуд верховному друиду Фэндралу Оленьему Шлему в Дарнас.', },
  },
  [936] = {
    'Помощь верховному друиду Руническому Тотему',
    {'Извини, |3-6($C), можно тебя на минутку?$B$BКругу Кенария в Громовом Утесе требуются такие закаленные путешественники, как ты, для участия в чрезвычайно важном исследовании. Подробностей я не знаю, но могу сказать, что возглавляет этот проект Хамуул Рунический Тотем.$B$BЕсли тебя это интересует, отправляйся в Громовый Утес на Вершину Старейшин и поговори с самим Хамуулом.', },
    {'Поговорите с верховным друидом Хамуулом Руническим Тотемом в Громовом Утесе.', },
  },
  [937] = {
    'Зачарованная поляна',
    {'Меня отправили сюда с небольшим отрядом Стражей для защиты Древа-оракула от гарпий, что устроили себе гнезда по всей поляне. Мы стараемся потихоньку вытеснить их отсюда.$B$BКогда Древо-оракул попыталось послать гонца в Дарнас, на него напала стая гарпий и растерзала.$B$BЕсли ты считаешь, что это задание тебе по плечу, иди к их гнездовью и принеси их пояса в доказательство своего подвига.', },
    {'Соберите 6 поясов Кровавых перьев и принесите из часовому Арании Проблеску Солнца на Поляну Оракула.', },
  },
  [938] = {
    'Туманна',
    {'Мех самки саблезуба имеет характерный серый цвет, сливающийся с фоном окружающего леса. Вы видите, что она сильно пострадала: глубокие рваные раны видны на ее спине и брюхе.$B$BОна смотрит на вас, и в ее голубых глазах читается глубокая мудрость. Похоже, она хочет следовать за вами.', },
    {'Проводите Туманну к Стражу Арании Проблеску Солнца к лунному колодцу близ Древа-оракула.', },
  },
  [939] = {
    'Флейта Ксаварика',
    {'Вы рассматриваете простую деревянную флейту, которую подобрали, сразив Ксаварика.$B$BУзловатое дерево кажется твердым, но когда вы вертите ее в руках, все спирали и полоски на флейте вдруг начинают вращаться и двигаться. Вас внезапно охватывает нехорошее ощущение...$B$BВы встряхиваетесь, уверенные, что это просто игра воображения.$B$BНо, возможно, у других сатиров найдется разгадка тайны вашей находки. Отнесите ее друиду Эридане Грусти Ветра, которая живет в южной части Оскверненного леса.', },
    {'Принесите флейту Ксаварика и 5 образцов уз скверны Нефритового Пламени Эридане Грусти Ветра в южную часть Оскверненного леса.', },
  },
  [940] = {
    'Тельдрассил',
    {'Шум леса донес до меня весть о твоих подвигах, $N. Когда я ощутила, что гарпии покинули свои гнездовья, великий покой распространился по лесу и его обитатели снова ощутили себя в безопасности. Я боялась посылать очередное сообщение верховному друиду, но теперь я знаю, что ты доставишь его ему в целости и сохранности.$B$BПередай ему письмо и знай, что лес будет охранять тебя по дороге в каменные джунгли.', },
    {'Передайте отчет Древа-оракула верховному друиду Фэндралу Оленьему Шлему в Дарнас.', },
  },
  [941] = {
    'Сердце-семя',
    {'Я удалил этот смрадный мох с сердца, но оно все еще остается запятнанным...$B$BПопытайся посадить его в мою кадку. Она наполнена плодородной землей, которая может очистить и исцелить это сердце.', },
    {'Посадите запятнанное сердце в кадку Деналана.', },
  },
  [942] = {
    'Рассеянный геолог',
    {'"Эта окаменелость поразительно похожа на другую, которую я однажды видел в Стальгорне. Геолог по имени Бутылопуз приносил ее на ежегодную конференцию Лиги Исследователей. Он был уверен, что в этой окаменелости скрыта мощь, которую можно каким-то образом высвободить.$B$BПоследнее, что я слышал, что Бутылопуз обосновался в таверне "Большая вода" и ждет, чтобы его сопроводили к раскопу Вельгара. Найдите его и покажите ему окаменелость, найденную Сомнамбулером. Может быть, он сумеет раскрыть секреты прошлого.', },
    {'Отвезите таинственную окаменелость археологу Бутылопузу в Гавань Менетилов.', },
  },
  [943] = {
    'Рассеянный геолог',
    {'Геолог Вельгар, который сейчас находится на раскопе в Болотине, держит окаменелость у себя. Когда я обнаружил камень Релу, то был уверен, что это ключ к тайне окаменелости.$B$BПока я шел по дороге к Вельгару, на меня напали ящеры, и реликвия пропала. Не знаю, какая из крапчатых тварей проглотила ее, но если вам удастся ее вернуть, я смогу высвободить силу этих артефактов.$B$BПринесите мне и камень Релу, и окаменелость, хранящуюся у Вельгара.', },
    {'Археолог Бутылопуз в Гавани Менетилов хочет, чтобы вы принесли ему камень Релу и окаменелость Бутылопуза.', },
  },
  [944] = {
    'Меч Властителя',
    {'Очень странно, что наги появились здесь, на Темных берегах. Но еще страннее – клан Сумеречного Молота. Они поклоняются старым, очень старым богам этой земли. Богам, давным-давно низвергнутым.$B$BМеч Властителя, что к югу отсюда, – место, где пал один из этих древних богов.$B$BПойди туда и поищи других служителей культа Сумеречного Молота.$B$BВозьми этот фиал виденья. Используй его для создания гадальной чаши, когда разведаешь все у Меча Властителя. Посредством этой чаши поговори со мной.', },
    {'Соберите информацию, а затем используйте фиал виденья для создания гадальной чаши. С помощью чаши поговорите с Ону.', },
  },
  [945] = {
    'Спасение Терилун',
    {'Помогите!$B$BЯ бродила у Меча Властителя, как вдруг меня окружили эти жуткие сектанты. Счастье, что я умею отлично прятаться!!!$B$BМожете помочь мне отсюда выбраться? И еще, мне нужно будет сообщить моей сестре Тересиль, что я в порядке. Она в Ясеневом лесу, к югу отсюда, у святилища Эссины.', },
    {'Помогите Терилун спастись, а потом сообщите Тересиль, что ее сестра в безопасности. Тересиль можно найти у Святилища Эссины в Ясеневом лесу.', },
  },
  [947] = {
    'Пещерные грибы',
    {'Я тут составляю одно зелье, для которого нужны редкие грибы, растущие в одной-единственной пещере. Пещера эта находится за водопадом Скалистый, к востоку и чуть севернее в горах.$B$BЯ бы и сам туда пошел, но в Роще Древних мне порекомендовали держаться подальше от того места. Наши почтенные союзники чувствуют, что в этой пещере таится какое-то незнакомое зло.$B$BПрошу вас, $N, соберите для меня эти грибы. А в процессе заодно приглядитесь: может быть, вы сможете подтвердить опасения Дерев.', },
    {'Принесите 5 ножек струпника и 1 мертвянку Баритасу Лунному Сумраку в Аубердин.', },
  },
  [948] = {
    nil,
    {'Ону, древо мудрости из Рощи Древних, знает о вашем путешествии к Скалистому водопаду и хочет поговорить с вами. Роща расположена к югу отсюда, у подножья гор.$B$BДрева терпеливы и мудры, $N. Но если Ону ищет вашего совета касательно того, что вы видели у водопадов, то, боюсь, дело срочное.', },
    {'Поговорите с Ону в Роще Древних.', },
  },
  [949] = {
    'Лагерь Сумеречного Молота',
    {'Что замышляет клан Сумеречного Молота у Меча Властителя, неизвестно. Но наверняка что-то недоброе$B$BОбыщите их лагерь у Меча Властителя... Ищите то, что может помочь нам понять их намерения.$B$BВедь мы должны узнать об их целях раньше, чем они выработают план действий.', },
    {'Найдите подсказку в лагере клана Сумеречного Молота, расположенном у Меча Властителя.', },
  },
  [950] = {
    'Возвращение к Ону',
    {'Вглядываясь в сумеречный фолиант, можно сойти с ума. Буквы начинают плыть и плясать перед глазами даже после мельком брошенного взгляда, и некогда прекрасный орнамент принимает гротескные формы.$B$BВырвать одну страницу с безумными каракулями – вот и все, что вам удается сделать, пока вы не потеряли сознание.', },
    {'Принесите Ону лист с безумными письменами.', },
  },
  [951] = {
    'Реликвии Матистры',
    {'Пергамент, который ты $Gобнаружил:обнаружила; близ Меча Властителя, – страница древнего текста... написанная эльфами! Она отягощена проклятиями и загрязнена скверной клана Сумеречного Молота, но похоже, что служители культа используют древнюю эльфийскую магию для того, чтобы вернуть в наш мир своих древних хозяев.$B$BНаправляйся к руинам Матистры, расположенным к северо-востоку отсюда, и поищи в траве и средь разбитых камней древние реликвии. Изучая их, мы сможем выяснить, каким образом в клане Сумеречного Молота намерены использовать магию эльфов.', },
    {'Принесите Ону в Рощу Древних 6 реликвий Матистры.', },
  },
  [952] = {
    'Роща Древних',
    {'Если уж у тебя нашлось время передать письмо для древа-оракула, то, мне кажется, для тебя не составит труда отнести донесение в Рощу Древних на Темные берега, прямо к югу от Аубердина.$B$BТы наверняка сможешь свободно добраться туда на гиппогрифе, я уверен, что ты сумеешь это сделать. Отнеси это Ону, древу мудрости. Он ждет от меня вести, точно так же, как я ждал вести с Поляны Оракула.', },
    {'Доставьте письмо Фэндрала Ону в Рощу Древних на Темные берега к югу от Аубердина.', },
  },
  [953] = {
    'Падение Амет\'Арана',
    {'К востоку отсюда находятся руины Амет\'Арана. Сейчас их населяют неупокоенные духи высокорожденных, некогда живших в его стенах, но когда-то Амет\'Аран был местом, где слуги Азшары свободно практиковались в своей могучей магии.$B$BМеня послали исследовать эти руины, и я наткнулась на две большие таблички, на которых была неразборчиво начертана история Амет\'Арана и его падения. Пока я пыталась прочесть руны, на меня напали призраки, и я сбежала.$B$BПожалуйста, если можете, отправляйтесь в руины и расшифруйте таблички вместо меня.', },
    {'Изучите таблички, рассказывающие об Амет\'Аране и его падении, а затем возвращайтесь к часовому Тише Лунный Клинок, находящейся на Темных берегах.', },
  },
  [954] = {
    'Башал\'Аран',
    {'Руины Башал\'Арана, расположенные к востоку отсюда, населены демоническими служителями зла. Злые духи и сатиры, обосновавшиеся там, питаются магическими энергиями Башал\'Арана, и силы их растут от постоянной подпитки энергией.$B$BНо при этом я заметил, что к одному святилищу они не приближаются. На западной стороне руин, на вершине небольшого утеса, распространяется странное синее свечение... Там и должно быть объяснение нежеланию демонов приближаться к этому месту.$B$BЯ хотел бы, чтобы вы его исследовали.', },
    {'Найдите источник странного синего свечения в руинах Башал\'Арана.', },
  },
  [955] = {
    'Башал\'Аран',
    {'Если бы я решил поведать тебе о своей жизни, не сомневаюсь, что повествование превзошло бы пределы твоего терпения. Скажем так: в моей долгой жизни было много страданий, и существование в этом призрачном обличье, быть может, самая ужасная из моих мук.$B$BХотя мои слова могут показаться лицемерием, я уверяю тебя, что был бы невероятно благодарен, если бы ты $Gпомог:помогла; мне найти то, что держит меня здесь. Некая печать связывает меня, и, изучая серьги злых духов и греллей, я могу напасть на ее след.', },
    {'Соберите 8 серег греллей для Астериона в Башал\'Аране.', },
  },
  [956] = {
    'Башал\'Аран',
    {'Если грелли вступали в тесный контакт с той силой, что запечатывает мою вечную тюрьму, то, подозреваю, я знаю, в чем дело. Несомнено эта печать находится во владении сатира, предводительствующего ими.$B$BЯ убежден, что моя догадка верна, $N. Она наверняка у одного из сатиров. Если ты сможешь ее добыть, то ты почти откроешь дверь моей темницы. Мне не сдержать слез...', },
    {'Добудьте древнюю печать из лунного камня и принесите ее Астериону в Башал\'Аран.', },
  },
  [957] = {
    'Башал\'Аран',
    {'Эта печать, заточившая меня, некогда была изготовлена руками одного из самых могущественных высокорожденных. В Амет\'Аране – точно таких же руинах к югу отсюда – до сих пор горит древнее синее пламя. В этом огне печать может быть уничтожена.$B$BБудь осторожнее в тех руинах, $N...', },
    {'Уничтожьте древнюю печать из лунного камня у древнего пламени в Амет\'Аране, затем вернитесь к Астериону в Башал\'Аран.', },
  },
  [958] = {
    'Инструменты высокорожденных',
    {'$C. Вот задание, которое я вам поручаю.$B$BВидели ли вы руины Амет\'Арана? Если нет – их можно найти на востоке от главной дороги, несколько к югу отсюда. Когда-то Амет\'Аран служил домом для многих могущественных высокорожденных, а теперь это – памятник разрушению, совершенному их жалкими подражателями.$B$BМне говорили часовые, что духи высокорожденных по-прежнему обитают там и что они используют свои древние магические приспособления. Эти реликвии нужно конфисковать, чтобы мы могли их уничтожить.', },
    {'Добудьте 7 реликвий высокорожденных для Тандриса Ветропряда в Аубердине.', },
  },
  [959] = {
    'Неприятности в порту',
    {'Ой! $B$BКак видишь, у меня сегодня приключилась неприятность! Утром из Пиратской Бухты пришла поставка. И какая! Никогда не видел столько ящиков. Я хотел побыстрее закончить, поэтому поднимал больше, чем следовало. В конце концов все рухнуло на землю.$B$BНе успел я оглянуться, как эта тварь, Безумный Магглиш, схватил бутылку портвейна 99-летней выдержки и бросился наутек. Я гнался за ним до самых Пещер Стенаний, но внутрь я не ходок.$B$BМожет, тебе хватит пороху, чтобы найти его?', },
    {'Оператор крана Биггльфузз из Кабестана попросил вас отнять бутылку портвейна 99-летней выдержки у Безумного Магглиша, который прячется в Пещерах Стенаний.', },
  },
  [961] = {
    'Размышление Ону',
    nil,
    nil,
  },
  [962] = {
    'Змеецвет',
    {'Королевское фармацевтическое общество из великого Подгорода Лордерона направило меня сюда с очень необычной целью, $N. Возможно, вы сумеете помочь мне, а, стало быть, и Темной Госпоже в трудах на благо Отрекшихся.$B$BНе так давно я изучала редкий образец флоры под названием змеецвет. Я уверена, что в большом количестве это растение таит в себе великие возможности.$B$BУвы, змеецвет можно найти только в самых темных закоулках Пещер Стенаний, опасной системе подземелий в Степях.', },
    {'Аптекарь Зама из Громового Утеса просит вас принести ей 10 змеецветов.', },
  },
  [963] = {
    'Во имя вечной любви',
    {'После битв за Источник Вечности я узнал, что Амет\'Аран разрушен, жители его погибли – в том числе и моя возлюбленная Анайя.$B$BЯ никогда не думал, что и через тысячи лет воспоминания об Анайе будут тревожить мои сны. Бродя в помрачении рассудка по лесам на Темных берегах, я очутился в руинах Амет\'Арана... где и увидел неупокоенный дух моей любимой.$B$BЕе нужно освободить, но у меня просто рука не поднимается. Нужно уничтожить ее призрак.', },
    {'Освободите дух Анайи Вестницы Зари и принесите ее подвеску Кириллиану Белому Когтю в Аубердин.', },
  },
  [964] = {
    'Фрагменты скелета',
    {'Чтобы отомкнуть двери Некроситета необходим Костяной ключ. Его делают из остатков скелета – точнее, нескольких скелетов – и вплавляют в прочнейший из металлов в подходящей форме. Чтобы ключ заработал, необходим перстень власти одного из тех существ, что могут открыть портал данной им силой.$B$BНо не будем отвлекаться, для начала нам понадобятся фрагменты скелетов. Для этой цели подойдут скелеты внутри Андорала, да и те, что снаружи.', },
    {'Принесите 15 фрагментов скелетов аптекарю Дитерсу в Бастион в Западных Чумных землях.', },
  },
  [965] = {
    'Башня Алталакса',
    {'Привет тебе, $Gюный:юная:r; |3-6($R). Меня зовут Элисса Звездный Ветер, и моя задача – защищать Аубердин от всякого вреда.$B$BС этой целью я послала Балтула Удар Тени вести наблюдение за странными событиями, происходящими близ башни Алталакса к северо-востоку отсюда.$B$BНо он уже давно должен был вернуться. Боюсь, что он столкнулся в лесу с какой-то непредвиденной опасностью. Я была бы тебе очень благодарна, если бы ты $Gсмог:смогла; найти его и удостовериться, что с ним все в порядке.', },
    {'Найдите Балтула Удар Тени возле башни Алталакса на Темных берегах.', },
  },
  [966] = {
    'Башня Алталакса',
    {'Группа чернокнижников обосновалась вокруг и внутри башни. Я хотел было вернуться в Аубердин и доложить Элиссе раньше, но побоялся, что могу что-то пропустить, пока буду отсутствовать.$B$BДелгрен подозревал, что подобная компания может собираться у башни, но не знал, зачем.$B$BНесколько обрывков свитков пергамента, которые носят с собой чернокнижники, попали в мои руки, но мне нужно знать больше, чтобы разгадать эту головоломку. Однако я предостерегаю вас от попытки вторгнуться в саму башню: чернокнижники, засевшие там, гораздо сильнее!', },
    {'Соберите 4 свитка истрепанного пергамента для Балтула Удара Тени возле башни Алталакса.', },
  },
  [967] = {
    'Башня Алталакса',
    {'Мой хозяин, Делгрен Очиститель – паладин, любезно предоставивший свою помощь в защите наших лесов от сил демонов и нежити. Он многое поведал мне о Божественном Свете и воинском искусстве.$B$BДелгрен должен немедленно узнать о деятельности клана.$B$BВы найдете его далеко на юге отсюда, на Заставе Мейстры в Ясеневом лесу. Поторопитесь – я боюсь, что угроза клана Темной Нити растет с каждым часом.', },
    {'Доставьте письмо Балтула Делгрену Очистителю в Ясеневый лес.', },
  },
  [968] = {
    '"Подземные Силы"',
    {'Хотя книга ужасно истрепана и исцарапана, ее название на обложке все же можно разобрать:$B$B"Подземные Силы".$B$BВ книге перечисляются непроизносимые имена со зловещими титулами, способами почитания и предпочтительными способами жертвоприношения... И среди этих жертвоприношений числятся живые разумные создания.$B$BНа внутренней стороне обложки написано: "Руны и Жребии Костохвата, Заброшенный грот, Стальгорн. Владелец: Геррик Костохват".$B$BЕсли Костохват торгует подобными книгами, может быть, он выкупит и эту?', },
    {'Принесите книгу "Подземные Силы" Геррику Костохвату в Заброшенный грот в Стальгорне.', },
  },
  [969] = {
    'Да пребудет с тобой удача!',
    {'Ах, одного взгляда на тебя достаточно, чтобы сказать: ты захочешь себе талисман на удачу. Мау\'ари всегда все знает! $B$BНа юг пойди – найдешь ущелье, а в нем великанов, сделанных из кусков льда. Кристаллы можно отобрать у гигантов, но можешь собирать их и $Gсам:сама; в ущелье. Эти кристаллы имеют отличную отражающую способность. $B$BПринеси мне 4 кристалла великанов Ледяного Молота, и я сделаю тебе такой талисман.', },
    {'Соберите 4 кристалла великанов Ледяного Молота для знахарки Мау\'ари из Круговзора.', },
  },
  [970] = {
    'Башня Алталакса',
    {'Письмо Балтула приводит меня в ужас. Этот клан Темной Нити – сущее бельмо у меня на глазу, и его надо устранить! Мне приходилось иметь дело с мерзавцами из Темной Нити к северо-востоку отсюда в Ордиль-Аране. У одного из них есть самоцвет Души, в котором, как я думаю, заключается секрет силы клана.$B$BПринеси его мне, а уж я сумею разгадать его тайну.', },
    {'Добудьте светящийся самоцвет Души и вернитесь к Делгрену Очистителю на заставу Мейстры.', },
  },
  [971] = {
    'Знание в пучине',
    {'Насколько вам известно, я собираю знания. Древние знания. Могучие знания. Знания, что отворяют пути, знания, что могут пробудить Тех-Что-Спят.$B$BХодят слухи о старинном манускрипте Лоргалиса, будто бы он покоится в Непроглядной Пучине Ясеневого леса. Некогда там жили эльфы, но они давно уже мертвы. Эльфы эти обладали великим знанием, до того как их город превратился в руины.$B$BНайдите манускрипт в Непроглядной Пучине Сделайте это, и я не забуду вашей услуги. Даже после... скончания дней.', },
    {'Принесите манускрипт Лоргалиса Геррику Костохвату в Заброшенный грот в Стальгорне.', },
  },
  [972] = {
    'Сапта воды',
    nil,
    nil,
  },
  [973] = {
    'Башня Алталакса',
    {'Илкруд Магтрулл. Да, я знаю о нем. Это могучий орочий чернокнижник, который поселился в Святилище Огненной Расщелины, месте великого зла на юго-западе Ясеневого леса.$B$BДоныне он был не более чем досадной помехой, потому я и не трогал его, но, похоже, пришла пора с ним разобраться.', },
    {'Принесите фолиант Илкруда Магтрулла Делгрену Очистителю на заставу Мейстры.', },
  },
  [974] = {
    'Найти источник',
    {'Эти горячие источники всегда были для меня загадкой! Откуда берется жар? $B$BЕсли учесть, что источники расположены совсем недалеко от Вулкана Огненного Венца, можно предположить, что их подогревает вулкан. Я разработал термометр, чтобы измерять температуру вулкана, и теперь хочу выяснить, в какой точке она наиболее высока. Термометр дает результаты по шкале Кракленгейта. $B$BВот, держи, отправляйся на хребет и выясни, где там точка с самой высокой температурой!', },
    {'Выясните, где находится точка самой высокой температуры на Вулкане Огненного Венца, по просьбе Кракла из Кратера Ун\'Горо. $B$BДля измерения температуры щелкните по термометру правой кнопкой мыши. Проверяйте все жаркие точки, пока не будет обнаружена точка с самой высокой температурой.', },
  },
  [975] = {
    'Сокровище Мау\'ари',
    {'А теперь, прежде чем я дам тебе этот вот оберег, объясню, как им пользоваться. $B$BЕсли он у тебя с собой, окажется, что определенные предметы будут в добыче с тех созданий, с которыми ты бой вести будешь. А если нет, то и предметов не найдешь. Понятно? $B$BТы узнаешь их по имени – они называются э\'ко. Если найдешь такие – приноси Мау\'ари, она поможет тебе найти им применение. $B$BПостой-ка тут, пока я твой оберег доделаю.', },
    {'Еще раз поговорите со знахаркой Мау\'ари через минуту, чтобы получить талисман на удачу.', },
  },
  [976] = {
    'Поставка для Аубердина',
    {'Делгрен попросил меня доставить письма и припасы часовому Звездный Ветер в Аубердин, но мне очень не хочется ехать в одиночку. В лесу опасные враги, а я не хочу, чтобы эти припасы и информация попали не в те руки.$B$BЕсли ты с остальными согласитесь охранять меня, то я смогу уехать прямо сейчас. Под вашей защитой я точно доставлю все это в Аубердин.', },
    {'Поговорите с Делгреном Очистителем на заставе Мейстры после того, как проводите Фиро через Ясеневый лес.', },
  },
  [977] = {
    'Йети где-то рядом…',
    {'Теперь, когда мне есть чем прикрыть механические движущиеся части, мне нужна только одна вещь. Ну, точнее, две. Два рога! $B$BСамые большие рога – у патриархов и матриархов клана, так что, естественно, именно их-то я и хочу. Эта разновидность йети обитает в пещере к юго-востоку отсюда. Их там очень много, ты без труда их отыщешь. $B$BПомни, я хочу самые красивые рога – пожалуйста, не приноси ни побитых, ни сломанных!', },
    {'Добудьте 2 безупречных рога йети для Уми Румпельшникер из Круговзора.', },
  },
  [978] = {
    'Осененные луной дикие совухи',
    {'Перья диких совухов из Внутренних земель, кажется, содержат следы магии. Эти силы… непохоже, что создания способны их контролировать, больше похоже на то, что магия просто является частью их сущности. $B$BЗа время моих исследований я слыхал, что большая популяция дикосовухов населяет заснеженные земли Зимних Ключей. Вдруг у этих существ есть те же свойства? Пожалуйста, $N, помогите мне найти ответ. Отправляйтесь в Зимние Ключи, соберите выпавшие перья и возвращайтесь с ними ко мне.', },
    {'Соберите в Зимних Ключах 10 осененных луной перьев и принесите их Эреласу Янтарному Небу в деревню Рут\'теран.', },
  },
  [979] = {
    'В поисках Раншаллы',
    {'Да-да, на эти перья, похоже, наложены точно такие же чары, как и те, что я прежде заметил… Поскольку я совершенно не уверен в том, что именно мы нашли, я очень хочу, чтобы вы продолжили поиск ответов, $N. $B$BМоя коллега недавно отправилась в Зимние Ключи изучать там диких совухов в их естественной среде обитания. Может, вы нагоните ее и выясните, что ей удалось узнать?', },
    {'Найдите Раншаллу в восточной части Зимних Ключей.', },
  },
  [980] = {
    'Новые источники',
    {'Слыхал я, что существуют и другие горячие источники – в местечке Зимние Ключи, далеко к северу отсюда. И вот в чем загвоздка – в тех местах нет никаких вулканов! Очень любопытно... $B$BЧто ж, тут можно сделать лишь одно – отправиться в Зимние Ключи и поговорить с моей тамошней приятельницей. Ее зовут Донова Снегонора, она живет прямо рядом с источниками. $B$BНу, счастливой дороги! Еще раз спасибо за помощь с термометром.', },
    {'Отправьтесь в Зимние Ключи и побеседуйте с Доновой Снегонорой.', },
  },
  [981] = {
    'Башня Алталакса',
    {'За всю проделанную вами работу вы заслужили хорошую награду. Возвращайтесь к Делгрену и скажите ему, что Атрикус Нарассин лежит, сраженный вашей рукой, а клан Темной Нити сокрушен, и остатки его рассеяны.', },
    {'Поговорите с Делгреном Очистителем на Заставе Мейстры.', },
  },
  [982] = {
    'Безбрежное море, глубокий океан',
    {'У побережья Темных берегов, к северу от нас, есть остатки двух потерпевших крушение кораблей – "Серебряной Зари" и "Туманной Пелены". Некоторое время назад моряки обоих кораблей не поладили с отвратительными мурлоками, когда плыли по безбрежному морю в сторону Аубердина. Теперь корабли лежат на дне океана, став добычей мурлоков$B$BОба капитана погибли в ту ночь, и их судовые журналы и ценные вещи по-прежнему лежат там, в сейфах. Я хотел бы, чтобы вы добыли их для нас; это много значило бы для членов команды.', },
    {'Добудьте сейф "Серебряной Зари" и сейф "Туманной Пелены" для Горбольда Стальной Руки в Аубердине. Оба предмета можно найти в останках потерпевших крушение кораблей к северу от деревни.', },
  },
  [983] = {
    'Жужжалка 827',
    {'Слушай! Помоги мне с моим последним изобретением, Жужжалкой. С ее помощью можно общаться на расстоянии!$B$BМожет быть, ты уже $Gвидел:видела; их – такие ящики с рычагами. С ними одна проблема – за ними нужен постоянный уход.$B$BУ каждой из них есть своя проблема, но я нашел хитрое решение. Я поместил все машины в районе обитания существ, части которых нужны для починки каждой конкретной машины. Сейчас неисправна Жужжалка 827. Она находится чуть южнее Аубердина, совсем рядом с ним. Чтобы ее починить, нужно 6 ножек краба. Я заплачу...', },
    {'Добудьте 6 ножек крабов и поместите их в Жужжалку 827.', },
  },
  [984] = {
    'Насколько велика угроза?',
    {'Некоторые из моих собратьев спаслись от порабощенных скверной фурболгов в Тельдрассиле, а я пообещал остановить злодеяния, пока кровь не полилась рекой... или не произошло чего-то еще хуже.$B$BЯ уже вижу признаки присутствия скверны на Темных берегах, но она еще не так распространилась. Я думаю, было бы логично продолжить исследования фурболгов. Может быть, вы разыщете один из их лагерей, а затем вернетесь ко мне, если увидите следы осквернения?', },
    {'Найдите оскверненный лагерь фурболгов на Темных берегах и вернитесь к Терентису в Аубердин.', },
  },
  [985] = {
    'Насколько велика угроза?',
    {'Вы уже поднаторели в разведке на вражеских территориях, $N. Но способны ли вы не только следить за врагами, но и биться с ними?$B$BНе все искатели приключений предпочитают честную битву лицом к лицу искусству скрытности и уклонения.$B$BЕсли вы считаете, что готовы к сражению, то наибольшую угрозу нашему народу представляют сейчас фурболги в лагере к югу от Аубердина. Вы найдете там фурболгов Чернолесья. Убейте 8 землепроходцев и 5 заклинателей ветра, а потом возвращайтесь сюда, ко мне.', },
    {'Убейте 8 землепроходцев и 5 заклинателей ветра из племени Чернолесья, а затем вернитесь к Терентису в Аубердин.', },
  },
  [986] = {
    'Пропавший хозяин',
    {'$N, ваше мастерство уже один раз сослужило мне хорошую службу в сложной ситуации. Могу ли я вновь возложить на вас задачу помочь Лютому Когтю и его хозяину Волкору? Я могу сделать для вас волшебный плащ, который позволит вам беспрепятственно ходить вблизи враждебных существ Темных берегов, пока вы будете искать Волкора.$B$BДля изготовления плаща, чтобы хватило материала, мне нужно 5 прекрасных шкур лунопарда, снятых со взрослых самцов или матриархов. Этих кошек можно найти около Петляющей реки, к югу отсюда, или еще дальше на юг, близ Ясеневого леса.', },
    {'Найдите 5 прекрасных шкур лунопарда и принесите их Терентису в Аубердин.', },
  },
  [990] = {
    'Путь в Ясеневый лес',
    {'$N, моя госпожа – Раэна Волкопас – ожидает тебя в городе Астранаар, что в Ясеневом лесу. Возможно, с твоей помощью мы сможем хотя бы немного ослабить порчу, пустившую здесь корни.$B$BОтправляйся отсюда на юг и держись дороги – ты легко найдешь путь, если будешь держаться этого направления.', },
    {'Найдите в Ясеневом лесу Раэну Волкопас.', },
  },
  [991] = {
    'Раэна – санитар Ясеневого леса',
    {'$N, один мой старый друг тоже помогает часовым здесь, в Ясеневом лесу, но он еще не вернулся.$B$BОн думает отыскать один предмет, который, по его мнению, ослабит нападения фурболгов на наш народ – это жезл, сделанный ныне покойным злым волшебником. $B$BПрежде чем уйти, он упомянул, что будет искать драгоценный камень для жезла.$B$BОн также говорил, что камень может находиться в святилище на озере Фалатим у подножия западных гор. Камень хранился там до того, как святилище было разрушено. $B$BНайди моего друга, $N, пожалуйста.', },
    {'Найдите в Ясеневом лесу Терониса.', },
  },
  [992] = {
    'Исследование воды',
    {'У нас, в Водной компании Прибамбасска, образовалась вакансия на должность младшего геодезиста на временную работу. Интересуешься?$B$BКочевники захватили все наши колодцы, приходится искать альтернативные источники воды! В пустыне есть вода, надо только ее отыскать!$B$BВозьми это устройство для поиска воды и принеси мне пробу из пруда возле Заставы Скорбных Песков. Правда, это место на виду у троллей. Когда закончишь, принеси мне включенное устройство!', },
    {'Воспользуйтесь нетронутым устройством для поиска воды возле пруда на Заставе Скорбных Песков. Забрав пробу, отнесите устройство старшему геодезисту Рудознатцу в Прибамбасск.', },
  },
  [993] = {
    'Пропавший хозяин',
    {'Плащ готов, $N. Пришло время найти Волкора. Я могу только надеяться, что мы успеем.$B$BМагия, скрытая в плаще, если ее пробудить, продержится недолго: может быть, минут пять. $GСам:Сама; решай, когда использовать: я бы предложил подождать, пока не найдешь Лютого Когтя.$B$BЕсли то, что он сказал мне – правда, ты найдешь его хозяина в пещере к югу отсюда. Лютый Коготь будет ждать тебя у дороги. Помаши ему, когда увидишь, чтобы привлечь его внимание, и он укажет тебе путь к своему хозяину.', },
    {'Найдите Волкора на Темных берегах и отдайте ему зачарованный плащ лунопарда.', },
  },
  [994] = {
    'Спасение с помощью силы',
    {'Ладно, $N, силой так силой. Давайте убираться отсюда, как только вы будете готовы. Мы пробьемся к дороге – Лютый Коготь должен все еще ждать. А там мы уже сможем разделиться.$B$BТерентис сразу же захочет услышать ваш рассказ, поэтому вы направитесь в Аубердин, когда мы отсюда выберемся.', },
    {'Защищайте Волкора, пока не доберетесь до дороги, а затем поговорите с Терентисом в Аубердине.', },
  },
  [995] = {
    'Спасение с помощью скрытности',
    {'Ну, ладно... Если вы уверены, попробуем выскользнуть отсюда. Мы уйдем, как только вы будете готовы.$B$BНе забудьте после бегства отсюда вернуться к Терентису в Аубердин. А я туда отправлюсь сам по себе, потому что, вероятно, буду двигаться быстрее вас.', },
    {'Совершите побег из пещеры фурболгов и встретьтесь в Аубердине с Терентисом.', },
  },
  [996] = {
    'Оскверненный ветроцвет',
    nil,
    nil,
