  [1000] = {
    'Новое пограничье',
    {'Внимание, герои!$B$BКруг Кенария ищет компетентных помощников для исследования нового обширного пограничья Калимдора – запретных диких краев, полных невообразимых опасностей, что лежат к западу от Танариса и Прибамбасска! Кругу нужны добровольные и честные помощники, что будут действовать в его интересах!$B$BВсе заинтересованные лица да обращаются в Круг Кенария! За дополнительной информацией обращайтесь к верховному друиду Хамуулу Руническому Тотему на вершину Старейшин Громового Утеса!', },
    {'Обратитесь к верховному друиду Хамуулу Руническому Тотему на Вершине Старейшин Громового Утеса по поводу помощи Кругу Кенария в исследовании границ Калимдора.', },
  },
  [1001] = {
    'Жужжалка 411',
    {'Тоненький голосок пробивается откуда-то из глубины машины.$B$B"Бацбах у аппарата! Следующая Жужжалка находится к северу от Аубердина, прямо на берегу.$B$BИтак, номер той Жужжалки – 411, и для ее починки нужно три глаза крепкозуба. Эти твари водятся недалеко от берега! Крепкозубы Темных берегов... *И-ик!* Извини...$B$BКак и в прошлый раз, когда поместишь в Жужжалку то, что нужно, она выдаст тебе награду. *И-ик!* Что? Не, со мной все в порядке!"', },
    {'Добудьте 3 глаза крепкозуба с крепкозубов Темных берегов, которые водятся в море неподалеку от Жужжалки 411.', },
  },
  [1002] = {
    'Жужжалка 323',
    {'Жужжалка издает треск статических помех, и вы слышите голос Бацбаха.$B$B"Шшшледуюшшшая жужшшшалка – номер 323. Она к шшшеверу от Аубердина, но... а хде жшшше эль? Шшшто? О, она шшштоит на дороге, так шшшто ты легко ее найдешшшь около мосшшшт".$B$BБацбах что-то неразборчиво бормочет, а потом вы слышите отчетливый звук глотка.$B$B"В эту нужшшшно положшшшить клыки лунопардов... шшшесть".', },
    {'Добудьте 6 клыков лунопарда и поместите их в Жужжалку 323.', },
  },
  [1003] = {
    'Жужжалка 525',
    {'Сквозь булькающий шум и звуки капающей жидкости, исходящие от машины, вы слышите неверный голос Бацбаха.$B$B"Шшшледуюшший далеко на юг по доррроге. Найти должно быть нетрудно... шшшразу рядом на обочшшине... возле огражшшдения. Думаю, я шшпрятал его в каких-то кусшшштах... Кусшшшты... Хи-хи... Да, Жужжшшалка 525 снимет сшшшедые шшшкальпы с этттих сшшшедых колючешшшшерстныхх медведей. Божшшшежшшш мой, шшшто за пойло. Гнусшшшные... Хи-хи-хи..."$B$BВы слышите звуки жадных глотков, и машина выключается. Затем, совершенно неожиданно, вновь оживает.$B$B"Четыре!"', },
    {'Соберите 4 седых скальпа с седых колючешерстных медведей к югу от Аубердина и положите их в Жужжалку 525.', },
  },
  [1004] = {
    'Новое пограничье',
    {'Внимание, герои!$B$BКруг Кенария ищет компетентных помощников для исследования нового обширного пограничья Калимдора – запретных диких краев, полных невообразимых опасностей, что лежат к западу от Танариса и Прибамбасска! Кругу нужны добровольные и честные помощники, что будут действовать в его интересах!$B$BВсе заинтересованные лица да обращаются в Круг Кенария! За дополнительной информацией обращайтесь к верховному друиду Хамуулу Руническому Тотему на вершину Старейшин Громового Утеса!', },
    {'Обратитесь к верховному друиду Хамуулу Руническому Тотему на Вершине Старейшин Громового Утеса по поводу помощи Кругу Кенария в исследовании границ Калимдора.', },
  },
  [1007] = {
    'Древняя статуэтка',
    {'Я хотел бы дать тебе поручение, $N.$B$BЯ на своей маленькой лодочке плыл над затопленными руинами Зорама, когда на меня напали наги, выныривая из воды и вцепляясь в меня когтями! Я убежал, унеся с собой все, что смог, и чего хватило лишь вот на этот жалкий лагерь.$B$BНо когда я добрался до берега и побежал... я потерял одну дорогую мне вещь.$B$BПрошу тебя, $N, найди место засады – это на побережье к северу – и отыщи древнюю статуэтку. Именно из-за нее я отважился подвергнуть себя опасностям Зорамского взморья.', },
    {'Принесите древнюю статуэтку Талену в его лагерь близ Зорамского взморья.', },
  },
  [1008] = {
    'Зорамское взморье',
    {'По нашему северо-западному побережью, известному как Зорамское взморье, расползается зло.$B$BТам покоятся руины злосчастного города Зорам, разрушенного при Расколе и поглощенного морем. Для ночных эльфов он был утрачен на много веков. Утрачен и почти забыт.$B$BТеперь туда вернулись наги, и мы не знаем, почему. Но причина не так важна – мы должны перебить этих тварей и загнать их обратно в глубины!$B$BВозвращайся, когда выполнишь поручение.', },
    {'Принесите 20 голов наг из клана Зловещего Хвоста Шиндрелл Огненный Всполох в Астранаар.', },
  },
  [1009] = {
    nil,
    {'Боюсь, тайна этой древней статуэтки так и останется нераскрытой, если не найти ключа к этому древнему замку.$B$BЭтим ключом является кольцо – кольцо Зорама. Оно принадлежало правителям этого города до того, как волны Раскола поглотили его.$B$BДолгие годы кольцо считалось утраченным, пока наги недавно не нашли его.$B$BИх предводительница, Руузель, живет на острове на северной окраине Зорама. Прошу тебя, $N, добудь мне это кольцо!', },
    {'Принесите кольцо Зорама Талену на Зорамское взморье.', },
  },
  [1010] = {
    'Батранов волос',
    {'Есть одна травка, что росла некогда в древних руинах Убежища Батрана на севере. Звалась эта травка Батранов волос и славилась тем, что лечила болезни души.$B$BВ этой деревне болеет ребенок, и сдается мне, что это болезнь не телесная. Не сходишь ли ты в Убежище Батрана и не поищешь ли Батранов волос? Мне он может понадобиться, чтобы вылечить ребенка.$B$BУбежище Батрана лежит к северу от заставы Мейстры и прямо к югу от границы с Темными берегами.', },
    {'Принесите 5 стеблей Батранова волоса Орендилу Широколисту в Ясеневый лес.', },
  },
  [1011] = {
    'Чума Отрекшихся',
    {'Отрекшиеся, нежить из Орды, проникли в Ясеневый лес, и рыщут среди развалин и курганов. Мы уверены, что их цель – распространить чуму по землям ночных эльфов. Мы должны убедиться в этом!$B$BОтрекшиеся разбили лагерь к югу отсюда, рядом с Обителью Дор\'Данил, где они готовят яды и заразу и разливают их по бутылкам. Проложи себе путь в этот лагерь и укради бутыль с заразой. Принеси ее сюда. Я исследую ее и узнаю, для чего она.', },
    {'Принесите бутыль заразы Кайнету Штилю в Лесную Песнь.', },
  },
  [1012] = {
    'Безумные друиды',
    {'Когда я боролся с ядами Отрекшихся, мне было видение. Я узрел друидов Обители Дор\'Данил, они были отравлены... убиты нежитью! Теперь они стали блуждающими духами, разлученными с телами и обезумевшими от этого.$B$BЧтобы души этих друидов обрели покой, ты $Gдолжен:должна; войти в Обитель Дор\'Данил и уничтожить ее некогда могучих предводителей – Таниэля Темный Лес, Этиля Зов Луны и Мавориса Проблеск Солнца.$B$BСделай это и возвращайся ко мне.', },
    {'Убейте друидов: Таниэля Темный Лес, Этиля Зов Луны и Мавориса Проблеск Солнца, затем возвращайтесь к Кайнету Штилю в Лесную Песнь.', },
  },
  [1013] = {
    'Книга Ура',
    {'$N, в крепости Темного Клыка хранится книга – книга Ура, я хочу видеть ее в своей коллекции. Ур был великим магом Даларана до того, как оказался в рядах Плети, и его труды по иным мирам представляют большой интерес... для некоторых Отрекшихся.$B$BОтправляйся в крепость Темного Клыка и найди книгу. Принеси мне ее, и я сообщу о твоих заслугах Темной Госпоже.', },
    {'Принесите книгу Ура хранителю Бел\'дугуру в Район Фармацевтов в Подгород.', },
  },
  [1014] = {
    'Смерть Аругалу!',
    {'Аругал по-прежнему сидит в крепости Темного Клыка. Мы не можем считать Серебряный бор стратегическим оплотом Темной Госпожи до тех пор, пока Аругал жив. $B$BЯ позабочусь о том, чтобы искоренить его магию, $N. Но сделать так, чтобы Аругал встретил заслуженную смерть – это я оставляю вам. $B$BОтправляйтесь в крепость Темного Клыка и положите конец скверным деяниям Аругала раз и навсегда. И принесите мне голову этого злодея!', },
    {'Убейте Аругала и принесите его голову Далару Ткачу Рассвета в Гробницу.', },
  },
  [1015] = {
    'Новое пограничье',
    {'Внимание, герои!$B$BКруг Кенария ищет смельчаков, готовых исследовать окраины Калимдора! Забытые пустоши, полные неизвестных опасностей, лежат к западу от Tанариса и Прибамбасска. Круг Кенария призывает храбрых героев изучить эти земли.$B$BВсе желающие должны пройти собеседование в Круге Кенария. Обращайтесь к верховному друиду Фэндралу Оленьему Шлему в Дарнасе!', },
    {'Поговорите с верховным друидом Фэндралом Оленьим Шлемом из дарнасского Анклава Кенария относительно призыва Круга Кенария к изучению окраин Калимдора.', },
  },
  [1016] = {
    'Браслеты элементалей',
    {'Бозьми этот божественный свиток – он поможет определить создателя этих элементалей. Но тебе понадобится кое-что взять у этих духов для фокусировки магии свитка – их браслеты прекрасно подойдут.$B$BХватит нескольких неповрежденных браслетов с этих существ. Другие для наших целей бесполезны, но зато у торговцев за них можно кое-что выручить, если пожелаешь продать. Как только ты добудешь неповрежденные браслеты, попытайся использовать свиток. Возвращайся ко мне с результатами, и мы продолжим.', },
    {'Соберите 5 неповрежденных браслетов элементалей, а затем задействуйте на них божественный свиток. После отнесите разгаданный свиток Стражу Велене Звездный Всполох в Приют Серебряного Ветра.', },
  },
  [1017] = {
    'Маг-призыватель',
    {'Есть только один маг из нежити в Степях, способный наслать порчу на стольких элементалей воды. И он единственный, кому хватит дури попытаться это сделать. Сарилус Грязнорожденный, наверное, сидит на вершине Вершины Багрового Тумана, да смеется надо мной. $B$B$N, тебе придется проникнуть на вражескую территорию и уничтожить этого мерзавца. Мне надоела его бестолковость, и надо показать ему, что бывает с теми, кто донимает часовых. $B$BУничтожь его и принеси мне его голову. Но соблюдай осторожность, Орда может подкараулить тебя.', },
    {'Убейте Сарилуса Грязнорожденного и принесите его голову часовому Велене Звездный Всполох в Приют Серебряного Ветра.', },
  },
  [1018] = {
    'Новое пограничье',
    {'Внимание, герои!$B$BКруг Кенария ищет компетентных помощников для исследования нового обширного пограничья Калимдора – запретных диких краев, полных невообразимых опасностей, что лежат к западу от Танариса и Прибамбасска! Кругу нужны добровольные и честные помощники, что будут действовать в его интересах!$B$BВсе заинтересованные лица да обращаются в Круг Кенария! За дополнительной информацией обращайтесь к верховному друиду Хамуулу Руническому Тотему на вершину Старейшин Громового Утеса!', },
    {'Обратитесь к верховному друиду Хамуулу Руническому Тотему на Вершине Старейшин Громового Утеса по поводу помощи Кругу Кенария в исследовании границ Калимдора.', },
  },
  [1019] = {
    'Новое пограничье',
    {'Внимание, герои!$B$BКруг Кенария ищет смельчаков, готовых исследовать окраины Калимдора! Забытые пустоши, полные неизвестных опасностей, лежат к западу от Tанариса и Прибамбасска. Круг Кенария призывает храбрых героев изучить эти земли.$B$BВсе желающие должны пройти собеседование в Круге Кенария. Обращайтесь к верховному друиду Фэндралу Оленьему Шлему в Дарнасе!', },
    {'Поговорите с верховным друидом Фэндралом Оленьим Шлемом из дарнасского Анклава Кенария относительно призыва Круга Кенария к изучению окраин Калимдора.', },
  },
  [1020] = {
    'Лекарство Орендила',
    {'$N, я изготовил лекарство, которое, надеюсь, поможет больному ребенку. Отнеси его в Астранаар отцу ребенка, Пельтуру Белой Луне.$B$BЧтобы добраться до Астранаара, ступай по дороге сначала на юг, потом на восток.', },
    {'Принесите лекарство Орендила Пельтуру Белой Луне в Астранаар.', },
  },
  [1021] = {
    'Сатиры коварны! Дриады в опасности!',
    {'Послушай, я не знаю, кто ты, но мне нужна помощь. Мои сестры отправились отнять у одного сатира Ветвь Кенария. Они сказали, что скоро вернутся, но уже целых два дня прошло!$B$B$N, ты $Gдолжен:должна; найти их! Они искали развалины храма ночных эльфов в Ксавиане на северо-западе. Не знаю, что и думать. Найди Анилию, это она повела их на поиски.', },
    {'Найдите Анилию в Ксавиане, что в Ясеневом лесу.', },
  },
  [1022] = {
    'Воющая Долина',
    {'Хотя мы затратили много средств и сил на то, чтобы вытеснить остатки демонов из Оскверненного леса на север, успехи наши невелики. Нам удалось не допустить большую часть демонов в Ясеневый лес.$B$BНа севере, у границы Оскверненного леса, разрушенное святилище Мел\'Тандрис захватили загадочные волколюди. Из-за их пронзительных воплей это место называют теперь Воющей Долиной. Фолиант Мел\'Тандрис, что хранится в святилище может пролить свет на появление там волколюдей.', },
    {'Отправляйтесь в Воющую Долину, изучите фолиант Мел\'Тандрис, затем возвращайтесь к часовому Мелирии Ледяной Тени в Лесную Песнь.', },
  },
  [1023] = {
    'Раэна – санитар Ясеневого леса',
    {'Осматривая вещи Терониса, вы находите дневник, который все еще в неплохом состоянии.$B$BВы берете дневник и оставляете труп Терониса. $B$BОстается только найти камень, который искал Теронис. Раэна говорила, что он где-то здесь. Возможно, его забрали мурлоки...', },
    {'Найдите светящийся самоцвет и вернитесь к Раэне Волкопас в Астранаар с дневником Терониса.', },
  },
  [1024] = {
    'Раэна – санитар Ясеневого леса',
    {'Тебе придется провести исследование самостоятельно, но, судя по заметкам Терониса, следующим твоим шагом, похоже, будет поиск остальных фрагментов созданного Дартолом жезла. Я бы начала поиск от лунного колодца к востоку от озера Ирис. Когда будешь $Gготов:готова;, ступай по этой дороге и сверни на север у небольшого местечка для отдыха слева от тебя.$B$BДриада Шаэл\'дрин будет тебе лучшим, чем я, проводником. Найди ее.$B$BВозьми с собой этот самоцвет – он может понадобиться Шаэл\'дрин, если она решит помочь тебе восстановить жезл.', },
    {'Найдите Шаэл\'дрин у лунного колодца на северо-востоке.', },
  },
  [1025] = {
    'Лучшая защита – нападение',
    {'Если ты желаешь испытать свои умения и заодно помочь жителям Ясеневого леса, я дам тебе еще одно поручение, прежде чем ты отправишься к Лунному колодцу.$B$BНа дороге, что сворачивает на восток перед рекой Фалфаррен, фурболги в последнее время нападают на путников. Их извращенный разум питается страхом, а кровожадность утоляется кровью невинных жертв.$B$BПомоги часовым защитить эти края, отплати этим чудовищам их же монетой.$B$BДумаю, их лагерь находится к югу от дороги... будь $Gосторожен:осторожна;.', },
    {'Убейте 1 дозорного, 2 ведмедей, 10 тотемистов и 12 воинов из племени Гиблой Чащи, а затем вернитесь к Раэне Волкопас в Астранаар.', },
  },
  [1026] = {
    'Раэна – санитар Ясеневого леса',
    {'Хотелось бы мне, чтобы у тебя были более хорошие новости.$B$BРаэна права, я знаю о творении Дартола и древнях этих краев – или знала прежде. Они теперь все осквернены, их природа искажена.$B$BЕсли твоя цель – найти еще один фрагмент жезла, то тебе придется найти ключ, чтобы отпереть сундук, в котором он лежит.$B$BДеревянный ключ – на одном из древней к северо-востоку отсюда, близ Оскверненного леса. Как только добудешь его, ищи небольшую лощинку к западу от дороги близ границы Оскверненного леса... Сундук должен быть спрятан где-то там.', },
    {'Найдите деревянный ключ и обломок жезла Дартола прежде, чем вернуться к Шаэл\'дрин у Лунного колодца.', },
  },
  [1027] = {
    'Раэна – санитар Ясеневого леса',
    {'Последний фрагмент жезла был отдан на хранение друидам Дор\'Данила. Я понятия не имею, где они его спрятали. Сожалею, что больше ничем не могу тебе помочь.$B$BМогу только сказать, что тебе следует идти по дороге, что ведет в Степи. Друиды скрывались к востоку от этой дороги, но они тебе уже не помогут – недавно их перебили... мне не очень хочется об этом думать.$B$BКогда найдешь последний фрагмент, принеси его мне, и я помогу тебе воссоздать жезл.', },
    {'Найдите последний фрагмент жезла Дартола и вернитесь к Шаэл\'дрин у Лунного колодца.', },
  },
  [1028] = {
    'Раэна – санитар Ясеневого леса',
    {'Вот собранный жезл, $N. Восстановить его было просто... Труднее будет найти святилище, в котором жезл можно будет наполнить силой.$B$BНо при твоих целеустремленности и усердии, после всех твоих достижений тебе не слишком трудно будет его найти.$B$BСтупай на юго-восток – там очень мало торных дорог, так что будь осторожнее – и посматривай на север. Святилище скрыто в горах, что окружают эту долину.$B$BКак только ты окажешься там, положи жезл в святилище... а остальное сделает магия. Хе-хе.', },
    {'Найдите потаенное святилище и снова наполните чарами жезл Дартола.', },
  },
  [1029] = {
    'Раэна – санитар Ясеневого леса',
    {'Советую тебе найти Раэну и сказать ей, что жезл воссоздан. Я уверен, она знает, куда тебе направиться дальше. $B$BУдачи, $N.', },
    {'Вернитесь к Раэне Волкопас в Астранаар.', },
  },
  [1030] = {
    'Раэна – санитар Ясеневого леса',
    {'Увы, не могу сказать, что у него было на уме.$B$BЕсли он сказал правду о силе жезла, то тогда ты сможешь принимать тот или иной образ. Какой – не знаю.$B$BМожет, он намеревался использовать его мощь, чтобы подобраться поближе к этим мерзостным фурболгам, но даже не затронутые порчей существа убили бы его, не будь он осторожен. Дартол, превращенный при помощи жезла, должен был бы поговорить с фурболгом, не подпавшим под порчу Мха Скверны.$B$BНа мой взгляд, надо начать поиски к юго-востоку от озера Мистраль.', },
    {'Найдите не затронутого порчей фурболга и поговорите с ним при помощи жезла преображения Дартола.', },
  },
  [1031] = {
    'Ветвь Кенария',
    {'Анилия кладет руки вам на плечи и привлекает вас к себе. Задыхаясь, она показывает на находящуюся на севере арку, затем глаза ее закрываются, и из последних сил она шепчет: "Он забрал ее... Гелтарис..." И в душе вы понимаете, что Анилия умирает, жаждая, чтобы Ветвь Кенария была возвращена дриадам.', },
    {'Убейте Гелтариса и верните Ветвь Кенария Иллиане в Лесную Песнь.', },
  },
  [1032] = {
    'Убийство сатиров!',
    {'Поверить не могу – Анилии нет! Как часто мы вместе резвились в лесах... Я почти ощущаю... печаль.$B$BЯ знаю, от чего мне станет легче на душе. Пожалуйста, убей сатиров, что отняли у меня Анилию! Принеси мне их рога в доказательство своего деяния, и я буду навек тебе благодарна.', },
    {'Принесите 16 рогов сатиров Иллиане в Лесную Песнь.', },
  },
  [1033] = {
    'Слеза Элуны',
    {'Я надеялся, что лекарство Орендила спасет Релару, но, боюсь, оно лишь немного ослабило болезнь, поселившуюся в теле моей дочери. Мы должны найти более мощное средство! К счастью, Орендил говорил мне, какие еще он попытается применить лекарства, если первое не поможет.$B$BСлеза Элуны – это такой камень, который можно найти только в одном месте – на острове в озере Ирис к востоку от Астранаара. Орендил сказал, что этот камень снимает порчу... прошу тебя, $N, принеси мне Слезу Элуны, и да покончит она с болезнью моей дочери!', },
    {'Принесите Слезу Элуны Пельтуру в Астранаар.', },
  },
  [1034] = {
    'Руины Звездной Пыли',
    {'Слеза Элуны немного облегчила страдания моей дочери. Кошмары стали реже ее мучить, хвала богине. Но... лихорадка все еще сжигает ее.$B$BОрендил рассказал мне о морозной пыли, которую находят среди руин Звездной Пыли. Может, она усмирит лихорадку Релары. Собери немного этой пыли, прошу тебя, и принеси ее мне.$B$BРуины Звездной Пыли находятся на юге, $N. Да ускорит твой шаг Элуна!', },
    {'Принесите 5 горстей звездной пыли Пельтуру в Астранаар.', },
  },
  [1035] = {
    'Зеркало Небес',
    {'Лихорадка Релары прошла, но она еще очень слаба. Я не верю, что только время вернет ей былую силу. Мне кажется, тут нужна мощь лунного камня.$B$BМы обычно собирали лунный камень у озера Зеркало Небес к юго-востоку отсюда... пока не пришли чащобники.$B$BИх предводитель, Чащобный Оракул, собрал все рассыпанные лунные камни и припрятал их. Ты $Gдолжен:должна; отнять лунные камни у Оракула.$B$BИ, боюсь, для этого тебе придется его убить.', },
    {'Отнимите лунный камень Упавшего Неба у Чащобного Оракула и принесите его Пельтуру в Астранаар.', },
  },
  [1036] = {
    'Суши весла, сухопутная крыса',
    {'$N... Да, я слышал про тебя. Говорят, что ты не друг Пиратской Бухте – как и мы, пираты Кровавого Паруса. Командир флота Фираллон ищет здоровых и крепких воинов, чтобы послать их на особое задание.$B$BЗвучит заманчиво? Тогда отправляйся на "Багровый Парус" и поговори с ним. Он в одной из кают. Корабль на стоянке на Диком берегу к востоку отсюда, рядом с "Буруном" и "Госпожой Удачей".', },
    {'Поговорите с командиром флота Фираллоном на борту "Кровавой завесы" на побережье Тернистой долины.', },
  },
  [1037] = {
    'Велинда Песнь Звезд',
    {'Велинда Песнь Звезд была моей предшественницей здесь, в Ясеневом лесу. Поначалу казалось, что она держдит под контролем ситуацию в Оскверненном лесу, но понемногу ее усилия стали сходить на нет. И однажды она просто исчезла.$B$BМеня послали сюда продолжить ее труды. Но боюсь, я ничего не знаю об этой жрице. Возможно, Тин\'тель Танец Клинка, одна из командиров часовых, знает подробности ее исчезновения, о которых не знаю я.$B$BНаверняка она поймёт значение такой информации.', },
    {'Поговорите с Тин\'тель Танцем Клинка на Террасе Воинов в Дарнасе.', },
  },
  [1038] = {
    'Эффекты Велинды',
    {'Значит, это ты вычитал в фолианте Мел\'Тандрис? Думаю, ничего страшного не будет, если ты осмотришь ее вещи. Этот ключ позволит тебе отпереть сундук, в котором она держала свои пожитки в казарме часовых. Она вела дневник, и если что-то можно узнать, то только из него.$B$BДолжна тебе сказать, что часовые считают, что у нее была причина уйти, и ждут ее возвращения в любой момент. Эта жрица многими своими делами заслужила наше доверие.', },
    {'Поищите в сундуке Велинды ее дневник, затем принесите его вместе с ключом Тинтэль Танцующий Клинок в Дарнас.', },
  },
  [1039] = {
    'Порт в Степях',
    {'Кабестан – единственный порт в Степях. Скорее всего, Велинда нашла там торговое судно, которое шло в бухту Черноводья в Азероте. Мы ограничили сделки с гоблинами, которые руководят портом, но смотритель пристани наверняка знает, какие пассажиры взошли на борт, а какие сошли на берег.$B$BСледуй по дороге на юго-восток через Ясеневый лес, и попадешь в Степи. Смотри по сторонам, $N, эти края патрулируют солдаты Орды. Но в порту ты будешь в безопасности.', },
    {'Поговорите с управляющим пристанью Головокружилкинсом в Кабестане, что в Степях.', },
  },
  [1040] = {
    'Дорога до Пиратской Бухты',
    {'А, наконец-то, нашел. Надо было сразу сказать, что она проезжала здесь давно. Посмотрим.$B$BВелинда. Оплатила проезд до Пиратской Бухты на "Черной Скопе". Никаких дополнительных пометок нет – значит, она благополучно добралась до места назначения. $B$BБольше ничем я помочь не могу, хотя она спрашивала, какие средства передвижения есть по ту сторону моря, и я упомянул Раззгота, водящего караваны из Пиратской Бухты. Быть может, Велинда отправилась с ним. $B$BНу все, давай, иди. Не весь же день с тобой болтать.', },
    {'Плывите в Пиратскую Бухту и поговорите с караванщиком Раззготом.', },
  },
  [1041] = {
    'Путь каравана',
    {'Оказывается, я ошибался насчет тебя, а я это не часто признаю.$B$BОказывается, я помню эту Велинду. Не каждый день жрица ночных эльфов желает отправиться в путь со старым грязным – но превосходным для транспортировки товаров – караваном вроде моего.$B$BПо пути на север мы разделились, и она направилась в Темнолесье. Тамошний клерк ведет записи. Может, он знает что полезное.$B$BОсторожнее в джунглях, они даже в лучшие времена – опасное место.', },
    {'Поговорите с клерком Далтри из Темнолесья.', },
  },
  [1042] = {
    'Семья Карвин',
    {'Нет, у меня не сохранилось записей о пребывании Велинды Песни Звезд в Темнолесье... Хотя, прошу прощения за прямоту, не могу представить, чтобы жрица ночных эльфов снимала комнату в таверне, если ты понимаешь, о чем я.$B$BА вот о волко-людях я слышал, и не раз. Только недавно Калор явился в город, неся целую связку их голов. Он работает на Карвинов, которые охотятся на демонов и прочих чудовищ. Поговори с Джонатаном, главой их дома.', },
    {'Поговорите с Джонатаном Карвином в Темнолесье.', },
  },
  [1043] = {
    'Коса Элуны',
    {'Кажется, ты говоришь правду... Я не до конца понимаю твои мотивы, но если ты $Gготов:готова; избавить Сумеречный лес хотя бы от небольшой части воргенов, я не буду допытываться. Важен результат.$B$BВоргены захватили рудник на юге. Они появились внезапно, но, насколько нам известно, нашествие началось именно с рудника.$B$BСтупай по своим делам, но если найдешь что-нибудь важное, расскажи об этом мне. Мы готовы принять любую помощь в нашей войне с силами зла.', },
    {'Отыщите признаки местонахождения Косы Элуны и вернитесь к Джонатану Карвину в Темнолесье.', },
  },
  [1044] = {
    'Полученные ответы',
    {'Мы разберемся с воргенами, не беспокойся. Искореним зло, которое твоя подруга невольно принесла в наши леса. Мы не в обиде на нее. Странные вещи творятся в наши времена, $N, и тьма не ведает пощады.$B$BНе буду тебя задерживать. Уверен, многие захотят узнать о том, что ты $Gобнаружил:обнаружила; в рудниках Сумеречного леса.', },
    {'Вернитесь к Тин\'тель Танцу Клинка в Дарнас.', },
  },
  [1045] = {
    'Раэна – санитар Ясеневого леса',
    {'Ночные эльфы стары, как время, но их надменность разрушает их собственный мир. А теперь еще и наши жизни. Но это уже никак не получится контролировать...$B$BЯ много в чем могу обвинять эльфов, но мы все равно отвечаем за свои деяния. Даже не подвергнувшись порче, мы способны творить великое зло.$B$BРэн Кровавый Клык – такое зло. Жаждущий мощи, алчный... порча только сделала его сильнее. Меня сжигает ненависть к эльфам, но я не хочу зла их детям, так что сами должны обуздать своих.', },
    {'Убейте Рэна Кровавого Клыка, 4 кровозубых стражей и принесите череп Рэна Кролгу у озера Мистраль.', },
  },
  [1046] = {
    'Раэна – санитар Ясеневого леса',
    {'Возвращайся в свое племя и поведай им о нашей победе.$B$BПокажи им свой трофей и насладись славой...', },
    {'Принесите череп Рэна Кровавого Клыка Раэне Волкопас в Астранаар.', },
  },
  [1047] = {
    'Новое пограничье',
    {'Внимание, герои!$B$BКруг Кенария ищет смельчаков, готовых исследовать окраины Калимдора! Забытые пустоши, полные неизвестных опасностей, лежат к западу от Tанариса и Прибамбасска. Круг Кенария призывает храбрых героев изучить эти земли.$B$BВсе желающие должны пройти собеседование в Круге Кенария. Обращайтесь к верховному друиду Фэндралу Оленьему Шлему в Дарнасе!', },
    {'Поговорите с верховным друидом Фэндралом Оленьим Шлемом из дарнасского Анклава Кенария относительно призыва Круга Кенария к изучению окраин Калимдора.', },
  },
  [1048] = {
    'В монастырь Алого ордена',
    {'Хотя Темную Госпожу больше всего тревожит продвижение Короля-лича на север, мое дело – заботиться о более насущных проблемах.$B$BИ главная из них – человечишки, зовущие себя Алым орденом. Тирисфальские леса кишмя кишат маленькими алыми муравьишками.$B$BОтправляйся в монастырь, |3-6($C), и избавь нас от верховного инквизитора Вайтмейн и ее подчиненных.', },
    {'Убейте верховного инквизитора Вайтмейн, командира Могрейна из Алого ордена, воителя Ирода из Алого ордена и псаря Локси. Затем возвращайтесь к Вариматасу в Подгород.', },
  },
  [1049] = {
    '"Компендиум павших"',
    {'Намерения Служителей Земли относительно наших пораженных болезнью братьев чисты. Но кому ведомы замыслы тех, кто нашептывает всякое Совету Старейшин?$B$BУ Отрекшихся, с которым мы ныне в союзе, вся история полна лжи. Слишком мы поторопились с союзом. Возможно, Кэрн прислушается к предупреждениям из Оргриммара.$B$BВ недрах Монастыря Алого ордена находится "Компендиум павших", который стерегут безумные фанатики-люди. В том, что они творят, сквозит безумие, однако их исследования могут оказаться для нас полезными.', },
    {'Добудьте "Компендиум павших" из монастыря в Тирисфальских лесах и возвращайтесь к Ведуну Искателю Истины в Громовой Утес.', },
  },
  [1050] = {
    '"Мифология Титанов"',
    {'Будущее неотвратимо! Мы стремимся к нему подобно тому, как дождевые капли стремятся к Великому морю.$B$BНо, изучая прошедшее, мы узнаем, что ждет нас в грядущем, и готовимся к встрече с неведомым.$B$BПрежде чем великие беды обрушились на Лордерон, в Тирисфальских лесах находился оплот знаний. Ходят слухи, что в библиотеке старого монастыря хранится книга под названием "Мифология Титанов".$B$BПринеси ее мне – и, возможно, вместе мы сможем заглянуть в тайны прошлого!', },
    {'Добудьте "Мифологию Титанов" из монастыря и принесите ее библиотекарю Мае Белокожке в Стальгорн.', },
  },
  [1051] = {
    'Месть Воррела',
    {'Моя воля свободна, и все же я не могу более служить Темной Госпоже. Я чувствую, что время, отпущенное мне в Азероте, подходит к концу. Жизненные силы покидают меня.$B$BДознаватель Вишас всему виной! Он пытал меня много недель! Мало того, он отобрал у меня обручальное кольцо и нацепил его на палец своей жене! Чудовище!$B$BЯ жажду мести!$B$BЭтот глупец похвалялся своей прекрасной женой Нэнси, что живет у озера Лордамер. Я хочу, чтобы ты $Gотыскал:отыскала; девку и $Gвытряс:вытрясла; из нее душу вместе с моим кольцом! Верни кольцо моей жене Монике, она живет в Мельнице Таррен.$B$BИсполни мою последнюю волю.', },
    {'Верните обручальное кольцо Воррела Сенгутца Монике Сенгутц в Мельнице Таррен.', },
  },
  [1052] = {
    'Путями Алого ордена',
    {'Мы, рыцари Алого ордена, заявляем свои права на укрепления от Дольного Очага до Тирисфальских лесов. Мы гордимся нашей борьбой за чистоту Лордерона!$B$BВы подтвердили свою репутацию, сражаясь против нежити в Пустошах. Но настоящая угроза исходит из Лордерона.$B$BОтправляйтесь в Восточные королевства, в город Южнобережье. Отнесите письмо с моими рекомендациями и печатью рыцарю Ролею Благочестивому. Он проводит вас в Монастырь Алого ордена.', },
    {'Отнесите рекомендательное письмо брата Антона Ролею Благочестивому в Южнобережье.', },
  },
  [1053] = {
    'Во имя Света!',
    {'Я всегда служил Алому ордену, гордился им и почитал служение за честь. Мне было чем гордиться, ведь мы спасали Азерот от нежити.$B$BНо после некоторого времени, проведенного в монастыре Тирисфальских лесов, я понял, что не ведал об истинных намерениях высшей иерархии ордена. Каждого, кто не принадлежит к ордену, они почитают грешниками. Невинных мужчин и женщин подвергают пыткам, полагая их нечистыми.$B$BВо имя Света, Алый орден должен быть сокрушен. И вам, $N, по силам остановить это чудовище.', },
    {'Убейте верховного инквизитора Вайтмейн, командира Могрейна из Алого ордена, воителя Ирода из Алого ордена и псаря Локси, после этого вернитесь с докладом к Ролею Благочестивому в Южнобережье.', },
  },
  [1054] = {
    'Устранение угрозы',
    {'Прямо к северу от Астранаара живут фурболги Колючего Меха. Мои разведчики доносят, что в последние несколько месяцев они чрезвычайно умножились числом. Если они слишком расплодятся, то могут набраться наглости напасть на Астранаар!$B$BУбей их, сколько сможешь, главное – принеси мне череп их предводителя в знак того, что их усилия напрасны...пока. $B$BЯ останусь в Астранааре и буду ждать вестей от тебя. Да пребудет с тобой Элуна.', },
    {'Принесите череп Дала Жилотяга Раэне Волкопас в Астранаар.', },
  },
  [1055] = {
    'Раэна – санитар Ясеневого леса',
    {'Воссозданный жезл у вас в руках. Шаэл\'дрин захочет услышать о ваших успехах.', },
    {'Вернитесь к Шаэл\'дрин у лунного колодца.', },
  },
  [1056] = {
    'Путешествие к Пику Каменного Когтя',
    {'Лесные духи говорят мне, что ты $Gотважен и легок на подъем:отважна и легка на подъем;.$B$BНа юге, неподалеку от озера Мистраль, находится туннель, именуемый Туннель Когтя. Через этот туннель ты попадешь в место, называемое Утес Ветрорезов, в Когтистых горах. Попав туда, иди на юго-запад мимо Скалистого озера, а затем на север, вверх по крутому склону, пока не доберешься до Пика Каменного Когтя.$B$BТам ждет хранитель Албагорм. Выслушай его просьбу, |3-6($R).$B$BПутешествие будет опасным.', },
    {'Отправляйтесь к хранителю Албагорму на Пик Каменного Когтя.', },
  },
  [1057] = {
    'Возвращение Обугленной долины',
    {'Несомненно, будучи защитником лесных духов, можешь догадаться, почему я так встревожен.$B$BЧтобы достигнуть этого пика, тебе пришлось пройти через горы, и, конечно же, от глаз твоих не ускользнуло искажение, постигшее эту землю. Алчность привела к уничтожению священных холмов.$B$BОбугленная долина на юге сожжена механической армией жадных промышленников. Хуже того, клан Смертельной Ярости, вторгшийся на эти земли, отказывается пропустить нас – а ведь мы могли бы начать восстановление тех краев!$B$BИзгони прочь злобных гарпий, $N!', },
    {'Хранитель Албагорм с Пика Каменного Когтя просит вас убить 7 гарпий, 7 душительниц, 7 душегубок и 7 бродяжниц из стаи Кровавой Ярости.', },
  },
  [1058] = {
    'Лесная магия Джин\'Зила',
    {'Я в восхищении!$B$BТри маленьких клетки для трех маленьких эльфов. Ну-с, что бы нам сделать с нашими дорогими друзьями-древолюбами? Что ж, дадим им попробовать... как бы это они сказали... лесной магии!$B$BМне нужна добрая порция слизи Каменного Когтя. Принеси мне несколько сумеречных усиков этих ужасных кошек. И конечно же, потребуется множество глаз бегового оленя – глаз всегда не хватает! Да, и еще, последнее, чуть не забыл: драгоценную чешуйку волшебного дракончика.$B$BЖиво в Каменный Коготь, $N, чтобы я мог сварить немножко лесной магии!', },
    {'Знахарь Джин\'Зил из Малака\'джина требует принести 5 порций слизи Каменного когтя, 5 сумеречных усиков, 30 глаз бегового оленя и чешуйку волшебного дракончика.', },
  },
  [1059] = {
    'Возвращение Обугленной долины',
    {'Мы одержали победу над кланом Смертельной Ярости. Но они скоро соберут силы и вернутся, если не предпринять решительные действия. $B$BДалеко на юге Пустошей находится земля, известная как Тысяча Игл. Если идти на запад Тысячи Игл до тех пор, пока не достигнешь границ густого леса Фералас, то встретишь друида по имени Фалфиндер Хранитель Дорог.$B$BЭтот сильный и мудрый друид способен призвать великих лесных духов, которые помогут в восстановлении Обугленной долины.$B$BПоведай ему о нашей беде! Отправляйся на его поиски немедленно!', },
    {'Отправляйтесь к Фалфиндеру Хранителю Дорог, живущему на границе Тысячи Игл и Фераласа.', },
  },
  [1060] = {
    'Письмо Джин\'Зилу',
    {'Ты $Gсделал:сделала; мне много добра, $N. Можно сказать, ты мой талисман удачи, и я хочу поручить тебе кое-что еще.$B$BГарпии не всегда жили в Степях, они происходят из гор Каменного Когтя, раскинувшихся на северо-западе. Хоть там и опасно, мне нужно послать письмо старому другу, живущему там, знахарю Джин\'Зилу.$B$BОн будет рад, узнав о смерти Серены Кровавое Перо, а тебе в тех краях понравится. Занятное место.', },
    {'Отнесите письмо Дарсока Джин\'Зилу в пещеру Малака\'джин в Когтистых горах.', },
  },
  [1061] = {
    'Духи Каменного Когтя',
    {'Духи Когтистых гор разгневаны: гоблины и их приспешники разоряют и выжигают земли. Земля возопила, и те, кто способен слышать – услышали.$B$BВ Когтистые горы отправилась Сирит Камнеломм, многообещающий шаман Орды. Она сообщает, что гоблины вырубили много леса, и просит о помощи.$B$BОтправляйся в Когтистые горы, найди там Сирит. Чтобы туда добраться, ступай от Перекрестка в Степях по дороге на запад. Сирит стоит лагерем близ Долины Высокого леса.', },
    {'Поговорите с Сирит Камнелом на границе Когтистых гор и Степей.', },
  },
  [1062] = {
    'Вторжение гоблинов',
    {'Торговая Компания, которой управляют гоблины, вторглась в Когтистые горы. Они валят деревья, выжигают большие участки леса. Духи этих земель вне себя от боли и ярости. Мы должны остановить Торговую Компанию!$B$BОтправляйсся на северо-запад, за Долину Высокого леса, к Утесу Ветрорезов. Ты найдешь гоблинов и их прислужников за работой – покажи же им, что Орда не позволит разорять Когтистые горы. Поговори с ними на том языке, который они лучше всего понимают...$B$BНа языке беспощадной силы!', },
    {'Убейте 15 лесорубов Торговой Компании, затем вернитесь к Сирет Камнелом на границу Каменного Когтя и Степей.', },
  },
  [1063] = {
    'Мудрая старуха',
    {'Чтобы узнать, как окончательно успокоить духов Когтистых гор, нам нужно посоветоваться со старейшинами тауренов.$B$BЯ мало знаю о том, кто предводительствует тауренами, но одно имя мне известно: Магата. Я слышала, что она очень стара и много знает. Если кто из тауренов и может оказать нам помощь касательно духов, то это она.$B$BМагата живет в главном городе тауренов Громовом Утесе, на Вершине Старейшин – это северо-восточная вершина города. Поговори с Магатой и расскажи ей о том, что творится в Когтистых горах.', },
    {'Поговорите с Магатой в Громовом Утесе.', },
  },
  [1064] = {
    'Помощь Отрекшихся',
    {'Таурены в сродстве с этой землей, и мне больно слышать о том, что творится в Каменном Когте. Но я боюсь, что для исцеления этой земли нам прежде придется справиться с той заразой, что терзает нас самих.$B$BРазве не удача, что Отрекшиеся теперь с нами? Они много знают о заразных болезнях. Я уверена, что они смогут помочь нам и тем укрепят доверие между нашими народами.$B$BПоговори с аптекаршей Замой. Она – ученая и представитель Отрекшихся. Ты найдешь ее у Прудов Видений под Уступом Духов.', },
    {'Поговорите с аптекаршей Замой у Прудов Видений в Громовом Утесе.', },
  },
  [1065] = {
    'Путешествие на Мельницу Таррен',
    {'Нам придется прибегнуть к чрезвычайным мерам, чтобы избавить Каменный Коготь от Торговой Компании. Но только без кровопролития.$B$BУ одного моего коллеги может найтись то, что нам как раз для этого случая и нужно. Его зовут Лидон, он живет в городе Мельница Таррен в предгорьях Хилсбрада, в далеком краю Лордерон.$B$BЧтобы добраться до Хилсбрада, отправляйся на дирижабле в нашу столицу, Подгород. Оттуда иди на север в Тирисфальские леса, что к юго-западу от Серебряного бора, а затем на юг, в Хилсбрад.$B$BНайди Лидона и передай ему эту записку. Там написано, что нам от него нужно.', },
    {'Принесите записку Замы аптекарю Лидону в Мельницу Таррен.', },
  },
  [1066] = {
    'Кровь невинных',
    {'Я создам яд, который остудит пыл Торговой Компании. Они станут слабыми и покорными. Это же решит проблемы Орды в Каменном Когте, да?$B$BНо для создания отравы мне понадобится кровь невинных, а ее нелегко найти.$B$BК счастью, Синдикат, группа грабителей, насчитывает в своих рядах несколько темных магов... а они собирают кровь невинных для своих нечистых целей.$B$BТемных магов Синдиката можно найти в крепости Дарнхольд на юго-востоке.', },
    {'Принесите 5 фиалов крови невинных аптекарю Лидону в Мельницу Таррен.', },
  },
  [1067] = {
    'Возвращение в Громовой Утес',
    {'Вот отрава. Если воспользоваться ей против Торговой Компании, результаты должны оказаться весьма благоприятными – возможно, удастся добиться даже распростанения новой заразы. Мне не терпится испытать свою работу в деле!$B$BВпрочем, прежде отнеси ее аптекарю Заме. Она должна придумать наиболее эффективный способ использования отравы.', },
    {'Отнесите отраву Лидона аптекарю Заме в Громовой Утес.', },
  },
  [1068] = {
    'Механические измельчители',
    {'Есть связь между духовной магией шаманов и природной магией друидов. Именно поэтому мы, шаманы, можем слышать мольбы духов Каменного Когтя. Я могу разобрать не все, что они шепчут, однако одно послание – одно видение – было четким...$B$BВ этом видении я узрела на Утесе Ветрорезов, на северо-западе Долины Высокого леса, две человекоподобные машины, которые валили деревья с невероятной скоростью. Этих металлических тварей нужно остановить!$B$BВ явленном мне видении я заметила, что машины эти отмечены странными символами:$B$BXT:4 и XT:9.', },
    {'Уничтожьте шреддеры XT:4 и XT:9, затем вернитесь к Сирит Камнелом на границу Когтистых гор и Степей.', },
  },
  [1069] = {
    'Яйца мохового паука',
    {'В Пиратской Бухте фантастический спрос на омлеты из паучьих яиц! Проблема в том... что яиц в Бухте нет.$B$BЯ чую прибыль!$B$BНа Утесе Ветрорезов и в Сиширском каньоне, в Когтистых горах на северо-западе, живут моховые пауки. Принеси мне их яйца, и я неплохо заплачу!$B$BНа Утесе Ветрорезов паучьи кладки можно найти под оставшимися деревьями.$B$BВ Сиширском каньоне... хм, если у тебя слабый желудок, можешь собирать яйца только на Утесе Ветрорезов...', },
    {'Принесите Мебоку Миззриксу из Кабестана 15 яиц мохового паука.', },
  },
  [1070] = {
    'На страже в Когтистых горах',
    {'Привет тебе, $N.$B$BСудя по виду, ты много путешествуешь. Тебе приходилось бывать в Когтистых горах? Эта земля опустошена Торговой Компанией и прочими врагами ночных эльфов. $B$BНедавно часовой Волкопас послала туда мою подругу Келу Копье Тени, чтобы она узнала, что происходит в тех землях, однако до сих пор от нее нет вестей. Я еще беспокоюсь и потому, что Альянс отправил вместе с ней гнома-мага... это, несомненно, повод для тревоги.$B$BМожет быть, ты найдешь ее и пошлешь весточку мне?', },
    {'Найдите Келу Копье Тени, которая наблюдает за долиной, выходящей к Утесу Ветрорезов в Когтистых горах.', },
  },
  [1071] = {
    'Отсрочка для гнома',
    {'Понимаешь ли, $N, я состою в Просвещенной Ассамблее Чародейства, Алхимии и Технических Наук, и моя цель... нет, мой ДОЛГ! – показать ночным эльфам, что единение магии, реактивов и механизмов поможет спасти их леса.$B$BОднако это тяжелая война, а гоблины, похоже, плодятся, словно кролики. У меня даже нет времени воплотить в жизнь свои новые изобретения!$B$BМне нужно время для планирования и организации. Но для этого мне потребуется твоя помощь – сдерживать натиск гоблинов у Утеса Ветрорезов к востоку отсюда...', },
    {'Убейте 10 лесорубов и 10 лесозаготовителей Торговой Компании и вернитесь к Гаксиму Ржавошиппи в Каменный Коготь.', },
  },
  [1072] = {
    'Старый коллега',
    {'Устройство, о котором я думаю – моя самая продвинутая версия на сегодняшний день. Но мне нужно особое зелье, чтобы это устройство заработало. Я думаю, нам нужно добыть хороший состав, поскольку эта миссия может стать для тебя последней.$B$BА для этого нам нужно мощное взрывчатое вещество: нитромирглицероний.$B$BЕдинственный, кто может создать НГ-5, это мой старый приятель – Ломак Демонтирс. Поищи его в Городе Механиков.$B$BЕсли уговоришь его сделать для нас немного НГ-5, то я начну работать над размещением своих устройств.', },
    {'Поговорите с Ломаком Демонтирсом в Стальгорне.', },
  },
  [1073] = {
    'Неумение + реактивы = потеха!',
    {'Ха-ха-ха!.. Тебе нужен нитромирглицероний, чтобы остановить гоблинов, уничтожающих лес?!$B$BМне все равно, знаешь ты Гаксима или нет... я дам тебе этот состав, чтобы посмотреть, как кого-нибудь разнесет на мелкие кусочки: или тебя, или гоблинов.$B$BНо есть проблемка: у меня кончились химикалии.$B$BНо вот что я тебе скажу: принесешь мне нужные зелья, и я прямо сразу же смешаю для тебя составчик.$B$BПо крайней мере, ты докажешь, что кое-что понимаешь в алхимии, прежде, чем я передам тебе образец своего величайшего творения.', },
    {'Принесите 4 крохотных флакона с зельем маны и 2 слабых эликсира стойкости Ломаку Демонтирсу в Стальгорн.', },
  },
  [1074] = {
    'Неумение + реактивы = потеха!',
    {'Ну вот тебе, держи. Тока поосторожней... это летучая смесь.$B$BЕсли Гаксим тебе и вправду друг, передай ему от меня привет. А если нет – взлетишь на воздух, вот будет тебе потеха.', },
    {'Отнесите нитромирглицероний Гаксиму Ржавошиппи в Каменный Коготь.', },
  },
  [1075] = {
    'Свиток от Маурена',
    {'Теперь о том, что касается дистанционных взрывов, входящих в мой план... ах да, я, кажется, еще не объяснил этого?$B$BНу так вот, вкратце: задействовав немного магии, я смогу сделать так, чтобы взрыв произошел почти без твоего участия. Я соберу такое устройство, что тебе останется просто нажать кнопку, а где-то там взорвется НГ-5.$B$BНо мне нужно заклинание, которое есть у моего друга в Штормграде – его зовут Коллин Маурен. Ты сможешь отыскать его там в квартале магов. Он человек, так что будь $Gвнимателен:внимательна;, не ошибись.', },
    {'Найдите в Штормграде Коллина Маурена.', },
  },
  [1076] = {
    'Демоны в Западном Крае',
    {'А, кажется, я понял. Заклинание послания... полагаю, именно этого эффекта он хочет добиться. $B$BМне нужно создать свиток с запечатленным на нем заклинанием. Для этого мне нужен прах Пыледемона.$B$BПока я собираю остальные компоненты, почему бы тебе не отправиться в Западный Край и не найти там одно из этих мерзких существ? Они опасны, но я уверен, что ты со всем справишься, если желаешь участвовать в одном из экспериментов Гаксима.$B$BКогда ты вернешься, я буду здесь.', },
    {'Принесите прах Пыледемона Коллину Маурену в Штормград.', },
  },
  [1077] = {
    'Особая посылка для Гаксима',
    {'Вот, $N. Это то, что нужно.$B$BКогда в следующий раз увидишь Гаксима, передай ему от меня привет. Хотелось бы мне узнать, как закончится эта история.$B$BВсего тебе хорошего.', },
    {'Отнесите свиток с посланием Гаксиму Ржавошиппи в Каменный Коготь.', },
  },
  [1078] = {
    'Поиск для Маурена',
    {'Путешественники постоянно спрашивают меня о Когтистых горах. Похоже, все ищут там приключений, причем самых разных: кому-то нужны виверны, кому-то – элементали, а у кого-то дела с Торговой Компанией.$B$BВ Обугленной долине, прячущейся среди пиков Когтистых гор, некогда обитали особые виды василисков: их чешуя, размолотая в порошок, была прекрасным реактивом для некоторых заклинаний, созданных мною. $B$BЕсли эти василиски еще живут там, мне надо заполучить хотя бы несколько чешуек.$B$BЕсли не очень торопишься, удели на это немного времени, я хорошо заплачу тебе.', },
    {'Принесите 8 кристаллических чешуй Коллину Маурену в Штормград.', },
  },
  [1079] = {
    'Секретная операция – Альфа',
    {'Так вот, $N, смотри и слушай внимательно. Все устройства доделаны и готовы к использованию.$B$BВозьми эти листы – здесь все объясняется про детонаторы, взрывчатку и про то, как их разместить.$B$BПомни, успех твоей миссии не зависит от того, сколько разрушений ты сможешь причинить. План "Альфа" увенчается успехом в том случае, если ты принесешь технические чертежи Торговой Компании, находящиеся на лесопилке к югу от Скалистого Озера.', },
    {'Добудьте технические чертежи Торговой Компании с лесопилки на Утесе Ветрорезов.', },
  },
  [1080] = {
    'Секретная операция – Бета',
    {'План Бета – почти то же, что и план Альфа. $N.$B$BМы проводим отвлекающий маневр, а потом похищаем их драгоценные письма.$B$BГлавное – получить письма... помни это.$B$BНа листках, которые я дал тебе, сказано, где поместить взрывчатку. Не погибни и вернись с добытыми письмами...', },
    {'Добудьте документы Торговой Компании из Рудника Ветрорезов у восточного края Утеса Ветрорезов.', },
  },
  [1081] = {
    'Приглашение от Тиранды',
    {'$N, я уверена, что Тиранда Шелест Ветра лично хотела бы поблагодарить тебя за участие в этой миссии.$B$BМожет быть, это и незаметно с первого взгляда, но деяния, совершенные с твоей помощью, способны помочь нам закрепиться в Когтистых горах.$B$BЕсли хочешь и далее помогать нам в борьбе, отправляйся на встречу с Тирандой в Храм Луны в Дарнасе.', },
    {'Найдите в Дарнасе Тиранду Шелест Ветра.', },
  },
  [1082] = {
    'Новости для часового Тенисил',
    {'$N, твоя помощь здесь оказалась поистине бесценной.$B$BЧасовые Ясеневого леса вознаградят тебя, если ты уведомишь их, что я и Гаксим живы. Скажи им, что мы трудимся над тем, чтобы остановить Торговую Компанию и исцелить эти земли.$B$BНайди в Астранааре Тару Тенисил, она обрадуется твоему возвращению и хорошим новостям.', },
    {'Вернитесь к часовому Тенисил в Ясеневый лес.', },
  },
  [1083] = {
    'Разъяренные духи',
    {'Не могу сказать точно, что Торговая Компания сделала с Долиной, однако ясно, что обитающие там существа взяли над ними верх.$B$BПредставители Торговой Компании подожгли лес, они использовали свои убийственные изобретения для того, чтобы как можно сильнее опустошить Долину.$B$BНо в невежестве своем они также освободили нечто иное: пылающих духов, элементалей огня, камня и ярости.$B$BИ этой земле никогда не узреть улыбку Элуны, пока эти существа бродят на свободе.$B$BПринеси мне их дымящиеся угли, дабы доказать, что тебе удалось сократить число разъяренных духов.', },
    {'Принесите 8 тлеющих углей от любых элементалей огня из Обугленной долины Келе Копье Тени в Когтистые горы.', },
  },
  [1084] = {
    'Раны Дерев',
    {'Мне кажется очевидным, что защитники Долины – древни – вели какие-то дела с Торговой Компанией, прежде чем их оттеснили прочь. К несчастью, даже эти некогда великие охранители ныне впали в нечестие.$B$BГаксим полагает, что их падение каким-то образом связано с оружием, которое гоблины использовали во время отступления.$B$BЯ сказала ему, что возьму образец из тела раненого древня и отдам на изучение, чтобы понять, правда ли это.$B$BПринеси мне немного шрапнели, чтобы гном мог дать мне ответ...', },
    {'Принесите образец ржавой шрапнели Келе Копье Тени в Каменный Коготь.', },
  },
  [1085] = {
    'На страже в Когтистых горах',
    {'Положение здесь, в Каменном Когте, явно не способствует совместной работе ночного эльфа и гнома.$B$BТорговая Компания опустошила эти земли, и большая часть моего времени уходит на то, чтобы положить предел уничтожению.$B$BНе суди Гаксима преждевременно; он понимает, что нам с ним необходимо трудиться вместе.$B$BЕсли ты хочешь помочь нам, то для начала поговори с Гаксимом.$B$BИзвини, что пока не могу отослать тебя обратно к Таре с хорошими новостямиI. Может быть, вскоре...', },
    {'Поговорите с Гаксимом в Когтистых горах.', },
  },
  [1086] = {
    'Аэропорт ветролетов',
    {'Отрава готова. Остается только ее доставить адресату. И это придется сделать тебе, $N.$B$BУ Торговой Компании есть средства доставки сообщений и легкого груза от штаб-квартиры до Когтистых гор. Скорее всего, это маленький аэропорт, подходящий не для дирижабля, а для ветролета. Обыщи все области Когтистых гор, находящиеся под контролем Торговой Компании, и найди этот аэропорт, если он существует. Как найдешь, помести туда отраву.$B$BПоложишь – беги оттуда.', },
    {'Отнесите распылитель ядовитого тумана в аэропорт Торговой Компании и вернуться к аптекарю Заме.', },
  },
  [1087] = {
    'Наследие Кенария',
    {'Ты и представить себе не можешь, как я ненавижу эти омерзительные земли! Отродья Кенария и калдорайские друиды, обитающие на этом пике, используют свою жалкую силу, чтобы подавить поток магии, даже так далеко на юг от их гнусного обиталища... их драгоценных лесов.$B$BЯ так хочу вернуться в Джеденар и купаться в потоках магии, но мой господин не позволяет мне вернуться, пока моя работа здесь не будет завершена.$B$BНе сомневаюсь, что ты любишь ночных эльфов и их союзников не больше, чем я. Помоги мне, и мы оба внакладе не останемся.', },
    {'Убейте 4 сыновей Кенария, 4 дочерей Кенария и 4 кенарийских ботаников по поручению Брайлин Огненной Длани из Приюта у Солнечного Камня.', },
  },
  [1088] = {
    'Ордан',
    {'Они думали, что могут остановить течение магии. Но они ошиблись. Они думали, что смогут спрятать Ордана – но я заполучу его голову!$B$BМагия дает мне способность видеть истину. Да... вот он где. Скрещение путей... На вершине великого древа в Ясеневом лесу. Название... Приют в Ночных Лесах. Один из старших сыновей лесного бога. Они унесли его прочь с этого пика прежде, чем я смогла найти его, однако его время на исходе.$B$BПринеси мне его голову, и я поделюсь с тобой могуществом.', },
    {'Принесите голову Ордана Брайлин Огненной Длани к Приюту у Солнечного Камня.', },
  },
  [1089] = {
    'Логово',
    {'На Пике Каменного Когтя друиды построили Логово Когтя, где хранят множество могущественных предметов.$B$BОни продумали сложный план, чтобы не дать этим предметам попасть в чужие руки.$B$BК счастью, мне удалось... вытянуть некоторые подробности из одного из них.$B$BВ маленьком светильнике у входа в логово ты найдешь спрятанный там ключ Хранителя Врат – с его помощью ты сможешь открыть тайники, расположенные вокруг логова. А предметы из этих тайников помогут тебе открыть сокровищницу, размещенную в одном из центральных помещений.', },
    {'Отправляйтесь к пещере на Пике Каменного Когтя. При помощи Ключа Хранителя Врат добудьте предметы, спрятанные друидами. Используйте эти предметы для того, чтобы открыть сокровищницу Логова Когтя.', },
  },
  [1090] = {
    'Приказы Геренцо',
    {'Знаешь, когда-то я был второй по важности персоной во всем этом деле. Все изменилось, когда главным стал Геренцо. Уволил моего начальника Зиза Физзикса, а меня послал работать в копях. Но Зиз велел мне оставаться здесь и собирать сведения. Его новое начальство хорошо заплатит нам, если мы узнаем, что планирует Геренцо.$B$BВ любом случае, я точно знаю, что Геренцо приказал добыть как можно больше этой золотисто-зеленой руды. Я могу раздобыть образец, а ты передашь его Зизу. Но пока я этим занимаюсь, следи, не покажется ли стража.', },
    {'Возьмите образец загадочной руды у Пузника после того, как он ее добудет.', },
  },
  [1091] = {
    'Новости для Каэлы',
    {'$N,отнеси эти письма Каэле. Уверен, что она захочет это узнать. По крайней мере, часовые точно захотят.$B$BИ обязательно расскажи ей о наших успехах и о том, что мы не добились бы ничего без чуточки магии.', },
    {'Отнесите документы Торговой Компании Келе Копье Тени в Когтистые горы.', },
  },
  [1092] = {
    'Приказы Геренцо',
    {'Полагаю, мне тоже нужно убираться отсюда. Не пройдет много времени, как слухи о том, что произошло здесь, распространятся достаточно высоко, и вряд ли меня в следующий раз встретят там с добром да лаской.$B$BМне нужно выбраться наружу и встретиться с Зизом. Надеюсь, он мне подыщет новую работенку!!!$B$BЯ попытаюсь тут слегка подчистить за собой, но тебе придется доставить образец руды Зизу немедля. Он говорил мне, что разбил свой лагерь где-то за западном склоне Утеса Ветрорезов. Удачи!', },
    {'Доставьте Неопознанную Руду Зизу Физзиксу в Когтистые горы.', },
  },
  [1093] = {
    'Супер-дровосек 6000',
    {'Ну, Торговая Компания и наломала дров! О чем они только думали? Гоблинская компания нанимает гнома!$B$BК счастью для меня, Пароклапан всегда может узнать гения с первого взгляда. Он нанял меня сразу же, как меня выгнали оттуда!$B$BМне сказали, что компания строит какие-то огромные лесорубные машины, и Клапан хочет, чтобы я украл для него чертежи. К несчастью, Геренцо – вот ведь злобный гном! – хочет меня убить... может быть, ты вместо меня украдешь чертежи? Они должны быть у кого-то из операторов компании.', },
    {'Добудьте чертежи "Супер-дровосека 6000" для Зиза Физзикса с Когтистых гор.', },
  },
  [1094] = {
    'Дальнейшие указания',
    {'В этом конверте – все сведения, которые я смог собрать до того, как меня выпнули за дверь... Внутренние документы Торговой Компании, кое-какие руководства, технические бумажки... О, и, конечно, чертежи "Супер-потрошителя 6000".$B$BПаровой Клапан обосновался в Кабестане в Степях. Тебе не доставит труда разыскать его, это такой мелкий зеленый тип.', },
    {'Доставьте запечатанный конверт Пароклапану в Кабестан, что в Степях.', },
  },
  [1095] = {
    'Дальнейшие указания',
    {'В Нижней шахте было много разговоров про этого гнома, нанятого Торговой Компанией. Торговые олигархи не одобряют, что Раздунк связался с Геренцо.$B$BРидлвокс велел мне позаботиться об этой проблеме, и я не сомневаюсь, что это указание от одного из богатых торговых принцев. Разумеется, директор мне об этом не говорил. Скажем так, у меня такое подозрение.$B$BКак бы то ни было, Геренцо необходимо устранить, и Физзику придется хорошо поработать.', },
    {'Отнесите Зизу Физзиксу в Когтистых горах новые приказы.', },
  },
  [1096] = {
    'Геренцо Терминатрикс',
    {'Один раз я встречал Геренцо и, скажу тебе, видок у него отвратнее некуда. Такое впечатление, что его плоть гниет и отпадает, части его тела заменены металлическими деталями и механизмами.$B$BК северо-востоку отсюда, высоко над Скалистым озером, вблизи водяного колеса находится пункт, откуда Геренцо наблюдает за всей местностью. Я не смог бы найти дорогу наверх, не говоря уж о том, чтобы справиться с Геренцо... Но тебе, кажется, это вполне под силу.$B$BЕсли ты убьешь его по моей просьбе, то получишь щедрое вознаграждение!', },
    {'Принесите механическую руку Геренцо Терминатрикса Зиззу Физзиксу в Когтистые горы.', },
  },
  [1097] = {
    'Просьба Элмора',
    {'Дворфу-оружейнику Гриманду Элмору нужна помощь с доставкой ценного груза. Думаю, он хочет отправить посылку домой, на север.$B$BТы мне кажешься $Gопытным путешественником:опытной путешественницей;... Так что если ты не против взять с собой небольшой груз, то поговори с Гримандом; конечно, тебе и тут можно найти занятие, но мы стараемся поддерживать хорошие отношения с дворфами.$B$BГриманда Элмора можно найти в оружейной лавке в Штормграде, в Квартале Дворфов. Это в северо-восточной части города.', },
    {'Поговорите с Гримандом Элмором в Штормграде.', },
  },
  [1098] = {
    'Пропавшие стражи смерти',
    {'Готовясь к нападению на крепость Темного Клыка, мы отправили на разведку двух стражей смерти. Они не возвращаются вот уже много дней, но без их отчета нам не организовать успешного нападения!$B$BИтак, вот твое задание, $N. Отправляйся в крепость Темного Клыка на юге и найди стражей смерти Адаманта и Винсента. Найди их и помоги им, если сможешь.$B$BЭто очень опасное и важное задание, $N. Не подведи нас.', },
    {'Найдите стражей смерти Адаманта и Винсента.', },
