  [1100] = {
    'Дневник Хмурня',
    {'Вы медленно открываете потрепанный дневник Хенрига Хмурня...', },
    {'Прочитайте дневник Хенрига Хмурня.', },
  },
  [1101] = {
    'Хозяйка Лабиринтов',
    {'Бедный Гералат! И этот дворф... Хмурень. Каким он был храбрецом!$B$BНельзя, чтобы их усилия пропали даром. Эта карга... Чарлга Остробок... нужно ее убить.$B$BРазумеется, поход в Лабиринты Иглошкурых будет опасным, а за помощью в Дарнас посылать некогда. Так что тебе, $N, придется собрать отряд самостоятельно.$B$BПринеси мне медальон Чарлги Остробок в доказательство ее смерти.', },
    {'Принесите медальон Чарлги Остробок Фалфиндеру Хранителю Путей в Таланааре.', },
  },
  [1102] = {
    'Отмщение грядет!',
    {'Кэрн – благородный вождь, объединивший наш народ здесь, в Громовом Утесе.$B$BНо я не могу так легко простить тех, кто изгнал нас с земель наших предков. Мой народ много десятилетий прожил в южных Степях. Те края для нас священны. Но нас изгнали многочисленные враги.$B$BМесть ждет старую ведьму Чарлгу Острый Бок, которая повелевает нечестивым воинством в Лабиринтах Иглошкурых.$B$BПринеси мне сердце этой ведьмы, $N, и я смогу спокойно умереть.', },
    {'Принесите сердце Чарлги Остробок Ольду Каменному Копью в Громовой Утес.', },
  },
  [1103] = {
    'Зов Воды',
    {'Привет, $Gбратан:сеструха;! Ты тута, чтобы очистить воду и доказать Ислен, что не лыком $Gшит:шита;?$B$BЯ тока что прикончил оскверненного духа воды и как раз собираюсь сунуть его браслеты и остатки воды в жаровню.$B$BА эт што? А-а-а. Тьев понимает, бывает непруха. Что ж, Тьев нехило тебе поможет, если тебе снова нужен чистый водный дух.$B$BПринеси мне сапту воды от Ислен, покажи, что $Gготов:готова; к новому заходу, а я уж приготовлю тебе, че нужно.', },
    {'Принесите Тьеву Мордуну, который находится в Серебряном бору, сапту воды, если вам не удалось поговорить с низшим воплощением воды.', },
  },
  [1104] = {
    'Яд Соляных равнин',
    {'Я нарисовал уже столько чертежей нового болида! И все их надо непременно воплотить в реальность!!!$B$BЯ разрабатываю новое топливо, которое будет гореть жарко-прежарко! Пока что у меня не получается, но, кажется, я знаю, что мне может помочь...$B$BУ скорпидов с Мерцающей равнины совершенно особый яд, в нем чрезвычайно много солей. Он уникален! Как раз он-то мне и нужен.$B$BПринеси мне их яда и поверь: тогда наши болиды просто взлетят!', },
    {'Принесите 6 порций соленого яда скорпида Пшиксу Медноштифу на Мерцающую равнину.', },
  },
  [1105] = {
    'Крепкие панцири',
    {'Мой брат составляет новое топливо для болида. Он в этом деле просто гений! Правда, боюсь, его новое гениальное топливо разъест всю машину...$B$BПоэтому я хочу укрепить топливные баки.$B$BЗдесь, на равнинах, живут черепахи с очень прочными панцирями. Из них-то я и хочу сделать это покрытие – оно должно выдержать топливо моего брата.$B$BНу как, не желаешь ли взяться за эту работу и принести мне несколько панцирей?', },
    {'Принесите 9 крепких черепашьих панцирей Виззлу Медноштифу на Мерцающую равнину.', },
  },
  [1106] = {
    'Мартек Изгой',
    {'Я разрабатываю новый двигатель, с которым мой болид помчится быстрее ветра! Помчится так, что даже сможет взлететь! Осталось мне сделать поршни, которые выдержат очень, очень высокое давление. Все мои испытания провалились потому, что я не смог сделать достаточно крепких поршней!$B$BНо есть тот, кто может мне помочь.$B$BЕго зовут Мартек Изгой. Он отличный кузнец и знает о металлах больше других. Вот, отвези ему это письмо, я знаю, он поможет мне!$B$BПравда, живет он далеко – в Азероте, в Бесплодных землях, в одном лагере с гоблином!$B$BНайди его!', },
    {'Отнесите письмо Пшикса Мартеку Изгою в Бесплодные земли.', },
  },
  [1107] = {
    'Средство против трения',
    {'Наш болид – это самая быстрая машина на равнинах! Она такая быстрая, что... я боюсь, что от трения ее разнесет на куски.$B$BЧтобы этого не произошло, мне нужен материал для покрытия движущихся частей машины. Он должен быть твердым, но не хрупким, и выдерживать соляную коррозию, иначе на Мерцающей равнине он долго не протянет.$B$BСлышали когда-нибудь про Коварный риф, $N? Это у побережья Тернистой долины. Там обитает племя мурлоков под названием Соляная Чешуя, и их хвостовые плавники как нельзя лучше подойдут для моего материала.', },
    {'Принесите 10 соленых хвостовых плавников Виззлу Медноштифту на Мерцающую равнину.', },
  },
  [1108] = {
    'Индарилий',
    {'Я здесь не только потому, что хочу держаться ото всех подальше. В Бесплодных землях очень много металла, $N, но добыть его сумеет не всякий.$B$BЭтот металл называется индарилий, и говорят, что он обладает удивительной сопротивляемостью к температуре и механическим воздействиям. Как раз то, что нужно Пшиксу для его машины.$B$BНо я должен все проверить. Принесите мне немного индарилия, а я исследую его свойства.$B$BНастоящая индарилиевая руда залегает глубоко в шахтах Ульдамана, но трогги из племени Каменного Свода иногда тоже носят с собой обломки индарилия. Принесите их мне.', },
    {'Принесите 10 пластин индарилия Мартеку Изгою в Бесплодные земли.', },
  },
  [1109] = {
    'Груды гуано',
    {'Как видишь, $N, в Королевском фармацевтическом обществе проводят весьма... любопытные эксперименты.$B$BПоскольку я мастер-аптекарь, в мои обязанности входит наблюдать за самыми интересными проектами. Работы полно, а времени в обрез!$B$BХоть ты и |3-6($C), но, судя по всему, немало $Gстранствовал:странствовала;. Не поможешь мне?$B$BНам очень нужно редкое вещество. Оно на редкость мерзкое, и его получают только от особой разновидности летучих мышей, которые водятся в дыре под названием Лабиринты Иглошкурых. Принеси мне гуано летучих мышей Лабиринтов, и мы примемся за работу...', },
    {'Принесите 1 кучку гуано летучей мыши Лабиринтов опытному аптекарю Фаранеллу в Подгород.', },
  },
  [1110] = {
    'Детали болида',
    {'Хе-хе, да тут такие гонки бывают, как же без происшествий! Вы только оглянитесь – все Мерцающие равнины просто усыпаны обломками разбитых болидов.$B$BИ за все эти детали гномы и гоблины могут хорошенько заплатить. Они вечно ищут, какую бы еще рухлядь привинтить на свои тачки.$B$BСтупайте и принесите мне побольше деталей, а я вам хорошенько заплачу.', },
    {'Принестие 30 деталей болида Кравелу Углебороду на Мерцающую равнину.', },
