  [3022] = {
    'Обращаться бережно',
    {'$N, я вижу, как ты $Gпредан:предана; сему достойному делу. Да, конечно, ты можешь мне помочь!$B$BОтнеси этот ящик с яйцами Эреласу Янтарному Небу. Он друид, и заботится об этих яйцах, помогает вылупиться маленьким гиппогрифам и обучает их. Его можно найти в Тельдрассиле, в маленькой деревушке Рут\'теран на юге.', },
    {'Отнесите тщательно упакованный ящик Эреласу Янтарному Небу в деревню Рут\'теран.', },
