  [4621] = {
    'Стой, Адмирал!',
    {'Слышь, ты, морской волк... Нам, пиратам Кровавого Паруса, жить да поживать бы, когда бы не Пиратская Бухта. Ух, мы порезвились бы вдоволь! А если б ты $Gпырнул:пырнула; ножом командира флота Морского Рога,чихали бы мы тогда на его посудины! Если б ты $Gпырнул:пырнула; ножом барона Ревилгаза, мы б и их закона не боялись.$B$BВ общем, коль хочешь стать у нас почетным адмиралом – да, АДМИРАЛОМ, я не шучу, прирежь обоих, и все дела. Иди, $Gпарень:девица;, возвращайся, когда дело будет сделано.', },
    {'Убейте командира флота Морского Рога и барона Ревилгаза из Пиратской Бухты, а потом возвращайтесь к командиру флота Фираллону на борту "Багрового Паруса" неподалеку от побережья Тернистой долины.', },
  },
  [4641] = {
    'Твое место в мире',
    {'Ну вот, $N, ты и $Gстал взрослым... достаточно взрослым:стала взрослой... достаточно взрослой;, чтобы сражаться во имя Орды и во славу вождя.$B$B*Калтунк меряет вас взглядом.*$B$BДа, ты вполне годишься!$B$BРазумеется, тебе уже хочется повстречаться с огромным драконом или каким-нибудь демоном и задушить его голыми руками, но, пожалуй, начать стоит с чего-нибудь... не такого грозного.$B$B*Калтунк хохочет.*$B$BОтправляйся к Горнеку, он сумеет подыскать дело, которое будет тебе по плечу, $Gюный:юная:c; |3-6($C). Горнека ты найдешь в Логове, на западе.', },
    {'Поговорите с Горнеком. Вы помните, как Калтунк отметил на карте его местонахождение и упомянул, что Горнек живет в Логове, здании, расположенном на западе.', },
  },
  [4642] = {
    'Слияние',
    {'В этом сосуде находится слизнюк, созданный из пробы, которую ты $Gпринес:принесла; из Оскверненного леса. Отнеси его к кратеру Ун\'Горо и отыщи чистого слизнюка. Подберись поближе, выпусти наш экземпляр и посмотри, что они сделают друг с другом.$B$BПо завершении эксперимента принеси мне пробу того, что останется. Я буду ждать тебя здесь, в Районе Фармацевтов.', },
    {'Принесите образец перемешанной слизи химику Барабосу в Подгород.', },
  },
  [4661] = {
    'Проба – образцы из Оскверненного леса',
    nil,
    nil,
  },
  [4681] = {
    'Останки на берегу',
    {'Та тварь на берегу – не единственная на Темных берегах. Вдоль побережья их еще много, да и в воде тоже. Я хотела бы, чтобы вы исследовали другие известные нам останки. Мне сообщили, что они находятся в воде, непосредственно к западу от Аубердина, возле затонувшего судна. Возвращайтесь ко мне с тем, что вам удастся найти полезного для нас.$B$BКстати, в ваших странствиях вы можете найти и другие подобные останки. Если вы обнаружите что-то, обязательно принесите нам для дальнейших исследований!', },
    {'Извлеките останки морской черепахи из скелета морской черепахи, лежащего в воде к западу от Аубердина, а затем поговорите с Гвеннид Блай\'Леггонд в Аубердине.', },
  },
  [4701] = {
    'Устранение опасности',
    {'Тебе несомненно приходилось видеть воргов, которыми командуют орки Черной Горы.$B$BВот что интересно – сколько мы их ни убиваем, их не делается меньше. Разведчики доносят, что воргов растят и обучают на пике Черной Горы, в столице орков этого клана. Отправляйся туда и поищи источник опасности. Отыщи... и уничтожь.$B$BУдачи, $N. Да хранит тебя Кенарий...', },
    {'Отправьтесь на пик Черной горы и уничтожьте источник опасности. Хелендис кричит вам вслед одно имя: Халикон. Именно его упоминали орки в связи с воргами.', },
  },
  [4721] = {
    'Одичавшие стражи',
    {'Гонцы вернулись из Зимних Ключей с сообщением о том, что в его южной части есть группа небольших пещер. У них сложилось впечатление, что этот район обжили дикие совухи. Если они охраняют что-то важное, то, я думаю, именно там мы это и найдем.$B$BС этими дикими совухами вам будет справиться намного сложнее, чем в предыдущий раз, $N. Эти создания, похоже, ввергает в безумие та самая вещь, которую они столь яростно защищают.', },
    {'Убейте для Трулла Баловня Судьбы из Оскверненного леса 10 яростных совухов в Зимних Ключах.', },
  },
  [4722] = {
    'Выброшенная на берег морская черепаха',
    {'Вы наткнулись на останки морской черепахи, некоторое время назад выбросившейся на Темные берега. Плоть ее уже объели мурлоки, обосновавшиеся неподалеку от останков. Вы можете набрать достаточное количество образцов, которые пригодятся Гвеннид Блай\'Леггонд.', },
    {'Отнесите останки морской черепахи Гвеннид Блай\'Леггонд в Аубердин.', },
  },
  [4723] = {
    'Выброшенная на берег морская тварь',
    {'Останки гигантского крепкозуба – намного больших размеров, чем можно было бы ожидать на Темных берегах, – лежат у побережья Туманного предела. Как это существо очутилось здесь, неизвестно; мурлоки, объедающие тушу, кажутся слишком слабыми и неспособными даже толпой притащить ее сюда.$B$BВ то время как некоторая часть костей уже начисто обглодана, останков еще достаточно, чтобы набрать образцов для Гвеннид Блай\'Леггонд.', },
    {'Отнесите кости морской твари Гвеннид Блай\'Леггонд в Аубердин.', },
  },
  [4724] = {
    'Праматерь стаи',
    {'Ходят слухи, что ворги, управляемые орками Черной Горы, – создания сверхъестественных сил. Говорят также, что это не настоящие ворги, а потомство пса-демона, подаренное оркам властителем преисподней. $B$BПравда куда более проста, но не менее ужасна. Ворги Черной Горы – потомство Халикон, огромной праматери воргов. Каков из себя ее самец? Вряд ли кто из видевших его выжил, чтобы поведать об этом. $B$BНужно нанести удар по самому сердцу логова воргов. Уничтожь Халикон. И прошу тебя, не забывай об осторожности!', },
    {'Убейте Халикон, праматерь стаи воргов Кровавого Топора.', },
  },
  [4725] = {
    'Выброшенная на берег морская черепаха',
    {'Еще одни останки морской черепахи лежат прямо на берегу. К панцирю прикреплена упряжь. Скорее всего, черепаху выгнали на берег неизвестные нам твари; она не выбросилась на берег сама, хотя другие останки и наводят на эту мысль. Теперь останками черепахи завладели мурлоки племени Серой Мглы, питающиеся мертвечиной.$B$BОбследуя упряжь, вы обнаруживаете ящик со странными пометками. Может быть, Гвеннид Блай\'Леггонд сможет что-то понять?', },
    {'Отнесите ящик со странными пометками Гвеннид Блай\'Леггонд в Аубердин.', },
  },
  [4726] = {
    'Сущность детеныша дракона',
    {'Я уже несколько недель изучаю драконов в Пылающих степях, и кажется, наконец-то наметился определенный прорыв! Я создала устройство – Драко-инкарцинатор 900. Оно может поймать сущность детеныша дракона!$B$BПо крайней мере, я на это рассчитываю.$B$BВозьми это устройство и испытай его на детеныше дракона в Пылающих степях. Пока он будет под воздействием инкарцинатора, одолей его и выпусти из него сущность.$B$BЕсли это сработает, будет замечательно! И принеси мне образцы на изучение!', },
    {'Принесите 8 сущностей детенышей драконов и Драко-инкарцинатор 900 Тинки Кипеллер в Пламенеющий Стяг в Пылающих степях.', },
  },
  [4727] = {
    'Выброшенная на берег морская черепаха',
    {'Скелет морской черепахи лежит на песчаной отмели на севере Темных берегов. Мурлоки племени Серой Мглы либо не нашли ее тушу, либо по каким-то причинам не рискнули приблизиться к ней. В любом случае, от черепахи осталось достаточно, чтобы взять образцы для Гвеннид Блай\'Леггонд, проводящей расследование по поручению Храма Луны.', },
    {'Отнесите останки морской черепахи Гвеннид Блай\'Леггонд в Аубердин.', },
  },
  [4728] = {
    'Выброшенная на берег морская тварь',
    {'Растерзанные останки того, что на вид кажется похожим на гигантское морское млекопитающее, лежат на берегу. Около них валяется тачка и различные инструменты, как будто кто-то раньше уже пытался изучать останки. Хотя мурлоки основательно растащили скелет, останков достаточно, чтобы найти подходящий образец костей для Гвеннид Блай\'Леггонд в Аубердине.', },
    {'Отнесите кости морской твари Гвеннид Блай\'Леггонд в Аубердин.', },
  },
  [4729] = {
    'Редкие звери Киблера',
    {'Должно быть, тебе интересно, что я здесь делаю? Взгляни на клетки! Разве не ясно?$B$BЯ очень люблю животных, но по роду занятий я торговец. Я разыскиваю и приручаю редких зверей, чтобы богатые и знаменитые клиенты могли сделать их своими питомцами.$B$BТеперь ты, наверное, хочешь спросить: "Чем тебе помочь, Киблер?" Отвечаю: возьми эту клетку и поймай несколько щенков-воргов. За это я приручу одного из них и отдам тебе!', },
    {'Отправьтесь на пик Черной горы и отыщите там щенков воргов легиона Кровавого Топора. Посадите маленьких тварей в клетку и отнесите Киблеру.', },
  },
  [4730] = {
    'Выброшенная на берег морская тварь',
    {'Полуразложившиеся тело твари, похожей на огромное морское млекопитающее, лежит на берегу. Группа мурлоков, более сильных, чем обитающие ближе к Аубердину, обосновалась вокруг погибшего создания. Останков достаточно, чтобы отобрать образцы костей, которые можно отнести Гвеннид Блай\'Леггонд.', },
    {'Отнесите кости морской твари Гвеннид Блай\'Леггонд в Аубердин.', },
  },
  [4731] = {
    'Выброшенная на берег морская черепаха',
    {'Еще одни останки морской черепахи лежат у береговой линии. На панцире есть упряжь, которая еще частично держится. Возможно, несчастная тварь не сама выбросилась на берег (что кажется наиболее вероятным по отношению к другим погибшим тварям), а была пригнана к береговой линии неизвестными существами. Могучие мурлоки племени Серой Мглы сделали остов этой твари своим жилищем.$B$BОбследуя упряжь, вы обнаруживаете ящик со странными пометками. Возможно, Гвеннид Блай\'Леггонд сумеет что-то разобрать?', },
    {'Отнесите ящик со странными пометками Гвеннид Блай\'Леггонд в Аубердин.', },
  },
  [4732] = {
    'Выброшенная на берег морская черепаха',
    {'Вы обнаружили тело морской черепахи, выбросившейся на Темные берега некоторое время назад. Оно почти дочиста объедено мурлоками племени Серой Мглы – теми самыми, что поселились вокруг останков. Но похоже, что несколько подходящих образцов для Гвиннед Блай\'Леггонд вы сможете отобрать.', },
    {'Отнесите останки морской черепахи Гвеннид Блай\'Леггонд в Аубердин.', },
  },
  [4733] = {
    'Выброшенная на берег морская тварь',
    {'Останки животного, похожего на гигантского крепкозуба, лежат, вынесенные на отмель в скалистой Сумрачной Излучине. Этот крепкозуб в несколько раз больше любого из тех, что обитают в здешних водах. Откуда бы это создание ни приплыло, вряд ли оно родом с Темных берегов.$B$BХотя туша животного уже потеряла целостность, останков достаточно, чтобы найти подходящие образцы для Гвеннид Блай\'Леггонд.', },
    {'Отнесите кости морской твари Гвеннид Блай\'Леггонд в Аубердин.', },
  },
  [4734] = {
    'Заморозка яйца',
    {'$N, я работаю над созданием нового устройства, яйцехладоскопа! Оно предназначено для того, чтобы направить сгусток холода на яйцо дракона... и заморозить его! Можешь испытать эту штуку?$B$BГоворят, что на пике Черной горы у драконов есть питомник, в котором полно яиц. Подходящее место для испытаний! Заморозь яйцо и расскажи, сработал прибор или нет.$B$BНе беспокойся. Замороженные драконьи яйца совершенно безобидны. А вот остальных надо опасаться!', },
    {'Испытайте прототип яйцехладоскопа на одном из яиц в Гнездовье на Пике Черной Горы.', },
  },
  [4735] = {
    'Сбор яиц',
    {'$N, я, наконец, готова исследовать настоящие яйца драконов! Возьми это устройство-коллекционер. Он предназначен для захвата драконьего яйца и подготовки его к транспортировке. Возвращайся в Гнездовье и собери при помощи этой штуки несколько яиц.$B$BНе забудь, первым делом их надо заморозить, иначе из них могут вылупиться драконы, не успеешь собрать!$B$BКак только наберешь достаточно яиц, неси их мне!', },
    {'Принесите 8 драконьих яиц и устройство-коллекционер Тинки Кипеллер в Пламенеющий Стяг в Пылающих степях.', },
  },
  [4736] = {
    'Поиски Менары Расщепительницы Бездны',
    {'А, привет, $N. Твое обучение идет неплохо. Настолько неплохо, что, я думаю, пришло время вознаградить тебя за прилежание – и за то, что сила магии все еще не поработила тебя.$B$BЕсли тебя заинтересовали мои слова, поговори с Менарой Расщепительницей Бездны из Кабестана. В настоящий момент она живет в башне Страхада Фарсана, построенной на утесе над городом.$B$BОна не очень доброжелательно настроена, поэтому похвалы от нее не жди. Но если ты будешь стараться, твои усилия не пропадут втуне.', },
    {'Поговорите с Менарой Расщепительницей Бездны в Степях.', },
  },
  [4737] = {
    'Поиски Менары Расщепительницы Бездны',
    {'Ха! Я вижу, ты стремишься к могуществу. Не сомневаюсь, рано или поздно ты падешь жертвой соблазна и жажды магии.$B$BПфа! Ты $Gглуп:глупа;! Время покажет, что я был прав, – ничто другое, одно лишь время…$B$BНо мне кажется, что не все согласны с Зевростом. Менара готова создать предметы, которые помогут тебе обрести могущество и обуздать собственную силу. Если хочешь, ступай в Кабестан. Она живет в башне над городом.', },
    {'Поговорите с Менарой Расщепительницей Бездны в Степях.', },
  },
  [4738] = {
    'Поиски Менары Расщепительницы Бездны',
    {'Пойми одно, $N: я считаю, что ты не $Gготов:готова; к такой ответственности. Я уверена, что вовлекать новичков в создание любых предметов силы, тем более, непроверенных, недопустимо.$B$BНо решать не мне, а сильным мира сего, которые хотят наделить тебя такими дарами.$B$BЕсли ты хочешь узнать побольше, советую поговорить с Менарой Расщепительницей Бездны из Кабестана. Обычно ее можно найти в башне над портом.', },
    {'Поговорите с Менарой Расщепительницей Бездны в Степях.', },
  },
  [4739] = {
    'Поиски Менары Расщепительницы Бездны',
    {'$C! Да, ты!$B$BВижу, ты не полностью $Gэкипирован:экипирована;, хотя уверенности и $Gподнабрался:поднабралась;. Да-да, теперь я это понимаю.$B$BМенара Расщепительница Бездны научит тебя нескольким вещам. Она немало времени посвящает обучению молодежи тайным искусствам. Тем, кто проходит ее испытания, она даже дает дары.$B$BЕсли хочешь поучиться, ступай в город Кабестан в Степях. Она живет над городом в башне Страхада.', },
    {'Поговорите с Менарой Расщепительницей Бездны в Степях.', },
  },
  [4740] = {
    'Разыскивается: Глубомрак!',
    {'Внимание!$B$BНазначена награда за голову мурлока, именуемого "Глубомрак". Эта злобная тварь повинна в смерти как минимум одного часового и подозревается в потоплении как минимум двух грузовых судов в прибрежных водах Темных берегов!$B$BГлубомрака в последний раз видели в поселении мурлоков к югу от Аубердина, и, судя по всему, он охраняет хижины своих сородичей. Награда за голову Глубомрака ждет смельчака у часового Глинды Нал\'Шии в Аубердине.$B$BСовет Аубердина', },
    {'Найдите и убейте мурлока, известного как Глубомрак. Предполагается, что это существо охраняет и защищает хижины мурлоков в поселении к югу от Аубердина, у воды.$B$BО смерти Глубомрака доложите часовому Глинде Нал\'Шии в Аубердине.', },
  },
  [4741] = {
    'Одичавшие стражи',
    {'В северных областях Зимних Ключей дикие совухи совсем озверели. Необходимо положить этому конец!$B$BУменьши численность осененных луной совухов, а заодно исследуешь местность, где они обитают.$B$BМеста там дикие, но ты справишься. Возвращайся, когда убъешь 13 совухов.', },
    {'По заданию Трулла Баловня Судьбы из Оскверненного леса убейте 13 осененных луной совухов.', },
  },
  [4742] = {
    'Печать вознесения',
    {'Видишь, в этой печати есть три простых углубления. В них надо вставить три камня власти; после этого властитель Змейталак, владыка нижней крепости, должен будет закалить печать в пламени черных драконов.$B$BНайди эти камни! И помни, $Gсмертный:смертная;: шансы, что у кого-то из трех генералов нижней крепости будет при себе нужный нам камень, невелики. Не отступайся! Ищи, будь $Gвнимателен:внимательна; и не теряй решимости!', },
    {'Найдите самоцвет Тлеющего Терновника, самоцвет Черной Вершины и самоцвет Кровавого Топора. Верните их Ваелану вместе с простой печатью Вознесения.$B$BКамни могут быть у трех генералов: у воеводы Вуна из племени Тлеющего Терновника, у вождя Омокка из клана Черной Вершины и у повелителя Змейталака из клана Кровавого Топора.', },
  },
  [4743] = {
    'Печать вознесения',
    {'Очень немногие черные драконы из верхней крепости способны закалить печать в пламени своего дыхания. Змейталак – один из них, но его волю сломить невозможно.$B$BТебе надо добраться до Драконьих топей в Пылевых топях. Там ты найдешь древнего дракона, известного под именем Огнебор. Ты $Gдолжен:должна; сломить его волю, $N. И когда он ослабнет, тебе нужно будет воспользоваться этой сферой. Но помни, $Gсмертный:смертная;: его разум силен, и ты сможешь удерживать его под контролем не более нескольких секунд. За эти секунды тебе нужно успеть закалить печать.', },
    {'Идите в Драконьи топи в Пылевых топях. Найдите Огнебора и бейте его, не жалея, чтобы сломить его волю.$B$BКак только воля великого зверя ослабнет, надо немедленно положить перед ним заготовку печати Вознесения и воспользоваться сферой Энергии дракона, чтобы подчинить себе его разум и направить его пламя на заготовку.', },
  },
  [4761] = {
    'Тандрис Ветропряд',
    {'При разведке в лагере фурболгов вы обнаружили информацию, которую нужно довести до сведения Тандриса Ветропряда. Он любезно согласился исполнять обязанности старейшины в Аубердине, мудростью своей направляя повседневную жизнь деревни. Прошу вас поделиться с ним вашими находками в деле фурболгов.$B$BЯ уверен, что у него есть свои идеи по поводу источника наведенной на них порчи. Может быть, вы сможете вместе с ним выработать план восстановления природного равновесия!', },
    {'Поговорите с Тандрисом Ветропрядом в Аубердине.', },
  },
  [4762] = {
    'Река Скалистая',
    {'Воды реки Скалистой исполнились злобы и порчи. Река впадает в Туманный предел, и я боюсь, что скоро беда затронет и Аубердин. Я подозреваю, что в загрязнении повинны фурболги Чернолесья, живущие в верховьях реки, но я также полагаю, что они не являются подлинной причиной этой порчи.$B$BВозьмите эту пробирку и отправляйтесь к устью реки, что к северу отсюда. Идите вверх по реке до первого водопада и там возьмите пробу воды. Ориентиром вам послужит высокий мост через реку. Когда возьмете пробу, вернитесь ко мне в Аубердин.', },
    {'Отправляйтесь на север от Аубердина, к первому водопаду вверх по реке Скалистой, и там возьмите пробу.$B$BС пробой воды Скалистой реки возвращайтесь в Аубердин к Тандрису Ветропряду.', },
  },
  [4763] = {
    'Порабощенные фурболги Чернолесья',
    {'Мы узнали, что источником порчи, наведенной на фурболгов, являются сатиры. Они держат своих жертв в повиновении с помощью талисманов, через которые направляют магию. Если для фурболгов еще есть шансы на спасение, мы должны выманить сатиров-поработителей и отнять у них талисманы!$B$BНаполни эту чашу из нашего Лунного колодца и стащи образцы пищи фурболгов из их северного лагеря. Смешай все и поставь чашу возле большого костра у реки; любой фурболг, поевший из этой чаши, на некоторое время очистится, что привлечет внимание сатира... которого нужно убить!', },
    {'Наполните пустую чашу Очищения из Лунного колодца Аубердина.$B$BСоберите образцы фруктов, орехов и зерна в расположенных на севере лагерях фурболгов.$B$BСмешайте все в чаше и поместите ее возле ближайшего к реке Скалистой большого костра в северном лагере, вызвав таким образом сатира.$B$BСнимите с него Талисман Скверны и принесите его Тандрису Ветропряду в Аубердин.', },
  },
  [4764] = {
    'Пряжка Роковой оснастки',
    {'Высоко в горах, на пике Черной горы, есть чертог, который некогда звался Залом Сокровищ. Именно там дворфы Черного Железа хранили свои реликвии, трофеи и предметы искусства.$B$BЭто было давно. Говорят, что зал обрушился, и теперь используется... менее достойно. Но я думаю, что там остались дворфийские реликвии.$B$BОдна из них – пряжка Роковой оснастки. Ее поверхность так плотно инкрустирована редкими самоцветами, что любой дворф начинает плакать от одного ее вида.$B$BИменно эта пряжка и станет твоей целью, $N.', },
    {'Принесите пряжку Роковой оснастки Майре Светлое Крыло в Пылающие степи.', },
  },
  [4765] = {
    'Доставить Риджвеллу',
    {'Я упаковала пряжку Роковой оснастки в ящик для отправки в Штормград. $N, ты мне очень $Gпомог:помогла;, но я хочу попросить еще об одной услуге.$B$BДоставь этот ящик графу Ремингтону Риджвеллу. Он в Крепости Ветров. Не сомневаюсь, он обрадуется.', },
    {'Отнесите ящик Риджвелла Ремингтону Риджвеллу в Крепость Штормграда.', },
  },
  [4766] = {
    'Майра Светлое Крыло',
    {'Мой агент, Майра, отправилась в Пылающие степи, чтобы обыскать развалины города Тауриссана. В разрушенном обиталище дворфов Черного Железа таится множество ценных вещей... которые украсят наш семейный музей.$B$BМайара говорит, что кое-что нашла, но ей нужна помощь. Правда, это находится не в Тауриссане. Вместо этого требуется устроить вылазку на пик Черной горы.$B$BПодробности мне неизвестны, но если тебя заинтересовало мое предложение, поговори с Майрой в Пылающих степях.', },
    {'Поговорите с Майрой Светлое Крыло на Дозоре Моргана в Пылающих степях.', },
  },
  [4767] = {
    'Ветрокрыл',
    {'Мне велели набрать новых виверн, чтобы выдрессировать их и использовать в качестве ездовых животных. Их поголовье постоянно растет, и я надеюсь, что когда-нибудь сам смогу стать всадником на ветрокрыле. Однако виверны пользуются большим спросом, и нам их постоянно не хватает. Может быть, вы, $N, сможете мне помочь?$B$BЯ хочу попросить вас отправиться в Скальное гнездовье и набрать с кладок виверн немного яиц. Отсюда ступайте на северо-запад, держитесь поближе к стене каньона – и увидите дорогу, ведущую наверх. Но будьте очень осторожны: виверны не любят чужаков, а уж за свои кладки они будут готовы разорвать кого угодно.', },
    {'Соберите в Скальном гнездовье 10 яиц виверн и отнесите их Элу на Заставу Вольного Ветра.', },
  },
  [4768] = {
    'Табличка Темнокамня',
    {'Табличка Темного Камня – это записки покойного дворфа Урхельда Темного Камня. Он был величайшим алхимиком древности, но современники считали его сумасшедшим и не придавали значения его изысканиям.$B$BИзвестно, что он записал результаты своих трудов. Вырезал их на табличках. И у меня есть основания предполагать, что одна из этих табличек хранится в Пике Черной горы, в зале, который когда-то назывался залом Удачи. Как его называют сейчас, я не знаю, ибо многие уходили в Черную Скалу, но немногие вернулись назад.$B$BЕсли тебя не пугают опасности Пика Черной горы, принеси мне табличку, и я щедро тебя награжу.', },
    {'Принесите табличку Темного Камня тенемагу Вивиан Лягроб в Каргат.', },
  },
  [4769] = {
    'Вивиан Лягроб и табличка Темнокамня',
    {'Вивиан Лягроб прислала мне весть из Каргата, что в Бесплодных землях. Она выяснила, что в Пике Черной горы хранится один древний текст, который может помочь нам в изысканиях. Однако это всего лишь догадка, требующая подтверждения. Нужно, чтобы кто-то добыл этот текст из Пика. Но сразу скажу: это будет непросто.$B$BЕсли возьмешься за эту работу, обратись к Вивиан в Каргат.', },
    {'Переговорите с темным магом Вивианой Лягроб.', },
  },
  [4770] = {
    'Путь домой',
    {'Пожалуйста, |3-6($C), помогите мне! Я уже несколько дней прячусь в этом Скальном гнездовье, и за это время не встретил ни одного знакомого лица! Я пришел сюда изучать виверн и, похоже, немного переборщил – пришлось спасаться от них бегством, вот тогда-то я и заблудился.$B$BМне срочно нужно вернуться на заставу Белого Плеса! Мотега не знает, что я потерялся, а когда узнает, что я полез в Скальное гнездовье один, он мне голову оторвет!$B$BЗастава прямо к востоку отсюда... пожалуйста, проводите меня туда!', },
    {'Выведите Паоку Ходячую Гору из Скального гнездовья, а потом поговорите с Мотегой Огненной Гривой на заставе Белого Плеса.', },
  },
  [4771] = {
    'Рассветный гамбит',
    {'Я поместила рассветный гамбит в ящик, в котором были драконьи яйца. Ты $Gдолжен:должна; отнести его в самую глубину Некроситета.$B$BВектус, маг из нежити, руководит группой студентов Плети. Доставь рассветный гамбит в комнату, которую они называют смотровой. Если все пойдет как надо, устройство уничтожит всю нежить, находящуюся в этот момент в комнате.$B$BПосле этого уничтожить Вектуса будет проще пареной репы. Так ведь?', },
    {'Отнесите рассветный гамбит в Демонстрационную комнату в Некроситете. Уничтожьте Вектуса и возвращайтесь к Бетине Биггльцинк.', },
  },
  [4781] = {
    'Материалы для зачарованного золотого кровавого одеяния',
    {'Следующее задание не сложнее первого, но тебе все равно потребуется помощь.$B$BДля вышивки мне нужна золотая нить, но ее не так просто найти, и мало кто знает, как ее делать. К счастью, этот секрет ведом одному из тех, кто верен нашему делу и старается помогать тем, кто стремится пополнить наши ряды.$B$BОтнеси золотой слиток Зиску Ладноскрою в Пиратскую Бухту. Он отличный портной и с радостью спрядет для тебя нить. Золотые слитки могут изготавливать опытные горняки. Обратись к ним.', },
    {'Отнесите слиток золота Зиску Ладноскрою в Тернистую долину.', },
  },
  [4782] = {
    'Материалы для зачарованного золотого кровавого одеяния',
    {'Возвращайся к Менаре и скажи ей, что работа предстоит долгая, но я постараюсь ради нее... и ради тебя. Передай ей, что пока ты собираешь остальные материалы, я закончу делать нить. Тогда ты придешь и заберешь ее.$B$BУдачи, $N. Ах, да, передай привет Страхаду, раз уж будешь рядом. Старый болван... как его угораздило построить башню рядом с Кабестаном?', },
    {'Возвращайтесь к Менаре Расщепительнице Бездны в Степи.', },
  },
  [4783] = {
    'Материалы для зачарованного золотого кровавого одеяния',
    {'Следующие материалы можно найти в Пустошах, далеко на западе от Степей, за Когтистыми горами.$B$BТам все еще царит хаос со времен Раскола. Это место, где предстоит серьезное испытание твоей силе и хитрости – там бродят омерзительные твари и приспешники Пылающего Легиона.$B$BПринеси мне кровь сатиров племени Ярой Ненависти, что обитают на северо-востоке, и простой камень Преисподней от младших инферналов Поля Маннорок.$B$BИ то, и другое понадобится для создания одеяния.', },
    {'Принесите 10 фиалов крови сатира племени Ярой Ненависти и 1 простой камень Преисподней в Степи, Менаре Расщепительнице Бездны.', },
  },
  [4784] = {
    'Материалы для зачарованного золотого кровавого одеяния',
    {'Осталось отыскать три составляющих, $N, и твое одеяние будет готово.$B$BВо-первых, нить из чистого золота, которую делает Зиск. Отправляйся к нему в Пиратскую Бухту и принеси нить.$B$BВо-вторых, нужны тлеющие угли элементалей огня с нагорья Арати. Если я правильно помню, духов можно найти в западной части нагорья, рядом со стеной Торадина.$B$BИ, наконец, мне нужен осколок души, созданный тобой.$B$BСобрав все составляющие, возвращайся в башню, и я закончу работу над одеянием.', },
    {'Принесите Менаре Расщепительнице Бездны в Степи нить из чистого золота, 2 тлеющих угля и осколок души.', },
  },
  [4785] = {
    'Нить из чистого золота',
    nil,
    nil,
  },
  [4786] = {
    'Завершенное одеяние',
    {'А теперь я завершу работу над одеянием. Это займет немного времени. Не уходи далеко.', },
    {'Дождитесь, пока Менара Расщепительница Бездны закончит работу над вашим одеянием, и еще раз поговорите с ней.', },
  },
  [4787] = {
    'Древнее яйцо',
    {'В пророчестве Мошару говорится о древнем яйце. Это реликвия тех времен, когда тролли правили громадными империями, и в нем заключена сила, которая сможет сдержать сущность Хаккара. Пожалуй, это единственный способ избавить наш мир от зла.$B$BПринеси мне древнее яйцо, $N. Говорят, оно спрятано в Джинта\'Алоре, что во Внутренних землях, в пещере за амфитеатром в верхней части города.', },
    {'Принесите древнее яйцо Йе\'кинье в Танарис.', },
  },
  [4788] = {
    'Последние таблички',
    {'Пока я изучаю третью и четвертую таблички Мошару, отыщи пятую и шестую! Это будет нелегко, потому что они в руках троллей из племени Тлеющего Терновника на пике Черной горы. Этот пик – огненная гора между Пылающими степями и Тлеющим ущельем.$B$BУдачи, $N. Она тебе понадобится.', },
    {'Принесите пятую и шестую таблички Мошару геологу Железному Башмаку в Танарисе.', },
  },
  [4801] = {
