  [4808] = {
    'Фелнок Сталлист',
    {'Мне придется попросить тебя еще об одной услуге.$B$BВ результате исследования добытых тобой сущностей, я поняла, как можно заморозить яйца драконов и сохранить их! Мне нужно испытать собственную теорию, но первым делом понадобятся кое-какие приспособления. Мой коллега, Фелнок Сталлист, умеет их делать.$B$BОтнеси это письмо Фелноку и объясни ему, что мне нужно. Фелнок живет в городе Круговзоре, в Зимних Ключах.', },
    {'Отнесите письмо Тинки Фелноку Сталлисту в Круговзор, что в Зимних Ключах.', },
  },
  [4809] = {
    'Рога хладнокрылов',
    {'У меня есть почти все, что нужно Тинки, не хватает только одного важного компонента – рогов хладнокрылов. Рога этих химер – великолепный источник стужи, но годятся только безупречные рога без единой трещинки! Чтобы выполнить заказ Тинки мне нужно несколько таких рогов.$B$BДобудь их, и я соберу посылку для Тинки.', },
    {'Добудьте 8 безупречных рогов хладнокрыла.', },
  },
  [4810] = {
    'Возвращение к Тинки',
    {'Ну вот, все готово и запаковано! Доброго пути, не забудь передать Тинки, что она мой должник!', },
    {'Отнесите ящик Фелнока Тинки Кипеллер в Пылающие степи. Ее можно найти возле Пламенеющего Стяга.', },
  },
  [4811] = {
    'Красный кристалл',
    {'К востоку отсюда становится все больше враждебно настроенных лунных совухов. Когда-то их считали милыми и спокойными, почти мистическими созданиями. В то время как кое-кто продолжает почитать их, тревога о безопасности Аубердина вынуждает меня смотреть на вещи более реалистично.$B$BЯ получила сообщения, что лунных совухов привлекает большой красный кристалл, находящийся в горной гряде на востоке Темных берегов. Никто не только не знает, что это за кристалл, но есть ли он на самом деле. Я хочу, чтобы вы установили его местонахождение и доложили мне о том, что обнаружите.', },
    {'Направляйтесь на восток от Аубердина и поищите большой красный кристалл в горной гряде на востоке Темных берегов. Вернитесь с докладом к часовому Глинде Нал\'Шие в Аубердин.', },
  },
  [4812] = {
    'Пока струится вода...',
    {'Возьмите эту пустую колбу и наполните ее водой из лунного колодца в Аубердине. Ее могучая магия должна помочь нам разгадать структуру кристалла. Чтобы это сделать, просто облейте кристалл, а остальное должно произойти само собой.$B$BХотя я не думаю, чтобы вам что-то угрожало, я все же попрошу вас быть осторожнее. За горами на востоке находится Оскверненный лес, и если этот кристалл как-то связан с тем местом (как я подозреваю), он может оказаться очень опасным.', },
    {'Наполните пустую колбу для воды в Лунном колодце Аубердина, а затем исследуйте красный кристалл, находящийся в горной гряде на востоке Темных берегов.', },
  },
  [4813] = {
    'Скрытые фрагменты',
    {'С помощью воды Лунного колодца выяснилось, что в кристалле заключено несколько мелких фрагментов костей, а также половина челюсти. Кажется, челюсть принадлежала гуманоиду, но точно нельзя сказать, не разбив кристалл, – а это даже для самой мощной магии может оказаться почти непосильной задачей.$B$BЗадание выполнено, и все, что вам остается, – это вернуться в Аубердин и доложить обо всем часовому Глинде Нал\'Шие.', },
    {'Доложите о результатах ваших изысканий часовому Глинде Нал\'Шие в Аубердине.', },
  },
  [4821] = {
    'Странное яйцо',
    {'Ходят слухи, что здесь, в Тысяче Игл, появилось яйцо какого-то странного существа. Но те, кто видел его, не смогли подойти достаточно близко и рассмотреть яйцо поподробнее. Его охраняют змеи, да так, словно это яйцо – их собственное!$B$BЯ хочу, чтобы вы нашли его и принесли мне для изучения. Говорят, что оно находится в логове змеев, но вообще-то таких гнезд несколько, они разбросаны вдоль стен каньона к югу и северо-востоку от заставы.', },
    {'Принесите странное яйцо Хагару Грозовому Копыту на Заставу Вольного Ветра.', },
  },
  [4822] = {
    'Женщинам – цветы, детям – ...',
    {'МОРОЖЕНОЕ! Пожалуйста, пожалуйста, пожалуйста, добудь для меня немного мороженого!!! Я больше всего люблю клубничное, а самое лучше клубничное мороженое делают Тигуль и Форор! Это мое самое любимое мороженое на свете!$B$BЯ пробовал его только один раз, когда меня водили на гонки на Мерцающей равнине, но говорят, его сейчас продают у нас в городе! Пожалуйста! Пожалуйста-препожалуйста! Преогромное пожалуйста!!!!', },
    {'Купите своему подопечному клубничное мороженое. Кажется, мальчик предпочитает мороженое Тигуля и Форора.', },
  },
  [4841] = {
    'Упокоение кентавров',
    {'$N, я поручаю вам охоту на этих кентавров. Отомстите им за нас как следует – вырежьте весь их лагерь к северу от Заставы Вольного Ветра. Пусть рука ваша будет тверда!', },
    {'Убейте 12 разведчиков, 10 пастухов и 6 ветроловок из племени Галак, после чего вернитесь к дозорному утесов Долгорогу на Заставу Вольного Ветра.', },
  },
  [4842] = {
    'Выяснение причин',
    {'По правде говоря, хотя в Зимних Ключах вулканов нет, я думаю, для возникновения этих горячих источников есть не менее опасные причины…$B$BВпрочем, наверное, ты $Gсам:сама; захочешь все увидеть. Тебе нужно будет пересечь все Зимние Ключи, до самого юга этого края. Там, где воздух станет плотным и удушливым, а небо потемнеет, ты как раз и найдешь то, что я считаю причиной возникновения горячих ключей.$B$BСходи взглянуть. Я уверена, ты вернешься с кучей вопросов.', },
    {'Последуйте указаниям Доновы Снегоноры, а затем вернитесь к ней.', },
  },
  [4861] = {
    'Разъяренные дикие совухи',
    {'Эй, послушай, $Gпаренек:девчушка;! Меня какие-то кровожадные дикие совухи гнали всю дорогу из моего лагеря! Подозреваю, я слишком близко подобрался к разгадке того, что же они там охраняют. $B$BЯ еле ноги унес оттуда. Боюсь, мой коллега оказался менее везучим. Ты как, не против того, чтобы бросить вызов диким совухам и отправиться в мой лагерь? Очень уж дороги мне мои припасы, а также я хотел бы вернуть амулет, который извлек вчера во время раскопок.', },
    {'Найдите разоренный лагерь Джерона Гранильщика и его припасы на юге Зимних Ключей.', },
  },
  [4862] = {
    'Товар на любителя',
    {'Верь – не верь, но есть народ, который готов отлично заплатить за необычную зверушку, причем чем она злее, тем лучше!$B$BЯ послал на пик Черной горы Блестяшку, чтобы собрать паучьи яйца, но она уже несколько недель не возвращается. Может, ты придешь ей на выручку?$B$BВот что, $N. Ступай-ка туда и принеси мне паучьи яйца. Я тебе не только хорошо заплачу, но еще и приручу для тебя одного паучка!', },
    {'Отправьтесь на пик Черной горы и принесите Киблеру 15 яиц скального паука.$B$BСудя по всему, яйца надо искать недалеко от пауков.', },
  },
  [4863] = {
    'Разъяренные дикие совухи',
    {'Вы озираетесь, но видите ничего такого, чтобы могло понадобиться Джерону. Но заметно, что найденный вами ящик был вскрыт силой, как будто тут не обошлось без диких совухов...$B$BВзглянув на восток, вы замечаете покинутый фургон. Стоит посмотреть не осталось ли там каких-то вещей Джерона.', },
    {'Найдите опрокинутый фургон Джерона Гранильщика.', },
  },
  [4864] = {
    'Разъяренные дикие совухи',
    {'Изучив содержимое ящика, вы решаете, что Джарону могут пригодиться лежащие там инструменты. Однако среди них нет и следа амулета.$B$BПохоже, дикие совухи обитают к северу и к востоку отсюда, и вы решаете, что ваш следующий шаг – встретить этих существ лицом к лицу и забрать у них амулет.', },
    {'Подберите Припасы Джерона из снега, и найдите дикого совуха, похитившего амулет, о котором говорил Джерон.$B$BПринесите Припасы и Ожерелье Синих перьев Джерону Гранильщику в его дом в Зимних Ключах.', },
  },
  [4865] = {
    'Змеиная дикость',
    {'Если бы я не видел это собственными глазами, то ни за что бы не поверил! Месть обрушилась на Тысячу Игл!$B$B*Хагар утирает лоб.*$B$B$N, надо действовать быстро! Скорее, иди и поговори с Мотегой Огненной Гривой, он на заставе Белого Плеса, это к северо-западу отсюда, прямо возле дороги. Он подскажет нам, что делать!', },
    {'Расскажите о найденном Мотеге Огненной Гриве на Заставе Белого Плеса, что в Тысяче Игл.', },
  },
  [4866] = {
    'Материнское молоко',
    {'Должно быть, это один из сильнейших ядов на свете!$B$BВо время одной из вылазок на пик Черной горы я слышал, как орки говорят о его смертоносности. Они называют его "материнское молоко" в честь Дымной Паутины, огромной паучихи, поселившейся в одной из пещер Лабиринта.$B$BИ вот что мне нужно от тебя: образец молока из твоих собственных вен. Ядовитая железа не подойдет! Поэтому дай ей отравить себя, а потом возвращайся!$B$BА я тебя... подою!$B$B<Потирает руки в нетерпении.>', },
    {'Найдите в самом сердце пика Черной горы мать Дымную Паутину. Сражайтесь с ней, пока она не введет вам свой яд. Скорее всего ее также придется убить. Когда яд будет в вас, вернитесь к Джону-Оборванцу, чтобы он смог добыть яд.', },
  },
  [4867] = {
    'Аррок Смертный Вопль',
    {'Ар-рх! Трудно говорить!$B$BВозьми эту записку! Принеси мне мой амулет!', },
    {'Прочитайте записку Вароша. Принесите Варошу его амулет.', },
  },
  [4881] = {
    'Убийственный заговор',
    {'Таурен по имени Канати Серое Облако собрался разбить в Тысяче Игл охотничий лагерь. Мы должны убить его и опустошить лагерь, уничтожив всех его сторонников. Собираемся на маленькой заставе к юго-западу от Великого подъемника.', },
    {'Предупредите Канати Серое Облако на заставе Белого Плеса, что на него готовится покушение.', },
  },
  [4882] = {
    'Умение хранить секреты',
    {'Вы не можете понять, важна ли ваша находка – ожерелье, упавшее с шеи недавно убитого дикого совуха. Оно источает яркий пульсирующий свет.$B$BМожет быть, Трулл Баловень Судьбы сможет что-то рассказать – похоже, он очень много знает о диких совухах. Возможно, стоит его расспросить об этом ожерелье?', },
    {'Отнесите ожерелье синих перьев Труллу Баловню Судьбы на Заставу Отравленной Крови.', },
  },
  [4883] = {
    'Умение хранить секреты',
    {'Хотя я располагаю некоторыми сведениями о совухах, с которыми ты $Gборолся:боролась; в Зимних Ключах, однако об их магии мне ничего не известно. Но я знаю кое-кого, кто с удовольствем изучит это ожерелье и скорее всего сумеет рассказать тебе о его свойствах.$B$BНара Буйногривая из Громового Утеса настоящий эксперт в подобных вещах, тебе стоит поговорить с ней. Удачи, $N!', },
    {'Отнесите ожерелье синих перьев Наре Буйногривой.', },
  },
  [4901] = {
    'Стражи алтаря',
    {'На самой вершине гребня находится, как я полагаю, алтарь богини Элуны. Насколько я знаю, нам нужно зажечь пять факелов – в каждой пещере, и тогда мы получим доступ к алтарю.$B$BЯ расшифровала заклинания, вырезанных на камне в древних руинах Кел\'Терила, которые, как я думаю, являются ключом к активации алтаря. Помогите мне зажечь факелы, пока я буду читать заклинание.$B$BЕсли у нас все получится, вы сможете доложить Эреласу о результатах!', },
    {'Защитите Раншаллу, пока она пытается активировать алтарь Элуны. Сообщите о ваших находках Эреласу Янтарному Небу в деревне Рут\'теран.', },
  },
  [4902] = {
    'Питомцы Элуны',
    {'Это поразительно!$B$BМне всегда было интересно, какова же истина… Что ж, вы просто обязаны поделиться этими сведениями! Сразу же отправляйтесь к верховному друиду Фэндралу Оленьему Шлему – вы найдете его в Дарнасе, в Круге Кенария. Расскажите ему подробно о случившемся – так же, как рассказали мне. Я уверен, что ему будет очень интересно!$B$BЭто важная информация, $N!', },
    {'Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнасе.', },
  },
  [4903] = {
    'Приказ полководца Клинозуба',
    {'По приказу полководца Клинозуба следующие жители Пика Черной горы подлежат уничтожению: $B$BОсобо опасный преступник вождь Омокк. $B$BЖестокий и беспощадный тролль воевода Вун.$B$BПовелитель Змейталак, десятник в малом городе.$B$BТакже необходимо доставить в штаб все найденные при них документы. $B$BУспех – слава и награда.$B$BПоражение – позор и забвенье.', },
    {'Убейте вождя Омокка, воеводу Вуна и повелителя Змейталака. Найденные при них важные бумаги Черной горы доставьте полководцу Клинозубу в Каргат.', },
  },
  [4904] = {
    'Долгожданная свобода',
    {'Арнак хотел жениться на мне, и поэтому похитил меня, когда я отвергла его предложение. Мой брат пытался остановить его, но Арнак оказался сильнее. Он... он убил моего брата и привел меня сюда. Мой бедный брат...$B$B$N, не могли бы вы вывести меня с Пика Темного Облака? Я хочу назад, к Талье! Она, наверное, с ума сходит от беспокойства.', },
    {'Выведите Лакоту Песню Ветра с Пика Темного Облака, а потом поговорите с Талией Янтарной Шкурой на Заставе Вольного Ветра.', },
  },
  [4906] = {
    'Снова порча!',
    {'Другой лагерь сатиров угрожает северной части Оскверненного леса. Тамошними сатирами предводительствует жестокий Ксаварик. Эти подлые предатели и обманщики радуются, поражая порчей тварей Оскверненного леса.$B$BКсаватрас и его приспешники – только часть большого плана по созданию оскверненного лунного колодца. А затем всем этим воспользуется Ксаварик.$B$BТеперь, когда Ксаватрас уже не мешается у нас под ногами, заверши это дело, $N. Отправляйся на север и срази Ксаварика.', },
    {'Эридана Грусть Ветра из Оскверненного леса просит вас убить 8 призывателей Пламени, 8 предателей и 8 плутов из племени Нефритового Пламени, а также Ксаварика. По выполнении задания возвращайтесь к Эридане.', },
  },
  [4907] = {
    'Тинки Кипеллер',
    {'Привет, $N. Помнишь Тинки Кипеллер? Она-то тебя помнит! Она послала мне весточку, чтобы я передал тебе при случае. Ей снова нужна твоя помощь. Кажется, это опять связано с изучением драконов.$B$BСтупай в Пылающие степи, в Пламенеющий Стяг, и поговори с ней.', },
    {'Поговорите с Тинки Кипеллер. Ее можно найти возле Пламенеющего Стяга в Пылающих степях.', },
  },
  [4921] = {
    'Пропавшая без вести',
    {'Мы бились в маленьком лагере тауренов и разделились в бою – на нее наседали разом трое дыбогривов. Силы были неравны. Я отвлек на себя очень многих и увидел, что набежали новые враги и одолевают ее. Впав в ярость, я бросился на врагов, но они с легкостью одержали надо мной верх.$B$BЯ пришел в себя и увидел, что таурен-друид ухаживает за мной, – он набрел на меня на Золотом пути.$B$BПрошу тебя, |3-6($C), узнай что-нибудь о моей жене.', },
    {'Отыщите жену Манкрика и возвращайтесь к нему в Перекресток.', },
  },
  [4941] = {
    'Мудрость Эйтригга',
    {'Ренд жив? $B$BБыть того не может! Я думал, он погиб несколько десятков лет назад. $B$BТебе нужно обратиться за советом к Эйтриггу, $N! Никто не знает о Черной горе больше него, и если то, что написано здесь, правда, Эйтриггу стоит об этом знать. Никому нельзя отказывать в праве на месть. $B$BКогда поговоришь с Эйтриггом, ступай к вождю за указаниями.', },
    {'Поговорите с Эйтриггом в Оргриммаре. После этого обратитесь к вождю Траллу. $B$BВ последний раз Эйтригг был в в зале приемов Тралла.', },
  },
  [4961] = {
    'Очищение шара Орахила',
    {'А, сфера призывателя – так вот откуда исходит искажение. Если ты хочешь ей пользоваться, придется изгнать из нее демона. С этим я легко справлюсь.$B$BТебе придется куда труднее. Как только демон выйдет из сферы, убей его.$B$BИ спеши. Если ты будешь возиться слишком долго, он вернется в сферу, и нам придется начать сначала!$B$BПодготовься к схватке, $N. Вспомни самые сильные заклинания и держи под рукой зелья. Если у тебя есть друг, можешь позвать его. Сражение будет нелегким.', },
    {'Убейте демона сферы, а потом поговорите с Табетой.', },
  },
  [4962] = {
    'Осколок гончей Скверны',
    {'Итак, ты $Gвыбрал:выбрала; гончую скверны. Отлично.$B$BВозьми этот рубин, он позволит подчинить ее душу. Делай, как обычно: когда тварь окажется на пороге смерти, воспользуйся камнем, и он все сделает за тебя.$B$BСоветую отправиться в Пустоши и попробовать подчинить одну из тамошних гончих скверны. Они отлично подойдут.$B$BКогда добьешься успеха, отнеси плененного духа и рубин Менаре.', },
    {'Возьмите рубин Сквернолова и поймайте в него одну из гончих скверны в Пустошах. Добившись успеха, отнесите плененный дух Гончей Скверны и рубин Сквернолова Менаре Расщепительнице Бездны из Степей.', },
  },
  [4963] = {
    'Осколок инфернала',
    {'Итак, ты $Gвыбрал:выбрала; инфернала. Отлично.$B$BВозьми этот изумруд, он позволит подчинить его душу. Делай, как обычно: когда тварь окажется на пороге смерти, воспользуйся камнем, и он все сделает за тебя.$B$BСоветую отправиться в Пустоши и попробовать подчинить одного из инферналов. Они отлично подойдут.$B$BКогда добьешься успеха, отнеси плененного духа и изумруд Менаре.', },
    {'Возьмите Пылающий изумруд и поймайте в него одного из инферналов в Пустошах. Добившись успеха, отнесите плененный дух инфернала и пылающий изумруд Менаре Расщепительнице Бездны из Степей.', },
  },
  [4964] = {
    'Завершение шара Дар\'орахила',
    {'Я немедленно займусь твоим шаром. Надо вселить в него нового демона. После этого у меня будет выбор: превратить его в сферу или в посох. Я всегда считала, что соответствие избранному оружию чернокнижника – это главное... Не вижу причин менять свое мнение.', },
    {'Дождитесь, пока Менара Расщепительница Бездны закончит работу над шаром Дар\'орахила, и еще раз поговорите с ней.', },
  },
  [4965] = {
    'Знание шара Орахила',
    {'Так-так-так… приятно видеть, как ты набираешься могущества, $N. Мне кажется, ты $Gготов:готова; поговорить с Менарой Расщепительницей Бездны в Степях. Если вы раньше не встречались, то, поверь мне, другого такого специалиста по созданию предметов – особенно сфер, в которые можно заключить демона – не найти.$B$BИнтересно? Тогда поищи ее. Она живет в башне Страхада над Кабестаном.', },
    {'Поговорите с Менарой Расщепительницей Бездны в Степях.', },
  },
  [4966] = {
    'Защитить Канати Серое Облако',
    {'Кентавров намного больше, чем я думал, $N. Пожалуйста, помогите мне отбиться от них.', },
    {'Защитите Канати Серое Облако от нападения кентавров.', },
  },
  [4967] = {
    'Знание шара Орахила',
    {'Ты так и не $Gпоумнел:поумнела; и по-прежнему стремишься в западню. Да, говорю я дерзко. Я смотрю на тебя и смеюсь. Ты жалкий червь!$B$BНо мое мнение не имеет значения. Главное, что думают те, кто желает иметь с тобой дело! И в данном случае наделенные силой полагают, что ты заслуживаешь большего.$B$BЕсли ты к чему-то стремишься, поговори с Менарой Расщепительницей Бездны в башне над Кабестаном. Кажется, ты с ней уже $Gвстречался:встречалась;.', },
    {'Поговорите с Менарой Расщепительницей Бездны в Степях.', },
  },
  [4968] = {
    'Знание шара Орахила',
    {'Хм-м... Тебе удалось добиться немалого... Поразительно. Я думала, что ты давно $Gсошел:сошла; с ума, $Gраздираемый:раздираемая; скверной магией, просочившейся в твою душу. Тогда, пожалуй, двинемся дальше?$B$BЕсли ты хочешь испытать свое искусство, отыщи Менару Расщепительницу Бездны в башне Страхада над Кабестаном. Она поможет тебе сделать следующий шаг.', },
    {'Поговорите с Менарой Расщепительницей Бездны в Степях.', },
  },
  [4969] = {
    'Знание шара Орахила',
    {'Ха-ха! Ты растешь над собой, $Gюный:юная:c; |3-6($C).$B$BСо временем инферналы и повелители ужаса склонятся перед тобой!$B$BПока же тебе приходится полагаться на оружие, скованное руками смертных.$B$BПохоже, ты $Gготов:готова; создать еще одно. Если хочешь, с тобой может поговорить Менара Расщепительница Бездны! Она живет неподалеку от башни Страхада над портовым городом Кабестан. Отправляйся туда, как решишь, что время уже пришло.', },
    {'Поговорите с Менарой Расщепительницей Бездны в Степях.', },
  },
  [4970] = {
    'Накормить ледопардов',
    {'Если хочешь достойно проявить себя, начни с простого, но очень важного дела. $B$BДобудь в окрестностях мясо химер и медведей-щербозубов. Принеси его мне, чтобы я мог кормить им ледопардов, которых я дрессирую.', },
    {'Соберите 5 кусков мяса медведей-щеброзубов и 5 кусков мяса хладнокрылых химер для Риверна Холодного Ветра в Зимних Ключах.', },
  },
  [4971] = {
    'Вопрос времени',
    {'Кто-то вмешался в ход времени в Андорале! Скорее всего, это дело рук Плети. Я могу попытаться сохранить неизменным ход времени здесь и исправить повреждения, но мне не должны мешать. В этом ты мне и поможешь.$B$BВ Андорале поселились временные паразиты, падальщики, питающиеся обрывками потока времени. Их можно обнаружить только по синему сиянию. Они очень мешают мне. Пожалуйста, $N, искорени этих паразитов, воспользовавшись временным переместителем.', },
    {'Воспользуйтесь временным переместителем рядом с одним из амбаров Андорала и найдите временных паразитов.$B$BУбейте 10 временных паразитов и верните временной переместитель Хроми в Андоральскую таверну в Западных Чумных землях.', },
  },
  [4972] = {
    'Отсчет времени',
    {'Мне нужно отправить тебя в мир за пределами этой комнаты, $N. Сейчас все объясню.$B$BВидишь, как эта комната отличается от остального Андорала? Мои драконы существуют во многих пластах реальности и времени. Чтобы исправить ущерб, нанесенный Андоралу, я действую из другого временного пласта, чтобы не загрязнить его сверх меры. Для продолжения работ мне необходимы особые устройства, оставшиеся со времен прихода чумы. Они находятся в сейфах в руинах некогда гордого города.', },
    {'Найдите 5 часов Андорала, спрятанных в сейфы среди руин. Принесите их Хроми в таверну Андорала в Западных Чумных землях.', },
  },
  [4973] = {
    'Отсчет времени',
    {'Если ты $Gготов:готова; еще раз оказать мне помощь, я попрошу тебя принести еще пять часов из руин Андорала. Это очень поможет мне на равных бороться со злобной силой, пытающейся изменить ход времени.$B$BВзамен часов ты получишь еще один дар, который, мне кажется, здорово пригодится тебе в будущем.', },
    {'Найдите 5 часов Андорала, спрятанных в сундуки среди руин. Принесите их Хроми в таверну Андорала в Западных Чумных землях.', },
  },
  [4974] = {
    'За Орду!',
    {'Ренд осмеливается делать подобные заявления, потому что он пользуется поддержкой черных драконов.$B$BТы, $N, проникнешь в Залы Вознесения, найдешь там "вождя" Ренда Чернорука и уничтожишь его – ВО ИМЯ ОРДЫ!!!$B$B*Тралл ударяет кулаком по своему трону.*$B$BВ следующий раз, как я увижу тебя в своих покоях, ты торжествующе поднимешь ввысь его отрубленную голову и вручишь ее своему вождю. Выполни это – и станешь одним из героев Орды!', },
    {'Отправляйтесь в Пик Черной горы и убейте вождя Ренда Чернорука. Принесите его голову в Оргриммар.', },
  },
  [4975] = {
    'Завершение шара Дар\'орахила',
    {'Я немедленно займусь твоим шаром. Надо вселить в него нового демона. После этого у меня будет выбор: превратить его в сферу или в посох. Я всегда считала, что соответствие избранному оружию чернокнижника – это главное... Не вижу причин менять свое мнение.', },
    {'Дождитесь, пока Менара Расщепительница Бездны закончит работу над шаром Дар\'орахила, и еще раз поговорите с ней.', },
  },
  [4976] = {
    'Возвращение к Менаре',
    {'Теперь, когда демон изгнан, ты можешь отнести шар Менаре в Кабестан, $N. Передай ей от меня привет, да и $Gсам:сама; не робей. Надеюсь, что тебе удастся противостоять искушениям скверны, которые, несомненно, будут подстерегать тебя на избранном пути.$B$BЖелаю удачи.', },
    {'Отнесите очищенную сферу инфернала Менаре Расщепительнице Бездны в Степи.', },
  },
  [4981] = {
    'Агент Блестяшка',
    {'Никогда нельзя верить гоблинам! Рано или поздно все приходят к этому выводу, $N. $B$BЕе зовут Блестяшка, она была одним из лучших наших агентов. Потом оказалось, что стерва вела двойную игру – работала на какого-то типа по имени Киблер, нелегально торгующего экзотическими животными в Пылающих степях, и использовала средства Орды для финансирования своих махинаций! $B$BОна должна быть доставлена ко мне живой. $B$BПоследний раз ее видели у Пика Черной горы. $B$BИ смотри, чтобы она не заговорила тебе зубы!', },
    {'Отправьтесь к пику Черной горы и выясните, что сталось с Блестяшкой.', },
  },
  [4982] = {
    'Вещи Блестяшки',
    {'По ночам, когда городская стража не особенно ревностно исполняет свои обязанности, я незаметно брожу по улицам и беру на заметку все, что может иметь хоть какую-то ценность для стратегических планов К.Э.К. Все подмечаю и все записываю.$B$BНо вчера ночью случился прокол. В темноте я запнулась о натянутую веревку, и включился сигнал тревоги. Пришлось срочно прятать все оборудование для разведки и мчаться в укрытие. И что самое ужасное, сейчас мне уже не удастся забрать свои вещи из тайника. В городе ввели усиленный режим патрулирования! Вся информация утрачена.$B$BРазыщи мои вещи, верни их мне, и я тихо-мирно вернусь в К.Э.К.', },
    {'Найдите вещи Блестяшки и верните их владелице. Блестяшка сказала, что она спрятала свое оборудование где-то на нижнем ярусе подземелья.', },
  },
  [4983] = {
    'Донесение Блестяшки',
    {'Передай этот рапорт Лекслорту. Теперь я верну себе доброе имя, потому что мое донесение, безусловно, сослужит хорошую службу К.Э.К. и даст им значительное стратегическое преимущество в борьбе с этими орками.$B$BПойти с тобой, говоришь? У меня много работы, недосуг мне носиться туда-сюда. Беги, донесение надо доставить как можно скорее!', },
    {'Передайте донесение Блестяшки гроссмейстеру Лекслорту в Каргат.', },
  },
  [4984] = {
    'Страдания природы',
    {'Хотя бороться с Плетью в одиночку бесполезно, я все равно прибыл в эти земли, чтобы лечить израненную природу. Чума поражает зверей и травы, не ведая пощады. Прошу, продолжи мое дело.$B$BНесчастных животных не спасти, остается прекратить их страдания. Ступай, отыщи больных волков и прикончи их. Прошу тебя, $N, избавь их от мучений – и мне тоже станет полегче.', },
    {'Убейте 8 больных волков и вернитесь к Мулгрису Глубокой Реке в Удел Страданий в Западных Чумных землях.', },
  },
  [4985] = {
    'Страдания природы',
    {'На востоке отсюда, рядом с границей Восточных Чумных земель, некогда жили гордые и могучие гризли.Чума затронула и их. Теперь они бродят по округе, страдая от застывающих мышц и гниющей плоти и меха.$B$BПрошу тебя, избавь их от страданий. Единственное исцеление для них – быстрая смерть. Ты можешь даровать ее.', },
    {'Убейте 8 болезненных гризли и вернитесь к Мулгрису Глубокой Реке в Удел Страданий в Западных Чумных землях.', },
  },
  [4986] = {
    'Покрытая письменами дубовая ветвь',
    {'В безжизненной руке друида-таурена – удивительно живая дубовая ветвь. Внимательно осмотрев ее, вы видите вырезанные на ней друидические символы.$B$BСхватившись за ветвь, вы замечаете, что она слишком теплая для простой ветви. У вас в подсознании возникает образ древа Тельдрассила, точнее, самого Дарнаса. Более того, в голове так и вертятся слова "Круг Кенария". Кажется, в ветку вложили знания, куда ее лучше отнести.', },
    {'Судя по магии, вложенной в покрытую письменами дубовую ветвь, друид-таурен желал отнести ее в Круг Кенария в Дарнасе. Попросите о помощи одного из тамошних друидов.', },
  },
  [4987] = {
    'Покрытая письменами дубовая ветвь',
    {'В безжизненной руке друида-таурена – удивительно живая дубовая ветвь. Внимательно осмотрев ее, вы видите вырезанные на ней друидические символы.$B$BСхватившись за ветвь, вы замечаете, что она слишком теплая для простой ветви. У вас в подсознании возникает образ Громового Утеса в Мулгоре, точнее, Вершины Старейшин. Более того, в голове так и вертятся слова "Круг Кенария". Кажется, в ветку вложили знания, куда ее лучше отнести.', },
    {'Судя по магии, записанной в покрытую письменами дубовую ветвь, друид-таурен желал отнести ее в Круг Кенария в Громовом Утесе. Попросите о помощи одного из тамошних друидов.', },
  },
  [5001] = {
    'Вещи Блестяшки',
    {'Обычно я стараюсь не связываться с такими, как ты, |3-6($R), но, похоже, сейчас я застряла между троллем и горным завалом. Видишь ли, я выполняю важное задание для Орды. Однако та информация, которую мне удалось раздобыть, касается также и благополучия Альянса. Поверь мне, я не питаю симпатий к Альянсу, но угроза Черной горы – это гораздо серьезнее, чем какая-то там толпа грязных орков.$B$BПомоги мне найти мои вещи, а я передам тебе сведения, которые могут оказаться полезными для твоих сородичей.$B$BНу что, по рукам?', },
    {'Найдите вещи Блестяшки и верните их владелице. Удачи!', },
  },
  [5002] = {
    'Донесение Максвелла',
    {'Я – честная гоблинша и хозяйка своего слова, |3-6($R). А теперь слушай внимательно...$B$BЭтих орков используют в основном в качестве вьючных животных или пушечного мяса для черных драконов!$B$BИх предводитель не кто иной, как Нефариан, сын Смертокрыла. Также мне удалось узнать, где именно в крепости засели приспешники Нефариана.$B$BОтнеси эту карту маршалу Максвеллу в Пылающие степи. Я там отметила все места, где найти этих извергов.', },
    {'Передайте маршалу Максвеллу информацию Блестяшки.', },
  },
  [5021] = {
    'Лучше поздно, чем никогда',
    {'"Доставьте посылку... Доставьте ее, пока я не очутилась в ловушке!"$B$BПо всему дому разносятся причитания напуганной женщины. Привидение скорчилось в углу на втором этаже. Кажется, она не понимает, что вокруг, да и вас едва замечает.$B$B"Ты хочешь доставить мою посылку? Прошу тебя, сделай это, пока хуже не стало. Я собиралась уехать отсюда. Должно быть, там все еще стоит мой конь... или я опоздала?"', },
    {'Судя по сбивчивым объяснениям призрачной женщины, она хочет доставить посылку. Бедняга уверяет, что посылка лежала там, где стоял ее конь. Где именно это было, женщина не сказала.', },
  },
  [5022] = {
    'Лучше поздно, чем никогда',
    {'Вы берете посылку, стирая с нее слой пыли и грязи. Она адресована Эмме Фелстоун из Штормграда; посылка должна была быть доставлена четыре года назад.$B$BХотя вы не уверены, что Эмма Фелстоун жива, всем известно, что в Штормграде существует реестр жителей города. Справки можно наводить у королевских представителей Переписи Штормграда.', },
    {'Выясните у королевских представителей Переписи Штормграда местонахождение Эммы Фелстоун. Офицера-переписчика можно найти в Ратуше.', },
  },
  [5023] = {
    'Лучше поздно, чем никогда',
    {'Вы берете посылку, стирая с нее слой пыли и грязи. Она адресована Иеремии Фелстоуну из Лордерона; посылка была подготовлена к отправке четыре года назад.$B$BХотя вы не уверены, что Иеремия Фелстоун жив, всем известно, что в Подгороде, прежде именовавшемся Лордероном, существует реестр жителей города. Справки можно наводить у королевских инспекторов Переписи Подгорода.', },
    {'Поговорите с королевскими инспекторами Подгорода и наведите справки о Иеремии Фелстоуне. Офицера-переписчика можно найти рядом с пунктом регистрации гильдий и гербовых накидок.', },
  },
  [5041] = {
    'Снабжение Перекрестка',
    {'Нападение на караваны лишает нас необходимых припасов.$B$BТебе хватило храбрости выступить против племени Иглогривых, и я вновь попрошу тебя о том же, но на сей раз посматривай, нет ли где украденных у нас припасов? Может быть, они в лагерях этих тварей, и иглогривы жиреют на чужом добре?$B$BНарод Перекрестка будет у тебя в неоплатном долгу.', },
    {'Найдите и верните Торку из Степей ящики с припасами из Перекрестка.', },
  },
