  [5067] = {
    'Поножи Тайны',
    {'В то время как изготовление такого предмета еще никому не удавалось, считается, что это все же возможно – при удачном стечении обстоятельств.$B$BЕсли искатель тайных знаний желает наделить поножи могучей силой, вот что должно быть найдено:$B$B*Безупречная звериная шкура.$B$B*Истлевшая шовная нить поганища.$B$B*Арканитовые Слитки.$B$B*Бальзамировочный состав Ледяного Шепота.$B$BИ УЗРИШЬ ТЫ ЧУДО!', },
    {'Для изготовления поножей Тайны вам нужно принести Малифою Черномолоту следующее: 1 безупречную звериную шкуру; 5 истлевших шовных нитей поганища, 5 арканитовых слитков, 5 порций бальзамировочного состава Ледяного Шепота; $B$BВам придется также заплатить 50 золотых монет за изготовление этого предмета.', },
  },
  [5068] = {
    'Кираса кровавой жажды',
    {'Говорят, что кираса кровавой жажды была создана лишь один раз, и изготовлена она была для легендарной убийцы Гароны. Суждено ли еще одному подобному доспеху увидеть свет? Сомнительно...$B$BИ все же! Наши мастера могут воспроизвести этот доспех, если дать им необходимые компоненты:$B$B*Безупречную звериную шкуру.$B$B*Истлевшая шовная нить поганища.$B$B*Арканитовые Слитки.$B$B*Шкуру тени.$B$BИ УЗРИШЬ ТЫ ЧУДО!', },
    {'Для изготовления кирасы кровавой жажды вам нужно будет принести Малифою Чернобойцу следующее: 1 безупречную звериную шкуру; 10 истлевших шовных нитей поганища; 5 арканитовых слитков; 5 шкур тени.$B$BВы также должны будете уплатить 50 золотых монет за изготовление доспеха.', },
  },
  [5081] = {
    'Миссия Максвелла',
    {'Эти сведения заполняют последние пробелы в наших данных о пике Черной Горы.$B$BМы должны напасть со всей решительностью, $N.$B$BПервым делом нужно убить их воеводу, Вуна. Без его боевых планов орки Черной Горы будут ввергнуты в хаос.$B$BСледом должен умереть вождь огров, Омокк. Трусливые огры Черной Горы окажутся беспомощны.$B$BИ, наконец, повелитель всей цитадели зла: Змейталак. Когда он умрет, командование будет обезглавлено.', },
