  [5082] = {
    'Угроза со стороны Зимней Спячки',
    {'Сюда в Зимние Ключи я пришла, чтобы изучать Источники ледяного огня. Я заметила, что у воды здесь какие-то… странные свойства. Мне пока не удалось определить источник этих свойств, да и, по правде говоря, в последнее время у меня возникли кое-какие сложности, из-за фурболгов племени Зимней Спячки.$B$BИх, кажется, тянет к источникам – так же, как и меня. Они яростно защищают воду и не подпускают меня близко к двум большим ключам к северу отсюда.$B$BЯ хотела бы продолжить свои изыскания. Ты не поможешь мне?', },
    {'По просьбе Доновы Снегоноры из Зимних Ключей убейте 8 землепроходцев, 8 дозорных и 8 тотемистов из племени Зимней Спячки.', },
  },
  [5083] = {
    'Огненная вода Зимней Спячки',
    {'Тот сосуд, что вы нашли у фурболга племени Зимней Спячки, пуст, но внутри осталось несколько капель странной зеленой жидкости. Он почти сияет.$B$BОбнаружили ли вы нечто важное?$B$BВозможно, Донова Снегонора могла бы что-то вам разъяснить, поскольку она уже довольно давно живет у горячих ключей.', },
    {'Принесите Донове Снегоноре пустую флягу из-под огненной воды.', },
  },
  [5084] = {
    'Поддавшиеся порче',
    {'Зеленая жидкость… немного напоминает мне жижу, которую я видела, когда пробиралась через Оскверненный лес. И я видела несколько гонцов племени Зимней Спячки, входящих во владения Древобрюхов… Возможно, это имеет отношение к той склянке, что вы нашли, $N.$B$BСразу за Крепостью Древобрюхов, в Оскверненном лесу, живет большая группа фурболгов. Они зовутся племенем Мертвого Леса, и давно попали под власть скверны. Да, фурболги этого племени совершенно безумны. Может, сходишь и поищешь свидетельства того, что между племенами Зимней Спячки и Мертвого Леса есть какое-то сообщение?', },
    {'Исследуйте поселение фурболгов племени Мертвого Леса в северной части Оскверненного леса в поисках ключа к разгадке.', },
  },
  [5085] = {
    'Таинственная слизь',
    {'Зеленая субстанция внутри котла кажется точно такой же, какую вы обнаружили в сосуде. Это странное слизистое вещество может оказаться таким же полным скверны, как и фурболги, что его изготовили…$B$BЕсли фурболги племени Зимней Спячки его используют, значит ли это, что вскоре они окажутся в таком же состоянии, как и племя Мертвого леса?$B$BЛучше всего сообщить об этих находках Донове, прихватив и образец жидкости из котла.', },
    {'Вернитесь к Донове и сообщите ей о своих находках.', },
  },
  [5086] = {
    'Ядовитый Ужас',
    {'Я изучила образцы, с которыми вы вернулись, и хотя точно пока ничего сказать не могу, я думаю, что в них есть следы ядовитых компонентов. Однако единственный способ узнать наверняка – это найти что-то для сравнения.$B$BЯ думаю, что фурболги Мертвого Леса могут собирать эти компоненты с других зараженных скверной существ в Оскверненном лесу: в частности, с элементалей, населяющих Железнолесье, которых называют ядовитым ужасом.$B$BСобери для меня несколько образцов для сравнения, но будь $Gосторожен:осторожна; – там очень опасно!', },
