  [5126] = {
    'История Лоракса',
    {'Сатир расхаживает туда-сюда и разговаривает сам с собой. Решитесь ли вы задать ему вопрос?', },
    {'Поговорите с Лораксом. Выслушайте то, что он вам расскажет.', },
  },
  [5127] = {
    'Демонова кузня',
    {'Лоракс заключает сделки, да, но он не имеет дел с никчемными смертными.$B$BНу ладно, с тобой я сделку заключу... возьми эту пику, она усилена мощной энергией Скверны. Когда найдешь Горалука Треснувшую Наковальню, убей его и проткни этой пикой его остывающее сердце. Он продал свою душу, и она будет моей. Верни мне пику, а также кирасу, которую похитил этот вор, и я научу тебя тому, чему хотел научиться он.', },
    {'Отправляйтесь на пик Черной горы и найдите Горалука Треснувшую Наковальню. Убейте его, а потом воткните в труп окровавленную пику. Таким образом пика вытянет его душу и окрасится ей.$B$BКроме того, вам нужно найти заготовку рунической кирасы.$B$BОтнесите пику и кирасу Лораксу в Зимних Ключах.', },
  },
  [5128] = {
    'Слова великого вождя',
    {'Племя Зимней Спячки больше не представляет для меня угрозы, но если тебе по-прежнему хочется выяснить, что побудило фурболгов готовить и использовать эту странную огненную воду, думаю, вам стоит поискать Келека Стража Неба. $B$BОн принадлежит к группе, известной как Изумрудный Круг – это группа друидов, посвятивших себя работе над исцелением Оскверненного леса от скверны. $B$BКелек много работал с фурболгами – может быть, он сможет больше меня рассказать об этих записях. $B$BИ еще раз спасибо тебе, $N.', },
    {'Отнесите неразборчиво исписанный журнал Келеку Стражу Неба в южную часть Оскверненного леса.', },
  },
  [5141] = {
    'Кожевничество: чешуя дракона',
    {'Кожевничество школы Дракона – непростое ремесло, однако весьма прибыльное. Я научу тебя изготовлять отличные кольчужные доспехи, но при одном условии. $B$BВыбрав этот путь, ты откажешься от изучения школ традиций предков и силы стихий. На пути к вершине мастерства ты $Gдолжен:должна; выбрать лишь одну из трех. Кроме того, я должен взглянуть на лучшие твои работы – в том числе на доспехи скорпида. И наконец, докажи мне, что ты $Gспособен:способна; добыть основу нашего ремесла – чешую дракона.', },
    {'Принесите 2 жесткие кирасы скорпида, 2 пары жестких перчаток скорпида и 10 потертых чешуек дракона Питеру Галену в Азшару. $B$BПо выполнении этого задания вы получите возможность обучения кожевничеству школы Дракона. $B$BЕсли вы выполните это задание, вы не сможете изучать кожевничество школы традиций предков и школы силы стихий.', },
  },
  [5142] = {
    'Малышка Памела',
    {'Многие погибли во время битвы при Дарроушире... и многие, избежавшие смерти, позавидовали мертвым.$B$BКогда битва началась, я спрятала мою племянницу Памелу и теперь не знаю, жива ли она. Я всего лишь странствующий дух, но мое сердце жаждет знать, все ли у нее хорошо.$B$BПожалуйста, $Gдобрый:добрая:c; |3-6($C), не поищешь ли ты мою племянницу в Дарроушире? Это к востоку отсюда, за пустошью Гаррона.', },
    {'Найдите Памелу Редпат в Дарроушире.', },
