  [5151] = {
    'Механизм гипервместимости',
    {'На наш караван напали кентавры, и выжить удалось только мне! А самый лучший мой механизм висел на шее могучей пантеры-охранницы! Даже кентавры дрожали, увидев эту зверюгу!$B$BВот ключ от ее клетки. Они забрали все, а ключ оставили. Убей пантеру, принеси мне мой гипервместитель, а я хорошо заплачу за него.$B$BТо, что осталось от нашего каравана, находится к северу отсюда.', },
    {'Убейте зверя и принесите механизм гипервместимости и ключ от клетки пантеры Визло Фингалету.', },
  },
  [5152] = {
    'Тетушка Марлен',
    {'О, я так одинока! Где моя семья? Тетушка Марлен велела мне ждать здесь, ушла и не вернулась. А ты случайно не $Gвидел:видела; моего папу?$B$BМожет, тетушка Марлен знает, где он. Она живет рядом с Андоралом, у нее домик на самом юге. Вроде бы он на холме... да, она говорила, что на холме.$B$BПожалуйста, $N! Мы же с тобой теперь друзья? Поговори с моей тетушкой!', },
    {'Поговорите с Марлен в южной части руин Андорала.', },
  },
  [5153] = {
    'Странный историк',
    {'Увы, Джозефу не дано воссоединиться с дочерью. Его душа была искажена Плетью, он сам стал чудовищем. Как жестока судьба!$B$BИ все же судьбу можно изменить.$B$BОтправляйся к его монументу и обыщи окрестные могилы. Тела самого Джозефа там нет, однако где-то под монументом лежит его обручальное кольцо. Отнеси кольцо Хроми. Это странная гномесса... с весьма странными способностями.$B$BХроми живет в развалинах таверны в северо-западной части Андорала.', },
    {'Принесите Хроми обручальное кольцо Джозефа.', },
  },
  [5154] = {
    'Анналы Дарроушира',
    {'Чтобы спасти Джозефа Редпата, придется обратиться к его прошлому. Надо прочитать, что говорится о нем в Анналах Дарроушира – книге, хранящейся в ратуше Андорала.$B$BПринеси мне книгу. Мы узнаем о судьбе Джозефа и, если повезет сможем... изменить ее.', },
    {'Принесите Анналы Дарроушира Хроми в Андорал.', },
  },
  [5155] = {
    'Силы Джеденара',
    {'Если уж мне придется положиться на тебя в этом деле, то прежде заслужи доверие.$B$BЯ должна точно знать, что ты – враг Совета Теней, тех, кто в ответе за великие злодеяния здесь и за границами леса.$B$BОтправляйся в чащу Оскверненного леса. На западе ты увидишь тропинку в Джеденар. Именно там Совет строит свои козни вместе с Пылающим Легионом.$B$BПроникни в недра одной из населенных ими оскверненных Обителей и перебей их. Потом вернись ко мне. Тогда я увижу, что ты искренне нам помогаешь.', },
    {'Отправляйтесь в Джеденар и убейте 4 джеденарских гончих, 4 джеденарских стражей, 6 джеденарских адептов и 6 джеденарских сектантов, затем возвращайтесь к Грете Замшелое Копыто в Оскверненный лес.', },
  },
  [5156] = {
    'Проверка на порчу',
    {'На севере отсюда, если идти по восточной дороге, лежит так называемая долина Рваных Ран. Это то место, где во время последнией великой войны призывали инферналов. Там до сих пор поганят землю кратеры – да и сами големы.$B$BЯ хочу узнать две вещи. Во-первых, шляются ли там до сих пор эти энтропийные духи? И второе – до сих ли пор заполнены эти кратеры поганой вонючей водой, что скапливалась в них долгие годы? Поможешь ли ты мне это узнать, $N? Но будь $Gосторожен:осторожна; – инферналы уничтожают все на своем пути.', },
    {'Найдите и уничтожьте 2 энтропических зверей и 2 энтропических ужасов при обследовании долины Рваных Ран. После проверьте, по-прежнему ли заполнены кратеры оскверненной водой. Затем возвращайтесь к Таруну Красному Перу в Оскверненный лес.', },
  },
  [5157] = {
    'Собрать оскверненную воду',
    {'Глубоко в недрах Джеденара лежит Оплот Теней – главный оплот скверны и зла.$B$BСовет Теней окружил свое логово защитными заклятьями, заключенными в жаровнях. Если уж мне выпала задача узнать побольше о нашем враге путем магического наблюдения, то тогда мне надо будет, чтобы кто-то потушил эти жаровни. Тогда я смогу тайно понаблюдать за Советом и выяснить, кто его возглавляет.$B$BЕсли сможешь – возьми эту флягу и наполни ее водой из Лунного колодца в центре Джеденара. Потом вернись ко мне.', },
    {'Возьмите пустую флягу и наполните ее водой из Лунного колодца, что находится в центре Джеденара. Возвращайтесь к Грете Замшелому Копыту в Оскверненный лес.', },
  },
  [5158] = {
    'В поисках духовной помощи',
    {'Отнеси эту оскверненную воду в Степи, $N. К югу от Кабестана на побережье скрытно живет моя старая приятельница, шаманка по имени Ислена Заклинательница Воды. Она может нам помочь. Ее узы с духами воды всегда были крепки. Веди себя благородно – и этого будет достаточно, чтобы убедить ее и духов воды помочь нам в нашем деле.$B$BИщи ее в маленьком лагере прямо у кромки воды. А на пиратов, $N, внимания не обращай... это все мелочи жизни, тебе они не угроза.', },
    {'Отнесите порченую воду из Лунного колодца Ислен Заклинателю Воды в Степи.', },
  },
  [5159] = {
    'Чистая вода возвращается в Оскверненный лес',
    {'Благодари духов, $N. Это они, а не я, помогли тебе.$B$BНыне я отдаю тебе эту очищенную воду. Передай Грете мой поклон и мои наилучшие пожелания. Она нащупала нечто более важное, чем сумели многие из нас... я уверена, что она вскоре все узнает.$B$BС Советом Теней шутки плохи, $N. Запомни это.$B$BТеперь ступай. Бери воду и возвращайся к Грете.', },
    {'Отнесите очищенную воду из Лунного колодца Грете Замшелому Копыту в Оскверненный лес.', },
  },
  [5160] = {
    'Матрона-защитница',
    {'Тебе придется отправиться в Калимдор, |3-6($R). Наша стая сейчас поселилась на холодных просторах Зимних Ключей. Когда доберешься туда, отправляйся в пещеры Мазторил.$B$BНа ледяном полу в пещерах ты найдешь письмена из кобальтовых рун. Сожми в руке пластинку моей чешуи, встань на руну – и перенесешься к Халех, нашей матроне-защитнице.$B$BОтдай ей пластинку моей чешуи. Халех посмотрит в нее, как в магический кристалл, и увидит все, что я видел и чувствовал в этом проклятом месте.', },
    {'Дойдите до Зимних Ключей и найдите Халех. Отдайте ей пластину чешуи Ауби.', },
  },
  [5161] = {
    'Гнев синих драконов',
    {'Мы понимаем, что пытается сделать проклятое порождение Смертокрыла, и мы знаем, что этого нельзя допустить.$B$BНо из-за Черной горы, которая защищает Нефариана, вмешательство стало для нас невероятно трудным.$B$BИ все же мы можем дать силу тебе, смертному существу, если ты решишься на это.$B$BЛежащий перед тобой путь полон опасностей. Примешь ли ты нашу помощь, решать тебе. Обдумай это и поговорим еще раз, когда ты примешь решение', },
    {'Если у вас есть желание продолжить, поговорите снова с Халех.', },
  },
  [5162] = {
    'Гнев синих драконов',
    {'Тайные силы контролирую я, и ради тайных сил пошлю я тебя к твоему следующему деянию.$B$BКогда будешь $Gготов:готова;, начнется твое путешествие. В Чумные земли лежит твой путь, и с Джезибой надлежит тебе говорить.$B$BЭто смертный, мудрый, как древние предки, и терпеливый, как сама недвижная земля. Ты найдешь его в Андорале.', },
    {'Поговорите с Джезибой в Чумных землях. Он обитает в Андорале.', },
  },
  [5163] = {
    'Йети где-то рядом…',
    {'Все готово, $N!$B$BИтак, я уже сообщила по почте моим друзьям, что у меня для них сюрприз. И тут на сцену выходишь ты!$B$BЯ дам тебе моего механического йети, а тебе нужно будет удивить их, нанеся визит каждому, включив йети и послав его по направлению к ним.$B$BКогда покажешь мое изделие Легакки в Круговзоре, Поливалке в Прибамбасске и Квикксилю в Укрытии Маршалла, возвращайся и расскажи мне, как все было!$B$BИ моего йети тоже, пожалуйста, приведи обратно.', },
    {'Возьмите механического йети Уми и напугайте им ее друзей:$B$B- Легакки в Круговзоре (Зимние Ключи), $B- Поливалку в Прибамбасске (Танарис), $B- Квикксиля в Укрытии Маршалла (Кратер Ун\'Горо)$B$BКогда проделаете все это, принесите механического йети обратно Уми.', },
  },
  [5164] = {
    'Перечень Заблудших',
    {'Халех не могла отыскать лучшего орудия справедливости. Прочти перечень Заблудших и обрети просветление.$B$BЕсли ты выстоишь... Если ты сделаешь то, о чем тебя просят и выживешь... Из тебя выйдет герой.', },
    {'Прочитайте перечень Заблудших.', },
  },
  [5165] = {
    'Погасить Пламя Защиты',
    {'Теперь тебе надо вернуться в Джеденар. Эти жаровни скорее всего расставлены в Оплоте Теней. Загаси их пламя, и защита, возведенная Советом, хотя бы на некоторое время падет. Я буду ждать этого мгновения. Когда оно настанет, я загляну в недра оплота и попытаюсь узнать, кто стоит во главе всего этого. Тогда – и только тогда – мы попытаемся атаковать их напрямую.', },
    {'При помощи очищенной воды Лунного колодца загасите пламя в четырех жаровнях защиты внутри Оплота Теней в Оскверненном лесу. Затем возвращайтесь к Грете Замшелому Копыту в Изумрудное святилище.', },
  },
  [5166] = {
    'Кираса разноцветного полета',
    {'По иронии судьбы, на изготовление мощной кирасы, призванной защитить от нападения всецветных драконов, идут их собственные шкуры.$B$BНа безупречную шкуру всецветного дракона нашивают их же чешую. В настоящий момент этих тварей не существует, и хочется верить, что и не будет.$B$BПоследний компонент, который труднее всего достать – это кровь героев.$B$BИ наконец, из этих компонентов Скульптор создает подлинный шедевр.', },
    {'Джезиба "Скульптор" может сковать кирасу Всецветных драконов, если вы принесете ему следующее:$B$B1 всецветный панцирь.$B$B10 сверкающих всецветных чешуек.$B$B10 порций крови героев.$B$B10 истлевших шовных нитей поганища.', },
  },
  [5167] = {
    'Ножные латы всецветного воина',
    {'На изготовление этих поножей идут взрослых всецветных драконов. Переливчатую чешую закрепляют на проволочной основе и зачаровывают, окропляя кровью героев. В конце следует обтянуть поножи изнутри шкурой тени.', },
    {'Джезиба \'Скульптор\' может сковать ножные латы Всецветного воина, если вы принесете ему следующее:$B$B1 всецветный панцирь.$B$B10 сверкающих всецветных чешуек.$B$B10 порций крови героев.$B$B5 шкур тени.', },
  },
  [5168] = {
    'Герои Дарроушира',
    {'Герои Дарроушира навсегда поселились в моем сердце, а эта книга, Анналы Дарроушира, рассказывает об их жизни.$B$BЗдесь рассказывается о паладине Дейвиле Яркосвете и о том, как Алый Марш забрал его манускрипт в ратушу Дольного Очага.$B$BЗдесь также говорится о моем брате, капитане Редпате. После его предательства, его щит раскололся и развалился на куски, и теперь лежит в самом неожиданном месте – рядом с амбаром в пустоши Гаррона.$B$BСобери эти реликвии, $N.', },
    {'Принесите манускрипт Дейвила и щит Редпата Карлину Редпату.', },
  },
  [5181] = {
    'Негодяи Дарроушира',
    {'В Анналах Дарроушира упоминается о самых мерзких воинах, бившихся в том сражении. Там рассказывается о том, как Альянс разбил силы лорда Хоргуса Опустошителя. Тело его было брошено в озеро к северо-западу от Перекрестка Корина, и череп его поныне там. $B$BМардук Черный так и не был побежден, но его легендарный меч был сломан и потерян. Он лежит на дне ущелья к западу от Перекрестка Корина.$B$BПринеси мне эти предметы, $N. Не знаю почему, но твоя судьба связана с ними.', },
    {'Принести череп Хоргуса и сломанный меч Мардука Карлину Редпату в Часовню Последней Надежды.', },
  },
  [5201] = {
    'Вторжение племени Зимней Спячки',
    {'Мое присутствие здесь мало кто замечает, $N. Но существует одна неприятность, укроненившаяся в Зимних Ключах: это фурболги племени Зимней Спячки.$B$BХотя лично я никогда не атаковал фурболгов, они выглядят безумными и боятся всего, что им встречается на пути. Я видел, как они убивали бесчисленное количество существ, чья вина состояла лишь в том, что они оказались не в то время и не в том месте.$B$BМне все это уже поперек горла – пожалуйста, помогите мне нанести им ответный удар. Убивайте фурболгов и не подпускайте их к Уступу Ледопардов.', },
    {'По заданию Риверна Холодного Ветра убейте 5 шаманов и 5 ведмедей из клана Зимней Спячки.', },
  },
  [5202] = {
    'Странный красный ключ',
    {'Маленький красный ключ сделан из какого-то материала, похожего на кость. Он тяжел, но на вид кажется хрупким. Зачем прислужнику Совета Теней этот предмет? Возможно, этот странный ключ открывает какой-то замок в Джеденаре?', },
    {'Обыщите Джеденар и найдите замок, который открывается странным красным ключом.', },
  },
  [5203] = {
    'Спасение из Джеденара',
    {'Но, как бы мне это ни было тяжко, в первую очередь мне нужно сделать вот что: найти меч Трея, именуемый Светлая Сталь и сбежать из Оплота Теней, чтобы рассказать моим командирам, что здесь творится. Ты поможешь мне, $N?$B$BНаше снаряжение тут неподалеку, а ты уже $Gпоказал:показала;, что сумеешь выбраться наверх.$B$BКогда мы выберемся отсюда, я направлюсь в Дарнас, если ты сможешь найти мою подругу Джессир на границе с Ясеневым лесом. Она должна была встретить нас у лагеря Изумрудного Круга неподалеку отсюда.$B$BЧто скажешь, $N? Поможешь мне еще раз?', },
    {'Сопроводите Арко\'нарин туда, где лежат меч Светлая Сталь и снаряжение Арко\'нарин, и защитите ее при побеге из Оплота Теней. Потом разыщите Джессиру Лунную Радугу в Оскверненном лесу и расскажите ей, что случилось с ее друзьями.', },
  },
  [5204] = {
    'Возмездие Света',
    {'Она делает вид, что с ней все в порядке. Ей хочется, чтобы я думала, будто она сама сумеет справиться с горем, но я же не слепая. Смерть Трея и его крики под пытками, которым его подвергали, долго мучили ее, и мы ничем не могли ей помочь. Но мы все же можем дать ей хоть немного покоя!$B$B$N, вернись в Оплот Теней, убей мучителя Трея и найди его останки. Почему бы нам самим не покончить с этим, пока Арконарин оплакивает своего друга?', },
    {'Вернитесь в Джеденар и убейте суккуба Ракайю, затем отыщите останки Трея Светогорна.', },
  },
  [5206] = {
    'Мародеры Дарроушира',
    {'Хроми утверждает, что собранные тобой реликвии могут служить реагентами для мощных чар. Однако требуется еще один ингредиент: черепа воинов Плети, участвовавших в битве при Дарроушире.$B$BВозьми этот кристалл и отправляйся за черепами лучших воителей Плети. Кристалл заколдован: он резонирует, если поднести его к черепу воителя, участвовавшего в битве.$B$BИди, $N. Ты найдешь воителей Плети на Ядовитой поляне к северу отсюда и в Грибной долине на западе.', },
    {'Принесите 5 резонирующих черепов и загадочный кристалл Карлину Редпату в Восточные Чумные земли.', },
  },
  [5209] = {
    '<UNUSED>',
    {'<UNUSED>', },
    nil,
  },
  [5210] = {
    'Брат Карлин',
    {'С помощью магии я дополнила Анналы Дарроушира – теперь они содержат сведения и о том, что случилось после битвы. И там говорится, что еще один из рода Редпатов пережил предательство Джозефа. Его имя Карлин, он сейчас в восточных пределах Чумных земель, в Часовне Последней Надежды.$B$BОтправляйся к нему и передай ему книгу.', },
    {'Отнесите дополненные Анналы Дарроушира Карлину Редпату в Восточные Чумные земли.', },
  },
  [5211] = {
    'Защитники Дарроушира',
    {'Многие защищавшие Дарроушир пали в битве. И многие из них восстали из мертвых, примкнув к Плети! Ты $Gдолжен:должна; отыскать эти несчастные души и избавить их от страданий.$B$BКаннибалы-вурдалаки, бормощущие вурдалаки, болезненные живодеры – многие из них были добрыми жителями Дарроушира. Уничтожив их тела, ты освободишь их души.$B$BТы найдешь их в Проклятом лесу, Перекрестке Корина и прочих опорных пунктах Плети в Восточных Чумных землях.', },
    {'Освободите 15 духов Дарроушира и возвращайтесь к Карлину Редпату.', },
  },
  [5212] = {
    'Плоть не лжет',
    {'Аптекари Отрекшихся не единственные в этом мире, кто искушен в науках.$B$BЯ уже много лет изучаю эту заразу, |3-6($R). Ранее я полагала, что активность чумы со временем уменьшается, однако то, что наблюдается в Чумных землях, поражает воображение.$B$BЗдесь вирус со временем становится все более активным!$B$BЯ должна продолжать исследования! Вирус наиболее активен в первые дни после заражения, поэтому мне нужны образцы тканей, пораженных совсем недавно. Отправляйся в Стратхольм и снабди меня препаратами для исследований, $N.', },
    {'Принесите 10 препаратов чумной плоти из Стратхольма Бетине Биггльцинк. Предположительно, сойдут ткани любой твари из Стратхольма.', },
  },
  [5213] = {
    'Вирус чумы',
    {'Тебе предстоит углубиться еще дальше в кварталы Стратхольма, $N. B$BЗа первыми воротами ты увидишь зиккураты. Известно, что твари Плети черпают из этих сооружений великую силу. Обыщи зиккураты и расскажи мне обо всем, что там увидишь.', },
    {'Отправляйтесь в Стратхольм и исследуйте зиккураты. Доставьте сведения о Плети Бетине Биггльцинк.', },
  },
  [5214] = {
    'Великий Фрас Сиаби',
    {'То, что произошло с Фрасом, поистине ужасно! Город осаждала нежить, но Фрас не хотел бросать свою лавку превосходного табака. Последнее, что он сказал мне: "Я не уйду, пока не сожгу все до последнего листочка, чтобы ничего не осталось врагу!"$B$BЯ тоже знаю толк в хорошем табаке, но не готов расстаться с жизнью ради каких-то сушеных листьев, пусть и наивысшего качества. Хотя... может там и вправду что-то уцелело?$B$BЧто скажешь, $N? Не поищешь табачку для старины Дымка?', },
    {'Найдите табачную лавку Фраса Сиаби в Стратхольме, отыщите в ней пачку лучшего табака Сиаби и принесите ее Дымку ЛаРу.', },
  },
  [5215] = {
    'Котлы Плети',
    {'В пределах бывшего Лордерона спрятано восемь котлов – четыре здесь, на западе, и еще четыре – на востоке. Они испускают ядовитые пары, распространяя чуму нежити по всей земле на радость Плети.$B$BКел\'Тузад слишком силен, чтобы взять да и угасить пламя под этими котлами, но, быть может, мы сможем придумать способ использовать их для собственной выгоды. Моя помощница, верховная жрица Макдонелл, поможет тебе выполнить задание; она продолжит инструктаж.', },
    {'Поговорите с верховной жрицей Макдонелл в лагере Промозглого Ветра в Западных Чумных землях.', },
  },
  [5216] = {
    'Цель: поле Джанис',
    {'Из четырех котлов Западных Чумных земель проще всего подобраться к тому, что в поле Джанис. Его охраняют менее опытные воины Плети, хотя не стоит их недооценивать.$B$BОтправляйся на поле Джанис, что на востоке отсюда и на северо-западе от Андорала, и вступи в схватку с тамошним Хранителем котла. У него есть ключ, который даст доступ к котлу. Отперев панель доступа, набери в бутылку образец ядов из котла; они понадобятся нам, чтобы подобрать противодействующие вещества.', },
    {'Отправляйтесь на поле Джанис в Западные Чумные земли, найдите и одолейте Хранителя котла, взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка c поля Джанис.', },
  },
  [5217] = {
    'Возвращение в Лагерь Промозглого Ветра',
    {'Вы наполняете бутыль из котла ядовитой жижей. Вроде бы должно хватить.$B$BЗаполучив яд, пора возвращаться к верховной жрице Макдонелл в лагерь Промозглого Ветра.', },
    {'Принесите полную бутылку с поля Джанис верховной жрице Макдонелл в лагерь Промозглого Ветра, что в Западных Чумных землях.', },
  },
  [5218] = {
    'Котел поля Джанис',
    nil,
    nil,
  },
  [5219] = {
    'Цель: Слезы Далсона',
    {'Один из чумных котлов расположен на поле бывшей фермы, которую ныне зовут Слезы Далсона. Но сегодня не время лить слезы, |3-6($C). Мы должны нанести удар по котлу и получить к нему доступ!$B$BОтправляйся в Слезы Далсона, к северу от Андорала, и убей Хранителя котла. Забери у него ключ, как и в прошлый раз, и получи доступ к самому котлу. Наполни эту бутылку ядом, ведь в каждом из котлов бурлит свое варево.', },
    {'Отправляйтесь в Слезы Далсона в Западных Чумных землях, найдите и одолейте Хранителя котла, взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка c поля Слез Далсона.', },
  },
