  [5241] = {
    'Дядюшка Карлин',
    {'Ты знаешь, иногда по ночам мне слышится плач дядюшки Карлина – как будто бы плач доносится откуда-то издалека, с востока.$B$BА вдруг он плачет обо мне... $N, пожалуйста, разыщи моего дядюшку и скажи ему, что со мной все в порядке.$B$BСкажи, что я здесь, жду его и очень скучаю по сказкам, которые он рассказывал мне на ночь... особенно той, про кроликов и черничное варенье. Она была такая забавная!', },
    {'Найдите Карлина Редпата.', },
  },
  [5242] = {
    'Окончательный удар',
    {'Увы, я вижу только один путь покончить с угрозой Оскверненному лесу – прямую атаку. Других путей в Оплот Теней нет, телепортировать тебя внутрь я тоже не могу. Единственный путь – прямой путь в бездну... и я также знаю, кого тебе следует искать в этой бездне. Его зовут Скверн\'дан... Владыка теней Скверн\'дан. Его власть там, внизу, смертоносна... Он – голова змеи. Убей его и двух его суккубов-прислужниц, и вернись ко мне. Это Скверн\'дан заключил союз с Легионом. Его надо остановить!', },
    {'Убейте Моору и Салию и принесите голову Владыки теней Скверн\'дана Грете Замшелому Копыту в Оскверненный лес.', },
  },
  [5243] = {
    'Святая вода',
    {'Я склонен рассматривать свое бытие в форме нежити как род болезни, |3-6($R). Болезни, которая требует лечения. Впрочем, для Серебряного Рассвета это не болезнь, а истинное благословение. Действительно, нет лучше орудия против язычников Плети, чем нежить.$B$BКоллеги чтят меня за мои деяния, однако почести ничего не значат для меня.$B$BЕсли ты хочешь содействовать делу Серебряного Рассвета, у меня есть для тебя работа.$B$BОтправляйся в Стратхольм и пополни наши запасы святой воды. Искать ее следует в брошенных ящиках с припасами.', },
    {'Отправляйтесь на север, в Стратхольм. Обыщите брошенные ящики с припасами и соберите 5 мер святой воды Стратхольма. Возвращайтесь к Леониду Барталомею Чтимому, как только воды будет достаточно.', },
  },
  [5244] = {
    'Руины Кел\'Терила',
    {'История Кел\'Терила – это история проклятой расы, прикованной к месту, где был совершен неудавшийся ритуал…$B$BВысокорожденные, которых вы найдете там, когда-то похитили священный артефакт, будучи уверенными, что смогут вытянуть его мощь, перелить ее в себя и стать еще более могущественными. Из-за их действий реликвия взорвалась, а куски ее ушли под лед озера...$B$BЕсли хотите узнать больше, поговорите с Джероном Гранильщиком. Он был там недавно и сможет рассказать вам обо всем.', },
    {'Поговорите с Джероном Гранильщиком в деревне Звездопада.', },
  },
  [5245] = {
    'Беспокойные духи Кел\'Терила',
    {'Несчастные существа эти духи высокорожденных, что и говорить. Они все еще ищут части самой вещи, которая навсегда привязала их к Кел\'Терилу, эту похищенную у них реликвию.$B$BЕсли тебе это интересно, можешь взять мою кирку и отправляться на юг к озеру, чтобы попытаться достать части этого артефакта изо льда.$B$BЯ не могу сказать что именно ты найдешь, но Аврора Зов Небес наверняка сможет. Она живет около башни Северного Перевала в северной части Восточных Чумных земель.', },
    {'Используйте кирку Джерона, чтобы добыть фрагменты реликвии высокорожденных, Принесите их Авроре Зову Небес в Восточные Чумные земли.', },
  },
  [5246] = {
    'Фрагменты прошлого',
    {'Я не понимала… Простите мои жестокие слова, |3-6($C).$B$BЯ не хочу вспоминать об ошибках своих предков, но если ваша просьба искренна, я помогу.$B$B*Аврора изучает фрагменты.*$B$BЯ уверена, что это – Кристалл Зин-Малора, артефакт, содержащий в себе могучие силы тайной магии. Возможно, его использовали и во благо, но из-за злых намерений тех, кто его похитил, кристалл раскололся.$B$BОтправляйтесь в Храм Зин-Малор в Азшаре – я могу лишь надеяться, что книга все еще там.', },
    {'Найдите Священные письмена высокорожденных в Храме Зин-Малор, в Азшаре и принесите их Авроре Зову Небес к Башне Северного перевалa, в Восточных Чумных землях.', },
  },
  [5247] = {
    'Фрагменты прошлого',
    {'Я смогу помочь вам, если воссоздание артефакта действительно является вашим желанием.$B$BЧтобы воссоединить фрагменты, мне потребуются вещи, обладающие сильными магическими свойствами. Слитки зачарованного тория и пять кристаллов восстановления… Также вам нужно будет набрать воды из... необычного источника... Найдите Зловещий пруд на Пути Элдрета в Забытом Городе, что в Фераласе, и наполните этот фиал.$B$BПока вас не будет, я попытаюсь извлечь из этих страниц знание, как восполнить урон, нанесенный этой реликвии.', },
    {'Принесите зачарованный слиток тория, 5 кристаллов Восстановления и фиал пугающей воды Авроре Зову Небес к башне Северного Перевала, в Восточных Чумных землях.', },
  },
  [5248] = {
    'Измученный воспоминаниями о прошлом',
    {'$N, Я скорблю о том, что раса эльфов была разрушена, подобно этому кристаллу. Я не знаю, соберутся ли когда-нибудь эти осколки воедино, и все же то, что я могу помочь тебе, уменьшает мою скорбь.$B$BМы не должны забывать о прошлом, $N...$B$BПожалуйста верни кристалл Зин-Малора к озеру Кел\'Терил. Найди там духа высокорожденного, который, так же как и я, сожалеет о допущенных ошибках и скажи ему, что мы стремимся все исправить.', },
    {'Отнесите кристалл Зин-Малора к озеру Кел\'Терил в Зимних Ключах и найдите духа высокорожденного, который согласится с вами поговорить.', },
  },
  [5249] = {
    'В Зимние Ключи!',
    {'В Зимних Ключах, к северу отсюда, много древних эльфийских руин. Это суровая земля, но она хранит множество секретов, и там есть места, которые стоят изучения.$B$BРабота здесь отнимает все мое время, так что у меня давно уже не было возможности самой добраться до Зимних Ключей. Но вам-то ничто не мешает!$B$BКогда будете там, поищите Вунда Ночного Охотника. Большую часть времени он проводит в деревне Звездопада и очень много знает о тех местах.', },
    {'Найдите в Зимних Ключах деревню Звездопада и поговорите там с Вундом Ночным Охотником.', },
  },
  [5250] = {
    'Звездопад',
    {'В Зимних Ключах, к северу от Оскверненного леса, много древних эльфийских руин. Это суровая земля, но она хранит множество секретов, и там есть места, которые стоят изучения.$B$BЯ так занята своими исследованиями здесь, что у меня давно уже не было возможности самой добраться до Зимних ключей. Но у вас-то нет никаких причин для отказа от путешествия туда и изучения руин.$B$BКогда будете там, поищите Вунда Ночного Охотника. Большую часть времени он проводит в Звездопаде и очень много знает о том районе.$B$BСкажите ему, что вы $Gмой ученик:моя ученица;.', },
    {'Найдите в Зимних Ключах деревню Звездопада и поговорите там с Вундом Ночным Охотником.', },
  },
  [5251] = {
    'Архивариус',
    {'Впадать в крайности всегда опасно, $N. Взять, к примеру, Алый орден. Неужели такой слепой фанатизм способен привести к чему-то хорошему? Скольких невинных убили они во имя Света.$B$BЭто просто святотатство!$B$BВот что, один из этих негодяев мне особенно интересен – это архивариус Галфорд из Стратхольма. Этот человек отвечает за самые ценные документы ордена. Уничтожь его и сожги его архивы! Если справишься с этим, получишь достойную награду.', },
    {'Отправляйтесь в Стратхольм и отыщите архивариуса Галфорда из Алого ордена. Убейте его и сожгите архив.', },
  },
  [5252] = {
    'Вестница Печали',
    {'Кри… кристалл…$B$BЗдешние высокорожденные находятся в плену их жалкого сущессствования…$B$BЯ полон горрречи, но я понимаю, в чем моя ошибка. И хотя изменить прошлое нееевозможно, теперь я обрел покой, зная, что тебе удалосссь добыть кристалл Зин-Малора...$B$BТы $Gдолжен:должна; вернуть крисссталл...$B$BОн должен быть воззвращен.', },
    {'Поговорите с Вундом Ночным Охотником в деревне Звездопада и узнайте у него, куда нужно поместить кристалл Зин-Малора.', },
  },
  [5253] = {
    'Кристалл Зин-Малора',
    {'Впечатляюще, $N. $B$BКристалл Зин-Малора никогда не вернется в храм, лежащий в руинах. Но я полагаю, что твои усилия уничтожить зло минувших лет не должно пойти прахом.$B$BНайди верховного друида Фэндрала Олений Шлем в Дарнасе, $N, и отдай ему кристалл. Он должен находится в руках тех, кто не злоупотребит его мощью. К тому же только самые сильные маги смогут управиться с артефактом такой мощи.', },
    {'Доставьте Кристалл Зин-Малора верховному друиду Фэндралу Оленьему Шлему в Дарнас.', },
  },
  [5261] = {
    'Иган Меховщик',
    {'Иган Меховщик ищет охотника на зараженных волков. Это добрая новость, поскольку в последнее время в Долине Североземья появилось слишком много больных животных.$B$BЕсли тебе это интересно, то поговори с Иганом. Он за углом аббатства, если свернуть налево.', },
    {'Поговорите с Иганом Меховщиком.', },
  },
  [5262] = {
    'Ошеломляющая истина',
    {'В воздухе еще вихрится пыль, а вы пытаетесь собраться с мыслями и осознать произошедшее. Весь ужас вашего положения все еще не укладывается в голове.$B$BВерховный рыцарь оказался демоном! И не просто демоном, а братом самого Вариматаса, верховного правителя Подгорода.$B$BВы бережно укладываете голову повелителя ужаса в суму. Разумеется, никто не поверит рассказанному вами без вещественных доказательств.$B$BНадо сообщить герцогу Николасу Зверенхоффу как можно скорее!', },
    {'Отнесите голову Бальназара герцогу Николасу Зверенхоффу в Восточные Чумные земли.', },
  },
  [5263] = {
    'Быстрее, выше, сильнее',
    {'Ты $Gпроявил:проявила; прирожденную склонность к разрушению и умеешь следовать приказам. Эти похвальные качества весьма редко сочетаются. Полагаю, ты можешь стать совершенным орудием высшего промысла.$B$B*Зверенхофф сжимает кулаки*$B$BКел\'Тузад усадил своего мальчика на побегушках править в Стратхольме. Докажи мне свою верность и доблесть. Уничтожь барона Ривендера и принеси мне его голову.', },
    {'Отправляйтесь в Стратхольм и убейте барона Ривендера. Принесите его голову герцогу Николасу Зверенхоффу.', },
  },
  [5264] = {
    'Лорд Максвелл Тиросс',
    {'Тебе оказана большая честь, $N.$B$BЛорд Максвелл хочет наградить тебя за твои заслуги перед Серебряным Рассветом!$B$BПоверь мне, это большая честь – Максвелл уже много лет не удостаивал никого аудиенции.', },
    {'Поговорите с лордом Максвеллом Тироссом. Вы найдете его внутри храма.', },
  },
  [5265] = {
    'Серебряный Оплот',
    {'Серебряный Оплот уже много лет закрыт и запечатан. Много лет не находилось достойных войти в него и прикоснуться к силе Серебряного Рассвета – до сегодняшнего дня.$B$BСеребряный Оплот распахнул врата перед тобой, $N.', },
    {'Откройте Серебряный Оплот и получите награду.', },
  },
  [5281] = {
    'Мятущиеся души',
    {'Многие погибли во время битвы при Стратхольме. Многие предпочли расстаться с жизнью, бросившись в полымя пожара, чем повергнуться порче и примкнуть к Плети.$B$BТеперь они скитаются по улицам Стратхольма в обличье бесплотных призраков.$B$BМы должны помочь им, $N.$B$BУ меня есть помощник по имени Эган, он спец по призракам и духам. Найди его. Он, вероятно, сейчас в Восточных Чумных землях близ Стратхольма, проверяет свои изобретения.', },
    {'Найдите Эгана. В последний раз его видели недалеко от Стратхольма.', },
  },
  [5282] = {
    'Мятущиеся души',
    {'Только безграничная ненависть к Плети поддерживает во мне силы и желание жить. Но я не фанатик. Не такой, как этот сброд из Алого ордена – они скорее убьют невинную нежить, чем удостоят хоть каплей сочувствия.$B$BВозьми мой излучатель. С его помощью ты сможешь освободить истерзанные души, обреченные на скитания в Стратхольме.$B$BСтреляй в призраков, бродящих по улицам. Освободи их!', },
    {'Стреляйте в призрачных и неупокоенных горожан на улицах Стратхольма из бластера Эгана. Когда душа вырвется из призрачной оболочки, выстрелите в нее еще раз, и она обретет свободу.$B$BОсвободите 15 неупокоенных душ и возвращайтесь к Эгану.', },
  },
  [5283] = {
    'Искусство изготовления брони',
    {'Ты $Gпринял:приняла; верное решение.$B$BМы со стариной Железнусом друг друга не перевариваем. Этот кустарь косорукий знает, кто тут настоящий мастер.$B$BВеликие войны не выигрываются без хорошего доспеха. Когда дойдет до дела, никакой меч не спасет.$B$BИ никак иначе, так что возьмемся-ка за работу. Тебе нужно только показать мне, что у тебя есть природный талант к делу. Потом я выкую из тебя настоящего бронника и как следует отшлифую. Знаешь, как делать мифрил изысканным, а?', },
    {'Изготовьте и отдайте Грумнусу 4 изысканных мифриловых шлема, 2 изысканных мифриловых сапога и 1 изысканную мифриловую кирасу.', },
  },
  [5284] = {
    'Путь оружейника',
    {'Если станешь кузнецом, то к тебе очень часто будут обращаться за снаряжением всякие там герои и искатели приключений. Хотя порой работы невпроворот, спина ноет и дышать нечем, плата покрывает все с лихвой.$B$BДа, вот еще, $N, у кузнецов всегда водится золотишко!$B$BЕсли хочешь стать $Gодним:одной; из нас, тебе придется доказать, что ты сможешь заработать для меня пару звонких монет – то есть показать, что ты $Gусвоил:усвоила; все, чтобы стать специалистом по изготовлению оружия высшего качества. Приступай к работе!', },
    {'Сделайте и отдайте Железнусу 4 палаша лунной стали, 4 массивных железных топора, 2 тяжелых мифриловых топора и 2 большие черные палицы.', },
  },
  [5301] = {
    'Искусство изготовления брони',
    {'Моя работа приносит мне подлинное удовлетворение! Когда я вижу гордого воина Орды, защищенного моими доспехами, я понимаю, зачем родился на свет. Это как наваждение, $N, я без этого просто жить не могу.$B$BКонечно, этому я тебя научить не смогу – я могу только указать тебе путь. Ведь кузнечное ремесло, как и любая жизненная стезя, – это путешествие.$B$BЕсли ты хочешь стать $Gодним:одной; из избранных, я, конечно, готов тебя учить. Но сначала тебе нужно выполнить мое задание.', },
    {'Чтобы присоединиться к школе Брони, изготовьте и отдайте Окотосу 4 изысканных мифриловых шлема, 2 пары изысканных мифриловых сапог и 1 изысканную мифриловую кирасу.', },
  },
  [5302] = {
    'Путь оружейника',
    {'В один прекрасный день я создам легендарный молот. Молот, достойный вождя!$B$BЭто труд всей моей жизни, $N. Мое главное и единственное увлечение. Я вижу, что ты можешь стать $Gодним:одной; из нас – оружейником! И я помогу тебе в этом, если хочешь.$B$BТебе нужно только выполнить простое задание. При твоем мастерстве оно и впрямь несложное.', },
    {'Изготовьте и отдайте Боргошу 4 палаша лунной стали, 4 массивных железных топора, 2 тяжелых мифриловых топора и 2 больших черных палицы.', },
  },
  [5305] = {
    'Секрет безмятежности',
    {'В казематах Бастиона Алого ордена таится секрет безмятежности. Молотобоец из Багрового Легиона охраняет эту тайну. Срази его и принеси мне его фартук. За это я наделю тебя знанием о том, как изготовить зачарованный боевой молот!', },
    {'Отправляйтесь в Стратхольм и убейте Молотобойца из Багрового легиона. Возьмите его фартук и возвращайтесь к Лилит.', },
  },
  [5306] = {
    'Змеекамень Пленяющей Духов',
    {'Пусть ее красота не обманывает тебя, |3-6($C). Пленяющая Духов Вос\'гаджин безумна и смертельно опасна. $B$B*Килрам указывает на шрам у себя на лице.*$B$BДа, это ее рук дело – "удар любви", как она выразилась. $B$BНаконец настал момент для отмщения. $B$BВос\'гаджин ты найдешь на Пике Черной горы. Убей ее и принеси мне ее змеекамень. Сделай это, и я научу тебя секретам изготовления Лезвия Рассвета.', },
    {'Отправляйтесь на Пик Черной горы, убейте Темную охотницу Вос\'гаджин. Заберите у нее змеекамень и принесите Килраму.', },
  },
  [5307] = {
    'Порча',
    {'Если я краток с вами, на то есть причины. Чертежи для создания великого меча скверны были похищены Черной Стражей: элитным отрядом скелетов барона Ривендера. $B$BДа, именно, Ривендера из Стратхольма. $B$BОружейник Черной Стражи хранит теперь эти чертежи как зеницу ока. $B$BУбейте его и принесите доказательство его смерти, и в благодарность я научу вас тому, что нужно для изготовления пылающей рапиры!', },
    {'Найдите в Стратхольме оружейника Черной Стражи и уничтожьте его. Возьмите его Знак Черной Стражи и принесите Сирилу Плетебою.', },
  },
  [5321] = {
    'Спящий пробудился',
    {'Я шел к Заставе Мейстры, чтобы встретиться с Лиладрис Лунная Река, но решил остановиться здесь и немного вздремнуть. С тех пор как я очнулся от Изумрудного Сна, я все время такой сонный...$B$B*зевает*$B$BНе откажетесь проводить меня? Я так понимаю, Застава Мейстры находится к югу отсюда, в Ясеневом лесу, у дороги. Путь может быть опасен; я надеюсь, у вас есть друзья, которые смогут составить нам компанию.$B$BДа, и вот еще что: я могу заснуть по дороге, так что если вам нужно будет меня разбудить, сделайте это с помощью моего рога. Он в этом сундуке.', },
    {'Сопроводите Керлониана Вечный Сумрак к Лиладрис Лунной Реке на Заставу Мейстры в Ясеневом лесу.', },
  },
  [5341] = {
    'Сокровище Баровых',
    {'В Некроситете находятся сокровища семейства Баровых. Я, Алексий Баров, один из двух оставшихся наследников нашего дома. Второй – мой брат, Велдон.$B$BЭтот жалкий трус бежал во время войны и по-прежнему длит свое никчемное существование в человеческом обличье.$B$BСейчас он ищет документы, чтобы унаследовать все состояние. Доберись до этих документов раньше моего брата, и я озолочу тебя!', },
    {'Отправляйтесь в Некроситет и добудьте сокровище семьи Баровых. Оно состоит из четырех документов: на Каэр Дарроу, на Брилл, на Мельницу Таррен и на Южнобережье. После выполнения задания вернитесь к Алексию Барову.', },
  },
  [5342] = {
    'Последний из Баровых',
    {'Естественно, пока мой брат жив, могут возникать вопросы о порядке наследования. Я не допущу этого.$B$BПолагаю, ты, шваль, не побоишься замарать руки убийством.$B$BЗадание простое, надеюсь, ты справишься; если хочешь получить награду.$B$BУэлдон сидит под крылышком у Альянса в лагере Промозглого Ветра на востоке. Убей его и принеси мне голову.$B$B*Алексий зевает.*$B$BДавай-давай, иди.', },
    {'Отправляйтесь в лагерь Промозглого Ветра, на земли Альянса, и убейте Вэлдона Барова. Отнесите его голову Алексию Барову.$B$BСоблюдайте осторожность – скорее всего, Вэлдона охраняют подхалимы из Альянса.', },
  },
  [5343] = {
    'Сокровище Баровых',
    {'Милый братец Алексий. И зачем я, глупец, пощадил его? Я смотрел в лицо врага, нежити, и оставил его в живых.$B$B<Уэлдон сплевывает.>$B$BОн хочет отобрать МОЕ сокровище. Не бывать тому!$B$BНайди документы на мои владения, прежде чем эта мерзость дотянется до них своими костлявыми ручками, и получишь большую награду. Документы находятся в руинах Некроситета. Поспеши!', },
    {'Отправляйтесь в Некроситет и добудьте сокровище семьи Баровых. Оно состоит из четырех документов: на Каэр Дарроу, на Брилл, на Мельницу Таррен и на Южнобережье. После выполнения задания вернитесь к Вэлдону Барову.', },
  },
  [5344] = {
    'Последний из Баровых',
    {'Документы у меня в руках, осталось только одно. Алексия придется убить. Когда он умрет, я смогу вздохнуть спокойно, не ожидая гибели от руки наемного убийцы.$B$BЗадание простое. Алексий находится под покровительством Орды – в Бастионе.$B$BНет, я не требую, чтобы ты прямо $Gвзял и убил:взяла и убила; его, мне голова нужна! А как ты ее получишь... твое дело. Подобные размышления ниже моего аристократического достоинства.', },
    {'Отправляйтесь в Бастион – на земли Орды – и убейте Алексия Барова. Отнесите его голову Вэлдону Барову.$B$BСоблюдайте осторожность – скорее всего, Алексия охраняет ордынская мразь.', },
  },
  [5361] = {
    'Семейное древо',
    {'Мой брат, Натака, находится на Заставе Скитающихся Духов в Пустошах. Я хотел бы попросить вас... не доставите ли ему этот отчет? С тех пор, как я писал ему последний раз, прошел целый год, и вот сейчас кентавры опять что-то затевают. Мой брат должен узнать обо всем.$B$BВам нужно будет отправиться в Каменный Коготь – это горы к северу от Перекрестка, а потом свернуть на запад, в Обугленную долину. Пустоши находятся южнее долины. Следуйте по дороге на юг, и скоро увидите Заставу Скитающихся Духов.', },
    {'Доставьте отчет стража утеса Долгорога Натаке Длинному Рогу на Заставу Скитающихся Духов в Пустоши.', },
  },
  [5381] = {
    'Рука Ируксоса',
    {'В руки Пылающего Клинка попала демоническая шкатулка, в которой хранится могущественный артефакт – Рука Ируксоса. Орки Пылающего Клинка используют ее, чтобы открывать демонические порталы на Поле Маннорок. Мы должны остановить нашествие демонов, захлестывающее наш мир!$B$BРасколи красный кристалл в Крепости Громового Топора этой киркой демонов: так ты сможешь добыть демоническую шкатулку.', },
    {'Добудьте в Крепости Громового Топора демоническую шкатулку, а затем принесите ее и кирку демонов Тайге Мудрой Гриве в Деревню Ночных Охотников.', },
  },
  [5382] = {
    'Доктор Теолен Крастинов – Мясник',
    {'Крастинов виновен в тысячах смертей. Справедливость должна восторжествовать – пусть он вкусит собственного блюда.$B$BНайди его и заставь испытать мучения его бессчетных жертв. Когда он умрет, сожги наши останки. Пусть обратятся в пепел, $N.', },
    {'Найдите в Некроситете доктора Теолена Крастинова. Убейте его, затем сожгите останки Евы и Люсьена Саркофф. Выполнив задание, возвращайтесь к Еве Саркофф.', },
  },
  [5384] = {
    'Киртонос Глашатай (заклинание, визуальный эффект)',
    {'Вот она, $N.$B$B*Ева указывает на сосуд в мешке.*$B$BКровь невинных. Чистейшая кровь, умилостивление хозяина Крастинова.$B$BПри помощи этой крови можно вызвать Киртоноса, и именно она поможет его уничтожить.$B$BОтнести эту кровь на балкон, помести в жаровню. Киртонос явится на зов – он не сможет устоять. Когда он явится, обрушь на него ярость и гнев тысяч невинно убиенных.$B$BЕсли ты добьешься успеха, мы на славу отблагодарим тебя.', },
    {'Вернитесь в Некроситет с кровью невинных. Найдите балкон и вылейте кровь в жаровню. На зов явится Киртонос.$B$BСражайтесь как герой, не сдавайтесь! Уничтожьте Киртоноса и возвращайтесь к Еве Саркофф.', },
  },
  [5385] = {
    'Останки Трея Светогорна',
    {'Вы осторожно поднимаете мешок и собираетесь возвращаться к Джессире Лунной Радуге на границу Ясеневого леса и Оскверненного леса. Вы надеетесь, что паладин будет с почестями погребен, когда его останки будут доставлены его друзьям.', },
    {'Принесите останки Трея Светогорна Джессире Лунной Радуге в Оскверненный лес.', },
  },
  [5386] = {
    'Дорога рыбка к обеду',
    {'Давненько на Заставе Скитающихся Духов не наслаждались запахом жареной рыбки... Тролли, живущие в Деревне Ночных Охотников – отличные рыбаки, вот только из-за разбушевавшихся в наших краях кентавров мне никак не выкроить времени, чтобы добраться до них.$B$BПослушай-ка, окажи мне услугу: отправляйся в Деревню Ночных Охотников. Разыщи там Джинар\'Зиллена, потолкуй с ним и принеси мне рыбок-кровобрюхов: я их просто обожаю! На юг отсюда идет дорога: она приведет тебя аккурат в деревню троллей.', },
    {'Отправляйтесь в Деревню Ночных Охотников и поговорите с Джинар\'Зилленом, а затем принесите Натаке Длинному Рогу 2 рыбы-кровобрюха.', },
  },
