  [5441] = {
    'Ленивые батраки',
    {'Проклятые батраки! Им велено валить лес, но они все время пытаются отлынивать. Мне нужен кто-то, кто их приструнит. Ты, |3-6($R), мне, пожалуй, годишься. На тебе дубинку – ступай и всыпь этим бездельникам. Хороший тумак заставит их взяться за ум! Управишься – не забудь вернуть дубинку.$B$BПроклятые лентяи...', },
    {'Проучите ленивых батраков дубинкой старшины. Разбудите 5 батраков, потом верните дубинку штейгеру Тазз\'рилу в Долине Испытаний.', },
  },
  [5461] = {
    'Рас Ледяной Шепот – человек',
    {'Нежить боится жизни не меньше, чем живые – смерти. Ступившие на путь не-жизни нередко уничтожают все свидетельства прежнего бытия.$B$BК сожалению, для создания артефакта, способного превратить Раса Ледяного Шепота в смертного, мы должны отыскать какой-нибудь предмет, сохранившийся со времени его настоящей жизни.$B$BОтправляйся в разрушенный город Стромгард, родину Ледяного Шепота, и найди такой предмет. Удачи тебе, $N.', },
    {'Отправляйтесь в высокогорья Арати, в руины Стромгарда. Поищите в Стромгарде книгу Воспоминаний. Если удастся ее отыскать, отнесите ее мировому судье Мардуку.', },
  },
  [5462] = {
    'Рас Ледяной Шепот – гибель',
    {'Оскверненной душе можно противопоставить особые памятные предметы.$B$BГоворят, будто Ледяной Шепот встал под знамена Короля-лича в Стратхольме. Он добровольно лишил себя жизни с целью влиться в ряды нежити.$B$BИменно в тех землях следует искать память о его человеческом облике.$B$BВ часовне Последней Надежды ты найдешь Леонида Барталомея Чтимого, некогда бывшего рабом Плети, освобожденного королевой банши, но отдавшего свою верность Серебряному Рассвету. Быть может, он знает больше, чем я. В путь!', },
    {'Отправляйтесь в часовню Последней Надежды в Восточных Чумных землях и найдите Леонида Барталомея Чтимого. Покажите ему книгу Воспоминаний и расскажите обо всем, о чем удалось узнать.', },
  },
  [5463] = {
    'Дар Менетила',
    {'Рас Ледяной Шепот некогда тоже был человеком. Я собственными глазами видел, как он вручил свою бессмертную душу Королю-личу. Он приставил к своему горлу кинжал и с улыбкой перерезал себе глотку от уха до уха. Его мертвое тело упало в центр пентаграммы. Король-лич бросил взгляд на мертвого мага, и в ту же секунду Рас Ледяной Шепот восстал в обличье лича.$B$B*Леонид опускает глаза на мгновенье и снова продолжает рассказ*$B$BДа, |3-6($R), я помню. У того, что ты ищешь, есть имя – Дар Менетила: это земля, благословленная Королем-личом – священное место Плети.', },
    {'Отправляйтесь в Стратхольм и отыщите Дар Менетила. Положите книгу Воспоминаний на оскверненную землю.', },
  },
  [5464] = {
    'Дар Менетила',
    {'Души падших цепляются за любое олицетворение их прежное жизни и превращают любой значимый для них предмет в Книгу Души. Вы поднимаете предмет и прячете в сумку.', },
    {'Отнесите Книгу Души Леониду Барталомею в Восточные Чумные земли.', },
  },
  [5465] = {
    'Книга души',
    {'Отнеси книгу Мардуку, $N, он расскажет тебе, что делать дальше.', },
    {'Возвращайтесь к мировому судье Мардуку в Каэр Дарроу. Покажите ему Книгу Души.', },
  },
  [5466] = {
    'Рас Ледяной Шепот – лич',
    {'Осталось одно задание, $N – бросить вызов личу Расу Ледяному Шепоту.$B$BВоспользуйся против него силой Книги – не знай пощады! Терпи удары, которые обрушит на тебя злобная тварь. Не бойся боли!$B$BКогда ритуал будет завершел, Рас снова станет смертным. Именно в этот момент его необходимо убить. Срази злодея и забери его голову', },
    {'Отыщите в Некроситете Раса Ледяного Шепота. Найдя его, воспользуйтесь Книгой Души против его посмертного облика. Если удастся превратить Раса в смертного, убейте его и заберите человеческую голову Раса Ледяного Шепота. Отнесите голову мировому судье Мардуку.', },
  },
  [5481] = {
    'Задание Толстяка',
    {'Хозяину надо растения. У нас большие руки, цветы рвать никак. Ты нам помогать, мы тебя не трогать. Нам надо мракоцвет, он растет тут и у дороги.$B$BТы соберешь – отнеси хозяину Холланду на кладбище в Брилл.', },
    {'Соберите 3 ветки мракоцвета для младшего аптекаря Холланда на кладбище Брилла.', },
  },
  [5482] = {
    'Погибельник',
    {'Теперь ты понимаешь, в каком затруднительном положении я нахожусь, $N? Это нечто все еще ходит там и собирает не те растения. Я непременно отблагодарю тебя, если ты соберешь то, что мне нужно. Принеси мне погибельник, и я щедро тебя награжу.$B$BНасколько я знаю, его можно найти у братских могил, к северу от кладбища Брилла. Поторопись – и будь $Gосторожен:осторожна; – в тех местах полно гноллов.', },
    {'Соберите 10 веток погибельника для младшего аптекаря Холланда.', },
  },
  [5501] = {
    'Собиратель костей',
    {'Мы тут на золотой жиле сидим, на золотой жиле! Знаешь, что это? Кости кодо!.. Кости кодо изменят мир раз и навсегда! У меня масса клиентов, которые делают из этих костей мебель, музыкальные инструменты – даже взрывчатый порошок, чтоб тебе знать!$B$BСюда приходит караван, забирает у меня кости, а потом караванщики продают их в Деревне Ночных Охотников. И неплохая выручка, да, очень неплохая... Если пойдешь на юг отсюда, то доберешься прямиком до Кладбища кодо. Принеси мне десяток костей, а я дам тебе кое-что взамен. Ну что, идет?', },
    {'Принесите 10 костей с Кладбища кодо в хижину Кормека и отдайте Биббли Скобкофузу.', },
  },
  [5502] = {
    'Опекун Орды',
    {'Ты $Gбыл:была; очень $Gдобр:добра; ко мне, $N. Я и представить не мог, что Детская неделя пройдет так весело. Спасибо. Я не хочу печалиться из-за собственной судьбы – предки всегда говорили мне, что я должен быть выше всех несчастий. Знаешь, когда я вырасту, я хочу стать героем... как ты.$B$BКак ни жаль, мне пора домой. Давай вернемся, и я расскажу нашей попечительнице, что ты лучше всех!', },
    {'Отведите своего подопечного в приют Оргриммара, вернув вернув орочью детскую свистульку попечительнице сирот Найтингейл в Храмовый квартал.', },
  },
  [5503] = {
    'Жетон Серебряного Рассвета',
    nil,
    nil,
  },
  [5504] = {
    'Оплечья Рассвета',
    {'Тебе причитается награда за верную службу Серебряному Рассвету, $N. Тебе удалось завоевать всеобщее почтение. Мне поручено передать – ты можешь купить один из самых наших ценных предметов – оплечье защиты от магии.$B$BЕсли прикрепить его к доспехам, твое сопротивление силам одного из направлений магии возврастет. За это оплечье я попрошу от тебя не менее 10 знаков доблести Серебряного Рассвета.', },
    {'Принесите 10 знаков доблести Серебряного Рассвета интенданту Хасане в Бастион.', },
  },
