  [5513] = {
    'Оплечья Рассвета',
    {'Тебе причитается награда за верную службу Серебряному Рассвету, $N. Тебе удалось завоевать всеобщее почтение. Мне поручено передать – ты можешь купить один из самых наших ценных предметов – оплечье защиты от магии.$B$BЕсли прикрепить его к доспехам, твое сопротивление силам одного из направлений магии возврастет. За это оплечье я попрошу от тебя не менее 10 знаков доблести Серебряного Рассвета.', },
    {'Принесите 10 знаков доблести Серебряного Рассвета интенданту Бричлок в Часовню Последней Надежды в Восточные Чумные земли.', },
  },
  [5514] = {
    'Форма денег стоит',
    {'Тебе нужно поговорить с гоблином по имени Кринкель Сталлер. Он работает в Прибамбасске, в пустыне Танарис. Ему под силу сделать такую форму, но это обойдется недешево. Мы просим тебя не скупиться и заплатить ему.$B$BЯ алхимик, а не банкир. Будь у меня золото, я бы здесь не сидел.$B$BНу вот, опять я брякнул что-то не то. Ладно.$B$BТебя ждет Прибамбасск! Удачи!', },
    {'Отнесите Кринкелю Сталлеру из Прибамбасска прочные фрагменты скелета и 15 золотых монет.', },
  },
  [5515] = {
    'Мешок ужасов Крастинова',
    {'Мы сбросили с себя тяжелое бремя, но твое задание все еще не исполнено. Повелитель Крастинова, Киртонос, все еще жив.$B$BВпрочем, прежде чем бросить ему вызов, необходимо придумать, как его вызвать.$B$BГоворят, что кровь невинных от Крастинова к Киртоносу переправляет Джандис Барова.$B$BНайди ее в затопленных коридорах Некроситета и убей. Принеси все улики, которые только найдешь.', },
    {'Найдите в Некроситете Джандис Барову и уничтожьте ее. Заберите мешок ужасов Крастинова. Отнесите мешок Еве Саркофф.', },
  },
  [5517] = {
    'Многоцветная драгоценность Рассвета',
    {'$GБрат:Сестра; $N – нет числа твоим деяниям во славу Серебряного Рассвета. В знак признательности я готова расстаться с одним из наших знаменитых многоцветных оплечий Рассвета – тех самых, которые защищают от всех видов магии. Многоцветные оплечья Рассвета предназначены лишь для наших величайших героев!$B$BПринеси мне двадцать пять знаков доблести, и получишь одно из лучших наших изделий.', },
    {'Принесите 25 знаков доблести Серебряного Рассвета интенданту Бричлок в Часовню Последней Надежды в Восточные Чумные земли.', },
  },
  [5518] = {
    'Броня огров Гордока',
    {'Если вы собираетесь сразиться с королем этой помойки – а думаю, что без этого не обойтись, – тогда вам придется миновать капитана Давигрома. Конечно, можно его просто убить. Но возможно, вам стоит оставить его в живых.$B$BВы спросите – каким образом? Я сижу здесь уже довольно долго, и успел придумать, как можно изготовить личину огра, которая продержится достаточно долго, чтобы обмануть этих остолопов. Но, увы, больше чем на десять минут ее не хватит. Принесите мне все, что нужно, и я вам ее изготовлю. Торопиться-то мне все равно некуда...', },
    {'Принесите 4 рулона рунической ткани, 8 кусков грубой кожи, 2 мотка рунной нити и огрскую дубильную кислоту Уззлу Наперстяку. Он прикован в секторе Гордока в Забытом Городе.', },
  },
  [5519] = {
    'Броня огров Гордока',
    nil,
    nil,
  },
  [5521] = {
    'Многоцветная драгоценность Рассвета',
    {'$GБрат:Сестра; $N – нет числа твоим деяниям во славу Серебряного Рассвета. В знак признательности я готова расстаться с одним из наших знаменитых многоцветных оплечий Рассвета – тех самых, которые защищают от всех видов магии. Многоцветные оплечья Рассвета предназначены лишь для наших величайших героев!$B$BПринеси мне двадцать пять знаков доблести, и получишь одно из лучших наших изделий.', },
    {'Принесите 25 знаков доблести Серебряного Рассвета интенданту Искре Света из лагеря Промозглого Ветра.', },
  },
  [5522] = {
    'Леонид Барталомей',
    {'Я получила весть от своего господина Вектуса. На самом деле... не напрямую от него, но от некого Леонида Барталомея. Я не знаю его, однако он видел моего господина – а никто не может видеть моего господина, если он сам этого не пожелает.$B$BЛеонид сказал мне, что Вектусу необходимы драконьи яйца. Доставь их ему в Часовню Последней Надежды в Восточные Чумные земли.$B$BАй! За что? Разве Вектус не доверяет мне? Не обращай внимания, я не тебе. Просто доставь яйца куда следует и сообщи Леониду, что я сделала все, как было приказано.', },
    {'Принесите охлажденные яйца Леониду Барталомею в Восточные Чумные земли.', },
  },
  [5524] = {
    'Многоцветная драгоценность Рассвета',
    {'$GБрат:Сестра; $N – нет числа твоим деяниям во славу Серебряного Рассвета. В знак признательности я готова расстаться с одним из наших знаменитых многоцветных оплечий Рассвета – тех самых, которые защищают от всех видов магии. Многоцветные оплечья Рассвета предназначены лишь для наших величайших героев!$B$BПринеси мне двадцать пять знаков доблести, и получишь одно из лучших наших изделий.', },
    {'Принесите 25 знаков доблести Серебряного Рассвета интенданту Хасане в Бастион, что в Западных Чумных землях.', },
  },
  [5525] = {
    'Освободите Нотта!',
    nil,
    nil,
  },
  [5526] = {
    'Осколки сквернита',
    {'Твои знания об Эльдре\'Таласе – Забытом Городе – в Фераласе теперь очень пригодятся, $N. Именно в восточной части этого разрушенного города властвует нечестивый Алззин Перевертень. Ступай же туда со всей поспешностью и посмотри в лицо ожидающей тебя опасности. $B$BПлод Изобилия, ныне оскверненный и ставший Сквернитом, должен находиться неподалеку от Алззина. Когда он упадет, собери столько субстанции Сквернита, сколько сможешь. Закрой его в реликварий и принеси мне.$B$BУдачи тебе, и быстроты Кенария.', },
    {'Отыщите Сквернит в Забытом Городе и подберите его осколок. Есть шанс, что вам удастся его добыть, только убив Алззина Перевертня. Крепко заприте осколок в реликварии Чистоты, затем верните его Рабину Сатурне в Ночную Гавань в Лунной поляне.', },
  },
  [5527] = {
    'Реликварий Чистоты',
    {'Первый шаг – найти вместилище, способное удержать чрезвычайно оскверненную субстанцию и оградить обладателя от ее влияния. Единственное известное мне приспособление, в котором, возможно держать такую дрянь, как Сквернит, – это реликварий Чистоты.$B$BВ сердце пустошей Силитуса находятся руины поселения Ночных эльфов, деревня Южного Ветра. Я знаю, что реликварий хранился там много лет назад. Если хочешь помочь нам достичь успеха, обыщи эти руины в надежде найти его.', },
    {'Отправляйтесь в Силитус и найдите реликварий Чистоты в руинах деревни Южного Ветра. Если вам удастся его найти, возвращайтесь к Рабину Сатурне в Ночную Гавань в Лунной поляне.', },
  },
  [5528] = {
    'Лучшее пойло Гордока',
    nil,
    nil,
  },
  [5529] = {
    'Зачумленные детеныши дракона',
    {'До Серебряного Рассвета дошли слухи о том, что Плеть создает собственную армию драконов! Мы должны этому помешать – с помощью драконов Плеть сможет беспрепятственно перемещаться по всему Азероту!$B$BИх некроманты выращивают проклятых драконов в Некроситете, посреди озера Дарроумир.$B$BОтправляйся в Некроситет и убей как можно больше драконьих детенышей, пока они не успели вырасти и превратиться в неуязвимых монстров!', },
    {'Убейте 20 зачумленных детенышей дракона, затем возвращайтесь к Бетине Биггльцинк в Часовню Последней Надежды.', },
  },
  [5531] = {
    nil,
    {'Яйца, которые ты $Gпринес:принесла; мне, должны были быть использованы Плетью для создания чудовищных проклятых драконов. Охлажденные яйца черных драконов были бы бесценны для их опытов – это несомненно позволило бы Плети достичь своей подлой цели гораздо быстрее.$B$BОтнеси яйца Бетине Биггльцинк – она сведуща в науках и алхимии. Она обеспечила нас оружием против Плети. С твоей помощью мы нанесем этим тварям решающий удар.', },
    {'Принесите охлажденные яйца Бетине Биггльцинк.', },
  },
  [5533] = {
    'Некроситет',
    {'На острове Каэр Дарроу располагается Некроситет – школа, которая одновременно служит цитаделью плети. В ее недрах наши враги спокойно проводят исследования и делаются все сильнее. Путь преграждают двери, и без подходящего ключа внутрь не войти.$B$BАлхимик Арбингтон многое знает о Некроситете и придумал, как достать ключ. Ты $Gзарекомендовал:зарекомендовала; себя как человека с большими способностями, поэтому я доверяю тебе это задание. Обсуди подробности с алхимиком.', },
    {'Поговорите с алхимиком Арбингтоном на берегу Промозглого Ветра, что в Западных Чумных землях.', },
  },
  [5534] = {
    '"Пропавшее"оборудование Ким\'джаеля',
    {'Что?! Ты в своем уме?! Чем ты еще $Gнедоволен:недовольна;?! $B$BТы хоть представляешь, сколько стоят эти гранаты? За них дают... ладно, не важно... Хочешь денег? Хорошо. Прежде докажи мне, что ты $Gценный работник:ценная работница;.$B$BОтправляйся в руины Эльдарата и принеси мне некую руну, которую искали эльфы крови. Полагаю, она у наг.', },
    {'Найдите некую руну, которую хранят наги в руинах Эльдарата и принесите ее Ким\'джаелю в Азшару.', },
  },
  [5535] = {
    'Беспокойные духи',
    {'Давным-давно высокорожденные были самыми могущественными адептами тайной магии, когда либо существовавшими среди ночных эльфов. Порча, которая охватывает каждого, кто имеет дело с тайной магией, принесла много горя народу ночных эльфов и всему Азероту. $B$BЯ обещал Часовым, что буду освобождать встреченных мной духов от их смертных связей. Если поможешь мне в этом, я награжу тебя. $B$BДухи скитаются в святилище Песни Теней к северо-востоку отсюда.', },
    {'Уничтожьте по 6 теней высокорожденных обоих полов и возвращайтесь к Ло\'хату в Азшару.', },
  },
  [5536] = {
    'Земли, полные ненависти',
    {'Сатиры, искаженные и зловредные порождения ночных эльфов, ныне оскверняют священные земли, по которым некогда ступал Кенарий. Конечно, не в моих силах помочь эльфам справиться со всеми этими отвратительными созданиями, однако я поклялся Матери-Земле помочь эльфам избавиться от сатиров хотя бы здесь. $B$BНадеюсь, ты поможешь мне в этом деле. $B$BК северо-востоку отсюда ты найдешь клан Халдарр. Возвращайся ко мне, когда испытаешь свою силу.', },
    {'Уничтожьте 6 сатиров, 2 плутов Халдарра и 2 скверноподданных из племени Халдарр и возвращайтесь к Ло\'хату к границе Ясеневого леса.', },
  },
  [5537] = {
    'Фрагменты скелета',
    {'Чтобы отомкнуть двери Некроситета необходим Костяной ключ. Его делают из остатков скелета – точнее, нескольких скелетов – и вплавляют в прочнейший из металлов в подходящей форме. Чтобы ключ заработал, необходим перстень власти одного из тех существ, что могут открыть портал данной им силой.$B$BНо не будем отвлекаться, для начала нам понадобятся фрагменты скелетов. Для этой цели подойдут скелеты внутри Андорала, да и те, что снаружи.', },
    {'Принесите 15 фрагментов скелетов алхимику Арбингтону в Берег Промозглого Ветра в Западных Чумных землях.', },
  },
  [5538] = {
    'Форма денег стоит',
    {'Тебе нужно поговорить с гоблином по имени Кринкель Сталлер. Он работает в Прибамбасске, в пустыне Танарис. Ему под силу сделать такую форму, но это обойдется недешево. Мы просим тебя не скупиться и заплатить ему.$B$BЯ алхимик, а не банкир. Будь у меня золото, я бы здесь не сидел.$B$BНу вот, опять я брякнул что-то не то. Ладно.$B$BТебя ждет Прибамбасск! Удачи!', },
    {'Отнесите Кринкелю Сталлеру из Прибамбасска прочные фрагменты скелета и 15 золотых монет.', },
  },
  [5541] = {
    'Боеприпасы для Громострела',
    {'Хегнар Громострел продает ружья у дороги в Старую Наковальню. Стрелки и пушкари много упражняются возле его лавки, так что ему всегда нужны снаряды и патроны.$B$BНо... Последний ящик с боеприпасами, что я ему послал, пропал по дороге. Мой курьер рассказал мне, что он встал лагерем у Серой берлоги, там на него напали вендиго... и этот дурак бросил боеприпасы для Громострела!$B$B$N, $Gне мог бы ты:не могла бы ты; найти их и вернуть Громострелу? Он их ждет, и наверняка у него боеприпасы уже на исходе.', },
    {'Доставьте боеприпасы Хегнару Громострелу в Дун Мороге.', },
  },
  [5542] = {
    'Псы-демоны',
    {'Если ты собираешься здесь оставаться, придется тебе поработать в уплату за проживание. У нас есть ряд докучающих обстоятельств, с которыми неплохо бы разобраться.$B$BНачни с чумных псов и их отродий.$B$BЯ не многим смогу тебе отплатить, но горячую пищу и приятную беседу гарантирую.', },
    {'Убейте 20 карликовых чумных псов, 5 чумных псов и 5 бесноватых чумных псов. По выполнении задания вернитесь к Тириону Фордрингу.', },
  },
  [5543] = {
    'Окровавленные небеса',
    {'Горе тем, кто в глупости своей бродит по Чумным землям. В здешних лесах таится много опасностей – от Алого ордена, который убивает всех, кроме своих сторонников, до Плети, которая всегда рада пополнить свои ряды новой нежитью.$B$BДаже звери и птицы превратились в отвратительных хищных чудовищ. Я прошу тебя уничтожить тех, что нападают с небес – чумных нетопырей.', },
    {'Убейте 30 чумных нетопырей и вернитесь к Тириону Фордрингу.', },
  },
  [5544] = {
    'Мясо личинок-трупоедов',
    {'$R, у меня еда на исходе. Мне стыдно признаться, но угостить тебя нечем.$B$BНе поможешь старику? Единственные твари в этих краях, которых не поразила чума, это личинки-трупоеды. Они довольно гадкие, зато их мясо хранится много месяцев. Мне необходимо запасти несколько сотен фунтов на зиму!', },
    {'Найдите личинок-падальщиков и падальщиков-пожирателей. Убейте побольше и наберите их мяса. Возвращайтесь к Тириону Фордрингу, когда насобираете 15 кусков мяса червей-трупоедов.', },
  },
  [5545] = {
    'Тридцать три несчастья',
    {'У меня большие неприятности. Приближается срок поставки древесины, а я не успеваю ее отправить. Самое обидное, что она уже заготовлена – но волки и медведи, расплодившиеся к северу отсюда, не дают моим работникам ее забрать$B$BЯ уже договорилась с заместителем судьи Рейнером насчет зверья, но мне нужен кто-нибудь, кто доставит сюда эти дрова. Если ты принесешь мне восемь вязанок, я все успею.', },
    {'Принесите 8 вязанок дров Раэлен на Лесопилку Восточной долины.', },
  },
  [5561] = {
    'Отлов кодо',
    {'Гоблин Биббли думает, что только он умеет делать деньги в Пустошах. Ха! Единственный караван, который осмеливается странствовать по этим землям, полностью зависит от меня. С тех самых пор, как я научился омолаживать кодо и управлять старыми и умирающими зверями при помощи моего энергетического инкапсулятора, мы трое – я, караванщик Корк и Риггер – создали что-то вроде компании.$B$BВот, держи инкапсулятор: он действует на древних, старых и умирающих кодо, превращая их в ручных и заставляя следовать за тобой. Как сумеешь приручить кодо, веди его ко мне.', },
    {'Приведите пять прирученных кодо Смиду Заржавеню в Лагерь Заржавня. Не забудьте вернуть ему энергетический инкапсулятор.', },
  },
  [5581] = {
    'Порталы Легиона',
    {'Воззри: вот Рука Ируксоса. Поистине, ужасающее зрелище.$B$BЕсли ты намереваешься закрыть порталы демонов на Поле Маннорок, – а именно об этом я и хочу тебя попросить, – то она должна быть у тебя. Угроза со стороны демонов растет: мы должны закрыть их порталы, дабы демоническим существам более не было доступа в этот мир. Удачи тебе в этом нелегком деле, $N... и берегись стражей портала.', },
    {'Закройте 6 порталов на Поле Маннорок, а затем вернитесь к Тайге Мудрой Гриве в Деревню Ночных Охотников.', },
  },
  [5582] = {
    'Здоровая чешуя дракона',
    {'Это чешуйка была взята у пораженного проклятой чумой драконьего детеныша. Однако в отличие от других чешуек, изъязвленных и покрытых слизью, эта выглядит совершенно здоровой.$B$BЛюбопытное явление. Бетина Биггльцинк непременно захочет на это взглянуть.', },
    {'Отнесите здоровую чешую дракона Бетине Биггльцинк в Часовню Последней Надежды в Восточные Чумные земли.', },
  },
  [5601] = {
    'Сестренка Памела',
    {'Во время войны я бежала в Лордерон – мне посчастливилось спастись до того, как полчища Плети прошли по моим родным местам и превратили их в зачумленные руины. Мне повезло, но судьба была не столь благосклонна к моей семье. Боюсь, все они погибли во время битвы при Дарроушире.$B$BДеревня Дарроушир была частью Восточных королевств, теперь это еще одни развалины в южных пределах Восточных Чумных земель. Не $Gмог:могла; бы ты узнать, что сталось с моей семьей? Я так волнуюсь за них, особенно за мою сестренку Памелу.$B$BПожалуйста, отыщи ее!', },
    {'Найдите Памелу Редпат в Дарроушире.', },
  },
  [5621] = {
    'Облачение Луны',
    {'Мне нужна твоя помощь, $N. Я должна оставаться здесь, чтобы обучать других, но ты $Gмог бы:могла бы; помочь одной из наших раненых часовых к югу от Доланаара. Ее ранили, когда она патрулировала город, и ей нужна наша помощь. Ее зовут Шайя.$B$BКогда найдешь ее, исцели ее раны и укрепи ее тело заклятиями. По крайней мере, это защитит ее от бед на некоторое время. Когда выполнишь свой долг перед Элуной, возвращайся ко мне.', },
    {'Найдите стража Шайю и исцелите ее раны при помощи малого исцеления (ранг 2). Потом скажите ей Слово силы: "Стойкость". Затем вернитесь к Лорне Утреннему Свету в Доланаар.', },
  },
  [5622] = {
    'Во имя Элуны',
    {'$N, тебе предстоит отправиться в Доланаар, когда завершишь свои дела здесь, в Тенистой долине. Там ты разыщешь Лорну Утренний Свет. Она расскажет тебе больше о пути Элуны, и с ее помощью ты получишь свое первое облачение, говорящее о твоем статусе $Gжреца:жрицы; Элуны.$B$BБудь $Gосторожен:осторожна;, и да хранит тебя в пути Элуна!', },
    {'Поговорите в Тельдрассиле с Лорной Утренний Свет.', },
  },
  [5623] = {
    'Во имя Света',
    {'Приветствую тебя, $Gюный жрец:юная жрица;. Пришло время узнать больше о нашей вере. После того как ты закончишь все дела в Североземье, разыщи в Златоземье жрицу Жозетту. Знания ее велики; к тому же она уже слышала о тебе и о твоем стремлении к Истинному Свету. Она готова поделиться с тобой своей мудростью.', },
    {'Поговорите с жрицей Жозеттой в Элвиннском лесу.', },
  },
  [5624] = {
    'Облачение Света',
    {'Обычно я подвергаю всех последователей Света небольшому испытанию. Так я выясняю, достойны ли они своего первого облачения – символа того, что церковь их принимает и поддерживает.$B$BНо сейчас ситуация немного отличается. Один из жителей Златоземья видел раненого стража на берегу озера к востоку отсюда. Этого стража зовут Робертс. Найди его, исцели его раны, придай ему стойкости и вернись сюда. Если ты справишься, я сочту твое испытание завершенным.', },
    {'Разыщите стражника Робертса и исцелите его раны, пользуясь "Малым исцелением" (уровень 2), а затем одарите его заклинанием "Слово силы: Стойкость", после чего вернитесь в Златоземье к жрице Жозетте.', },
  },
  [5625] = {
    'Облачение Света',
    {'Как правило, примерно в эту пору $Gпослушники:послушницы; вроде тебя проходят испытание, в котором проверяется, достойны ли они носить облачения, говорящие об их статусе. Но, честно говоря, сейчас у меня есть и более важные дела. Горожане Караноса только что сказали мне, что к югу от городских стен они нашли раненого горного пехотинца по имени Дольф.$B$BИзлечи Дольфа и наложи на него "Слово Силы: Стойкость", и я сочту, что ты $Gпрошел:прошла; испытание. Справишься?', },
    {'Надите горного пехотинца Дольфа и исцелите его раны при помощи Малого исцеления (ранг 2). Также наложите на него "Слово силы: Стойкость", а затем вернитесь к Максану Ковальне в Каранос.', },
  },
  [5626] = {
    'Во имя Света',
    {'Эй! $N! У тебя есть свободная минутка?!$B$BТы давно уже следуешь пути Света, так ведь? Как насчет того, чтобы сходить к Максану Ковальне в Каранос? Он расскажет тебе побольше о твоей судьбе, а если ты окажешься $Gдостойным:достойной;, он тебе даст твои первые одеяния $Gжреца:жрицы;.$B$BТеперь ступай! Как только закончишь все свои дела здесь, иди в Каранос через туннель – Максан будет тебя ждать.', },
    {'Поговорите с Максаном Ковальней в Дун Мороге.', },
  },
  [5627] = {
    'Звезды Элуны',
    nil,
    nil,
  },
  [5628] = {
    'Возвращение домой',
    {'Если не ошибаюсь, $N? Рад, что ты $Gуделил:уделила; мне минутку внимания. Забраться так далеко от дома, да в такие юные годы, – мне по душе твое рвение! Если бы мне в молодые годы хватило духу постранствовать по миру...$B$BТут недавно тебя искала жрица из вашего ордена. Сказала, что тебе спешно надо вернуться в Храм Луны в Дарнасе и поговорить со жрицей Алатеей – это как-то связано с твоим обучением. Я бы на твоем месте не тянул.', },
    {'Поговорите в Дарнасе со жрицей Алатеей.', },
  },
  [5629] = {
    'Возвращение домой',
    {'Еще раз приветствую тебя, $N. Я знаю, что ты и так много помогаешь нашему народу, но надо и себе уделить хоть немного времени. Жрица Алатея прислала за тобой. Сдается, ты $Gпривлек:привлекла; внимание наших старейшин, и они считают, что ты $Gсозрел:созрела; для более великих дел. Отправляйся в дарнасский Храм Луны и найди там Алатею. Удачи тебе, и да хранит тебя Элуна.', },
    {'Поговорите в Дарнасе со жрицей Алатеей.', },
  },
  [5630] = {
    'Возвращение домой',
    {'Ну здравствуй, $Nдруг:подруга. Приятно видеть, что ты так далеко $Gзабрался:забралась;! Без обид, но многие из ваших не слишком хорошо переносят местные холода. Славно, что ты $Gслеплен:слеплена; из более крутого теста.$B$BНе так давно одна из ваших жриц искала тебя – говорила, чтобы ты $Gнашел:нашла; жрицу Алатею в Храме Луны в Дарнасе. Должно быть, это важно так что возвращайся туда как можно скорее.', },
    {'Поговорите в Дарнасе со жрицей Алатеей.', },
  },
  [5631] = {
    'Возвращение домой',
    {'Недавно в наших чертогах побывала одна жрица, и она искала вас, $Gюный:юная:r; |3-6($R). Она сказала, что ее послали за вами и что вы должны вернуться в Храм Луны в Дарнасе как можно скорее. По прибытии найдите жрицу – она расскажет вам больше.$B$BДа пребудет с вами Свет, $N.', },
    {'Поговорите в Дарнасе со жрицей Алатеей.', },
  },
  [5632] = {
    'Возвращение домой',
    {'Да-да, конечно. Здравствуй, $N. Недавно тут побывала посланница нашего ордена. Она искала тебя. Она мельком упомянула, что в Дарнасе случилось что-то важное и что тебе следует как можно скорее вернуться в Храм Луны. Сразу поговори там со жрицей Алатеей. Наверняка это касается твоих успехов.$B$BВ общем, возвращайся в Дарнас, как только сможешь.', },
    {'Поговорите в Дарнасе со жрицей Алатеей.', },
  },
  [5633] = {
    'Возвращение домой',
    {'Добро пожаловать, $Gдруг:подруга;. Приятно видеть тебя в наших чертогах. Да пребудет благость с тобой и твоим народом.$B$BПока ты тут $Gстранствовал:странствовала; в наших краях, тебя разыскивала здесь жрица из вашего храма. Она сказала, что тебе надо как можно скорее возвращаться в Храм Луны. Прости, большего не скажу, но мне показалось, что дело важное. Когда доберешься до Храма, поговори со жрицей Алатеей.', },
    {'Поговорите в Дарнасе со жрицей Алатеей.', },
  },
  [5634] = {
    'Молитва отчаяния',
    {'$N! Тебя ждут в Штормграде! Верховная жрица Лорена говорит, что тебе пора доказать свою способность перейти на новый уровень подготовки. Не мешкай. Ты найдешь Лорену в Соборе Света в центре города. Передай ей от меня привет и постарайся не завалить экзамен. Да хранит тебя Свет!', },
    {'Поговорите с Верховной жрицей Лореной в Штормграде.', },
  },
  [5635] = {
    'Молитва отчаяния',
    {'$N, тобой заинтересовались наши высшие иерархи. Они желают твоего скорейшего возвращения в Собор Света Штормграда. Вероятно, они считают, что для тебя наступило время нового обучения. Когда доберешься до собора, обратись прямо к верховной жрице Лорене, она расскажет тебе об остальном. Удачи! Да хранит тебя Свет!', },
    {'Поговорите с Верховной жрицей Лореной в Штормграде.', },
  },
  [5636] = {
    'Молитва отчаяния',
    {'Да благословит тебя Элуна, |3-6($R)! Надеюсь, что Свет не покидает тебя.$B$BТебя недавно разыскивал один из наших жрецов. Он просил передать тебе, что в Соборе Света Штормграда тебя ожидает Верховная жрица Лорена. Не трать времени даром, твой народ нуждается в тебе.', },
    {'Поговорите с Верховной жрицей Лореной в Штормграде.', },
  },
  [5637] = {
    'Молитва отчаяния',
    {'$N, тебе пора отправляться в Штормград. Верховная жрица Лорена ожидает тебя в Соборе Света, она хочет поговорить с тобой. Должно случиться что-то особенное, если тобой заинтересовалась сама Верховная жрица. Не знаю, к добру это или к худу. Думаю, что так или иначе все это окажется полезным для тебя.$B$BНе трать много времени, собирайся быстрее.', },
    {'Поговорите с Верховной жрицей Лореной в Штормграде.', },
  },
  [5638] = {
    'Молитва отчаяния',
    {'Эй, |3-6($R)! Тебя недавно искал один из ваших жрецов! Он просил тебе передать, что тебя очень ждут в Соборе Света. Еще сказал, что это очень важно, и мне кажется, что ему стоит верить. Он был настроен весьма благодушно, так что для тебя это хорошие новости. Тебе стоит поторопиться, чтобы не упустить этот шанс.$B$BДа хранит тебя Элуна, |3-6($R).', },
    {'Поговорите с Верховной жрицей Лореной в Штормграде.', },
  },
  [5639] = {
    'Молитва отчаяния',
    {'$N! Тебя ждут в Штормграде! Верховная жрица Лорена говорит, что тебе пора доказать свою способность перейти на новый уровень подготовки. Не мешкай. Ты найдешь Лорену в Соборе Света в центре города. Передай ей от меня привет и постарайся не завалить экзамен. Да хранит тебя Свет!', },
    {'Поговорите с Верховной жрицей Лореной в Штормграде.', },
  },
  [5641] = {
    'Бесстрашие',
    {'$N, ваш верховный жрец Рохан назначает вам аудиенцию в Стальгорне. Торопитесь, $Gмогучий мой друг:могучая моя госпожа;, ибо близится срок вашего очередного испытания. Хотя я хотел бы стать свидетелем особых обрядов вашей расы, но мое место здесь. Но да пребудет с вами Свет в пути вашем. Когда вы достигнете города, Рохан будет ждать вас в Палатах Магии.', },
    {'Поговорите с верховным жрецом Роханом в Стальгорне.', },
  },
  [5642] = {
    'Заслон от темной магии',
    {'В Оргриммаре происходит нечто важное, $N.$B$BОбычно посланцы, которые приезжают сюда за троллями-жрецами, держатся с ленцой. А на этот раз они, похоже, спешили! Не засиживайся в Громовом Утесе. Думаю, тебе стоит как можно скорее вернуться в Аллею Духов.', },
    {'Поговорите с Ур\'кьо в Оргриммаре.', },
  },
  [5643] = {
    'Заслон от темной магии',
    {'Как и многие из наших жрецов, члены твоего племени проходят обряды инициации. Эти обряды часто наделяют жрецов особыми способностями, которые помогают им выжить. Я чувствую, что тебе пришло время пройти такой обряд. Возвращайся в Аллею Духов в Оргриммаре и поговори с Ур\'кьо. То, чему он тебя научит, может оказаться чрезвычайно важным в будущем.', },
    {'Поговорите с Ур\'кьо в Оргриммаре.', },
  },
  [5644] = {
    'Всепожирающая чума',
    {'Доброго тебе дня, $N. Вижу, ты многому $Gнаучился:научилась;, и пришло время вернуться к Эльталисте. Есть вещи, рассказать о которых может лишь она, и то лишь самым достойным.$B$BЯ не скажу, что тебя ждет – пусть для тебя это будет сюрпризом. Хотя, помнится, когда я был молодым жрецом и только-только понял, что есть истинная сила... божественная сила... Вот это были деньки.$B$BОтправляйся в Подгород, поговори с Эльталисте, $N. Она ждет тебя в Квартале Воинов.', },
    {'Поговорите с Эльталисте в Подгороде.', },
  },
  [5645] = {
    'Бесстрашие',
    {'$N, ваш верховный жрец Рохан назначает вам аудиенцию в Стальгорне. Торопитесь, $Gмогучий мой друг:могучая моя госпожа;, ибо близится срок вашего очередного испытания. Хотя я хотел бы стать свидетелем особых обрядов вашей расы, но мое место здесь. Но да пребудет с вами Свет в пути вашем. Когда вы достигнете города, Рохан будет ждать вас в Палатах Магии.', },
    {'Поговорите с верховным жрецом Роханом в Стальгорне.', },
  },
  [5646] = {
    'Всепожирающая чума',
    {'Эльталисте оказала моему народу большое доверие, посылая ко мне учеников, а теперь я сделаю то же для ее племени.$B$BВозвращайся в Подгород, $N. Твоя госпожа ждет тебя в Квартале Воинов, чтобы рассказать еще больше о твоем народе и его обычаях. Ступай, не медли.', },
    {'Поговорите с Эльталисте в Подгороде.', },
  },
  [5647] = {
    'Бесстрашие',
    {'Твоя раса так сильна, $N. Мы гордимся союзом с вами. С каждым закатом ты и твои сородичи не только все ближе к разгадке вашего истинного происхождения, но и обретаете все большую уверенность в том, что вы на верном пути во многом. Именно эта уверенность приносит вам победу.$B$BНедавно из Стальгорна пришла весть. Тебя просят вернуться и поговорить с верховным жрецом Роханом в Палатах Магии. Не стоит заставлять его ждать. Да направит твой путь Элуна.', },
    {'Поговорите с верховным жрецом Роханом в Стальгорне.', },
  },
  [5648] = {
    'Одеяния духовности',
    {'Что ж, $N, вот ты и $Gготов:готова; к своему первому испытанию. К югу от Колючего Холма, сразу за городской чертой, лежит раненная орчиха из нашего отряда. Она сражалась с остатками прежнего Праудмурова войска, и ей не повезло в бою. Помочь ей может только кто-то из нас. Ее зовут Корджа. Найди ее, исцели и дай ей благословение наших предков.$B$BСделай это – и я сочту, что ты $Gподтвердил:подтвердила; свою готовность ступить на путь жреца.', },
    {'Найдите рубаку Кор\'жу и исцелите ее раны, пользуясь Малым исцелением (уровень 2). Затем скажите ей Слово силы: Стойкость – и вернитесь к Тай\'джин в Колючий Холм.', },
  },
  [5649] = {
    'В пользу духовности',
    {'Понимаешь, наш народ присоединился к Орде, потому что они к нам добры. Они – преданные друзья, они многому нас научили, внушили нам представление о чести... Но наши старые обычаи еще живы. Орки не одобряют многие из них, но никто не мешает нам придерживаться их втайне.$B$BТы это знаешь. Вот почему я посылаю тебя в Колючий Холм, к Тай\'джин. Она откроет тебе мудрость нашего племени. Ступай к ней, когда будешь $Gготов:готова;.', },
    {'Поговорите с Тай\'джин в Дуротаре.', },
  },
  [5650] = {
    'Одеяния Тьмы',
    {'Мы черпаем силу из тьмы и теней, $N, но ваше дело – не только дело тьмы. Вы поняли, что большая заслуга есть и в изучении искусства лечения. Это мудрый выбор. Но прежде чем познать тьму, вы должны познать и свет, а для этого нужно пройти испытание.$B$BК северу от кладбища находится раненый страж смерти по имени Кел – он уже очень долго защищает нас от гноллов, и ему требуется лечение. Но для того чтобы он мог и дальше исполнять свой долг, вы должны также придать ему сил. Используйте для этого свои заклинания, а потом возвращайтесь ко мне.', },
    {'Найдите стража смерти Кела и вылечите его раны заклинанием "Малое Исцеление" (ранг 2). После этого прочтите над ним "Слово Силы: Стойкость", а затем вернитесь к темному священнику Берилу в Брилл.', },
  },
  [5651] = {
    'Во славу Тьмы',
    {'Отрекшиеся извлекли много уроков из своего опыта и поняли такие вещи, о которых остальные только начинают догадываться. И несмотря на то что наш путь лежит во тьме, скоро вы поймете, что очень много вещей зависит от нее – так же как и от света. Вы научитесь использовать и то и другое... с должной осторожностью. Но никогда не забывайте того, что нас создали в тлене и бросили гнить, как какие-нибудь отбросы. Тьма – это наш дом.$B$BКогда будете готовы узнать больше, разыщите темного священника Берила в Брилле.', },
    {'Поговорите с темным священником Берилом в Тирисфальских лесах.', },
  },
  [5652] = {
    'Обессиливающий сглаз',
    nil,
    {'Поговорите с Ур\'кьо в Оргриммаре.', },
  },
  [5654] = {
    'Обессиливающий сглаз',
    {'Пора тебе встретиться с Ур\'кьо, $N. Ты найдешь его в Аллее Духов, в городе орков. Ступай туда и поговори с ним. Пора тебе познать древнюю мудрость нашего народа. Пора вступить в круг и стать настоящим жрецом нашего племени.', },
    {'Поговорите с Ур\'кьо в Оргриммаре.', },
  },
  [5655] = {
    'Обессиливающий сглаз',
    {'Мне нужно было удостовериться, что ты не уйдешь слишком далеко от дома, $N. Меня послали за тобой потому, что Ур\'кьо, величайший из наших жрецов, желает поговорить с тобой. Ступай туда. Ступай в Аллею Духов в городе орков и поговори с Ур\'кьо. Он ждет тебя.', },
    {'Поговорите с Ур\'кьо в Оргриммаре.', },
  },
  [5656] = {
    'Обессиливающий сглаз',
    {'Мое нестареющее сердце всегда греет мысль о том, что по миру бродит множество жрецов всех культур. Хорошо знать, что на свете есть сила, не имеющая отношения к тайной магии и вполне доступная нам. Вот почему я сочту за честь указать тебе путь, $N.$B$BУркьо, ваш великий жрец в Оргриммаре, просит тебя прибыть к нему. Ты найдешь его в Аллее Духов.', },
    {'Поговорите с Ур\'кьо в Оргриммаре.', },
  },
  [5657] = {
    'Обессиливающий сглаз',
    {'Домой! Возвращайся домой, $Gюный:юная:r; |3-6($R)!$B$BТвой духовный наставник Ур\'кьо просит тебя вернуться к нему и поговорить об избранном тобой пути.$B$BСтупай в Аллею Духов в Оргриммаре, великом городе Орды! Ты найдешь его там.', },
    {'Поговорите с Ур\'кьо в Оргриммаре.', },
  },
  [5658] = {
    'Прикосновение слабости',
    {'Далеко же ты $Gзабрался:забралась:c;, |3-6($C). Тебя ждут в большом городе под землей. Твоя госпожа, Эльталисте, велит тебе вернуться. Как и в моем племени, есть вещи, которым тебя может научить лишь она. И не задерживайся попусту. Лучше лети в Квартал Воинов на самом быстром дирижабле.', },
    {'Поговорите с Эльталисте в Подгороде.', },
  },
  [5659] = {
    'Прикосновение слабости',
    {'Тебе здесь не место, $N. Особенно сейчас, когда тебя ждут в Квартале Воинов. Эльталисте созывает всех своих жрецов и жриц. Пришло время поведать им о том, какой мощью они обладают.$B$BОна не переносит опозданий и пустых оправданий, поэтому советую тебе тронуться в путь.', },
    {'Поговорите с Эльталисте в Подгороде.', },
  },
  [5660] = {
    'Прикосновение слабости',
    {'Далеко же ты $Gзабрался:забралась:c;, |3-6($C). Тебя ждут в большом городе под землей. Твоя госпожа, Эльталисте, велит тебе вернуться. Как и в моем племени, есть вещи, которым тебя может научить лишь она. И не задерживайся попусту. Лучше лети в Квартал Воинов на самом быстром дирижабле.', },
    {'Поговорите с Эльталисте в Подгороде.', },
  },
  [5661] = {
    'Прикосновение слабости',
    {'Ты всего лишь гость в Деревне Кровавого Копыта, $N, а дом твой далеко. Меня послали сюда искать жрецов из моего племени. С ними хотят говорить вожди. Но и тебе я могу помочь.$B$BГоворят, что ваша главная жрица велит всем Отрекшимся прийти к ней – видно, ей есть что сказать.$B$BСтупай, поговори с Эльталисте в Квартале Воинов этого вашего города, как его там...', },
    {'Поговорите с Эльталисте в Подгороде.', },
  },
  [5662] = {
    'Прикосновение слабости',
    {'Мое почтение тебе и твоему народу, $N. Обычно я учу лишь своих соплеменников. Тебе же передала знания Эльталисте из города, некогда звавшегося Лордероном.$B$BВозвращайся к ней, ведь только она может передать тебе способности, отличающие воинов вашей веры от других.$B$BСтупай в Квартал Воинов Подгорода и отыщи там свою госпожу.', },
    {'Поговорите с Эльталисте в Подгороде.', },
  },
  [5663] = {
    'Прикосновение слабости',
    {'Эльталисте послала вести всем своим жрецам. Нам велено отправить молодежь в Подгород на обучение. Если ты в самом деле считаешь себя $Gодним:одной; из верных, возвращайся туда.$B$BЭльталисте живет в Квартале Воинов в глубинах Подгорода.', },
    {'Поговорите с Эльталисте в Подгороде.', },
  },
  [5672] = {
    'Благодать Элуны',
    nil,
    {'Поговорите в Дарнасе со жрицей Алатеей.', },
  },
  [5673] = {
    'Благодать Элуны',
    {'Знай, что здесь ты можешь в любое время обратиться к любому наставнику, $N. Впрочем, есть знания, которые мы не можем тебе предоставить. Среди них – учение вашей богини Элуны. Верным своим приверженцам она дает особые способности, и, если я не ошибаюсь, тебе пора вернуться домой, в Храм Луны, и пройти испытание.$B$BТак что как сможешь – возвращайся и поговори со жрицей Алатеей.', },
    {'Поговорите в Дарнасе со жрицей Алатеей.', },
  },
  [5674] = {
    'Благодать Элуны',
    {'Настал славный день, $N. Ты $Gдостиг:достигла; возраста, когда наши $Gжрецы:жрицы; изучают те способности, которые свойственны только ночным эльфам. Тебе следует вернуться в Храм Луны и поговорить со жрицей Алатеей, как только закончишь тут свои дела. Она испытает тебя, убедится в твоей готовности и лишь потом научит тебя Благодати Элуны.', },
    {'Поговорите в Дарнасе со жрицей Алатеей.', },
  },
  [5675] = {
    'Благодать Элуны',
    {'Не тяни время, $Gдруг:подруга;. Пора тебе возвращаться домой в Дарнас. Жрица Алатея велит возвращаться на обучение. Похоже, тебя решили допустить к испытаниям вроде тех, что проходят служители Света. Так что как сможешь – иди. Алатея в своем письме говорит, что будет ждать тебя в Храме Луны. Удачи.', },
    {'Поговорите в Дарнасе со жрицей Алатеей.', },
  },
  [5676] = {
    'Волшебное искажение',
    {'Тебе пора возвращаться в Штормград. Старшие иерархи призывают тебя продолжить тренировки. Жаль, что мне пришлось разыскивать тебя, но я делаю это во имя союза между нашими расами.$B$BБуду говорить откровенно, тайная магия не внушает мне никакого доверия, хоть по благословлению богов, хоть без них...$B$BТы понимаешь, о чем я говорю? Вернись в Собор Света. Верховная жрица Лорена ждет тебя.', },
    {'Поговорите с Верховной жрицей Лореной в Штормграде.', },
  },
  [5677] = {
    'Волшебное искажение',
    {'Мне бы хотелось показать тебе кое-какие из наших особых методик обучения, которые направят тебя по пути Света, но меня опередили. Тебе нужно вернуться в Штормград, $N... Прямо сейчас. Поверь мне, так будет лучше. Когда ты встретишься с Верховной жрицей Лореной из Собора Света, ты поймешь, о чем я говорю. Для вашей расы настало время подумать о будущем и выбрать свой собственный путь.', },
    {'Поговорите с Верховной жрицей Лореной в Штормграде.', },
  },
  [5679] = {
    'Всепожирающая чума',
    {'Эльталисте оказала моему народу большое доверие, посылая ко мне учеников, а теперь я сделаю то же для ее племени.$B$BВозвращайся в Подгород, $N. Твоя госпожа ждет тебя в Квартале Воинов, чтобы рассказать еще больше о твоем народе и его обычаях. Ступай, не медли.', },
    {'Поговорите с Эльталисте в Подгороде.', },
  },
  [5680] = {
    'Заслон от темной магии',
    {'Как и многие из наших жрецов, члены твоего племени проходят обряды инициации. Эти обряды часто наделяют жрецов особыми способностями, которые помогают им выжить. Я чувствую, что тебе пришло время пройти такой обряд. Возвращайся в Аллею Духов в Оргриммаре и поговори с Ур\'кьо. То, чему он тебя научит, может оказаться чрезвычайно важным в будущем.', },
    nil,
  },
  [5713] = {
    'Каждый выстрел – в цель',
    {'Я направлялась в Аубердин с важным донесением, когда меня атаковал фурболг Марош и его следопыты. Во время атаки я получила отравление, так что еле держусь. Я могу изготовить противоядие, но это займет некоторое время.$B$BХотела попросить вас защитить меня, пока я не приму противоядие. Я подсоблю вам своим луком, как смогу.$B$BЕсли мы выживем, то смогу доставить донесение в Аубердин, а вас попрошу поговорить с Онейей на Заставе Мейстры и проинформировать ее о случившемся.', },
    {'Защитите жизнь часового Айнаши, а потом поговорите с Ойнеей на Заставе Мейстры в Ясеневом лесу.', },
  },
  [5721] = {
    'Битва при Дарроушире',
    {'Теперь, $N, тебе предстоит принять участие в битве при Дарроушире и спасти Джозефа Редпата.$B$BПоложи реликвии на городскую площадь Дарроушира, и духи его жителей восстанут из могил. Начнется битва, и ты $Gдолжен:должна; сделать все возможное, чтобы Дейвил выжил в битве с Хоргусом, а Редпат должен подвергнуться порче и после этого быть убитым.$B$BВ этом случае дух Редпата будет спасен. Поговори с ним и возвращайся к его дочери Памеле.', },
    {'Положите сверток с реликвиями на городской площади Дарроушира.$B$BОберегайте Дейвила Яркосвета, пока не погибнет Хоргус.$B$BНе допустите смерти капитана Редпата до появления Редпата Оскверненного. $B$BУбейте Редпата Оскверненного. $B$BПоговорите с духом Джозефа Редпата, когда он вновь появится на городской площади, затем отправляйтесь к Памеле и поговорите с ней.', },
  },
  [5722] = {
    'В поисках потерянного ранца',
    {'В Огненной пропасти Магату интересуют не только трогги. До нее дошли слухи, что один из ее слуг, некий Маур Зловещий Тотем, будучи в пропасти, нашел нечто особенное, но выбраться оттуда ему не удалось. Она хочет, чтобы кто-нибудь нашел тело Маура – если оно еще цело – и выяснил, что же он такое нашел, пытаясь вести переговоры с троггами.$B$BЕсли найдешь что-нибудь интересное, принеси это Магате, и она наверняка не поскупится на награду.$B$BДа, пропасть ищи в самых темных закоулках Оргриммара.', },
    {'Обыщите Огненную пропасть в поисках тела Маура Зловещего Тотема и найдите все необычные предметы.', },
  },
  [5723] = {
    'Испытание силы врага',
    {'Назойливые твари, известные как трогги, вдруг полезли из заполненных лавой туннелей под Оргриммаром. Магата по своей доброте душевной хотела договориться с этими тварями, но они набросились на ее переговорщиков и перебили их. Но такого обращения с тауренами она допустить не может, так что теперь для нее эти твари – враги Орды.$B$BОна просит тебя разделаться с этими троггами, прежде чем они начнут угрожать Орде из-под земли. Найди Огненную пропасть и уничтожь их всех!', },
    {'Найдите в Оргриммаре Огненную пропасть, убейте 8 троггов и 8 шаманов Огненной пропасти и возвращайтесь к Рахауро в Громовой Утес.', },
  },
  [5724] = {
    'Возвращение потерянной сумки',
    {'Под телом погибшего таурена вы находите сумку. Убитый до сих пор крепко держит ее – видимо, он пытался уберечь ее во время нападения троггов.$B$BВысвободив сумку из его крепкой хватки, вы закидываете ее себе на плечо и готовитесь возвращаться наружу из туннелей, чтобы отправиться к Рахауро в Громовой Утес.', },
    {'Отнесите сумку Маура Зловещего Тотема Рахауро в Громовой Утес.', },
  },
  [5725] = {
    'Силы разрушения...',
    {'Тебе хорошо известно, что в Легионе принято всячески одаривать идейных последователей. Некоторым из этих даров лучше бы оказаться в руках у Темной Госпожи, чем у... простецов.$B$BНапример, в пещерах под Оргриммаром скрывается секта Совета Теней, известная как "Пламенеющий Клинок". Госпожа сообщила, что они получили две могущественные книги заклинаний – она хотела бы "позаимствовать" их. Выполни ее волю.', },
    {'Принесите книги "Заклинания Тьмы" и "Заклятия Пустоты" Вариматасу в Подгород.', },
  },
  [5726] = {
    'Тайные враги',
    {'Чего я не потерплю, $N, так это предательства среди своих. Однако было бы глупо действовать напролом и сразу арестовать единственного известного мне предателя – это не очистит наши земли от порчи, наоборот, зараза расползется еще сильнее.$B$BНо ты, $Gюный искатель приключений:юная искательница приключений;, можешь проникнуть туда, куда моим агентам ходу нет. Ты можешь узнать правду... можешь обнаружить настоящего главу заговора!$B$BЕсли у тебя достаточно отваги, отправляйся к скале Черепа к востоку от Оргриммара, сорви с одного из чернокнижников Пылающего Клинка лейтенантский знак отличия и принеси его мне.', },
    {'Принесите лейтенантский знак отличия Траллу в Оргриммар.', },
  },
  [5727] = {
    'Тайные враги',
    {'Ну-ка, посмотрим, стоил ли принесенный тобой знак отличия таких усилий.$B$BВ городе живет чернокнижник, который уверен, будто я ему полностью доверяю. Он и не подозревает, что мне известно о его предательстве. На самом деле он – предводитель Пылающего Клинка... Однако не торопись вступать с ним в бой. Он нам еще нужен, мы можем использовать его, чтобы навредить нашим врагам.$B$BОтнеси ему этот знак отличия в расселину Теней в Оргриммаре, поговори с ним и проверь, примет ли он тебя за $Gодного:одну; из своих. А потом возвращайся ко мне.', },
    {'Отнесите лейтенантский знак отличия Ниру Огненному Клинку. Убедите его в своей преданности Пылающему Клинку. Вернитесь к Траллу в Оргриммар.', },
  },
  [5728] = {
    'Тайные враги',
    {'Хм, предводители Пламенеющего Клинка... Это серьезно. Если Ниру ими дорожит, значит, именно их и нужно ликвидировать. Этот сатир... Баззалан, и тот, второй, которого упоминал Ниру, – он кто, чернокнижник? – их следует убить.$B$BВозвращайся в расселину Теней и проникни в Огненную пропасть, $N. Разыщи этих предводителей Пламенеющего Клинка и убей их. Но будь $Gосторожен:осторожна;, чтобы Ниру не узнал, что это твоих рук дело. Пусть по-прежнему считает тебя $Gодним из братьев:одной из сестер; по оружию.', },
    {'Убейте Баззалана и Жергоша Призывателя Духов и вернитесь в Оргриммар к Траллу.', },
  },
  [5729] = {
    'Тайные враги',
    {'Думаю, теперь тебе следует попытаться войти в число приближенных Ниру. Если он действительно так взбудоражен, как мне доносят, мы убьем сразу двух зайцев. Во-первых, тебе, возможно, удастся разузнать побольше о Совете или, по крайней мере, о Пылающем Клинке, и, во-вторых, он, возможно, начнет доверять тебе настолько, что попросит ему помогать. Ему сейчас отчаянно не хватает помощников.$B$BВозвращайся в Расселину и поговори с ним, но не особо навязывайся.', },
    {'Поговорите с Ниру Огненным Клинком в Оргриммаре.', },
  },
  [5730] = {
    'Тайные враги',
    {'Теперь ты $Gодин:одна; из моих лейтенантов! Готовься, |3-6($C). Скоро я призову тебя.$B$BМожно считать, что с Пламенеющим Клинком я зря потратил время, но это не значит, что другие планы Совета Теней должны пострадать. Я сделаю все, что смогу, чтобы свести к минимуму потери здесь, в Огненной пропасти. А пока что распоряжусь, чтобы мои агенты в Степях и Ясеневом лесу начали выполнять наши последние замыслы. Возвращайся ко мне как можно скорее.', },
    {'Поговорите с Траллом в Оргриммаре.', },
  },
  [5741] = {
    'Скипетр Света',
    {'Говорят, что Пылающие Клинки, обосновавшиеся на востоке в Крепости Громового Топора, заполучили Скипетр Света, добыв его у нагов. Легенда гласит, что этот скипетр обладает властью погружать целые города в пучины моря. Мы не можем позволить, чтобы такая сила оставалась в их руках.$B$BТвой долг – вырвать скипетр из недостойных рук! Сейчас он у ясновидца клана Пылающего Клинка на одной из башен Крепости Громового Топора.', },
    {'Отыщите Скипетр Света и принесите его Азору Альдаморту в башню Этель-Ретор.', },
  },
  [5742] = {
    'Искупление',
    {'Ты славно $Gпоработал, друг мой:поработала, подруга;. Отдохни, а я пока расскажу тебе мою историю до конца.', },
    {'Выслушайте Тириона Фордринга.', },
  },
  [5761] = {
    'Убить тварь',
    {'Главная задача, которую поручил мне наш великий вождь, – это извести тех тварей, которые повинны в том, что в великий город нашего владыки проникают демоны. Клан Пылающего Клинка – главная, но не единственная угроза. Есть еще и Пламенеющий Клинок, клан, который обосновался в Огненной пропасти и втайне смущает умы простых представителей Орды.$B$BЧтобы их остановить, нужно убить их предводителя, стража Скверны по имени Тарагаман Ненасытный.$B$BУбей его. Я уверен, Вождь будет рад получить его сердце...', },
    {'Спуститесь в Огненную пропасть, убейте Тарагамана Ненасытного и принесите его сердце Ниру Огненному Клинку в Оргриммаре.', },
  },
  [5762] = {
    nil,
    {'У меня тут посылка для нового покупателя по имени Хеминг Эрнестуэй-младший. Посылка шла очень долго, и Хеминг давно уехал, не дождавшись ее. Говорят, что теперь он где-то в Тернистой долине, охотится там на зверей вместо отца. Перед отъездом он оставил мне немного денег, чтобы я переслал ему посылку.$B$BОн очень богатый дворф, а с такими полезно водить дружбу, правда? Так что... не желаете ли доставить Хемингу эту посылку?$B$BГоворят, он разбил лагерь в Тернистой долине, к северу от Гром\'гола.', },
    {'Отнесите ящик Крейвела Хемингу Эрнестуэю младшему в Тернистую долину.', },
  },
  [5763] = {
    'Охота в Тернистой долине',
    {'Давным-давно пришел в эти земли дворф. Звали его Хеминг, и он хотел поохотиться на крупного зверя. С ружьем он умел обращаться просто великолепно, и много дней мы охотились с ним вместе, забыв даже о вражде, разделявшей наши народы…$B$BКогда он уходил в Тернистую долину, я поклялся, что, когда бы он ни вернулся, я встречу его как дорогого гостя. Ныне он отправился охотиться в неведомые земли, но успел воспитать сына, достойного отцовской славы.$B$BОтнеси этот рог кодо в подарок Хемингу-младшему. Ты найдешь его в Тернистой долине, к северу от лагеря Гром\'гол.', },
    {'Отнесите рог кодо, принадлежащий Руну, Хемингу Эрнестуэю младшему в Тернистую долину.', },
  },
  [5781] = {
    'Символ ушедших дней',
    {'Чтобы помочь Таэлану вернуться на путь истинный, мы должны найти то, что напомнит ему о прошлом.$B$BОдин из таких предметов – игрушка, которую я подарил ему на семилетие. Он очень дорожил этим маленьким боевым молотом, уменьшенной копией моего собственного оружия.$B$BКогда меня изгнали за предательство, его мать сказала сыну, что я погиб. Его отвели к моей пустой могиле в Крипте, и там он похоронил свой молот вместе со всеми воспоминаниями обо мне.$B$BПрошу тебя, отправляйся в Крипту и принеси молот Таэлана.', },
    {'Отправляйтесь в Крипту – в самую южную часть Восточных Чумных земель – и добудьте молот Таэлана. Отнесите его Тириону Фордрингу.', },
  },
  [5801] = {
    'Кузня Пламенного хребта',
    {'Чтобы выковать основу ключа тебе понадобится два ториевых слитка и мощный источник тепла. Торий добывай самостоятельно, а где найти подходящую плавильню, я подскажу.$B$BНа западе, в кратере Ун\'Горо, есть вулкан Огненного Венца – действующий. Лава из его жерла сплавит торий и кость воедино. Твои денежки потрачены не зря – эту форму для отливки можно просто-напросто окунуть в огненное озеро, а остальное сделает лава.$B$BВот и все, чем я могу тебе помочь. Как доделать ключ тебе подскажет Арбингтон.', },
    {'Отнесите форму для отливки отмычек и 2 ториевых слитка на вершину Вулкана Огненного Венца в Кратере Ун\'Горо. Окуните форму для отливки отмычки в озеро лавы. Получится недоделанная отмычка.$B$BОтнесите недоделанную отмычку алхимику Арбингтону на Берег Промозглого Ветра в Западные Чумные земли.', },
  },
  [5802] = {
    'Кузня Пламенного хребта',
    {'Чтобы выковать основу ключа тебе понадобится два ториевых слитка и мощный источник тепла. Торий добывай самостоятельно, а где найти подходящую плавильню, я подскажу.$B$BНа западе от Кратера Ун\'Горо располагается Вулкан Огненного Венца – действующий вулкан. Лава из его жерла сплавит торий и кость воедино. Твои денежки потрачены не зря – эту форму для отливки можно просто-напросто окунуть в огненное озеро, а остальное сделает лава.$B$BВот и все, чем я могу тебе помочь. Как доделать ключ тебе подскажет Дитерс.', },
    {'Отнесите форму для отливки отмычек и 2 ториевых слитка на вершину Вулкана Огненного Венца в Кратере Ун\'Горо. Окуните форму для отливки отмычки в озеро лавы. Получится недоделанная отмычка.$B$BОтнесите недоделанную отмычку аптекарю Дитерсу в Бастион, что в Западных Чумных землях.', },
  },
  [5803] = {
    'Скарабей Аража',
    {'Араж Призыватель правит Андоралом из самого сердца разрушенного города. Мы знаем, что он был главой Некроситета, пока не захватил целый город. Для завершения работы над ключом нам нужен его перстень – он называется скарабеем – из него мы создадим головку ключа.$B$BПроберись в сердце Андорала и лиши его власти над городом, теперь, когда с ним можно сражаться в открытую. Убив мерзавца, забери скарабея!$B$BКлюч от Некроситета ждет тебя, $N!', },
    {'Убейте Аража Призывателя отнесите его скарабея алхимику Арбингтону на Берег Промозглого Ветра, что в Западных Чумных землях.', },
  },
  [5804] = {
    'Скарабей Аража',
    {'Араж Призыватель правит Андоралом из самого сердца разрушенного города. Мы знаем, что он был главой Некроситета, пока не захватил целый город. Для завершения работы над ключом нам нужен его перстень – он называется скарабеем – из него мы создадим головку ключа.$B$BПроберись в сердце Андорала и лиши его власти над городом, теперь, когда с ним можно сражаться в открытую. Убив мерзавца, забери скарабея!$B$BКлюч от Некроситета ждет тебя, $N!', },
    {'Убейте Аража Призывателя и отнесите его скарабея аптекарю Дитерсу в Бастион, что в Западных Чумных землях.', },
  },
  [5805] = {
    'Добро пожаловать!',
    {'Добро пожаловать в "World of Warcraft"!$B$BВ знак особой благодарности за приобретение коллекционного издания "World of Warcraft" примите этот подарочный сертификат и передайте Мериссе Стилвелл в Златоземье. Вы получите подарок – маленького спутника, который будет сопровождать вас на пути к приключениям и славе.$B$BЕще раз благодарим вас и желаем приятного пребывания в "World of Warcraft"!', },
    {'Передайте подарочный сертификат Златоземья Мериссе Стилвелл.', },
  },
  [5821] = {
    'Наемный телохранитель',
    {'Обычно мы с Риггером проводим кодо мимо поселения кентавров из племени Колкар. Боевые барабаны кентавров слышно издалека, так что, едва заслышав их, мы останавливаемся, чтобы переждать опасность. Мертвый торговец, знаешь ли, плохой торговец!$B$BВот что я тебе скажу, |3-6($C): если ты будешь охранять нас по дороге мимо деревни Колкар, мы в долгу не останемся.$B$BРасстояние-то небольшое, да и делать почти ничего не придется – только проследить за тем, чтобы караван был цел. А награду ты получишь, не сомневайся, обращайся к Смиду Заржавеню, мы с ним обделываем кое-какие делишки. Он живет чуть дальше по дороге.', },
    {'Охраняйте караван Гизлтона на пути мимо деревни кентавров из племени Колкар. О награде поговорите со Смидом в Лагере Заржавня.', },
  },
  [5841] = {
    'Добро пожаловать!',
    {'Добро пожаловать в World of Warcraft!$B$BВ знак особой благодарности за приобретение коллекционного издания World of Warcraft примите этот подарочный сертификат и передайте его Йори Шлемолому в Караносе. Вы получите подарок – маленького спутника, который будет сопровождать вас на пути к приключениям и славе.$B$BЕще раз благодарим вас и желаем приятного пребывания в World of Warcraft!', },
    {'Принести Йори Шлемолому подарочный сертификат Караноса.', },
  },
  [5842] = {
    'Добро пожаловать!',
    {'Добро пожаловать в World of Warcraft!$B$BВ качестве особой благодарности за покупку коллекционного издания World of Warcraft примите этот подарочный сертификат и передайте его Орентилу Шепоту Ветра в Доланааре. Вас ждет подарок – маленький спутник, который будет сопровождать вас на пути к приключениям и славе. $B$BЕще раз благодарим вас и желаем приятного времяпрепровождения в World of Warcraft!', },
    {'Отнесите подарочный серитификат Доланаара Орентилу Шепоту Ветра.', },
  },
  [5843] = {
    'Добро пожаловать!',
    {'Добро пожаловать в World of Warcraft!$B$BВ знак особой благодарности за приобретение коллекционного издания World of Warcraft примите этот подарочный сертификат и передайте его Магге в Колючем Холме. Вы получите подарок – маленького спутника, который будет сопровождать вас на пути к приключениям и славе.$B$BЕще раз благодарим вас и желаем приятного пребывания в World of Warcraft!', },
    {'Передайте подарочный сертификат Колючего Холма Магге.', },
  },
  [5844] = {
    'Добро пожаловать!',
    {'Добро пожаловать в World of Warcraft!$B$BВ знак особой благодарности за приобретение коллекционного издания World of Warcraft примите этот подарочный сертификат и передайте его Ворну Небесному Провидцу в деревне Кровавого Копыта. Вы получите подарок – маленького спутника, который будет сопровождать вас на пути к приключениям и славе.$B$BЕще раз благодарим вас и желаем приятного пребывания в World of Warcraft!', },
    {'Отнесите подарочный сертификат деревни Кровавого Копыта Ворну Небесному Провидцу.', },
  },
  [5845] = {
    'Символ утраченной чести',
    {'Со смертью Утера орден Серебряной Длани утратил боевой дух.$B$BМой мальчик держался до последнего. Но, отступая в разоренную деревушку Северного Дола, он принял окончательное решение.$B$BВыжил ли кто из ордена, подумал он, да и какая разница?$B$BС этой мыслью Таэлан бросил знамя Серебряной Длани и отрекся от всего, что прежде было ему дорого. Его честь осталась на обагренной кровью земле Северного Дола.$B$BОтправляйся в Северный Дол и принеси мне этот символ утраченной чести.', },
    {'Отправляйтесь в Северный Дол, что в северо-восточной части Восточных Чумных земель и найдите там символ утраченной чести. После этого вернитесь к Тириону Фордрингу.', },
  },
  [5846] = {
    'Символ семейной любви',
    {'Когда Таэлан был маленьким, мы нередко ездили в Каэр Дарроу. В последний раз художница по имени Ренфри изобразила всю нашу семью на берегу. Это лучшее мое воспоминание о Таэлане и Карандре. Именно тогда, стоя рядом с женой и держа на руках сына, я чувствовал глубокую связь с собственной семьей, которую мне не дано было почувствовать вновь.$B$BЕсли эта картина все еще существует, найди ее.$B$BОтправляйся на разоренный остров Каэр Дарроу и выясни, уцелел ли художник или картина.', },
    {'Отправляйтесь на остров Каэр Дарроу в центральной части Чумных земель и поищите там картину.', },
  },
  [5847] = {
    'Добро пожаловать!',
    {'Добро пожаловать в World of Warcraft!$B$BВ качестве особой благодарности за покупку коллекционного издания World of Warcraft примите этот подарочный сертификат и передайте его Клэр Вилловер в Брилле. Вас ждет подарок – маленький спутник, который будет сопровождать вас на пути к приключениям и славе. $B$BЕще раз благодарим вас и желаем приятного времяпрепровождения в World of Warcraft!', },
    {'Отнесите подарочный сертификат Брилла Клэр Вилловер.', },
  },
  [5848] = {
    'Символ семейной любви',
    {'На стене моей мастерской... В казармах ордена... долгие годы висела картина.$B$BПосле суда над Тирионом я знала, что не смогу держать ее на виду. Я спрятала ее так, чтобы никто не нашел.$B$BОтправляйся в Стратхольм и проберись в место, что ныне зовется Бастионом Алого ордена. Найди там картину с двумя лунами. Сними верхний слой краски, и увидишь мой шедевр – "Символ семейной любви".$B$BДа направит Свет твою руку.', },
    {'Отправляйтесь в Стратхольм в северную часть Чумных земель. Найдите в Бастионе Алого ордена картину "Символ семейной любви", спрятанную за другой, изображающей две луны нашего мира.$B$BОтнесите картину Тириону Фордрингу.', },
  },
  [5861] = {
    'Найти Миранду',
    {'Ты $Gсделал:сделала; все, о чем я тебя просил. Остался один, последний шаг на пути к спасению.$B$BОтнеси все собранные тобой предметы Таэлану. К сожалению, Таэлан и его рыцари Алого ордена нападут на тебя, едва завидев.$B$BЕсть только один способ доставить мое сообщение – приняв обличье врага.$B$BНа юге отсюда находится гробница Утера. Там живет моя старая добрая подруга, Миранда. Найди ее, покажи все собранные тобой предметы, и она тебе поможет.', },
    {'Отправляйтесь к гробнице Утера, которая находится в южных Чумных землях. Покажите Миранде Колдунье подарок Тириона.', },
  },
  [5862] = {
    'Уловка против Алого Ордена',
    {'Я, как говорится, иллюзионистка. Хотя я могу создать иллюзию, благодаря которой ты проберешься в Дольный Очаг, помни – сила моя не безгранична.$B$BЕсли ты забредешь слишком далеко, иллюзия рассеется. Заклинание требует от меня значительного количества сил и сосредоточения, и я не слишком долго смогу поддерживать его.$B$BКогда будешь $Gготов:готова; – обращайся.', },
    {'Поговорите с Мирандой, пусть она наложит на вас Алую иллюзию. Отправляйтесь в Дольный Очаг, покуда она действует, и отнесите подарок Тириона верховному лорду Телану Фордрингу.$B$BДольный Очаг на севере, а Таэлан должен быть внутри Крепости Марденхольд.', },
  },
  [5863] = {
    'Поселение Песчаного Молота',
    {'Совсем недавно в Танарисе появились огры-кочевники. Они захватили большой участок пустыни к югу отсюда и построили целую крепость. Племена поменьше бродят по пустыне, захватывая руины тролльских городов.$B$BПоселение Песчаного Молота должно пасть! Не позволим же ограм превратиться в страшную силу.$B$BИх главаря зовут Гор\'марок Опустошитель. Мне кажется, что если его устранить, это станет началом конца огров.', },
    {'Анди Линн из Прибамбасска просит уничтожить поселение Песчаного Молота, убив 10 громил и 10 мародеров из клана Песчаного Молота и Гор\'марока Опустошителя.', },
  },
  [5881] = {
    'Смена караула',
    {'Орда не дает нам растерять силы, и это правильно! С самой Великой Войны мы здесь, в Калимдоре, живем неплохо.$B$BЯ прошу тебя оказать мне услугу: один парень по имени Гриш Выносливый Гонец охраняет Великий подъемник в южных пустошах. Передай ему это письмо: там говорится, что один из солдат запаса скоро придет ему на смену, чтобы он мог навестить свою семью. Гриш недавно просил об этом, и он будет рад услышать эти новости.', },
    {'Маггран просит вас передать письмо Гришу Выносливому Гонцу у Великого подъемника в Степи.', },
