  [5921] = {
    'Лунная поляна',
    {'На священной земле Лунной поляны рады всем друидам, невзирая на последние разногласия в Круге Кенария между верховным друидом Оленьим Шлемом и хранителем Ремулом – стражем Лунной поляны. Теперь, когда ты $Gвстал:встала; на путь друида-медведя, я научу тебя заклинанию, которое позволит тебе переноситься в Лунную поляну. Пользуйся им, когда тебе нужно знание или уединение Лунной поляны.$B$BВот первый урок: отправляйся в Лунную поляну и поговори с Дендритом Блеском Звезд в Ночной гавани о том пути, по которому ты ныне следуешь. Удачи тебе, $Gюный друид:юная дева-друид;.', },
    {'При помощи заклинания "Телепортация: Лунная поляна" перенеситесь в Лунную поляну. По прибытии поговорите с Дендритом Блеском Звезд в селении Ночная Гавань.', },
  },
  [5922] = {
