  [6661] = {
    'Охота на крыс',
    {'Эй ты, стой, да? Зарра... За... Зарррработать хочешь? У нас тут большая проблема с крысами и не хватает умелых рук.$B$BЧто ты там болтаешь? Все, что от тебя требуется – взять эту флейту крысолова и сыграть подходящую мелодию. Они пойдут за тобой хоть на край света!$B$BНо это лишнее – ты просто поймай пяток и принеси, они нужны нам живыми.$B$BРрррр...', },
    {'Поймайте 5 подземных крыс, используя Флейту крысолова, и принесите их Монти. Не забудьте вернуть флейту владельцу.', },
  },
  [6662] = {
    'Братишка Нипси',
    {'Мой братишка Нипси занимается производством котлет из подземных крыс на том конце подземки. Надо, чтобы ты $Gдоставил:доставила; этот ящик с крысами к нему, пока они не протухли. Так что давай, двигай!$B$BТы ведь $Gездил:ездила; раньше на поезде, да? Это очень просто. Заходи, когда поезд прибудет – и наслаждайся поездкой. Главное, не высовывай руки-ноги, не то на всю жисть калекой останешься.', },
    {'Отвезите Нипси в подземное депо Штормграда коробку таинственного мяса.', },
  },
  [6681] = {
    'Поместье, Черный Ворон',
    {'$N,$B$BЧерный Ворон очень заинтересовался тобой, $Gюный вор:юная воришка;. Мы наблюдали. Тебе не хватает ловкости, но это преходяще.$B$BЕсли ты хочешь стать $Gучеником:ученицей; мастера Черного Ворона и членом Лиги убийц, стоит только принести печать Черного Ворона (приложенную к этому письму) в поместье Черный Ворон.$B$BПервым заданием будет найти его среди холмов Хилсбрада и тем самым доказать свою ловкость.$B$BС уважением,$B$BФахрад.', },
    {'Отнесите печать Черного Ворона Фахраду в поместье Черный Ворон, сокрытое среди холмов Хилсбрада.$B$BНе теряйте бдительности. Поиски поместья – проверка на ловкость.', },
  },
  [6701] = {
    'Эмблемы Синдиката',
    nil,
    nil,
  },
  [6721] = {
    'Путь охотника',
    {'Привет тебе, $Gюный охотник:юная охотница;. Ты кажешься мне достаточно $Gопытным:опытной;, чтобы научиться обращаться с животным и обрести верного друга.$B$BТебе надо повидаться с Дазаларом в Доланааре. Он научит тебя, как обрести собственного питомца и спутника.', },
    {'Поговорите с Дазаларом в Доланааре, что в Тельдрассиле.', },
  },
  [6722] = {
    'Путь охотника',
    {'Привет тебе, $Gюный охотник:юная охотница;. Ты кажешься мне достаточно $Gопытным:опытной;, чтобы научиться обращаться с животным и обрести верного друга.$B$BТебе надо повидаться с Дазаларом в Доланааре. Он научит тебя, как обрести собственного питомца и спутника.', },
    {'Поговорите с Дазаларом в Доланааре, что в Тельдрассиле.', },
  },
  [6741] = {
