  [6761] = {
    'Новое пограничье',
    {'Я поручил своему заместителю, Матренгилу Медвежьей Лапе, разобраться с призывом о помощи из Лунной поляны. Так что по этому поводу говори с ним – НЕ со мной!$B$BИ позволь мне дать тебе один дружеский совет. Это я вывел нас всех из тьмы – я, а вовсе не Круг Кенария Лунной поляны, – когда наша раса потеряла все. Тельдрассил дает нам новую жизнь! Они наверняка попытаются внушить тебе совсем другое, но только глупец поверит их пропаганде!$B$BТы же, вроде, не из таких. Или я ошибаюсь?', },
    {'Поговорите с Матренгилом Медвежьей Лапой в Анклаве Кенария в Дарнасе.', },
  },
  [6762] = {
    nil,
    {'Мажордом хранителя Лунной поляны Ремула – Рабин Сатурна – ищет помощи. Нужно обследовать обширные пустоши к западу от Танариса... и еще дальше, к западу от Ун\'Горо. Найди его в Ночной гавани, главном поселении Лунной поляны.$B$BПоскольку твои труды будут на благо всем нам, советую тебе вести себя осторожно. Между Дарнасом и Лунной поляной трения... и как бы они не переросли со временем в нечто большее.', },
    {'Поговорите с Рабином Сатурной в селении Ночная гавань в Лунной поляне. Лунная поляна находится между Оскверненным лесом и Зимними Ключами, туда можно добраться по дороге, идущей от Крепости Древобрюхов.', },
  },
  [6781] = {
    'Больше обломков брони',
    nil,
    nil,
  },
  [6801] = {
    'Локолар Владыка Льда',
    nil,
    nil,
  },
  [6804] = {
    'Отравленная вода',
    {'Мы отправили отряд элементалей воды на разведку в восточные пределы Лордерона, пораженные чумой нежити. Теперь наши разведчики поражены скверной. Повелители Вод не могут допустить, чтобы проклятая чума угрожала нашему народу!$B$BВозьми этот фиал. В нем заключен один из аспектов Нептулона. Он исцелит пораженных скверной элементалей, уничтожив проникшую в их тела заразу. Однако этот процесс столь болезнен, что некоторые из них могут стать нестабильными.$B$BТебе придется убить колыхающихся волноплесков. Принеси мне их браслеты.', },
    {'Исцелите отравленных элементалей в Восточных Чумных землях с помощью аспекта Нептулона. Принесите 12 браслетов колыхающихся волноплесков и аспект Нептулона герцогу Гидраксису в Азшару.', },
  },
  [6805] = {
    'Буря в пустыне',
    {'Мы, элементали воды, противостоим существам низших стихий. Если и в самом деле хочешь быть $Gнашим союзником:нашей союзницей;, докажи это!$B$BДалеко на юге, в пустынных землях Силитуса, в скалистом уголке, именуемом долиной Кристаллов, кишат слуги воздуха и земли. Они защищают сокрытые там предметы силы от примитивных существ, смертных. Мне нет никакого дела до того, что они там стерегут, я хочу, чтобы ты $Gперебил:перебила; их.$B$BОтправляйся в Долину Кристаллов в Силитус и уничтожь наших врагов.', },
    {'Убейте 15 вихрей пыли и 15 пустынных грохотунов и возвращайтесь к герцогу Гидраксису в Азшару.', },
  },
  [6821] = {
    'Око Углевзора',
    {'Ты $Gдоказал:доказала; мне, что $Gспособен:способна; доблестно противостоять врагам и верно служить Повелителям Вод. Однако пока тебе приходилось выходить на бой с довольно слабыми противниками. Сможешь ди ты сразить более могущественного врага.$B$BСлушай мое повеление.$B$BНа вершине пика Черной горы орки изловили слугу огня, Пиростража Углевзора. Найди место, где они его держат, и перебей и стражу и пленника. Принеси мне его глаз – докажи свою преданность.', },
    {'Принесите око Углевзора герцогу Гидраксису в Азшару.', },
  },
  [6822] = {
    'Огненные Недра',
    {'Пришло время испытать твои силы всерьез, $N. Ты $Gготов:готова;?$B$BНа самом дне Глубин Черной горы скрывается путь к Огненным Недрам. Это оплот нашего врага, охраняемый самыми мощными служителями огня в нашем мире.$B$BОтправляйся в Огненные Недра, $N. Докажи мне, что у тебя хватит сил справиться с тем, что скрывается там.$B$BЕсли у тебя есть союзники, бери их с собой. Не стоит отправляться в Огненные Недра в одиночку.', },
    {'Убейте 1 Повелителя огня, 1 лавового великана, 1 древнюю гончую Недр и 1 лавового волноплеска и возвращайтесь к герцогу Гидраксису в Азшару.', },
  },
  [6823] = {
    'Агент Гидраксиса',
    {'Если в самом деле хочешь быть $Gнашим союзником:нашей союзницей;, докажи серьезность своих намерений в боях против наших врагов – слуг воздуха, земли и огня. Каждое нанесенное им поражение подорвет их позиции в этих землях и послужит доказательством твоей приверженности благу нашего народа.', },
    {'Добейтесь уважительного отношеия к себе со стороны Гидраксианских Повелителей Вод, затем отправляйтесь к герцогу Гидраксису в Азшару.', },
  },
  [6824] = {
    'Руки врага',
    {'$N, твоя помощь для нас неоценима, и мы с неохотой отправляем тебя на последнее задание – мы не хотим терять столь полезного союзника, однако работа есть работа.$B$BИмей в виду, если ты успешно выполнишь поставленную перед тобой задачу, это весьма существенно приблизит час нашей победы.$B$BМы хотим, чтобы ты $Gуничтожил:уничтожила; нескольких капитанов в Огненных Недрах: Люцифрона, Сульфурона, Гееннаса и Шаззраха. Убей их и принеси их отрубленные руки.$B$BЭто потребует от тебя всех сил, но если ты преуспеешь, Повелителям огня будет нанесен ужасный удар.', },
    {'Принесите руки Люцифрона, Сульфурона, Гееннаса и Шаззраха герцогу Гидраксису в Азшару.', },
  },
  [6825] = {
    'Небо зовет – флот Смуггла',
    nil,
    nil,
  },
  [6826] = {
