  [7066] = {
    'Семя Жизни',
    {'Несчастные кентавры – мои дети – пропадут без отца.$B$BОглянись – это место исполнено надежды и того, чему учил мой отец, Кенарий. И все это произошло от меня! Я не могу лишить их своей поддержки... хоть они и обратились против меня.$B$BУ меня есть к тебе просьба – я хочу успокоить своего брата. Возьми это... первое семя жизни, родившейся из моих останков. Скажи ему, что я не погиб, а мой дух ныне пребывает здесь. Уверен, он меня поймет.', },
    {'Найдите в Лунной поляне Ремула и отдайте ему Семя Жизни.', },
  },
  [7067] = {
    'Инструкции кентавра-парии',
    {'Хотя меня изгнали из племени как еретика, меня поддерживает вера. Только ей я и живу. Впрочем, мне не исполнить предначертанное без посторонней помощи... Причем помощи чужака, а не кентавра.$B$BКто-то должен войти в священные залы Мародона – гробницы, которую защищает мой народ, допуская внутрь лишь избранных.$B$BЕсли ты $Gсогласен:согласна; помочь мне, стоит только кивнуть. Я отдам тебе свиток с инструкциями. Прочти его и обдумай мои слова.', },
    {'Прочтите инструкции кентавра-парии, добудьте из Мародона амулет Соединения и верните его кентавру-парии из южной части Пустошей.', },
  },
  [7068] = {
    'Фрагменты осколка сумрака',
    {'Сильна магия в святом месте для кентавров, священном Мародоне. Племена кентавров Пустошей готовы биться за эти места до последней капли крови, но куда им! Мы свое возьмем!$B$BСтупай в ихний Мародон и найди там каменных тварей из осколков сумрака. Ты дашь Утель\'наю лиловые кристаллы, а уж он тебя не обидит!$B$BМеста там опасные, так что бери с собой друзей.', },
    {'Соберите в Мародоне 10 фрагментов осколков сумрака и отнесите их Утель\'наю в Оргриммар.', },
  },
  [7070] = {
    'Фрагменты осколка сумрака',
    {'Я недавно начал испытания различных кристаллов и их магических свойств. Леди Праудмур позволила мне обратиться к путешественникам в Пустошах с просьбой отыскать там некое место, называющееся Мародон. В руинах храма поселились огромные каменные элементали, состоящие из лиловых кристаллов, которые я нарек осколками сумрака.$B$BЕсли ты решишь направиться в ту сторону и захватишь по пути несколько кристаллов, я буду очень признателен. Более того, я могу сделать тебе из них украшение.', },
    {'Соберите в Мародоне 10 фрагментов осколков сумрака и отнесите их верховному магу Тервошу в Терамор на побережье Пылевых топей.', },
  },
  [7081] = {
    'Кладбища Альтеракской долины',
    {'Кладбища в Альтеракской долине представляют собой очень большую ценность. И Орда, и Альянс свирепо дерутся за право обладать этими сакральными местами. $GГотов:Готова; ли ты вступить в эту схватку, $N?$B$BЯ хочу, чтобы ты $Gзахватил:захватила; кладбище неприятеля. Найди кладбище с вымпелом Орды и вытащи из земли этот вымпел! Вот и все, задание выполнено.$B$BЕсли не сложно, можешь покараулить захваченное кладбище несколько минут, до прибытия наших войск.', },
    {'Захватите кладбище, затем возвращайтесь к сержанту Даргену Грозовой Вершине в Альтеракские горы.', },
  },
  [7082] = {
    'Кладбища долины Альтерака',
    {'Линия фронта в Альтеракской долине постоянно изменяется. Мы отступаем и снова отвоевываем территории в смертельной схватке с врагом. Особо яростные сражения идут за кладбища, $N.$B$BЯ приказываю тебе атаковать кладбище Альянса! Найди кладбище со знаменем Альянса в центре и вырви древко из земли!$B$BНа этом задание будет выполнено. Но тебе нужно будет подождать некоторое время, пока не придут наши войска.', },
    {'Захватите кладбище, затем возвращайтесь к капралу Тике Кровавому Рыку в Альтеракские горы.', },
  },
  [7101] = {
    'Башни и бункеры',
    {'Башни и бункеры – основные защитные сооружения, так что все они должны быть нашими! Твое следующее задание – захват вражеского бункера.$B$BЗайди в бункер Альянса – над ним развевается их флаг. Внутри ты увидишь знамя. Сними его, и на его месте появится белое знамя Орды. Дело сделано!$B$BБольше ничего не нужно, но если ты немного подождешь и не дашь Альянсу отбить бункер обратно, то он самоуничтожится!', },
    {'Захватите башню врага и возвращайтесь к капралу Тике Кровавому Рыку в Альтеракские горы.', },
  },
  [7102] = {
    'Башни и бункеры',
    {'Башни и бункеры – основные защитные сооружения, так что у нас их должно быть как можно больше! Я приказываю тебе атаковать вражескую башню или бункер в Альтеракской долине.$B$BЗайди в бункер Орды – над ним развевается их флаг. Внутри ты увидишь вымпел. Сними его, и на его месте появится белое знамя Альянса. Дело сделано!$B$BНо на этом задание еще не закончено. Если ты продержишься рядом с башней некоторое время, чтобы ее не захватила Орда, то она разрушится!', },
    {'Уничтожьте вымпел на вражеской башне или бункере и возвращайтесь к сержанту Даргену Грозовой Вершине в Альтеракские горы.', },
  },
  [7121] = {
    'Интендант',
    {'$N, интендант велел отправлять новых рекрутов к нему. Нам нужны припасы, которые хранятся в тайниках на рудниках Альтерака, так что, скорее всего, он отправит тебя за ними.$B$BСтупай по этой дороге на запад, там найдешь интенданта.', },
    {'Поговорите с интендантом клана Грозовой Вершины.', },
  },
  [7122] = {
    'Захват рудника',
    {'Рудники в Альтеракской долине – нечто большее, чем обычные сырьевые ресурсы. И Грозовая Вершина, и клан Северного Волка хранят на рудниках свои припасы. Одни – на Железном руднике, другие – на руднике Ледяного Зуба. Неплохо было бы захватить чужие припасы!$B$BЗахвати рудник и возращайся ко мне. Найди рудник, который никому не принадлежит, и убей того, кто там главный. После этого рудник перейдет к нам, и мы вышлем войска для его охраны.', },
    {'Захватите рудник, который не принадлежит Грозовой Вершине, и возвращайтесь к сержанту Даргену Грозовой Вершине в Альтеракские горы.', },
  },
  [7123] = {
    'Разговор с интендантом',
    {'$N, интендант клана Северного Волка набирает рекрутов для задания на рудниках Альтеракской долины. Поговори с ним, он расскажет тебе все подробно.$B$BСтупай в бункер по западной дороге, зайди в западный вход, там ты и найдешь интенданта.', },
    {'Поговорите с интендантом клана Северного Волка.', },
  },
  [7124] = {
    'Захват рудника',
    {'Битва, разгоревшаяся на землях Альтеракской долины, идет и за рудники – Железный и Ледяного Зуба. Они богаты сырьем, и к тому же их можно использовать для хранения припасов.$B$BПомоги нам, $N! Захвати один из этих рудников, во имя Орды! Отправляйся на рудник, который принадлежит нашим врагам, и убей того, кто там главный. Как только ты захватишь рудник, мы вышлем войска, чтобы взять его под охрану.', },
    {'Захватите рудник и возвращайтесь к капралу Тике Кровавому Рыку в Альтеракские горы.', },
  },
  [7141] = {
    'Битва за Альтерак',
    {'Лига Исследователей уверена, что в Альтеракской долине есть артефакты, но клан Северного Волка не позволяет нам проводить здесь исследования. Не дадим Орде задушить наши благородные научные начинания! Уничтожь генерала Северного Волка, Дрек\'Тара. Без него орки ничего сделать не смогут.$B$BДрек\'Тара можно найти в крепости Северного Волка, на юге Альтеракской долины. Убей его и возвращайся.', },
    {'Отправляйтесь в Альтеракскую долину, сразитесь с генералом Орды Дрек\'Таром и возвращайтесь к геологу Камнетерке в Альтеракские горы.', },
  },
  [7142] = {
    'Битва за Альтерак',
    {'Дворфы Грозовой Вершины вместе со своими союзниками вторглись в Альтеракскую долину, и мы должны выкинуть их отсюда! Если мы хотим выиграть битву за Альтерак наверняка, то нам нужно уничтожить их генерала, Вандара Грозовую Вершину!$B$BЕго можно найти в Дун Болдаре, на севере Альтеракской долины.$B$BУбей его, $N, и возвращайся обратно ко мне.', },
    {'Отправляйтесь в Альтеракскую долину и убейте генерала дворфов, Вандара Грозовую Вершину. После этого возвращайтесь к Вогге Смертобою в Альтеракские горы.', },
  },
  [7161] = {
    'Испытательные земли',
    {'Приветствую, |3-6($C). Много времени прошло с тех пор, когда мы в последний раз внимали зову битвы. Трудно пробудить давно забытые чувства, но если они проснулись, обязательно прольется кровь. Ярость боя зовет нас...$B$BВ долине Альтерака все новички должны пройти боевое крещение.$B$BГлубоко в пещерах гноллов из стаи Дикой Лапы хранится знамя клана Северного Волка. Отправляйся туда и добудь его. Возвращайся ко мне со знаменем, и получишь знак отличия новобранца.', },
    {'Отправляйтесь в пещеру Дикой Лапы, расположенную на юго-востоке от основной базы в Альтеракской долине, и добудьте знамя Северного Волка. Отнесите знамя Северного Волка воеводе Лаггронду.', },
  },
  [7162] = {
    'Испытательные земли',
    {'Ты $Gприбыл:прибыла; вовремя, |3-6($C). Эти дикари из клана Северного Волка ни в какую не желают сдаваться! Ничего, мы им покажем, на что способна Грозовая Вершина. Они склонятся перед нашей мощью!$B$BПеред тем, как отправиться в бой, ты $Gдолжен:должна; вступить в ряды Грозовой Вершины.$B$BК юго-западу от Дун Болдара находится пещера Ледяного Крыла. В ее недрах хранится знамя Грозовой Вершины. Отправляйся туда и добудь его. Принеси знамя мне, и получишь отличительный знак новобранца.', },
    {'Отправляйтесь в пещеру Ледяного Крыла, которая расположена в Альтеракской долине, на юго-западе от Дун Болдара, и добудьте знамя Грозовой Вершины. Отдайте знамя лейтенанту Мурпу в Альтеракских горах.', },
  },
