  [7181] = {
    'Легенда о Корраке',
    {'Вторжение Грозовой Вершины – это не единственная угроза в этих землях, солдат. Война в долине ведется на два фронта. Тролли-канибалы из племени Секиры Зимы тоже представляют собой силу, с которой надо считаться.$B$BИх предводитель – Коррак Кровопуск – жестокая и коварная тварь.$B$BУбийство Коррака нанесло бы непоправимый удар племени Секиры Зимы. Убей его и получи награду!', },
    {'Согласно легенде, вождь могущественного племени троллей Секиры Зимы периодически появляется на поле боя, внося хаос и опустошение в жизнь обитателей Альтеракской долины.$B$BЕсли Коррак появится, убейте его и возвращайтесь к воеводе Лаггронду.', },
  },
  [7201] = {
    'Последний элемент',
    {'У меня есть работа для тех, кто не боится заглянуть в глубины, $N. $B$BКлан Черного Железа научился создавать чрезвычайно мощных големов. $B$BИсходя из отчетов, присланных нашими шпионами, дворфы используют уникальные источники энергии, чтобы придать своим гоблинам невероятную разрушительную силу. $B$BПодумать только, что мы могли бы сделать с нашими поганищами, заполучи мы сущность Стихий, которую они используют... Можешь перевернуть город вверх дном, но я должна получить эту сущность. Без нее можешь не возвращаться! Награда будет сопоставима с затраченными усилиями.', },
    {'Отправляйтесь в Глубины Черной горы и добудьте 10 мер сущности Стихий. Стоит начать поиски с големов и их создателей. Вивиан Лягроб также бормотала что-то про элементалей.', },
  },
  [7202] = {
    'Коррак Кровопуск',
    {'Племя Секиры Зимы, местные аборигены, имеют скверную привычку украшать свой стол блюдами из мяса наших солдат.$B$BНичего, мы им покажем, как с нами связываться!$B$BНам удалось добыть книгу, в которой записаны имена не только их знати, но и самого вождя, которое они тщательно скрывают! Его зовут Коррак Кровопуск.$B$BСмерть Коррака – это смерть всего племени Секиры Зимы! Убей его и возвращайся.', },
    {'Согласно легенде, вождь могущественного племени троллей Секиры Зимы периодически появляется на поле боя, внося хаос и опустошение в жизнь обитателей Альтеракской долины.$B$BЕсли вам удастся найти Коррака, убейте его и возвращайтесь к лейтенанту Мурпу в Альтеракские горы.', },
  },
  [7221] = {
    'Разговор с геологом Камнетеркой',
    {'А, $N, с докладом, да? Отлично, Грозовая Вершина должна использовать всех, кто годится хоть на что-нибудь.$B$BПоговори с геологом Камнетеркой в Дун Болдаре. Она как раз набирает добровольцев для очень важного задания на вражеской территории. Ты найдешь ее в гарнизоне Дун Болдара, внизу.$B$BСмотри внимательно, когда будешь пробираться через Дун Болдар. Для |3-1($C), $Gкоторый:которая:c; не чурается работы, здесь найдется много дел.', },
    {'Поговорите с геологом Камнетеркой в гарнизоне Дун Болдара.', },
  },
  [7222] = {
    'Разговор с Воггой Смертобоем',
    {'$C? Отлично! Рады видеть тебя в Альтераке, $N. Дворфы и их союзники вторглись в нашу долину, так что Орда приветствует каждого, кто решил вступить в праведный бой!$B$BПоговори с Воггой Смертобоем. Он как раз набирает агентов для важного задания в крепости Альянса в Альтеракской долине!$B$BВоггу можно найти в крепости Северного Волка.', },
    {'Поговорите с Воггой в крепости Северного Волка.', },
  },
  [7223] = {
    'Обломки брони',
    {'В Альтеракской долине часто гремят сражения, и поставок из Стальгорна для нужд наших войск недостаточно.$B$BЕсли ты найдешь на поле боя обломки брони, приноси их мне, они нам пригодятся.', },
    {'Принесите 20 обломков брони Мурготу Подземной Кузне в Дун Болдар.', },
  },
  [7224] = {
    'Вражеский трофей',
    {'Если надешь на поле боя обломки вражеских доспехов, неси их мне. Из них выйдет отличная броня для наших собственных войск!', },
    {'Принесите 20 обломков брони кузнецу Регзару в деревню Северного Волка.', },
  },
  [7241] = {
    'Защита клана Северного Волка',
    {'Издалека доносится рокот барабанов войны, |3-6($C). Клан твоего вождя подвергается атакам презренных дворфов клана Грозовой Вершины из Стальгорна. $B$BСудя по твоему виду, можно решить, что ты $Gзакален в битвах и готов:закалена в битвах и готова; постоять за дело клана Северного Волка. $B$BА сейчас тебе предстоит сделать новый шаг к усовершенствованию твоего воинского мастерства. К северу от Мельницы Таррен, в Альтеракских горах, есть вход в Альтеракскую долину. Там тебе нужно отыскать воеводу Лаггронда.', },
    {'Отправляйтесь в Альтеракскую долину, расположенную в Альтеракских горах. Найдите воеводу Лаггронда у входа в тоннель и поговорите с ним, чтобы начать свою военную карьеру в клане Северного Волка. Альтеракская долина находится к северу от деревни Мельница Таррен у подножия Альтеракских гор.', },
  },
  [7261] = {
    'Королевское право',
    {'Здесь, в Альтеракской долине, идут тяжелые бои. С одной стороны на нас наступают тролли-каннибалы из племени Секиры Зимы, с другой – дикари из клана Северного Волка. Во имя короля Магни Бронзоборода, все они должны быть уничтожены! Владение этими землями – неотъемлемое суверенное право королевства Стальгорн!$B$BК сожалению, солдаты не растут на деревьях. Доложись лейтенанту Мурпу рядом с Альтеракской долиной и с честью приступай к несению службы в Грозовой Вершине!', },
    {'Отправляйтесь в Альтеракскую долину, к предгорьям Хилсбрада. Найдите лейтенанта Мурпа рядом со входом в туннель и поговорите с ним.$B$BВо славу Бронзоборода!', },
  },
  [7281] = {
    'Братская любовь',
    {'Ужасно... В сражении я столкнулся со своим братом, командиром Грозовой Вершины.$B$BИз всех четырех командиров Грозовой Вершины он самый опасный. Возможно, Королева банши сможет возродить его после смерти, и тогда я докажу ему ошибочность его поступков...$B$BМой брат находится в глубине территории врага. Убей его и возвращайся ко мне. Я уверен, солдат, что у тебя хватит сил выполнить это задание.$B$BНо лучше не отправляйся в одиночку.', },
    {'Отправляйтесь на территорию Грозовой Вершины и убейте командира Карла Филипса. После выполнения задания возвращайтесь к командиру Луи Филипсу.', },
  },
  [7282] = {
    'Братская любовь',
    {'Нужно избавить его от страданий, солдат! Воистину, он не ведает, что творит. Ужасная Королева банши сделала его своей бездумной марионеткой.$B$BСтупай вперед, через Поле брани, в глубину территории Северного Волка. Найди моего брата и уничтожь его. Его мучениям должен прийти конец.', },
    {'Отправляйтесь на территорию клана Северного Волка и убейте командира Луи Филипса. После выполнения задания возвращайтесь к командиру Карлу Филипсу.', },
  },
  [7301] = {
    'Свергнутые властители небес',
    {'Мы взяли их в плен, |3-6($R). Мы захватили командиров звена. В небе разразилась яростная битва, но в итоге они все же уступили! К сожалению, во время контратаки против крепости Северного Волка были сбиты наши лучшие пилоты. Нужно выяснить, что с ними случилось. $B$BОбыщи западную Смотровую башню и крепость Северного Волка. Там они были замечены в последний раз. Как можно тщательней осмотри эти районы.', },
    {'Отправляйтесь в крепость Северного Волка и поищите командиров звена Макарча, Сквороца и Ромеона. Вернитесь с отчетом к командиру Даффи.', },
  },
  [7302] = {
    'Свергнутые властители небес',
    {'Наша воздушная поддержка в полном беспорядке, $N. Командиров звена сбили на вражеской территории. Ты $Gдолжен:должна; узнать, что с ними стало, чтобы мы могли попытаться их спасти! $B$BСмуггла в последний раз видели над укрытием Ледяного Крыла. Боевую виверну Мааши заметили у лесопилки Грозовой Вершины. Маэстр... кто-то видел, как этот пустоголовый идиот разбился возле северного оплота Дун Болдара. $B$BЭто вся информация, что у меня есть. За дело! Как следует прочеши указанные районы.', },
    {'Выясните, что стало с командирами звена Смугглом, Маашей и Маэстром. Вернитесь с отчетом к командиру Мулфорту.', },
  },
  [7321] = {
    'Нежный черепаховый суп',
    {'Кто сказал, что нежить не понимает в кулинарии? Взять, к примеру, мой заветный рецепт нежного черепашьего супа. Да я знаю народ, что из мертвых бы восстал, только бы скушать тарелочку! Хе-хе...$B$BКстати, в последнее время мне ни тарелочки приготовить не удалось. Обычно я сам ходил на озеро Лордамер ловить хрустогрызов – у, грязные тварюги – но не могу сейчас бросить работу.$B$BЕсли ты добудешь мне немного черепашьего мяса, я научу тебя варить такой суп!', },
    {'Принесите 10 кусков черепашьего мяса Кристофу Френчу на Мельницу Таррен.', },
  },
  [7341] = {
    'Честная сделка',
    nil,
    nil,
  },
  [7342] = {
    'Стрелы – для неженок!',
    nil,
    nil,
  },
  [7361] = {
    'Благосклонность клана Черного Копья',
    {'*Нажьяк ворчит.* $B$BПрежде чем появились ночные эльфы, были тролли. И именно от троллей пошли ночные эльфы. $B$BИх злобные вылазки против моего народа нельзя более терпеть. Олений Шлем хочет стереть нас с лица земли, чтобы не осталось и упоминания о тех, от кого ведут свой род он и его собратья. Не бывать этому! $B$BЗа каждого ночного эльфа, которого ты убьешь и обезглавишь, Надьяк будет одаривать тебя благосклонностью народа Черного Копья. Слава о твоих деяниях распространится по всему Калимдору и Восточным королевствам. Иди же и послужи Черному Копью!', },
    {'Вам поручили убивать в Альтеракской долине ночных эльфов.$B$BУбив ночного эльфа, вернитесь с его отрубленной головой к Нажьяку Проклятуну в крепость Северного Волка.', },
  },
  [7362] = {
    'Союзник тауренов',
    {'Мудрая Магата прислала меня в эту зону боевых действий, чтобы изучать другие расы. Точнее, мне поручено собрать образцы тканей гномов. Магату особенно интересует их живучесть. Ведь гномы – эти крошечные, жалкие создания, – проявляют удивительную устойчивость как к боевым ранениям, так и к обычным тяготам жизни.$B$BТебе повезло – мне как раз нужны помощники, чтобы выполнить поручение Магаты. Приноси мне пряди гномьих волос, и таурены будут считать тебя своим союзником.', },
    {'Убивайте гномов в Альтеракской долине.$B$BУбив гнома, принесите Раваку Зловещему Тотему в крепость Северного Волка пучок волос гнома.', },
  },
  [7363] = {
    'Человечья жизнь',
    {'Люди так отвратительны!$B$B*Командир Филипс брезгливо кривится.*$B$BКонечно, сейчас я состою на службе у клана Северного Волка, и все же я по-прежнему остаюсь слугой Сильваны.$B$BИ в свободное от службы время я подрабатываю, помогая Темным аптекарям Подгорода.$B$BМне поручено собирать осколки человеческих костей, которые будут использованы, чтобы исцелять людей от их жалкого состояния.$B$BЯ также могу давать аналогичное поручение своим подчиненным.$B$BИщите их. Убивайте их. И приносите мне осколки их костей!', },
    {'Вам поручили убивать людей в Альтеракской долине.$B$BУбив человека, принесите командору Луи Филипсу (который бродит между линией фронта и крепостью Северного Волка) осколок человеской кости.$B$BОкончить человеческие страдания совсем несложно!', },
  },
  [7364] = {
    'Награда Гномрегана',
    {'Сам главный механик прислал меня на это поле брани, чтобы защитить наших собратьев! До нас дошли вести, что таурены нарочно выискивают и убивают гномов! А самое ужасное, что они срезают с трупов павших их роскошные волосы! Этому необходимо положить конец. Мы обязаны отомстить!$B$BМы лишим их копыт, |3-6($C)! Принеси мне их копыта! Докажи, что ты – союзник гномов!', },
    {'Убивайте тауренов в Альтеракской долине.$B$BУбив таурена, вернитесь к Дирку Надувалло в Дун Болдар с копытом таурена.', },
  },
  [7365] = {
    'Реквием Оленьего Шлема',
    {'Олений Шлем отдал приказ: во время сражений всех троллей необходимо истребить. Они должны ответить за свое наглое заявление, что ночные эльфы ведут свой род от троллей.$B$BУничтожь троллей и принеси мне их амулеты. Повторяю: мне нужны их амулеты.', },
    {'Ваше задание – убивать в Альтеракской долине троллей.$B$BУбейте тролля и возвращайтесь к Атраманису в Дун Болдар с амулетом тролля из клана Черного Копья.', },
  },
  [7366] = {
    'Милосердие архиепископа',
    {'Доброта архиепископа сравнима только с его мудростью. Надо иметь невероятно чуткую душу, чтобы относиться к подобной швали с милосердием...$B$B*Командир Филипс в недоумении пожимает плечами.*$B$BОн слишком добр...Просто слишком.$B$BАрхиепископ просил нас, чтобы мы имели сострадание к нежити и даровали им быструю и верную смерть. Чтобы проявить подобное великодушие, мы должны раздобыть их черные, пораженные болезнью сердца.$B$BПринеси мне сердца Отрекшихся, чтобы мы могли сжечь их с надлежащими церемониями в Соборе Света. Поспеши, $N!', },
    {'Ваше задание – убивать в Альтеракской долине Отрекшихся.$B$BУбейте Отрекшегося и возвращайтесь к Карлу Филипсу (он ходит между лесопилкой и рудником) с сердцем Отрекшегося.', },
  },
  [7367] = {
    'Обезвредить угрозу',
    {'Клан Северного Волка позволил заманить себя в совершенно идиотскую ловушку, |3-6($C). Надо быть более осмотрительным, действуя на территории врага.$B$BЕсли хочешь нанести им ощутимый удар, нужно уничтожить того, кто отвечает их за минные поля, – я говорю об эксперте по взрывчатым веществам. Пока он жив, минные поля существуют. Но если он будет мертв, поддерживать их станет некому.$B$BНе исключаю, что эксперта можно найти в башне рядом с гарнизоном Стылой Крови. Удачи тебе!', },
    {'Найдите эксперта по взрывчатым веществам из клана Северного Волка и убейте его. После завершения задания возвращайтесь к эксперту по взрывчатым веществам из клана Грозовой Вершины.', },
  },
  [7368] = {
    'Обезвредить угрозу',
    {'Про противопехотные мины я знаю все. Именно я, а не кто-нибудь, отвечаю за мины на всей линии фронта.$B$BЕсли ты хочешь обезвредить все минные поля Грозовой Вершины, сначала надо убить их эксперта по взрывчатым веществам. Этот трусливый дворф скорее всего прячется в первой башне Поля брани. Убей его, и минные поля будут обезврежены раз и навсегда.$B$BКак убьешь его, возвращайся ко мне. Добрые дела не должны оставаться без награды.', },
    {'Найдите эксперта по взрывчатым веществам из клана Грозовой Вершины и убейте его. После завершения задания возвращайтесь к эксперту по взрывчатым веществам из клана Северного Волка.', },
  },
  [7381] = {
    'Возвращение Коррака',
    {'Коррак мертв, но надолго ли? О магии троллей ходит грозная слава. Я не удивлюсь, если их мертвый вождь снова восстанет, чтобы править своим племенем.$B$BТы знаешь, что делать, если это случится.$B$B*Воевода Лаггронд проводит ребром ладони по горлу.*$B$BНо на этот раз принеси его череп. И гляди в оба, солдат!$B$B$GСвободен:Свободна;!', },
    {'В случае, если Коррак Кровопуск возвратится на Поле брани, найдите его и уничтожьте.$B$BПринесите череп Коррака воеводе Лаггронду в предгорья Хилсбрада.', },
  },
  [7382] = {
    'Коррак Бессмертный',
    {'Что-то здесь не так... Кто-нибудь догадался сжечь тело Коррака? Эти синекожие язычники ни перед чем не остановятся, чтобы возродить своего вождя.$B$BСмотри в оба, $N. Не удивляйся, если снова увидишь Коррака в живых. Убей его снова, но на этот раз обязательно сожги тело.$B$BЧтобы уж все проделать наверняка, отрежь ему голову и принеси череп мне, а все остальное пусть горит синим пламенем.', },
    {'В случае, если Коррак Кровопуск возвратится на Поле брани, найдите его и уничтожьте.$B$BПринесите череп Коррака лейтенанту Мурпу в предгорья Хилсбрада.', },
  },
  [7383] = {
    'Венец Земли',
    {'Но не все было ладно с Тельдрассилом. Олений Шлем долго лелеял надежду вырастить новое Древо жизни и даже тщательно спланировал это мероприятие. Но была одна небольшая проблема, с которой можно связать большинство бед Тельдрассила.$B$BНо пока я об этом говорить не буду. Тебе нужно посетить последний лунный колодец, к северо-востоку от Врат Дарнаса. Под ветвями Древа-оракула находится первый и самый могучий из наших колодцев. Набери воды из него и возвращайся ко мне.', },
    {'Наполните аметистовый фиал и принесит его Коритрасу Лунному Гневу в Доланаар.', },
  },
