  [7401] = {
    'Разыскиваются: ДВОРФЫ!',
    {'Согласно приказу сержанта Язры Кровавый Рык, все дворфы на поле сражения подлежат неукоснительному уничтожению.$B$BПосле боя из каждого убитого дворфа следует выдрать хребет, а тело оставить вонять и разлагаться, – в назидание всем, кто осмелится замахнуться на Орду.$B$BВыдранные хребты, как доказательство своих подвигов, сдавайте Язре Кровавый Рык.', },
    {'Ваша задача – убивать дворфов в Альтеракской долине.$B$BУбейте дворфа и вырвите ему хребет. Отнесите хребет дворфа сержанту Язре Кровавый Рык в крепость Северного Волка.', },
  },
  [7402] = {
    'Разыскиваются: ОРКИ!',
    {'Согласно приказу капрала Норга Грозовой Вершины, все солдаты Грозовой Вершины должны убить как можно больше орков.$B$BВ качестве трофея предъявляйте орочьи зубы.$B$BПеред тем, как вырывать оркам зубы, не забывайте надевать защитные перчатки!', },
    {'Ваше задание – убивать орков в Альтеракской долине.$B$BУбейте орка и доставьте зуб орка капралу Норгу Грозовой Вершине в Дун Болдар.', },
