  [7441] = {
    'Пузиллин и старейшина Аж\'Тордин',
    {'Представь себе, $Gчужестранец:чужестранка;: стоило мне зазеваться всего на секунду!.. я был очень занят: спасался от принца-отступника... – и меня тут же обворовали! Гнуснейший демон, бес Пузиллин, стащил мою книгу заклинаний, и ключ от некогда прекрасных чертогов Эльдре\'Таласа!$B$BКлюч мне теперь ни к чему: я обменял свое бессмертие на свободу, – но книга мне нужна позарез!$B$BОтыщи мне этого беса Пузиллина, и добудь у него книгу!$B$BИщи его в Квартале Криводревов Забытого Города.', },
    {'Отправляйтесь в Забытый Город и отыщите беса Пузиллина. Любыми доступными средствами убедите Пузиллина отдать вам книгу заклинаний Аж\'Тордина.$B$BЕсли добудете книгу, вернитесь к Аж\'Тордину в павильон Лорисс в Фераласе.', },
  },
  [7461] = {
    'Древнее безумие',
    {'Ну что, довольно ли ты $Gслышал:слышала;? Надеюсь, ты не устрашишься вступить в бой?$B$BЭтому безумию необходимо положить конец! Время бессмертных давным-давно миновало. Уничтожь стражей, охраняющих столпы, и ликвидируй силовое поле. А потом убей Бессмер\'тера. Это лишит принца большей части его силы, и тогда ты сможешь убить и его тоже. Помоги духам этого города упокоиться в мире!', },
    {'Перебейте стражей, которые охраняют 5 столпов, снабжающих энергией Тюрьму Бессмер\'тера. После того как столпы угаснут, силовое поле, удерживающее Бессмер\'тера, рассеется.$B$BВойдите в Тюрьму Бессмер\'тера и уничтожьте злого демона, который находится внутри. И, наконец, сразитесь с принцем Тортелдрином в Читальне.$B$BЕсли задание будет выполнено успешно, вернитесь во двор, к прародительнице Шен\'дралар.', },
  },
  [7462] = {
    'Сокровище Шен\'дралар',
    {'В библиотеке, Читальне, ты найдешь древний сундук, спрятанный под лестницей. Можешь взять оттуда, что тебе понравится.', },
    {'Вернитесь в Читальню и найдите сокровище Шен\'дралар. Получите свою награду!', },
  },
  [7463] = {
    'Магический напиток',
    {'А-а, |3-6($R), $Gпосвятивший:посвятившая:r;себя магии! Удивительно. Я не видывал подобных тебе уже... сколько? Которое у нас тысячелетье на дворе? Да уже лет пятьсот!$B$BУ меня есть кое-что, что поможет утолить твою жажду, но сначала тебе придется выполнить одно мое поручение.$B$B*Лидрос хихикает.*$B$BВ Эльдре\'Таласе завелась аномалия. В восточном крыле поселился элементаль воды. Надо его... того... занести в каталог.$B$BСтупай в Квартал Криводревов, и добудь мне частицу стихийной сущности Гидротвари. Принеси ее мне, а я тебя научу одной полезной штучке!', },
    {'Ступайте в Квартал Криводревов Забытого Города и убейте элементаля воды Гидротварь. Вернитесь в Читальню к сказителю Лидросу и принесите ему сущность Гидротвари.', },
  },
  [7481] = {
    'Эльфийские легенды',
    {'Несколько лет тому назад одинокий эльфийский мастер по имени Кариэль Винтхалус бежал из своей разоренной родной земли, унеся с собой несколько древних эльфийских артефактов.$B$BПоиски пропавшего мастера привели нас из руин Кель\'Таласа через Пылающие степи и морские просторы в Фералас.$B$BМы подозреваем, что эльф искал убежища в залах Эльдре\'Таласа (ныне известного как Забытый Город).$B$BОтыщи его, $Gстранник:странница;! Жив он или мертв, а знания, которые он унес с собой, отыскать необходимо!', },
    {'Попытайтесь найти в Забытом Городе Кариэля Винтхалуса. Вернитесь в Лагерь Мохаче к ведуну Королуску и сообщите ему все, что вам удалось узнать.', },
  },
  [7482] = {
    'Эльфийские легенды',
    {'Несколько лет тому назад одинокий эльфийский мастер по имени Кариэль Винтхалус бежал из своей разоренной родной земли, унеся с собой несколько древних эльфийских артефактов.$B$BПоиски пропавшего мастера привели нас из руин Кель\'Таласа через Пылающие степи и морские просторы в Фералас.$B$BМы подозреваем, что эльф искал убежища в залах Эльдре\'Таласа (ныне известного как Забытый Город).$B$BОтыщи его, $Gстранник:странница;! Жив он или мертв, а знания, которые он унес с собой, отыскать необходимо!', },
    {'Попытайтесь найти в Забытом Городе Кариэля Винтхалуса. Вернитесь в крепость Оперенной Луны к книжнице Рунический Шип и сообщите ей все, что вам удалось узнать.', },
  },
  [7483] = {
    'Манускрипт скорости',
    {'Мастер Винтхалус потерял "Манускрипт Скорости" в восточном крыле. Этот манускрипт позволит тебе наложить на все свое снаряжение небольшое ускоряющее заклятие. Детские игрушки, на самом деле…$B$BТем не менее, если тебя это все же интересует, принеси мне манускрипт, и кое-какие необходимые реагенты.$B$BХм... Насколько я помню, там требуется кровь героев, безупречный черный алмаз и большие сверкающие осколки. Впрочем, я могу и ошибиться!$B$B*Лидрос пожимает плечами.*', },
    {'Принесите сказителю Лидросу в Забытом Городе "Манускрипт Скорости", 1 безупречный черный алмаз, 2 больших сверкающих осколка и 2 образца крови героев, чтобы получить магический знак стремительности.', },
  },
  [7484] = {
    'Манускрипт средоточия',
    {'Этот манускрипт едва не вызвал интерес у одного из юных фамильяров хранителя мудрости! Бедняжка, ему было всего несколько недель от роду!$B$BТем из вас, кто интересуется магией, этот манускрипт может пригодиться!$B$BОн пропал где-то в этом крыле. Могу только предположить, что его прибрал к рукам один из духов высокорожденных.$B$BПринесите мне "Манускрипт Средоточия", несколько больших сверкающих осколков, шкуру тени и безупречный черный алмаз, и я создам для вас магический знак.', },
    {'Принесите сказителю Лидросу в Забытом Городе "Манускрипт Средоточия", 1 безупречный черный алмаз, 4 больших сверкающих осколка и 2 образца шкуры тени, чтобы получить магический знак сосредоточения.', },
  },
  [7485] = {
    'Манускрипт защиты',
    {'Потеряв этот манускрипт, он рыдал, как ребенок! Не пойми меня неправильно – мне и самому случалось плакать из-за книг, ведь нет большей утраты, чем утраченные знания, – но рыдать из-за этих почти бесполезных обрывков заклинаний? Абсурд! Мне даже подумалось, что принц, пожалуй, оказал ему услугу, отделив его плоть от костей.$B$BПринеси мне этот манускрипт вместе с безупречным черным алмазом, большими сверкающими осколками и истлевшей шовной нитью поганища, и я воссоздам этот магический знак!$B$BАх, да: в общем, ищи в северном крыле.', },
    {'Принесите сказителю Лидросу в Забытом Городе "Манускрипт Защиты", 1 безупречный черный алмаз, 2 больших сверкающих осколка и 1 истлевшую шовную нить поганища, чтобы получить магический знак защиты.', },
  },
  [7486] = {
    'Награда для героя',
    {'$N, твои заслуги перед Повелителями Вод превосходят самые смелые ожидания. Мы не могли предположить, что обитатель этого мира может обладать подобной силой!$B$BНе знаю, как у твоего народа принято выражать благодарность, однако могу пообещать тебе щедрую награду.$B$BОтправляйся в южную часть острова и найди сундук, сокрытый в волнах. В том сундуке твоя награда.$B$BИди, $N. Забери свою награду.', },
    {'Заберите свою награду из сундука Гидраксиса.', },
  },
  [7487] = {
    'Сродство с недрами',
    {'Земля испещрена трещинами и разломами – прямо у нас под ногами, |3-6($R). В двух шагах от того места, где ты сейчас стоишь, есть разлом, ведущий в недра в Черной горы, в самую вотчину Повелителя огня.$B$B$GУдивлен:Удивлена;? Прискорбно... Смертные не в силах понять то, что они не могут увидеть, пощупать, попробовать на зуб...$B$BУверяю тебя, ты стоишь прямо рядом с порталом и вполне можешь им воспользоваться.$B$BСмотрю, у тебя глаза заблестели. Послушай, приобрести сродство с недрами не так сложно. Отправляйся в Глубины Черной горы и принеси мне осколок из Огненных Недр. Я сделаю так, что ты сможешь видеть портал.', },
    {'Отправляйтесь через портал, ведущий к Огненным Недрам в Глубинах Черной горы и добудьте осколок из Огненных Недр. Принесите его Лотосу Хранителю Портала в Черной горе.', },
  },
  [7488] = {
    'Сеть Лефтендрис',
    {'Маг Лефтендрис, злокозненная эльфийка крови, чья жестокость уступает лишь ее извращенной приверженности к магии, бежала в Забытый Город! Она создала магическое устройство: сеть, улавливающую энергию этого места, – и мы опасаемся, что, если Летендрис не остановить, она нанесет непоправимый урон нашему миру!$B$BОстанови ее, $N. Отыщи ее и отними у нее эту сеть. Она, скорее всего, где-то неподалеку от сатиров в Квартале Криводревов Забытого Города. Принеси эту сеть мне, чтобы мы могли осторожно выпустить захваченную ею энергию на свободу...', },
    {'Принесите сеть Лефтендрис Латроникусу Лунному Копью в Крепость Оперенной Луны в Фераласе.', },
  },
  [7489] = {
    'Сеть Лефтендрис',
    {'Эльфийка крови Лефтендрис перешла все мыслимые границы! Она так жаждала все новой магической энергии, что бросила вызов своим собратьям и создала хитроумное устройство, сеть, предназначенную для того, чтобы всасывать отравленную магию Забытого Города. Она и теперь собирает эту энергию, намереваясь использовать ее против своих врагов!$B$BЛефтендрис необходимо остановить. Отыщи ее в Квартале Криводревов в Забытом Городе, повергни ее и принеси ее сеть мне, чтобы мы могли передать ее для изучения более здравомыслящим эльфам крови.', },
    {'Принесите сеть Лефтендрис Тало Терновому Копыту в Лагере Мохаче в Фераласе.', },
  },
  [7490] = {
    'Победа Орды',
    {'Тебе удалось совершить невозможное! Царица черных драконов лежит мертвой у твоих ног. Отруби ей голову и отнеси ее своему вождю.', },
    {'Отнесите голову Ониксии Траллу в Оргриммар.', },
  },
  [7491] = {
