  [7562] = {
    'Мор\'зул Вестник Крови',
    {'Ты хорошо $Gизучил:изучила; темные искусства и $Gобрел:обрела; такую власть над существам из других миров, что... быть может, пришло время подчинить себе одного из знаменитых коней погибели.$B$BВызвать его в наш мир нелегко, для этого потребуется великая сила и большие затраты, но если ты хочешь владеть таким скакуном, поговори с Мор\'зулом Вестником Крови. Он разбил лагерь рядом с Алтарем Бурь в Пылающих степях.$B$BСтупай, $N. Задание рискованное, но такие чернокнижники как ты не страшатся опасностей.', },
    {'Поговорите с Мор\'зулом Вестником Крови у Алтаря Бурь в Пылающих степях.', },
  },
  [7563] = {
    'Ярость крови',
    {'Кони погибели живут в другом мире, на огненных равнинах Зорота, и чтобы подчинить себе одного из них, необходимы могущественные руны. Я могу начертать эти руны на пергаменте, но для этого нужны особые чернила.$B$BВ далеких Зимних Ключах живут зверосовы. Некогда они были чистыми существами, но демоническое влияние Теснины Зловещего Шепота лишило их разума, и они бродят по снегам в полном безумии.$B$BИз их крови я и сделаю чернила. Остается ее добыть.', },
    {'Принесите 30 бутылок крови яростных зверей Мор\'зулу Вестнику Крови в Пылающие степи.', },
  },
  [7564] = {
    'Дикоглаз',
    {'Мой слуга, Горзиеки, довольно опытный алхимик. Он сможет приготовить чернила, о которых шла речь. Я уже подготовил принесенную тобой кровь; отнеси ее Горзиеки, пусть приступает к работе.', },
    {'Принесите ларец Крови Горзиеки Дикоглазу в Алтарь Бурь, что в Пылающих степях.', },
  },
  [7581] = {
    'Тюремные кандалы',
    {'Ты $Gдолжден:должна; добыть у младших демонов кандалы для стражника ужаса.$B$BКровь сатира из племени Буйного Нрава, что живут в Забытом Городе, для этого сгодится. Свернувшаяся кровь скрепит осколки Хедерины и даст силу хрустальной темнице.', },
    {'Отправляйтесь в Забытый Город, что в Фераласе и добудьте 15 порций крови сатиров из племени Исчадий Природы, что живут в Квартале Криводревов. Вернитесь к Дайо из Гниющего шрама по выполнении поручения.', },
  },
  [7582] = {
    'Тюремная камера',
    {'Демоны Хедерины, что живут у подножия горы Хиджал, выделяют яды Скверны, которые вызревают и твердеют, образуя антимагические кристаллы. Они известны как слезы Хедерины.$B$BДаже чудовищная сила стражника ужаса не сможет разбить стены тюрьмы, сделанной из этого материала.$B$BОтправляйся в Теснину Зловещего Шепота в Зимних ключах, и добудь этих слез.', },
    {'Отправляйтесь к Теснине Зловещего Шепота в Зимних ключах и добудьте 5 слез Хедерины у демонов Хедерины, что населяют эту теснину. Вернитесь к Дайо из Гниющего шрама по выполнении поручения.', },
  },
  [7583] = {
    'Заточение',
    {'Темница готова. Теперь самое трудное дело.$B$BТы $Gдолжен:должна; найти и схватить командира стражников ужаса в Гниющем шраме при помощи этой хрустальной темницы.$B$BОсторожнее! Стражник ужаса -коварный и смертельный враг, он будет пытаться уничтожить тебя, как только ты начнешь обряд заточения. Он будет отчаянно отбиваться, пытаясь избежать плена.$B$BОн будет смотреть только на тебя, $N. Потому я рекомендую взять с собой друзей, которые не дадут ему погубить тебя!', },
    {'Отправляйтесь в Гниющий шрам и и найдите командира стражников ужаса.$B$BПри помощи светящегося кристалла заточения поймайте командира стражников ужаса. Будьте готовы к отчаянным атакам и сопротивлению, поскольку демон будет пытаться избежать заточения.$B$BЕсли вы добьетесь успеха, отнесите пленного стражника ужаса Дайо в Гниющий шрам.', },
  },
  [7601] = {
    'Что хочет Ниби',
    {'Если хочешь научиться у меня призывать инфернала-слугу, ты $Gдолжен:должна; убить Крошиуса и добыть из его пылающих останков огненное ядро. Но это не так просто, как кажется. Крошиус чуть не дотла сгорел. Тебе придется снова воспламенить его тело и поднять его из праха. Но для этого тебе придется добыть другие ингредиенты из других демонических сущностей.$B$BМой бес по имени Бесенок покажет тебе дорогу.', },
    {'Поговорите с Бесенком в Оскверненном лесу.', },
  },
  [7602] = {
    'Безупречная субстанция Скверны',
    {'Если ты намереваешлся воскресить Крошиуса, тебе понадобится пламя Скверны. Я могу зажечь его, но для этого потребуются кое-какие ингредиенты.$B$BТебе придется принести мне безупречной субстанции Скверны из трех краев нашего мира.$B$BПервый тип субстанции ты найдешь у сатиров из племени Легаши, что в Азшаре. Второй тип – у джеденарских легионеров, а третий можно добыть у стражей Скверны в Выжженных землях.$B$BПринеси мне по порции каждой субстанции, и я зажгу пламя скверны, что позволит тебе оживить Крошиуса.', },
    {'Бесенок из Оскверненного леса просит вас принести ему три порции безупречной субстанции скверны из трех различных мест.$B$BБезупречной субстанцией в Азшаре владеют сатиры из племени Легаши. В Джеденаре она есть у джеженарских легионеров. Стражи Скверны в Выжженных землях владеют этой субстанцией в своих краях.$B$BДобудьте три типа безупречной субстанции Скверны и возвращайтесь к Бесенку в Оскверненный лес.', },
  },
  [7603] = {
    'Огненное ядро Крошиуса',
    {'Крошиус лежит грудой праха на северо-востоке отсюда, в долине Рваных Ран. Возьми этот пламень Скверны и воспламени его останки. Пламя оживит павшего инфернала.$B$BПосле этого он наверняка пришибет и тебя, и твоего приспешника.$B$BЕсли каким-то чудом тебе удастся уложить Крошиуса, возьми его огненное ядро и принеси Ниби.', },
    {'Обыщите долину Рваных Ран в Оскверненном лесу и найдите останки Крошиуса. Когда вы их найдете, поднесите к ним пламень Скверны и подождать, пока Крошиус не восстанет из праха. Когда инфернал очнется, убейте его и возьмите из его тела огненное ядро Крошиуса.$B$BПринесите огненное ядро Крошиуса Ниби Всемогущему в Оскверненный лес.', },
  },
  [7604] = {
    'Заключение договора',
    {'$GНижеподписавшийся:Нижеподписавшаяся; со всей ответственностью подтверждает, что сульфуронский слиток, переданный $Gим:ею; братству Тория (ООО "БТ"), является подлинником. $GНижеподписавшийся:Нижеподписавшаяся; также подтверждает, что ООО "БТ" не несет никакой ответственности за любой вред, могущий последовать в результате неосторожного обращения с Сульфурасом, как то: использование Сульфураса в качестве материала для ковки; самостоятельные разработки; попытки воспроизведения; продажа и употребление в пищу.$B$BПодпись под договором автоматически означает согласие $Gподписавшегося:подписавшейся; со всеми условиями, перечисленными в документе.$B$B_______________$B$BПодпись', },
    {'Подпишите договор с представителем братства Тория Локтосом Недобрым Торговцем, если вам нужен чертеж сульфуронского молота.', },
  },
  [7621] = {
    'Предупреждение',
    nil,
    nil,
  },
  [7622] = {
    'Баланс Света и Тени',
    {'Тебе предстоит сделать то, что оказалось не под силу мне – спасти крестьян, погибших во время бегства из Стратхольма.$B$BОни выйдут на свет, и ты $Gдолжен:должна; защитить их. Если ты не сможешь спасти слишком многих, мы продолжим влачить наше проклятое существование.$B$BЭто настоящее испытание для всех твоих способностей. Да поможет тебе Свет, $N.', },
    {'Спасите жизни 50 крестьян прежде, чем 15 из них будут убиты. Поговорите с Эрис Тайнопламень по выполнении задания.$B$BЧисло погибших крестьян можно узнать, посмотрев на Колья Смерти.', },
  },
  [7623] = {
    'Лорд Гиблопуст',
    {'Морзул говорит, что ты хочешь открыть портал в Зорот. Раз так, тебе не обойтись без зоротианской звездной пыли. А ее нелегко найти в Азероте!$B$BОна может быть лишь у одного существа – лорда Гиблопуста из Джеденара, сердца Совета Теней! Поговори с ним, вдруг он поделится звездной пылью.$B$BВпрочем, отправляясь в Джеденар, тебе необходимо принять злое обличье, чтобы добраться до Совета Теней. Значит, тебе нужны мои зелья Теней, а они недешевы...', },
    {'Купите зелья Теней у Горзиеки в Алтаре Бурь, что Пылающих степях.$B$BПриняв зелья, пройдите через Джеденар в Оскверненном лесу и поговорите с лордом Гиблопустом, который находится в Оплоте Теней.', },
  },
  [7624] = {
    'Улатек Предатель',
    {'Хочешь заслужить мою милость? Что ж, тогда убей, кого я укажу.$B$BМой слуга, Улатек, пресмыкается передо мной, а тем временем умышляет против меня вместе с лордом Хел\'нуратом из Зорота. Хел\'нурат хочет низвергнуть меня и захватить власть, а Улатек – его шпион...$B$BДокажи, что служишь мне, а не моим противникам. Вступи в бой с Улатеком в его собственном доме, Оплоте Теней на западе. Передай ему, что мне известна его тайна. Вот увидишь, он затрясется от страха. Принеси мне сердце предателя!', },
    {'Сразитесь с Улатеком и принесите сердце предателя лорду Гиблопусту в Джеденар.', },
  },
  [7625] = {
    'Зоротианская звездная пыль',
    {'Так ты $Gявился:явилась; в поисках зоротианской звездной пыли? Ты хочешь открыть портал в Зорот, владения моего соперника Хелнурата? Уж не одного ли из его драгоценных коней погибели ты хочешь украсть?$B$BОтлично. У моего слуги, Ур\'Дана, есть большой запас зоротианской звездной пыли. Обращайся к нему... если денег хватит.', },
    {'Купите зоротианскую звездную пыль у Ур\'Дана из Оплота Теней в Джеденаре, что в Оскверненном лесу. Отнесите ее Горзиеки Дикоглазу в Пылающие степи.', },
  },
  [7626] = {
    'Колокол Детмуры',
    {'Колокол Детмуры назвали в честь Детмуры Темноглазой. Это один из трех предметов, необходимых для создания круга Великого призыва. Сопряженный с Колесом и Свечой, он предоставит в твое распоряжение огромный запас магической энергии.$B$BМой слуга Горзиеки может создать такой колокол, но ему понадобится много эликсиров энергии Тьмы. Добудь их у алхимика и отнеси Горзиеки.', },
    {'Отнесите 2 эликсира энергии Тьмы Горзиеки Дикоглазу в Пылающие степи.', },
  },
  [7627] = {
    'Колесо Черного Похода',
    {'Колесо Черного Похода содержит в себе и направляет черную силу, необходимую для создания круга Великого призыва. Горзиеки может создать это колесо, но ему понадобятся большие сверкающие осколки, которые получают зачаровыватели, и руда черного железа, которую может добыть опытный горняк.$B$BПринеси моему слуге все необходимое.', },
    {'Отнесите 6 больших сверкающих осколков и 25 кусков руды черного железа Дикоглазу в Пылающие степи.', },
  },
  [7628] = {
    'Свеча Судного дня',
    {'Свеча Судного дня хранит в себе огонь, который опаляет всех, желающих прервать твой ритуал. Зажигая свечу при создании круга Великого призыва, ты сожжешь всех упрямых демонов!$B$BВпрочем, сдержать этот огонь довольно трудно, и для этого нужна чешуя черного дракона. Купи ее у мастера снятия шкур и принеси Горзиеки Дикоглазу.', },
    {'Принесите 3 чешуи черного дракона Горзиеки Дикоглазу в Пылающие степи.', },
  },
  [7629] = {
    'Доставка беса',
    {'Для подготовки пергамента Морзула осталось сделать только одно – напитать бумагу зоротианской звездной пылью. И... боюсь, здесь этого не сделать. Мне не хватит оборудования!$B$BВпрочем, я знаю об одной лаборатории, где есть все необходимое. Она находится в Некроситете, на острове Каэр Дарроу, в Чумных землях.$B$BЯ туда отправиться не смогу, но у меня есть идея. Возьми эту бутылку. В ней сидит довольно умный бес. Выпусти его в алхимической лаборатории, и он напитает пергамент звездной пылью.', },
    {'Отнесите беса в бутылке в алхимическую лабораторию Некроситета. После создания пергамента верните бутылку Горзиеки Дикоглазу.', },
  },
  [7630] = {
    'Арканит',
    {'Итак, Колокол, Колесо и Свеча готовы. Осталось создать черный магнит. Он понадобится во время ритуала, поддерживать прочие объекты, на случай, если они подведут.$B$BДля создания магнита мне понадобится арканит. У тебя его нет? Коли так, поищи его у алхимика.$B$BПринеси мне арканит, и я создам черный магнит.', },
    {'Принесите 1 арканитовый слиток Горзиеки в Пылающие степи.', },
  },
  [7631] = {
    'Зоротианский конь погибели',
    {'Все готово. Пришло время провести ритуал создания круга Великого призыва и воспользоваться пергаментом с рунами, начертанными зоротианской звездной пылью... И отрыть портал в Зорот.$B$BВсе инструкции по проведению ритуала я записал в тот свиток. Изучи его внимательно, потому что следует делать все в точности так, как написано, или успеха не добиться.$B$BУдачи, $N. И да попадут души твоих врагов в твой мешочек осколков.', },
    {'Прочтите инструкции Мор\'зула. Призовите зоротианского коня погибели, одолейте его и подчините его дух.', },
  },
  [7632] = {
    'Древний лист',
    {'Кожица листа светится ярко-зеленым. Его поверхность очень морщиниста, на ней видны многочисленные прожилки и бороздки.$B$BЕсли задуматься, то этот лист – наверняка нечто очень древнее и могущественное.$B$BМожет, поискать, кому он принадлежит?', },
    {'Найдите хозяина древнего окаменелого древесного листа. Удачи, $N, мир велик.', },
  },
  [7633] = {
    'Посвящение',
    nil,
    nil,
  },
  [7634] = {
    'Перетянутый жилами лист древня',
    {'Привествую тебя, |3-6($C). Я Хастат, древо мудрости. Я храню память тысячелетий.$B$BЕсли один из моих листьев свернуть надлежащим образом, он будет служить колчаном, подобных которым не было уже 10 000 лет. Если к этому колчану в пару получить еще и лук Древ, то силе их равных не будет.$B$BПринеси мне жил взрослого синего дракона, и получишь колчан.', },
    {'Хастат Древний просит вас принести ему жилу взрослого синего дракона. По выполнении задания возвращайтесь к Хастату в Оскверненный лес.', },
  },
  [7635] = {
    'Подходящая тетива',
    {'Вартус, конечно, предложил тебе посох для лука Дерев, но наложить чары на тетиву, без которой лук – не лук, могу только я.$B$BЭто простое дело, совсем плевое. Убей взрослого черного дракона и добудь из него жилу. Принеси мне эту жилу, и я зачарую ее так, что тетива для лука из нее будет, считай, вечной.', },
    {'Стома Древний просит вас принести ему жилу взрослого черного дракона. Добыв жилу, возвращайтесь к Стоме в Оскверненный лес.', },
  },
  [7636] = {
    'Ветвь ходячего Древа',
    {'Тайные слуги Пылающего Легиона все еще строят козни в нашем мире, |3-6($C). Они оскверняют все, к чему прикасаются. Природа чахнет и стенает от их присутствия.$B$BТы $Gдолжен:должна; выследить этих демонических разносчиков порчи, $N. Помни – ты $Gдолжен:должна; лично разобраться с ними – иначе сбегут. Пока ты $Gодин:одна;, они будут пытаться уничтожить тебя. Ты $Gдолжен:должна; сразить их, обратить их надменность в ничтожество!$B$BЯ дам тебе кусок моей коры. Письмена на ней приведут тебя к нашим врагам. Принеси мне их головы.', },
    {'Вы должны найти и уничтожить следующих четверых демонических разносчиков порчи:$B$BСимон Совратительницу.$B$BКлинфрана Безумного.$B$BСоленора Убийцу.$B$BАрториуса Судьбоносного.$B$BУничтожьте этих тварей и возвращайтесь к Вартусу Древнему в Оскверненный лес с их головами.$B$BВ поисках местонахождения этих демонов сверяйтесь с окаменевшей корой.$B$BВы ОБЯЗАНЫ выполнить это задание в одиночку.', },
  },
  [7637] = {
    'Жертва необходима',
    {'Чтобы подготовиться к этому заданию, ты $Gдолжен:должна; сначала показать умение жертвовать и умение судить... в первую очередь – жертвовать.$B$BДля выполнения задания ты $Gдолжен:должна; получить курильницу экзорцизма у Верховного жреца Рохана. Несмотря на то, что ее использование будет продиктовано благими намерениями и существенными причинами, за нее должна быть предложена соответственная компенсация.$B$BОтправляйся в Стальгорн и поговори с Верховным жрецом. Если возьмешь с собой достаточно звонкой монеты, чтобы компенсировать ему потерю курильницы, ты проявишь должную готовность к жертвам.$B$BА теперь иди, $N, время терять нельзя.', },
    {'Доберитесь до Стальгорна и получите курильницу экзорцизма у Верховного жреца Рохана. За курильницу нужно будет внести плату в размере 150 золотых монет.', },
  },
  [7638] = {
    'Лорд Грейсон Тенелом',
    {'Пришло твое время, $N, узнать о том, как найти то, что ты, без сомнения, хочешь заполучить уже в течение некоторого времени... твоего рыцарского коня.$B$BПоговори с лордом Грейсоном Тенеломом здесь, в Храмовом квартале Штормграда. Именно он не только раскроет тебе все секреты самого верного паладинского скакуна, но и поможет разобраться в задачах, которые встанут перед тобой, прежде чем ты сможешь раздобыть своего собственного коня.$B$BЯ желаю тебе всего наилучшего в этом испытании. Во имя Света, $Gмой благородный брат:моя благородная сестра;!', },
    {'Поговорите с лордом Грейсоном Тенеломом в Храмовом квартале Штормграда.', },
  },
  [7639] = {
    'Демонстрация истинного правосудия',
    {'Прими эту курильницу и будь $Gблагословен:благословенна;. Я сам применял ее в нескольких обрядах экзорцизма, и она хорошо послужит тебе в грядущих тяжких испытаниях.$B$BНыне возвращайся к лорду Грейсону Тенелому в Штормград, $N. Он даст тебе поручение.', },
    {'Принесите курильницу экзорцизма лорду Грейсону Тенелому в Храмовый квартал Штормграда.', },
  },
  [7640] = {
    'Изгнание призраков из Долины Ужаса',
    {'Я добавил в курильницу щепотку зараженной земли Долины Ужаса, что в Восточных Чумных землях. Ты $Gдолжен:должна; будешь использовать курильницу для поиска мест, которые несут на себе признаки беспокойного поведения потусторонних сил. Ищи зеленое свечение, исходящее из-под земли – так физический мир проявляет себя, мучимый миром потусторонним, и используй курильницу для вызова из-под земли злых духов.$B$BКогда свершишь суд над призраками, я поставлю перед тобой новую задачу.', },
    {'Используйте курильницу экзорцизма для вызова из-под земли призраков, мучающих Долину Ужаса. Когда убьете 25 духов долины Ужаса, вернитесь к лорду Грейсону Тенелому в Храмовый квартал Штормграда.', },
  },
  [7641] = {
    'Работа Гриманда Элмора',
    {'Без особого чепрака рыцарский конь паладина ничем не отличается от обычного скакуна. Чепрак для твоего скакуна не только должен быть изготовлен из наилучших материалов, но в силу сопутствующих обстоятельств нужно, чтобы он еще был в достаточной мере настроен на призрачный мир.$B$BНо все по порядку, $N – сначала нужно изготовить чепрак. В Квартале Дворфов Штормграда проживает один из самых лучших современных мастеров – Гриманд Элмор. Он берет за свою работу недешево, но качество его работы – как раз то, что нам нужно. Поговори с ним.', },
    {'Поговорите с Гримандом Элмором в Квартале Дворфов Штормграда.', },
  },
  [7642] = {
    'Сбор необходимого',
    {'Для чепрака тебе понадобятся четыре вещи: руническая ткань для подкладки, арканитовые слитки для покрытия, цветки слезы Артаса для настройки на призрачный мир и святая вода Стратхольма для духовного укрепления. На самом деле, вещей понадобится пять... если считать золото для меня. Эта плата – за весь мой труд, а также седло и стремена ручной работы.$B$BКогда наберешь все это, я смогу изготовить твой чепрак. А пока ты ищешь необходимое, я приступлю к работе.', },
    {'Принесите Гриманду Элмору в Квартал Дворфов Штормграда 40 отрезов рунической ткани, 6 арканитовых слитков, 10 цветков слезы Артаса, 5 сосудов со святой водой Стратхольма и 150 золотых монет.', },
  },
  [7643] = {
    'Дух древнего коня',
    {'Твоему чепраку нужно особое благословение, а его можно получить только у подходящего духа. Такой дух обитает, например, в башне Забытого Города – это Дух древнего коня. Сейчас его удерживает в плену Тендрис Криводрев. Убей эту тварь, чтобы освободить духа, $N.$B$BТолько имей в виду, что просто освободить – недостаточно. Тебе нужно сначала умилостивить духа, чтобы получить его благословение. Для этого понадобится особый лошадиный корм. Ты найдешь его в Южнобережье, если поговоришь с Меридет Карлсон.', },
    {'Купите лошадиный корм для Духа древнего коня у Меридет Карлсон в Южнобережье.$B$BУбейте Тендриса Криводрева в башне Забытого Города в Фераласе и освободите духа. Накормите духа кормом и получите благословление для арканитового чепрака.', },
  },
  [7644] = {
    'Благословенный арканитовый чепрак',
    {'После того как чепрак наброшен на спину духу древнего коня, он начинает чуть заметно светиться. На миг кажется, будто все вокруг звенит от напряжения и магической мощи... а потом чепрак исчезает со спины коня. Теперь он в вашем заплечном мешке.$B$BОчевидно, чепрак получил благословение. Теперь, согласно наставлениям лорда Победителя Тени, вам следует вернуться с этим чепраком к нему в Штормград.', },
    {'Вернитесь вместе с благословенным арканитовым чепраком к лорду Грейсону Тенелому в Храмовый квартал Штормграда.', },
  },
  [7645] = {
    'Сытный манный лошадиный корм',
    {'Серебряная Длань посчитала меня ЧОКНУТОЙ, когда я сказала, что могу сделать особый конский корм, который насытит призрачных лошадей. ВСЕ считали меня чокнутой! Пришло время отыграться!$B$BЯ сделаю, что тебе надо, ПАЛАДИН... ЗАЩИТНИК, тоже мне... Но обойдется это недешево. Мне понадобится сытное манное печенье, которое продают только в Серебряном Рассвете. Кроме того я требую – да, ТРЕБУЮ, золото в качестве компенсации за перенесенные страдания!$B$BПосмотрим, многим ли ты можешь пожертвовать, ПАЛАДИН.', },
    {'Принесите 20 cытных манных печений – ключевых составляющих сытного манного лошадиного корма – Меридет Карлсон в Южнобережье в предгорьях Хилсбрада. Продается печенье только в Серебряном Рассвете.$B$BКроме того, понадобится дать ей 50 золотых, чтобы успокоить ее ущемленное самолюбие.', },
  },
  [7646] = {
    'Магический кристалл',
    {'Теперь мы должны сконструировать магический кристалл – так начнется твой подвиг и освобождение плененного духа зловещего рыцарского коня. Собственно, кристалл уже почти готов. Катализатором для него послужила курильница, которую ты $Gиспользовал:использовала;, изгоняя духов из Долины Ужаса; осталось только сделать подходящую подставку для нашего прибора.$B$BДа, и еще нужны последние два компонента – азеротский алмаз и безупречный черный алмаз. Эти два камня станут маяком, пронзающим тьму!', },
    {'Принесите азеротский алмаз и безупречный черный алмаз лорду Грейсону Тенелому в Храмовый квартал Штормграда.', },
  },
  [7647] = {
    'Суд и расплата',
    {'Возьми эту суму – в ней собраны предметы, которые понадобятся тебе для выполнения задания.$B$BИспользуй магический кристалл в самом сердце подвала Главного склепа для вызова проклятых духов, над которыми нужно свершить суд. Во время испытания рассчитывай силы – духи могут оказаться сильнее, чем ты думаешь. После гибели духов появится сам рыцарь смерти. Срази его и верни потерянную душу рыцарского коня. Только тогда, $N, ты сможешь принести ей избавление и возложить чепрак на твоего нового скакуна.$B$BЖелаю удачи!', },
    {'Используйте магический кристалл в центре подвала Главного склепа в Некроситете. Победите вызванных духов, а затем – рыцаря смерти Темного Терзателя, который появится после поражения духов.$B$BВерните падшему скакуну Темного Терзателя его спасенную душу и покройте его благословенным зачарованным чепраком.', },
  },
  [7648] = {
    'Шедевр Гриманда',
    {'*Гриманд утирает пот со лба, сияя от гордости.*$B$BГотово, $Gдруг мой:моя леди;. Это одно из моих лучших изделий – не хуже, чем тот чепрак, что я собственноручно смастерил для лорда Победителя Тени, когда он отправлялся за своим конем. Благодарю тебя за доверие.$B$BНесомненно, и сам Грейсон захочет полюбоваться на мою работу. Покажи ему чепрак и передай мою искреннюю благодарность за его достойные дела.', },
    {'Покажите арканитовый чепрак лорду Грейсону Тенелому в Храмовом квартале Штормграда.', },
  },
