  [7701] = {
    'РАЗЫСКИВАЕТСЯ: надзиратель Мальториус',
    {'Именем его светлости надзирателя Скользорука должно убить агента Братства Тория надзирателя Мальториуса, командира подразделения Шлаковой Ямы войск Черного Железа в Котловане. Его голову должно принести в качестве доказательства. $B$BЗа наградой обращаться к дозорному капитану Лоло Долгобою.', },
    {'Дворф Черного Железа, вероломный надзиратель Мальториус, скрывается в глубине Шлаковой Ямы в Котловане, что в Тлеющем ущелье. $B$BУбейте надзирателя Мальториуса и принесите его голову дозорному капитану Лоло Долгобою.', },
  },
  [7703] = {
    'Неоконченное дело Гордоков',
    {'Ну, раз ты теперь король, может, попробуешь добыть сокровище Гордоков у того паскудного эльфа, который называет себя принцем, а? Тот король, что был до тебя, это дело профукал – так же, как и все прочие короли до него, с тех самых пор, как Тортелдрин, тот паскудный эльф, спер его у одного из королей!$B$BА вещица-то была ценная: латная рукавица Мощи Гордока! И в старых байках говорится, что воротить ее сможет только настоящий король Гордок. А еще говорится, что паскудный эльф до сих пор шляется где-то тут. Отыщи его, и будет тебе крутая рукавица, которую ты будешь носить по гроб жизни!', },
    {'Добудьте латную рукавицу Мощи Гордока и вернитесь с ней к капитану Давигрому в Забытом Городе.$B$BПо словам Давигрома, в "старых байках" говорится, что Тортелдрин – "страшный эльф", называющий себя принцем, – похитил эту рукавицу у одного из королей Гордоков.', },
  },
  [7704] = {
    'Размер имеет значение',
    {'Ну и ну... Вот так сюрприз! У горничной была огромная вышитая подушка, и вот теперь она в ваших руках. Эвонис Сажекурке непременно захочет заполучить ее. Посмотрим, что она сможет предложить взамен...', },
    {'Отнесите подушку горничной Крепкие Тиски Эвонис Сажекурке в Тлеющее ущелье.', },
  },
  [7721] = {
    'Энергия для уменьшения',
    {'Если я поступлю вопреки доводам разума, и все же буду по-прежнему содержать инженерную мастерскую на Забытом берегу, мне нужно будет как-то разобраться с этими зловредными элементалями. Чтоб им провалиться, этим необузданным стихиям!$B$BНо как повезло мне – а теперь и вам, $Gмой уважаемый помощник:моя уважаемая помощница;! Ведь эти элементали представляют собой идеальный источник энергии для моего ультрасжимателя! Так что ликвидируйте их, не стесняясь, только, смотрите, не выбрасывайте ядра элементалей воды!', },
    {'Соберите 10 ядер элементалей воды, убивая морских элементалей и морских валов на Забытом берегу в Фераласе, и принесите их Зорбину Дребезгуну, который находится неподалеку от порта.', },
  },
  [7722] = {
    'Какой еще плавень?',
    {'Когда этот негодяй Мальториус покинул клан, он унес с собой кое-что поважнее безупречнейшего чародейского кристалла. Он украл рецепт огненного плавня! Полагаю, это была его возможность выслужиться у дворфов Черного Железа. $B$BВерни нам эти секретные документы, $N. Мальториус наверняка держит их при себе, там, в Шлаковой Яме.', },
    {'Верните секретные документы по изготовлению огненного плавня, украденные надзирателем Мальториусом. Инспектор скорее всего находится в Шлаковой Яме в Котловане. Документы верните мастеру-кузнецу Закалону в Тлеющем ущелье.', },
  },
  [7723] = {
    'Отекшие пальцы',
    {'К несчастью, из-за проблем с пальцами я уже не способен на столь меткие удары, как раньше. Виноват местный климат и низкое атмосферное давление – пальцы здесь отекают и становятся похожими на гигантские телcамарские кровяные колбаски... $B$BТак что придется тебе побегать по моим делам.$B$BПервое на повестке дня: вышибить дух из тяжелых боевых големов ущелья. Не задерживайся.', },
    {'По поручению Хансела Большерука убейте 20 тяжелых боевых големов, которых можно найти в землях, окружающих Котлован в Тлеющем ущелье. По выполнении задания вернитесь к Ханселу.', },
  },
  [7724] = {
    'Огненная угроза',
    {'Как только старый Хансел собирается отдохнуть, нелегкая подбрасывает ему еще работенки... $B$BВот, пожалуйста, теперь большие лавовые пауки добавили проблем! Скользорук говорит, его храбрецы чуть ли не из пушек от них отстреливались... $B$BКороче, чтобы зря не пугать детишек и наших доблестных вояк, надо бы найти на пауков управу. Уверен, ты справишься.', },
    {'По указанию Хансела Большерука убейте 20 больших лавовых пауков в Тлеющем ущелье. Пауки водятся на вулканических плато и в озерах лавы в Тлеющем ущелье.$B$BКогда убьете достаточно пауков, вернитесь к Ханселу.', },
  },
  [7725] = {
    'Еще раз ослабим великанов',
    {'Я знаю, ты уже $Gсобирал:собирала; для меня остатки миниатюризации... но, если тебе захочется снова поразвлечься, имей в виду: мне всегда нужны новые остатки!$B$BРазумеется, такого большого вознаграждения, как раньше, я больше предложить не смогу, но зато я всегда буду рад замолвить за тебя словечко перед своими друзьями-гоблинами.$B$BПоверь мне, $Gпутник:путешественница;: гоблины к подобным отзывам прислушиваются чрезвычайно внимательно!', },
    {'Используйте ультрасжиматель Зорбина, чтобы уменьшить великанов любого вида, каких вы найдете в Фераласе. Принесите 10 миниатюрных великанов Зорбину Дребезгуну в порт Забытого берега в Фераласе.', },
  },
  [7726] = {
    'Новая энергия для уменьшения',
    {'О, вы не прочь снова поработать? Так героическое собирание кусков слизи, выпавших из тел элементалей, не утолило вашей жажды приключений? Ну, тогда вы, наверное, не огорчитесь, если я снова дам вам то же самое поручение?$B$BЛадно, шутки в сторону: мне нужны новые ядра элементалей воды. Но имейте в виду: сейчас почти все мои средства уходят на обустройство мастерской, так что награда, возможно, будет не столь велика... Зато обещаю вам, что расскажу всем своим ближним и дальним знакомым о том, как вы мне помогли! А у меня масса знакомых в картеле!', },
    {'Соберите 6 ядер элементалей воды, убивая морских элементалей и морских валов на Забытом берегу в Фераласе, и принесите их Зорбину Дребезгуну, который находится неподалеку от порта.', },
  },
  [7727] = {
    'Пламезавры? Ну и название...',
    {'Бедные мои ножки... У меня бурсит, $Gмолодой человек:милочка;. Да, большие шишки всегда наживают себе шишки. Ну и как мне заниматься истреблением всяких тварей, если пальцы моих ног в таком плачевном состоянии? В общем, тут-то ты мне и поможешь! $B$BСкрендж и Закалон хотят, чтобы я поубивал этих проклятых ящеров, чтобы расчистить путь для их исследовательских отрядов. Они, вишь ты, желают изучать озера лавы в Шлаковой Яме. А всякие пламезавры и прочие завры им, вишь ты, мешают. В общем, придется тебе направиться в Шлаковую Яму и слегка ускорить вымирание этих монстров.', },
    {'По поручению Хансела Большерука убейте 20 пламезавров, обитающих в озерах лавы в Шлаковой Яме – это часть Котлована в Тлеющем ущелье. $B$BПо выполнении задания вернитесь к Ханселу в Тлеющее ущелье.', },
  },
  [7728] = {
    'УКРАДЕНО: фурма и подзорная труба наблюдателя',
    {'Внимание, украдены следующие предметы: $B$B*Фурма Черного Железа – одна штука. $B$B*Подзорная труба наблюдателя – одна штука. $B$BПросьба вернуть за вознаграждение. $B$BБратство Тория точно знает, что эти предметы были украдены беспринципными и завистливыми дворфами Черного Железа из Тлеющего ущелья. $B$BПодзорная труба была великолепным прибором для наблюдения, а фурма – просто незаменимый инструмент. $B$BНашедшему просьба вернуть указанные вещи Скренджу.', },
    {'Найдите и верните фурму и подзорную трубу наблюдателя надсмотрщику Скренджу в Тлеющем ущелье. Единственная известная информация: вещи были украдены дворфами Черного Железа. Подзорная труба является бесценным прибором для наблюдения, а фурма – незаменимым кузнечным инструментом.', },
  },
  [7729] = {
    'ВАКАНСИЯ: устранитель конкурентов',
    {'Надсмотрщику Скренджу требуется опытный кандидат на должность устранителя конкурентов в Тлеющем ущелье. Пол неважен. $B$BЕсли вы считаете, что ваша квалификация достаточна для этой должности, отправляйтесь в Тлеющее ущелье и уничтожьте всех встреченных вами надсмотрщиков Черного Железа и работорговцев Черного Железа. $B$BВозвращайтесь к надсмотрщику Скренджу, когда ваша одежда пропитается кровью. $B$BСами поймете, когда настанет момент, так что вопросов не задавать.', },
    {'Убейте 15 надсмотрщиков и 15 поработителей из клана Черного Железа. По выполнении задания вернитесь к десятнику Скренджу в Тлеющее ущелье.', },
  },
  [7730] = {
    'Вторжение улья Цуккаш',
    {'Судя по сведениям, полученным с помощью разведки, враги, теснящие гноллов с юга, – это раса насекомоподобных существ. Неизвестно, разумны ли они – но, откровенно говоря, это и неважно. Если гноллы их так боятся, что предпочитают переть на нас, значит, эти твари так же опасны, как гноллы, а может, и еще опаснее.$B$BПроникни в те места, где обитают эти насекомые, Зукк\'аш. Это место называется "Гудящая Бездна". Постарайся уменьшить их численность, и доложи мне о том, что увидишь. В доказательство своих свершений принеси мне их панцири!', },
    {'Принесите 20 панцирей Зукк\'аш Хадокену Быстрому Страннику в Лагерь Мохаче в Фераласе.', },
  },
  [7731] = {
    'Жалохвост',
    {'Один из разведчиков, исследовавших Гудящую Бездну, доложил о виденном им гигантском насекомом – куда более крупном, чем все остальные замеченные им насекомые. Помимо необычных размеров, оно, по-видимому, окружено какой-то особой аурой, которой у прочих насекомых нет. Мы назвали его жалохвостом. $B$BНам хотелось бы изучить внутренности этой твари, чтобы понять, как лучше бороться с этой новой угрозой. Добудь ядовитые железы жалохвоста и принеси их мне!', },
    {'Убейте жалохвоста в Гудящей Бездне и принести железы ядохлеста Хадокену Быстрому Страннику в Лагерь Мохаче в Фераласе.', },
  },
  [7732] = {
    'Донесение об улье Зукк\'аш',
    {'Ты поступаешь весьма разумно, направляя свои усилия на борьбу с этими насекомыми, грозящими Лагерю Мохаче. Опасность далеко еще не миновала, и мне хотелось бы попросить тебя о еще одной услуге. Вот в этих бумагах изложены все сведения об улье Зукк\'аш, которые нам удалось раздобыть. Прошу тебя доставить их Зилзибину Барабанному Бою на Волок в Оргриммаре. Он специалист по подобным созданиям; надеюсь, добытые нами знания ему пригодятся.', },
    {'Доставьте отчет о нашествии насекомых-Зукк\'аш на Лагерь Мохаче Зилзибину Барабанному Бою, который проживает на Волоке в Оргриммаре.', },
  },
  [7733] = {
    'Борьба за качество',
    {'Я знаю, где можно добыть шкуры еще лучше этих – хотя в это и трудно поверить! На севере Фераласа, в глубине Пещеры Бешеного оврага, обитает другая стая йети. Они куда агрессивнее тех, с которыми тебе доводилось иметь дело прежде, так что если ты ищешь новых подвигов, тебя это наверняка заинтересует!$B$BА я хорошо вознагражу тебя за десяток шкур таких йети. Принеси их мне, и мы договоримся.', },
    {'Принесите 10 шкур йети из пещеры Бешеного оврага Пратту Макгруббену в Крепость Оперенной Луны в Фераласе.', },
  },
  [7734] = {
    'Борьба за качество',
    {'Я знаю, где можно добыть шкуры еще лучше этих – хотя в это и трудно поверить! На севере Фераласа, в глубине Пещеры Бешеного оврага, обитает другая стая йети. Они куда агрессивнее тех, с которыми тебе доводилось иметь дело прежде, так что если ты ищешь новых подвигов, тебя это наверняка заинтересует!$B$BА я хорошо вознагражу тебя за десяток шкур таких йети. Принеси их мне, и мы договоримся.', },
    {'Принесите 10 шкур йети из пещеры Бешеного оврага Джангору Быстрому Страннику в Лагерь Мохаче в Фераласе.', },
  },
  [7735] = {
    'Безупречная шкура йети',
    {'Вы добыли безупречную шкуру йети, особенно толстую и прочную – даже прочнее шкур йети с Дикого утеса.$B$BВам известно, что Пратту Макгруббену из Крепости Оперенной Луны всегда нужны шкуры йети, а уж эта его тем более заинтересует!', },
    {'Отнесите безупречную шкуру йети Пратту Макгруббену в Крепость Оперенной Луны в Фераласе.', },
  },
  [7736] = {
    'Пополнение запасов огненного плавня – королевская кровь',
    nil,
    nil,
  },
  [7737] = {
    'Завоевать еще большую благосклонность',
    nil,
    nil,
  },
  [7738] = {
    'Совершенная шкура йети',
    {'Вы добыли безупречную шкуру йети, особенно толстую и прочную – даже прочнее шкур йети из Пещеры Бешеного оврага.$B$BВам известно, что Джангору Быстрому Страннику из Лагеря Мохаче всегда нужны шкуры йети, а уж эта его тем более заинтересует!', },
    {'Отнесите безупречную шкуру йети Джангору Быстрому Страннику в Лагерь Мохаче в Фераласе.', },
  },
  [7761] = {
    'Распоряжения Ренда Чернорука',
    {'Недоумок, как-то мне очень сомнительно, что ты сумеешь сдержать чужаков и не дать им проникнуть в убежище повелителя. В случае твоей смерти – а подобный исход более чем вероятен – у этой сферы есть свой собственный механизм защиты, который не даст чужакам перенестись напрямую в Крыло Тьмы.$B$BСферой сможет воспользоваться только тот, кто отмечен меткой Драккисата. К счастью, Драккисат, в отличие от некоторых "интендантов", далеко не дурак и хорошо разбирается в своем деле. Он самолично охраняет клеймо!$B$B-Вождь Ренд Чернорук$B$BP.S. Уничтожь это письмо, идиот!', },
    {'Вот уж действительно идиот... Похоже, вам нужно найти это клеймо и выжечь на руке метку Драккисата, чтобы получить доступ к сфере Приказа.$B$BВ письме ясно сказано, что генерал Драккисат охраняет клеймо. Наверное, стоит проверить.', },
  },
  [7781] = {
    'Владыка Черной горы',
    {'Знакомое чувство ликования охватывает вас: победа!!! Перед вами лежит гигантская, покрытая боевыми шрамами голова Нефариана. Отнесите же ее своему королю!', },
    {'Отнесите голову Нефариана королю Вариану Ринну в Штормград.', },
  },
  [7782] = {
    'Владыка Черной горы',
    {'Отправляйтесь в Аллею Героев и поговорите с фельдмаршалом Афрасиаби.', },
    {'Поговорите с фельдмаршалом Афрасиаби в Аллее Героев рядом со Штормградом.', },
  },
  [7783] = {
    'Владыка Черной горы',
    {'Ты вновь испытываешь знакомое чувство – сладость победы! У твоих ног лежит массивная, покрытая боевыми шрамами голова Нефариана. Отнеси ее своему вождю, $Gвоитель:воительница;!', },
    {'Отнесите голову Нефариана Траллу в Оргриммар.', },
  },
  [7784] = {
    'Владыка Черной горы',
    {'Разыщи властителя Рунтака в Аллее Силы.', },
    {'Поговорите с властителем Рунтаком в Аллее Силы в Оргриммаре.', },
  },
  [7785] = {
    'Сосуд Возрождения',
    {'Изучи Сосуд Возрождения, смертный. Из него возродится Искатель Ветра!', },
    {'Изучите Сосуд Возрождения и вернитесь к верховному лорду Демитриану.', },
  },
  [7786] = {
    'Громораан Искатель Ветра',
    {'Я рассказал тебе все, что знаю, $N. Именно тебе предстоит отыскать элементий и уничтожить воплощение Повелителя огня.$B$BЕсли ты справишься, с тобой пребудет благословение Громораана.', },
    {'Чтобы освободить Громораана Искателя Ветра из тюрьмы, отнесите правый и левый наручник Ветроносца, 10 слитков элементия и сущность Повелителя огня верховному лорду Демитриану.', },
  },
  [7787] = {
    'Громовая ярость',
    {'Ты $Gодолел:одолела; Искателя Ветра. Отнеси этой лживой предательской скотине, Демитриану, дремлющий клинок и заставь его воспользоваться предоставленными ему материалами и пробудить Громовую ярость.', },
    {'Отдайте Дремлющий клинок Игривого Ветра верховному лорду Демитриану.', },
  },
  [7788] = {
    'Долой захватчиков!',
    {'$GЮный:Юная:c; |3-6($C)! Миновав этот проход, ты увидишь поле яростных сражений. Орда продолжает вырубать наши священные леса, уничтожая вечнозеленые кроны, чтобы питать свою военную машину!$B$BОтправляйся в Ущелье Песни Войны, чтобы оборонять Заставу Среброкрылых! Отражай нападения ордынских захватчиков!$B$BНаградой тебе будет талисман заслуг. Принеси мне такой талисман, $N, и получишь награду!', },
    {'Войдите в Ущелье Песни Войны, одержите победу над Ордой, получите почетный знак Ущелья Песни Войны и вернитесь с ним к часовому Дальной Песне в рощу Среброкрылых.', },
  },
  [7789] = {
    'Громи захватчиков-Среброкрылых!',
    {'Чащобы Ясеневого леса падут пред мощью Орды, |3-6($R)! Что бы ни делали часовые из клана Среброкрылых, им не совладать с нашим напором. Калимдор – это владения Орды! Как смеют они препятствовать нам пользоваться тем, что по праву наше?$B$BПусть гордость вскипает в твоей груди, когда ты доблестно пресекаешь их жалкие попытки остановить Орду! Разгроми часовых из клана Среброкрылых и заслужи почетный знак Ущелья Песни Войны. Вернись ко мне с этим знаком, $N – и получишь награду!', },
    {'Войдите в Ущелье Песни Войны, одержите победу над Альянсом, получите почетный знак ущелья Песни Войны и вернитесь с ним к капитану Лободробу в лагерь Мор\'шан.', },
