  [7828] = {
    'Охота на бродяг',
    {'В диких лесах Внутренних земель погибло много храбрых троллей, |3-6($R). Одни из самых опасных животных там – среброгривые волки. Они прячутся в высокой траве и всегда нападают неожиданно.$B$BНельзя закрывать глаза на такую опасность!$B$BУменьшите поголовье среброгривов!', },
    {'Убейте 5 среброгривых бродяг и 5 среброгривых ревунов по поручению охотника Мархора из деревни Сломанного Клыка во Внутренних землях.$B$BМархор сказал, что волки обитают в лесах Внутренних земель.', },
  },
  [7829] = {
    'Охота на дикарей',
    {'Одни из самых мерзких существ во Внутренних землях – дикие совухи. Сколько храбрых воинов Сломанного Клыка они уже разорвали своими когтями и клювами...$B$BУбейте всех диких совухов, которых встретите, |3-6($R). Ваша помощь будет взвешена, измерена и по достоинству оценена.', },
    {'Убейте 10 диких совухов по поручению охотника Мархора из деревни Сломанного Клыка во Внутренних землях.$B$BМархор сказал, что совухи обитают в лесах Внутренних земель.', },
  },
  [7830] = {
    'Отмщение за погибших',
    {'Мерзавцы из клана Громового Молота учат своих грифонов убивать троллей. Ужасно, конечно, но не беспокойся, мы этого так не оставим.$B$BПока что я хочу, чтобы ты $Gзанялся:занялась; уничтожением самых главных грифонов этих мест. Этих остроклювов зовут еще повелителями небес. $B$BУбей одного из них и принеси мне его перо.', },
    {'Убейте остроклюва-повелителя небес и принесите его перо охотнику Мархору в деревню Сломанного Клыка.$B$BМархор говорил, что остроклювы водятся в диких местах Внутренних земель.', },
