  [7839] = {
    'Хулиганы Порочной Ветви',
    {'Эти негодяи из племени Порочной Ветви напали на нашу деревню и украли мои инструменты! У меня, конечно, есть запасные, но тот набор, что они уволокли, был мне дорог как память. Когда я был еще мальчишкой, мне спас жизнь один гном, а потом он подарил мне тот набор.$B$BЯ очень хочу вернуть их, $N, я без них как без рук!$B$BМархор сказал, что мои инструменты видели в храмах Агол\'вата и Шаол\'вата, они оба на северо-востоке Внутренних земель.$B$BПожалуйста, верните мне инструменты!', },
    {'Найдите потерянные инструменты кузнеца Углежога из деревни Сломанного Клыка во Внутренних землях.$B$BУглежог сказал, что инструменты могут быть в одном из храмов троллей Порочной Ветви в северо-восточной части Внутренних земель. Также следует проверить на Осклизлой скале.', },
  },
  [7840] = {
    'Лярд потерял свой обед',
    {'Лярд хотел устроить пикник в секретном месте, а потом пришли злые тролли племени Порочной Ветви и побили Лярда большими палками. Лярд убежал в деревню, но потерял свой обед. Лярд голодный... Может, сходишь в секретное место и найдешь там мой обед? Лярд боится. Это на севере! Остров на севере, там секретное место!', },
    {'Найдите обед Лярда из деревни Сломанного Клыка во Внутренних землях.$B$BЛярд сказал, что потерял свой обед на острове к северу, где могут встретиться тролли из племени Порочной Ветви.', },
  },
  [7841] = {
    'Послание клану Громового Молота',
    {'Клан Громового Молота якшается с нашими врагами и заключает с ними союзы. В этот раз они спутались с высшими эльфами. Убейте любого высшего эльфа из Высокодолья, которого только встретите – пусть их тела послужат предупреждением.$B$BС троллями шутки плохи!', },
    {'Убейте 8 гонцов, 5 разведчиков, 5 стрелков и 2 следопытов Высокодолья по поручению Отхо Можи\'ко из деревни Сломанного Клыка.$B$BВысших эльфов из Высокодолья можно найти в Сторожке Кель\'Данил в северо-западной части Внутренних земель.', },
  },
  [7842] = {
    'Еще одно послание клану Громового Молота',
    {'Говорят, что дворфы Громового Молота и их грифоны – друзья не разлей вода. Хоть это и не очень красиво, но нужно воспользоваться этим преимуществом. Сделаем им еще одно предупреждение.$B$BУбей всех грифонов, каких только встретишь во Внутренних землях, и принеси мне длинное элегантное перо – это должно подействовать.', },
    {'Принесите длинное элегантное перо Отхо Можи\'ко из деревни Сломанного Клыка во Внутренних землях. Перо нужно собрать с местных грифонов, которых можно найти практически в любом месте Внутренних земель.', },
  },
  [7843] = {
    'Последнее послание клану Громового Молота',
    {'*Отхо вручает вам длинное окровавленное копье, обернутое перьями грифона*$B$BВот, берите. Отнесите это на Заоблачный пик, в самый центр тамошнего поселения. Воткните копье у колодца, пусть эти дворфы знают, что с нами шутки плохи! А потом возвращайтесь ко мне. Надеюсь еще увидеть вас $Gживым:живой;.', },
    {'Заберитесь на Заоблачный пик в западной части Внутренних Земель и оставьте возле колодца в центре города последнее послание клану Громового Молота. После выполнения задания возвращайтесь к Отхо Можи\'ко в деревню Сломанного Клыка.$B$BБерегитесь дворфов Громового Молота и других сторонников Альянса.', },
  },
  [7844] = {
    'Собратья-каннибалы',
    {'Во Внутренних землях живет три племени троллей, $N. На этом, собственно, сходство и заканчивается – мы все тролли и мы все живем здесь.$B$BПлемя Порочной Ветви, как следует из названия, самое отвратительное – тамошние каннибалы едят и троллей, и вообще всех, кто подвернется.$B$BЕсли бы Зул\'джин был здесь, он бы уничтожил все это отродье одним движением руки! Но Зул\'джина с нами нет, поэтому приходится справляться самим.$B$BВот вам задание – пойти и убить троллей из племени Порочной Ветви.', },
    {'Убейте 6 охотников за скальпами и 2 ворожеев из племени Порочной Ветви по поручению Яо\'джин из деревни Сломанного Клыка.$B$BЯо\'джин сказала, что эти тролли живут близ храмов Шаол\'вата и Агол\'вата в северо-восточной части Внутренних земель.', },
  },
  [7845] = {
    'Похищен старейшина Сломанный Клык!',
    {'Однажды ночью клан Порочной Ветви совершил набег на нашу деревню, и похитил моего мужа. Наши разведчики узнали, что он еще жив и находится в плену на самом верху Джинта\'Алора. Они сказали, что коварная жрица Ведьмиса хочет принести его в жертву их безлицему кровавому богу Хаккару.$B$BПрошу вас, $N, спасите его! Наша деревня не переживет такой тяжелой потери.', },
    {'Спасите старейшину Сломанного Клыка из Джинта\'Алора по поручению его жены из деревни Сломанного Клыка.$B$BСтарейшину держат в плену на самом верху Джинта\'Алор – главного поселения троллей племени Порочной Ветви, расположенного на юге Внутренних земель.', },
  },
  [7846] = {
    'Отыскать ключ!',
    {'Ключ от моих кандалов у Хита\'йи Хранительницы, она в пещере поблизости. Но будь $Gосторожен:осторожна;, там полно охраны!$B$BУбей Хранительницу и возьми у нее ключ, иначе мне отсюда не выйти.', },
    {'Убейте хранительницу Хита\'йю и заберите у нее ключ от кандалов, после чего возвращайтесь к старейшине Сломанному Клыку и освободите его.', },
  },
  [7847] = {
    'Возвращение к старейшине племени Сломанного Клыка',
    {'Вернитесь к моей жене, старейшей Зазубренный Клык! Скажите ей, что со мной все хорошо, и что скоро я буду дома. Она непременно наградит вас за это известие.', },
    {'Возвращайтесь к старейшине Сломанному Клыку и сообщите ей, что ее муж спасен.', },
  },
  [7848] = {
    'Сродство с недрами',
    {'Земля испещрена трещинами и разломами – прямо у нас под ногами, |3-6($R). В двух шагах от того места, где ты сейчас стоишь, есть разлом, ведущий в недра в Черной горы, в самую вотчину Повелителя огня.$B$B$GУдивлен:Удивлена;? Прискорбно... Смертные не в силах понять то, что они не могут увидеть, пощупать, попробовать на зуб...$B$BУверяю тебя, ты стоишь прямо рядом с порталом и вполне можешь им воспользоваться.$B$BСмотрю, у тебя глаза заблестели. Послушай, приобрести сродство с недрами не так сложно. Отправляйся в Глубины Черной горы и принеси мне осколок из Огненных Недр. Я сделаю так, что ты сможешь видеть портал.', },
    {'Отправляйтесь через портал, ведущий к Огненным Недрам в Глубинах Черной горы и добудьте осколок из Огненных Недр. Принесите его Лотосу Хранителю Портала в Черной горе.', },
  },
  [7849] = {
    'Воссоединение останков',
    {'Как и многие жители нашей деревни, мой близкий человек погиб в лесу. Это был мой брат-близнец Мальхор – он погиб в бою с троллями Порочной Ветви, тогда же, когда пропал муж старейшей клана Зазубренный Клык.$B$BМоего брата очень быстро казнили, а его останки бросили на съедение слизнюкам и волкам.$B$BЖизнь его мы не уберегли, но душу спасти еще можно. Пожалуйста, принесите мне его останки, $N.', },
    {'Принесите охотнику Мархору из деревни Сломанного Клыка во Внутренних землях кости и череп его брата-близнеца.$B$BМархор сказал, что останки его брата бросили на съедение слизнюкам и волкам Порочной Ветви – все эти твари водятся неподалеку от вершины Джинта\'Алор: слизнюки в компостной яме, а волки – в логове.', },
  },
  [7850] = {
    'Темные сосуды',
    {'Тролли племени Порочной Ветви проявляют в бою необычайную свирепость! Это все потому, что их околдовала недобрая коварная жрица Ведьмиса.$B$BВ поселении Джинта\'Алор можно найти очень много сосудов с порченой кровью, которые излучают темную магию безлицего кровавого бога и наделяют троллей сверхъестественными силами, сводя их с ума.$B$BУкрадите эти сосуды и принесите их мне, чтобы я сняла порчу и ослабила хватку кровавого бога.', },
    {'Принесите 5 сосудов с порченой кровью старейшине племени Сломанного Клыка во Внутренние земли. Сосуды с кровью находятся в поселении Джинта\'Алор.', },
  },
  [7861] = {
    'Разыскивается: коварная жрица Ведьмиса и ее прислужники',
    {'По приказу старейшины племени Сломанного Клыка разыскивается коварная жрица Ведьмиса и ее приспешники.$B$BТот, кто убьет Ведьмису и ее стражей Аман\'заси, получит отличную награду!', },
    {'Убейте коварную жрицу Ведьмису и 5 стражей Аман\'заси из племени Порочной Ветви, после чего доложите старейшине племени Сломанного Клыка во Внутренних землях.$B$BКоварную жрицу Ведьмису и стражей Аман\'заси можно найти в верхней части Джинта\'Алора во Внутренних землях.', },
  },
  [7862] = {
    'Требуется: капитан стражи в деревню Сломанного Клыка',
    {'Капитан стражи Мальхор погиб, и теперь эта должность свободна. Если вы хотите стать капитаном стражи в деревне Сломанного Клыка, выполните эти задания и подайте заявление старейшине племени Сломанного Клыка.$B$BУничтожьте берсерков из племени Порочной Ветви.$B$BУничтожьте темных охотников из племени Порочной Ветви.$B$BУничтожьте кровопийц из племени Порочной Ветви.$B$BУничтожьте пожирателей душ из племени Порочной Ветви.', },
    {'Убейте 5 берсерков, 3 темных охотника, 3 кровопийц и 2 пожирателей душ из племени Порочной Ветви.$B$BПосле выполнения задания вернитесь к старейшине Сломанному Клыку в деревню Сломанного Клыка.', },
  },
