  [8114] = {
    'Контроль над четырьмя базами',
    {'$N, ты бесстрашно $Gворвался:ворвалась; в самое пекло сражения в Низине Арати и $Gзаслужил:заслужила; на этом поле боя великие почести. Ты $Gпоказал:показала; нам, чего стоит |3-6($C) вроде тебя, и $Gпоказал:показала нашим врагам, что мы не сдадимся перед их подлостью!$B$BПоскольку ты уже $Gдоказал:доказала; нам свою ценность, у меня есть для тебя очень трудное задание. Я хочу, чтобы ты $Gзанял и удержал:заняла и удержала; как минимум четыре базы в Низине Арати. Если вам удастся это сделать, мы не только сумеем собрать стратегически важные ресурсы – мы сможем показать Орде наше превосходство!$B$BИдите, $N, и да сопутствует вам удача.', },
    {'Отправляйтесь в Низину Арати, захватите и удержите контроль над четырьмя базами одновременно, затем возвращайтесь к фельдмаршалу Освету в Опорный пункт.', },
  },
  [8115] = {
    'Контроль над пятью базами',
    {'$N, битва за Низину Арати – кровавое предприятие. Ваша помощь очень нужна Лиге Аратора и принимается с большой благодарностью.$B$BУ нас есть для вас еще одно задание.$B$BЛига хочет нанести решительный, сильный удар по оккупирующим Низину Арати Осквернителям. Мы хотим выбить их со всех стратегических точек поля боя!$B$BЭто будет нелегко, но если кто и в состоянии это сделать, так это именно вы.', },
    {'Возьмите под контроль одновременно все 5 баз в Низине Арати, а затем возвращайтесь к фельдмаршалу Освету в Опорный пункт.', },
