  [8149] = {
    'Почести герою',
    {'Во время Недели урожая в Азероте мы возносим хвалу героям, которые готовы жизнь отдать – а порой и отдают – за нас. Альянс славит множество героев, но нет повести печальнее на свете, чем повесть об Утере Светоносном.$B$BПомоги же нам воздать почести Утеру и отнеси эти подношения к его гробнице в Западных Чумных землях. Положи их к подножию его статуи и возвращайся ко мне. Я пробуду тут только до конца праздника, так что не трать времени и отнеси Утеру эти приношения.', },
    {'Пойдите к гробнице Утера в Западных Чумных землях, положите подношения у подножия его статуи и вернутсья к Вагнеру Молотобойцу в Стальгорн до конца Недели урожая.', },
  },
  [8150] = {
    'Почести герою',
    {'Во время Недели урожая мы воздаем почести героям, которые готовы отдать, – а порой и отдают, – за нас жизнь. В Орде множество храбрецов и героев, но нет более яркого примера мужества и самопожертвования, чем Гром Адский Крик.$B$BПомоги же нам воздать почести Грому. Отнеси эти подношения к его памятнику в Каньоне Гибели Демона в Ясеневом лесу. Положи их к ногам статуи и возвращайся ко мне. Я пробуду тут только до конца праздника, так что не теряй времени!', },
    {'Отправляйтесь к памятнику Грому Зычногласому в Каньон Гибели Демона в Ясеневом лесу и положите подношения у подножия памятника. Вернитесь к Явниру Нашаку в Оргриммар до конца Недели урожая.', },
  },
  [8151] = {
    'Талисман охотника',
    {'Огтинк из Азшары ищет опытных охотников вроде тебя. Огтинк – фурболг-охотник, отрекшийся от своего племени ради помощи Кругу Кенария. Он стремится наставлять собратьев по оружию и учить их соблюдению природного равновесия. Тебе не помешает поговорить с ним.$B$BОгтинг живет на скалах Азшары, на северо-востоке от руин Эльдарата.', },
    {'Поговорите с Огтинком из Азшары.', },
  },
  [8153] = {
    'Рога и копыта',
    {'Беговые олени являются обладателями самых мощных рогов из всех известних нам животных. Олень использует рога для обороны, атаки и нередко убийства. Совершенной формы рога являются свидетельством силы духа животного. $B$BДобывший такие рога и сам обретает небывалую силу духа. $B$BПоохоться на мохноногих оленей и принеси мне лучшую пару их рогов.', },
    {'Принесите пару великолепных рогов мохоногого оленя Огтинку в Азшару. Огтинк обитает среди скал в северо-востоку от руин Эльдарата.', },
  },
