  [8231] = {
    'Охота на волношлепа',
    {'Волношлеп – земноводное, встречающееся только в Азшаре. Эти твари сильны и очень живучи. $B$BВ их твердой блестящей чешуе, как в зеркале, отражается мастерство охотника. $B$BВыследи и убей нескольких на побережье Азшары и принеси мне их чешую.', },
    {'Принесите 6 чешуй волношлепа Огтинку в Азшару. Огтинк обитает среди скал в северо-востоку от руин Эльдарата.', },
  },
  [8232] = {
    'Зеленый дракон',
    {'Ты отправляешься на опасную охоту, |3-6($R). Убей зеленого дракона Морфаза и принеси мне его зуб. Морфаз обитает в Храме Атал\'Хаккара. Вход в него ты найдешь на Болоте Печали. $B$BНо помни, $Gодному:одной; тебе его не осилить. У дракона много врагов – найди соратников и уничтожь его! $B$BМногие, очень многие не прочь добыть зуб этой твари. Принеси его мне, и я достойно награжу тебя.', },
    {'Принесите зуб Морфаза Огтинку в Азшару. Огтинк обитает среди скал в северо-востоку от руин Эльдарата.', },
  },
  [8233] = {
    'Приглашение',
    {'Лорд Джорах Черный Ворон настоятельно приглашает вас к себе. Приглашение тайное, поэтому никаких подробностей.$B$BПоместье Черный Ворон находится в предгорьях Хилсбрада – горная тропа начинается на северо-западе от крепости Дарнхольд.', },
    {'Поговорите с лордом Джорахом Черным Вороном в его поместье.', },
  },
  [8234] = {
    'Запечатанная лазурная сумка',
    {'Начнем сначала. Я потерял небольшую сумку голубого цвета. В ней были мои личные вещи. $B$BНет нужды искать воров – они уже мертвы. Мне известно лишь, что эти дураки не сумели открыть сумку и продали ее какому-то шаману из племени Древобрюхов в Азшаре, выдав ее за священную реликвию. Очень умно. $B$BЯ хочу, чтобы ты $Gотправился:отправилась; в Азшару и $Gзаполучил:заполучила; у шамана мою сумку – хитростью или силой. Полагаю, что лучше все же хитростью. Сумку отнеси верховному магу Ксилему.', },
    {'Добудьте запечатанную лазурную сумку у шамана из племени Древобрюхов в Азшаре. Затем отнесите сумку верховному магу Ксилему в Азшаре.', },
  },
  [8235] = {
    'Зашифрованные фрагменты',
    {'Лорд Черный Ворон просил нас с тобой об услуге. Он желает снять заклятие с этой сумки. $B$BК сожалению, мои книги по снятию чар были захвачены и уничтожены дикарями. Каждый клочок, оставшийся от них, невероятно ценен для меня. $B$BЕсли ты сможешь принести мне зашифрованные фрагменты, я попробую восстановить хотя бы часть моих книг. Чтобы найти фрагменты, поохоться на обитающих в Азшаре зверей – они обычно подбирают все, что плохо лежит.', },
    {'Принесите 10 зашифрованных фрагментов верховному магу Ксилему в Азшару.', },
  },
  [8236] = {
    'Лазурный ключ',
    {'Мне потребуется время, чтобы расшифровать фрагменты и снять заклятие. Когда я закончу, то самолично доставлю сумку лорду Черному Ворону. $B$BОднако нам все еще нужен ключ. Говорят, что ключ присвоил зеленый дракон по имени Морфаз. Его логово находится в глубинах Храма Атал\'Хаккара – вход в него ты найдешь на Болоте Печали. $B$BСобери своих сподвижников и отправляйся к логову дракона. Лорд Черный Ворон ждет, когда ты принесешь ему ключ.', },
    {'Принесите лазурный ключ лорду Черному Ворону.', },
  },
  [8238] = {
    'Монеты племен: Гурубаши, Порочная Ветвь и Сухокожие',
    nil,
