  [8297] = {
    'Ресурсы Низины Арати',
    {'Войну выигрывают солдаты – любой солдат вам это скажет. Скажет он вам и то, что битва, которую ведут с плохим оружием или на пустой желудок, проиграна прежде, чем началась!$B$BВот почему так важна Низина Арати. В этой низине есть ключевые точки, где сосредоточены важные ресурсы. Металл, оружие, пища, древесина – все это необходимо и все это можно добыть там.$B$BИ вот чего я хочу от вас, $N. Отправляйтесь в Низину Арати, выиграйте битву, удержав больше баз, чем это удастся врагам, и принесите мне ящик с ресурсами.', },
    {'Выиграйте битву за Низину Арати, добудьте ящик с ресурсами из Арати и принесите его сержанту Макчисту в Опорный пункт.', },
  },
  [8298] = {
    'Новые ящики с ресурсами',
    {'Войну выигрывают солдаты – любой солдат вам это скажет. Скажет он вам и то, что битва, которую ведут с плохим оружием или на пустой желудок, проиграна прежде, чем началась!$B$BВот почему так важна Низина Арати. В этой низине есть ключевые точки, где сосредоточены важные ресурсы. Металл, оружие, пища, древесина – все это необходимо и все это можно добыть там.$B$BИ вот чего я хочу от вас, $N. Отправляйтесь в Низину Арати, выиграйте битву, удержав больше баз, чем это удастся врагам, и принесите мне ящик с ресурсами.', },
    {'Выиграйте битву за Низину Арати, добудьте ящик с ресурсами из Арати и принесите его сержанту Макчисту в Опорный пункт.', },
  },
  [8299] = {
    'Перекрыть линии поставок Аратора',
    {'Лига Аратора не жалеет средств в попытке вновь захватить контроль над Арати. Они, вместе со всеми расами Альянса, стекаются сейчас в эту отдаленную область, намереваясь распространить свои владения за пределы Опорного пункта. Мы должны остановить их, а наилучшее средство для этого – захватить их припасы.$B$BВы, $N, можете нам помочь. Отправляйтесь в Низину Арати, известную своими богатыми рудниками, плодородными почвами и умелыми ремесленниками. Захватите и удерживайте под своим контролем те базы, которые сможете, выиграйте битву и возвращайтесь ко мне с ресурсами, которые вам удастся добыть.', },
    {'Выиграйте битву за Низину Арати, добудьте ящик с ресурсами из Арати и принесите его стражу смерти Мортису в Павший Молот.', },
  },
  [8300] = {
    'Новые ящики с ресурсами',
    {'Лига Аратора не жалеет средств в попытке вновь захватить контроль над Арати. Они, вместе со всеми расами Альянса, стекаются сейчас в эту отдаленную область, намереваясь распространить свои владения за пределы Опорного пункта. Мы должны остановить их, а наилучшее средство для этого – захватить их припасы.$B$BВы, $N, можете нам помочь. Отправляйтесь в Низину Арати, известную своими богатыми рудниками, плодородными почвами и умелыми ремесленниками. Захватите и удерживайте под своим контролем те базы, которые сможете, выиграйте битву и возвращайтесь ко мне с ресурсами, которые вам удастся добыть.', },
    {'Выиграйте битву за Низину Арати, добудьте ящик с ресурсами из Арати и принесите его стражу смерти Мортису в Павший Молот.', },
  },
  [8301] = {
    'Путь праведника',
    {'Род Ноздорму инстинктивно не верит низшим расам. Хоть ты и $Gсмог:смогла; доказать мне, что у тебя задатки настоящего героя, пришло время доказать это моему повелителю.$B$BУлей Аши, улей Зора и улей Регал кишат силитидами, которые грозят нашему существованию. Убей как можно больше проклятых тварей и принеси мне фрагменты их панцирей. Когда у тебя закончится место в заплечных мешках, возвращайся и я укреплю твою решимость.', },
    {'Принесите Баристолфу 200 фрагментов силитидских панцирей.', },
  },
  [8302] = {
    'Рука праведника',
    {'Старательно выбирай своих представителей, $Gсмертный:смертная;. Всякий раз, выполнив задание, ты сможешь выбрать себе нового представителя.$B$BА теперь, не теряя времени, скажу: мне по-прежнему нужны обломки силитидских панцирей. Что стоишь? Иди!', },
    {'Принесите Баристолфу 200 фрагментов силитидских панцирей.', },
  },
  [8303] = {
    nil,
    {'Время пришло, $N.$B$BЯ должен извиниться за то, что так долго держал все в тайне. Но кругом враги, они могут оказаться у нас прямо под носом. Прошу тебя, прими этот фолиант, читай его и учись. Я с радостью делюсь с тобой знаниями. Теперь же отыщи нашего повелителя.', },
    {'Отыщите Анахроноса в Пещерах Времени в Танарисе.', },
  },
  [8304] = {
    'Дражайшая Наталия',
    {'Моя милая Наталия пропала.$B$BЧестно говоря, в последние недели перед исчезновением она довольно странно вела себя. Я несколько раз видел, как она говорила сама с собой. При этом Наталия уверяла, что все в порядке и ей необходимо продолжать исследования.$B$BВ последний раз я видел ее, когда она направилась в лагерь Бронзоборода, на юг.$B$BУ меня слишком мало солдат, $N. Сходи в лагерь Бронзоборода и допроси дворфов.', },
    {'Допросите обитателей лагеря Бронзоборода по поручению командира Мар\'алит из Крепости Кенария. Этот лагерь находится южнее Крепости Кенария.$B$BСобрав необходимые сведения, вернитесь к командиру Мар\'алиту.', },
  },
  [8305] = {
    'Давно забытые воспоминания',
    {'В пустыне неподалеку от Силитуса, рядом со священной стеной, есть хрустальная слеза. Эта слеза – символ великих потерь, понесенных во время войны Зыбучих песков. Отыщи эту слезу и загляни в ее глубины, дабы достичь просветления.', },
    {'Отыщите в Силитусе хрустальную слезу и загляните в ее глубины.', },
  },
  [8306] = {
    'В бездне безумия',
    {'Прошу тебя, $N, отправляйся в Улей Регал и узнай, что сталось с моей возлюбленной Наталией. Это будет очень непросто. Прежде чем нырять в бездну безумия, вернись и поговори с дворфами. Быть может, они подскажут тебе, как разобраться с силитидами из улья.$B$BИ последнее, $N. Если она в самом деле безумна, как говорят... сделай, что должно.$B$B*Командир Мар\'алит отворачивается в сторону.*', },
    {'Найдите возлюбленную командира Мар\'алита из Крепости Кенария в Силитусе. По имеющим сведениям, госпожу Наталию Мар\'алит можно найти в Улье Регал.$B$BПрежде чем отправляться в улей, не забудьте поговорить с дворфами из лагеря Бронзоборода. Они могут дать пару советов и подкинуть дополнительную работу.$B$BСледует помнить слова командира: "Сделай, что должно..."', },
  },
  [8307] = {
    'Блюдо из червей',
    {'Приспособиться к жизни в пустыне, мягко говоря, нелегко. Мне не хватает не только птиц и деревьев Аубердина, но и нормальной еды! Я делаю все, что моих силах, но выбор продуктов у нас невелик. Я изучаю местные травы, но их так мало…$B$BЗато я видела, как в лагерях Сумеречного Молота охотятся на песчаных червей. Но их мясо очень горькое, и я не представляю, как сделать его съедобным. Если ты окажешься в их лагере, смотри в оба. Как узнаешь что-нибудь – сообщи мне.', },
    {'Поищите в лагере Сумеречного Молота на западе от Крепости Кенария сведения о приготовлении мяса песчаных червей.', },
  },
  [8308] = {
    'Потерянное письмо Бранна Бронзоборода',
    {'Всем заинтересованным лицам:$B$BЕсли вы читаете это письмо, значит, я мертв или скоро умру. Трудно сказать. Большая часть команды уже мертва.$B$BНашел сведения про титана. Страшная битва между ним и Древним богом. Самый настоящий Древний Бог. Нашел древний туннель в Ан\'Кираж. Иду по нему.$B$BОтнесите это письмо в мой старый лагерь. Покормите, напоите и приласкайте моих обезьянок.$B$B- Бранн Бронзобород, оперативный исследователь$B$BP.S. Остерегайся безумного ночного эльфа, поклоняющегося Древнему богу.', },
    {'Отнесите письмо Бранна Бронзоборода в лагерь Бронзоборода в южной части Силитуса.', },
  },
  [8309] = {
    'Поиски рун',
    {'После нашего первого рейда в ульи и последовавшего безумия госпожи Мар\'алит, их этих трех ульев так и хлынули проклятые жуки! Мне кажется, их повышенная активность связана с письменами, покрывающими эти странные кристаллы.$B$BНам нужны оттиски с кристаллов из всех трех ульев. Заполучив их, мы можем попытаться понять причины этого безумия.$B$BВозьми копирующее устройство геолога и ступай в ульи, $N. Без оттисков из всех ульев даже не возвращайся.', },
    {'Рутгар Знакописец из лагеря Бронзоборода в Силитусе просит отправиться в улья Аши, Зора и Регал и сделать оттиски со всех покрытых письменами кристаллов.$B$BЧтобы сделать приличные копии древних рун, можно воспользоваться копирующим устройством геолога. По выполнении задания вернуться к Рутгару Знакописцу.', },
  },
  [8310] = {
    'Расшифровка',
    {'Мне кажется, я изобрел эликсир, который поможет нам расшифровать письмена и язык силитидов и киражей! Что мне нужно? Хорошо, что ты $Gспросил:спросила;! Мне нужны мозги! Точнее говоря, мозги всех видов силитидов, живущих в ульях. Принеси мне мозги, и работа Рутгара не пойдет насмарку.', },
    {'Добудьте для Франкала Каменного Моста из лагеря Бронзоборода в Силитусе мозг силитидов из ульев Зора, Аши и Регал. По выполнении задания вернитесь к Франкалу.', },
  },
  [8311] = {
    'Тыквовинские вкусности для Джеспера!',
    {'Хотел бы я пойти потыквовинничать, но не могу – приболел. Если ты вместо меня сходишь, я обменяю тебе сладости, что ты наберешь, на те, что у меня уже есть. Думаю, тебе они понравятся – они превратят тебя в разные смешные и страшные штуки!$B$BУ каждого из хозяев таверн в больших городах есть сладости, которые они раздают на Тыквовин. Поговори с ними и выполни их задания. А еще в Стальгорне живет гном по имени Талваш, и он тоже раздает сладости. Когда соберешь их все, принеси мне сюда!', },
    {'Поговорите с хозяевами таверн в Штормграде, Стальгорне и Дарнасе, а также с Талвашом дель Кисселем в Стальгорне. Выполните то, что они потребуют за предлагамые ими сладости.$B$BВернитесь к Джесперу в сиротский приют Штормграда и принесите ему марципан Дарнаса, леденец Гномрегана, нугу из Штормграда и конфету Стальгорна.', },
  },
  [8312] = {
    'Тыквовинские вкусности для Крохи!',
    {'Я бы так хотела отправиться за конфетами! Но не могу, потому что болею... Сходи вместо меня, а? А я с тобой потом поменяюсь: отдам те конфеты, что у меня уже есть, взамен тех, которые дадут тебе! Вот эта конфетка тебе наверняка понравится: она позволяет превращаться в разных существ, забавных и страшных!$B$BУ всех хозяев таверн в больших городах непременно есть конфеты, которые они дарят на Тыквовин – надо только поговорить с ними и сделать то, что они попросят. И троллиха по имени Кали Ремик в деревне Сен\'джин тоже раздает конфеты. Когда соберешь все конфеты, принеси их мне, ладно?', },
    {'Поговорите с хозяевами таверн в Оргриммаре, Подгороде и Громовом Утесе и с Кали Ремик из деревни Сен\'джин. Выполните то, что они потребуют, за предлагамые ими сладости.$B$BВернитесь к Крохе в сиротский приют Оргриммара и принесите ей марципан Громового Утеса, леденец троллей Черного Копья, нугу из Оргриммара и конфету Подгорода.', },
  },
  [8313] = {
    'Наделение знаниями',
    {'Хотя вам вполне понятен рецепт по приготовлению мяса песчаного червя, в нем упоминаются незнакомые травы и специи. Быть может, Каландрата хорошо разбирается в растениях пустыни и сможет помочь вам.', },
    {'Принесите вырванную страницу с рецептом Каландрате в таверну Крепости Кенария.', },
  },
  [8314] = {
    'Разгадка тайны',
    {'Мы на грани крупного прорыва, $N! Принесенные тобой оттиски позволили нам воссоздать письмена на хрустальной призме. Очевидно, что для киражей и прочих непонятных сущностей они имеют большое значение и позволяют передавать силитидам сообщения.$B$BК несчастью, это все, что нам удалось понять. Необходимо прочитать послания, сокрытые в кристаллах.$B$BОтнеси покрытую письменами хрустальную призму геологу Ехидной в Крепость Кенария. Может, она сумеет тебе помочь.', },
    {'Рутгар Знакописец из лагеря Бронзоборода в Силитусе просит вас отнести покрытую письменами хрустальную призму геологу Ехидной в Крепость Кенария.', },
  },
  [8315] = {
    'Зов',
    {'Здесь изображена встреча посланника киражей с тремя силитидами у костей большого дракона. Над рисунком я вижу надпись, судя по всему, какой-то шифр. Не исключено, что это ключевой момент призыва посланника.$B$BЯ создам копию, попробуй воспользоваться ей в пустыне у Костей Граккаронда. Если я права, вожди решат, что им посылают какие-то данные и явятся на зов. Убей их и сразись с посланником. Ключ к кристаллам у него...', },
    {'Геолог Ехидная из Крепости Кенария просит добыть открыватель Кристаллов у киражского посланника.$B$BВам следует отнести письмена зова к Костям Граккаронда, на юг от Крепости Кенария, и нарисовать их на песке. Если появится киражский посланник, убейте его и заберите открыватель Кристаллов. При успехе вернитесь к геологу Ехидной.$B$BДля выполнения задания понадобится целая армия!', },
  },
  [8316] = {
    'Оружие войны',
    nil,
    nil,
  },
  [8317] = {
    'Помощь по кухне',
    {'Рецепт мне несомненно пригодится. Пора приниматься за работу, чтобы приготовить копченые пустынные клецки уже сегодня. Мне не помешала бы помощь, а уж я бы тебе отплатила сторицей.', },
    {'Каландрате из Крепости Кенария необходимы 10 копченых пустынных клецок.', },
  },
  [8318] = {
    'Тайные послания',
    {'Некоторые из служителей культа Сумеречного Молота носят с собой листы бумаги, на которых нацарапана какая-то неразборчивая писанина. Принеси мне целую пачку таких писулек.$B$BЯ не могу понять, что означает эта ерунда, но дело не в том! При помощи этих бумажек происходят тайные переговоры их группировок. Чем больше текстов мы у них отнимем, тем сложнее им будет обмениваться сообщениями!', },
    {'Принесите 10 зашифрованных Сумрачных текстов Бору Буйногривому в Крепость Кенария.', },
  },
  [8319] = {
    'Зашифрованные Сумеречные тексты',
    nil,
    nil,
  },
  [8320] = {
    'Геолорды из культа Сумеречного Молота',
    {'Сумеречный Молот, безумная секта, поклоняющаяся богам смерти и разрушения, разбила лагеря по всему Силитусу и день и ночь проводит свои ритуалы. Планы ее стронников нам неизвестны, но они как-то связаны с пульсирующими красными кристаллами, разбросанными по всей пустыне.$B$BХотя, быть может, однажды нам удастся разгадать эту тайну, но сейчас главное – избавиться от угрозы. Ступай в их лагеря и найди там геолордов из культа Сумеречного Молота. Они командуют этими змеюками, поэтому нанести удар необходимо именно по ним!', },
    {'Убейте 10 геолордов из культа Сумеречного Молота и вернитесь к Хууму Буйногривому в Крепость Кенария.', },
  },
  [8321] = {
    'Вайрал Подлый',
    {'Мы обнаружили еще одну базу Сумеречного Молота. Она зовется Сумеречная пещера и находится в северо-восточной части Силитуса. Их главарь, Вайрал Подлый, живет именно в этой пещере, и его-то тебе и предстоит победить. Одолев злодея, принеси мне его перстень-печатку.$B$BГоворят, что в Сумеречной пещере можно насмотреться таких ужасов, что век не забудешь.', },
    {'Принесите перстень-печатку Вайрала Хууму Буйногривому в Крепость Кенария.', },
  },
  [8322] = {
    'Тухлые яйца',
    {'Люди Южнобережья любят эль, особенно тот, что подают в местной таверне. Думаю, тыквовинского хаоса им маловато – надо бы подпортить им недельный, а то и месячный запас выпивки!$B$BВозьмите вот эти тухлые яйца и подбросьте в главную бочку с пивом в таверне Южнобережья. Однако вашего присутствия там не потерпят, поэтому приготовьтесь защищаться!$B$BКак только угостите все Южнобережье этим незабываемым элем, возвращайтесь на наш фестиваль!', },
    {'Бросьте тухлые яйца в главную бочку с пивом в таверне Южнобережья.', },
  },
  [8323] = {
    'Истинно верующие',
    {'Теперь, когда у меня есть Сумеречный словарь, мне остается попросить тебя о небольшой услуге.$B$BВ моей бывшей организации ежемесячно издаются тщательно закодированные сводки новостей.$B$BЕсли они получат новые сведения обо мне или решат перестать меня искать, об этом сообщат в "Истинно верующем".$B$BХотя меня, безусловно, интересует подобная информация, в этих сводках могут быть и другие полезные сведения. Принеси мне как можно больше зашифрованных текстов, добытых у сторонников Сумеречного Молота.', },
    {'Принесите 10 зашифрованных Сумеречных текстов отшельнику Ортеллу.', },
  },
  [8324] = {
    'Не теряя веры',
    nil,
    nil,
  },
  [8331] = {
    'Аурель Золотой Лист',
    {'Мы надеялись, что Сумеречный Молот свернет свои операции в Силитусе... Увы, мы ошиблись. Благодаря постоянным нападением на лагеря сектантов нам удалось выяснить, что Молот контролирует еще более страшная сила, Совет Бездны. Для связи со служителями культа этот совет пользуется ветровыми камнями, рассеянными по лагерям Молота. Пришло время выступить против Совета Бездны.$B$BПоговори с ремесленницей Аурель Золотой Лист, в восточной части Крепости Кенария. Мы задумали одну хитрость, для которой необходимо ее искусство.', },
    {'Поговорите с Аурель Золотой Лист в Крепости Кенария.', },
  },
  [8332] = {
    'Герцоги Совета',
    {'Мы думаем, что при помощи ветрового камня можно устроить засаду на герцога Совета Бездны. Для этого придется связаться с Советом через этот камень и убедить всех, что ты – $Gодин:одна; из командующих среднего ранга Сумеречного Молота.$B$BЧтобы эта хитрость удалась, необходим статусный медальон. Я могу создать его, но мне понадобится большой сверкающий осколок и талисманы храмовников Бездны.$B$BПринеси их мне, а остальное я сделаю сама.', },
    {'Принесите Аурель Золотой Лист в Крепость Кенария 1 большой сверкающий осколок и 3 талисмана Бездны.', },
  },
  [8333] = {
    'Статусный медальон',
    nil,
    nil,
  },
  [8341] = {
    'Лорды Совета',
    {'Чтобы вызвать лордов Совета Бездны, необходим больший ветровой камень. К тому же они согласны беседовать лишь с самыми высокопоставленными представителями Сумеречного Молота. Но... Я придумала, как поработать над твоей маскировкой и обмануть лордов Бездны!$B$BДля этого понадобится кольцо Власти, которое носят лишь вожди сектантов. Я готова создать такое кольцо, если ты принесешь мне большие сверкающие осколки и перстни Бездны.', },
    {'Принесите Аурель Золотой Лист в Крепость Кенария 5 больших сверкающих осколков и 3 перстня Бездны.', },
  },
  [8342] = {
    'Кольцо Власти служителя культа Сумеречного Молота',
    nil,
    nil,
  },
  [8343] = {
    'Открытие Золотого Листа',
    {'$N, Аурель придумала, как вызвать и одолеть правящую верхушку Совета Бездны! Это опасно, но ты $Gзаслужил:заслужила; уважение всего Круга Кенария. Мы знаем, что ты – $Gединственный:единственная;, кому по плечу такое задание.$B$BЖелаю удачи, $N. Когда решишь приняться за это задание, поговори с Аурель.', },
    {'Поговорите с Аурель Золотой Лист в Крепости Кенария.', },
  },
  [8348] = {
    'Перстень герцогов',
    {'Хуум говорит, что ты $Gпридумал:придумала;, как вызвать герцогов Бездны! Это большое достижение.$B$BЗнаешь, что бы еще хорошо сделать? Убить одного из них. Это произвело бы неизгладимое впечатление на их прихвостней, если ты понимаешь, о чем я. В доказательство собственного подвига принеси перстень герцога.', },
    {'Принесите перстень Бездны Бору Буйногривому из Крепости Кенария.', },
  },
  [8349] = {
    'Бор Буйногривый',
    {'Теперь у тебя есть медальон, и ты можешь вызвать герцога Бездны. Поговори с Бором Буйногривым. Он даст тебе задание.$B$BУдачи, $N. Если тебе снова понадобятся медальоны, возвращайся ко мне.', },
    {'Поговорите с Бором Буйногривым из Крепости Кенария.', },
  },
  [8351] = {
    'Разговор с Бором',
    {'Теперь ты можешь замаскироваться под высокопоставленного представителя Сумрачного Совета, и с тобой хочет поговорить Бор Буйногривый. Ступай, поговори с ним и готовься к трудному заданию...', },
    {'Поговорите с Бором Буйногривым из Крепости Кенария.', },
  },
  [8352] = {
    'Скипетр Совета',
    {'Мы с Хуумом только что обсуждали тебя. Он говорит, что ты $Gготов:готова; испытать себя и сразиться с лордом Бездны!$B$BДолжен предупредить, что это очень опасно. Возьми с собой подкрепление. И не пару друзей, а хороший боевой отряд. Если твои друзья стоят тебя, кто знает, может быть, удастся одолеть этого лорда!$B$BЕсли ты вдруг справишься – мне как всегда потребуются доказательства – то тебя ждет великая награда.', },
    {'Принесите скипетр Бездны Бору Буйногривому из Крепости Кенария.', },
  },
  [8353] = {
    'Курочка с мятой',
    {'А, тыквовинничаем для больных деток? Я сказал бы, ты малость $Gстароват:старовата; конфетки выпрашивать...$B$BЧто же, за мое лакомство – мятную конфету Стальгорна – ты... поквохчешь как курица! Да, курочки любят мятные конфетки... впрочем, может и не любят... главное, что конфетку ты не получишь, пока не поквохчешь!$B$BНу что, |3-6($C), будешь квохтать или нет?! Пора отрабатывать лакомство!', },
    {'Изобразите курицу для хозяина таверны Огневара.', },
  },
  [8354] = {
    'Курочка с мятой',
    {'А, ты у нас сегодня вместо детишек, да? Я сказал бы, ты малость $Gстароват:старовата; конфетки выпрашивать...$B$BЧто же, за мое лакомство – мятную конфету Подгорода – ты... поквохчешь как курица! Да, курочки любят мятные конфетки... впрочем, им не обязательно их ЛЮБИТЬ... главное, что конфетку ты не получишь, пока не поквохчешь!$B$BНу что, |3-6($C), будешь квохтать или нет?! Пора отрабатывать лакомство!', },
    {'Изобразите курицу для хозяина таверны Нормана и получите за это конфету Подгорода!', },
  },
  [8355] = {
    'Плата за леденцы',
    {'А, тебе нужны сладости! Ха-ха!$B$BНу, хотя ты и доброе дело делаешь, собирая сладости для детишек, так просто я тебя не отпущу. Есть у меня вкусные леденцы Гномрегана, и я отдам их детишкам... если ты изобразишь мне паровоз! Ха, вот это будет штука! За паровоз – целый леденец!$B$BТу-ту, |3-6($C)!', },
    {'Изобразите паровозик для Талваша дель Кисселя.', },
  },
  [8356] = {
    'Поиграй мускулами!',
    {'Какой у тебя... интересный костюмчик. Несмотря на то, что ты по доброте своей помогаешь ребенку получить сладости, мне все же придется попросить тебя сделать что-нибудь!$B$BНу-ка, ну-ка... ты выглядишь $Gтаким сильным и мускулистым:такой сильной и мускулистой; в этом... наряде... посмотрим-ка на твои бицепсы! Да, давай, поиграй-ка мускулами, $Gсилач:силачка;!$B$BПоиграешь мускулами – дам тебе вкуснейшей нуги. Это отличное тыквовинское угощение!', },
    {'Поиграйте мускулами перед хозяйкой таверны Аллисон и получите от нее за это нугу из Штормграда.', },
  },
  [8357] = {
    'Танец за марципан',
    {'Хоть это и не твоя прямая обязанность, лакомство тебе придется отработать! От традиции, пожалуй, отходить не будем...$B$BДумаю, я отдам тебе марципан за... ТАНЕЦ! Да-да, покажи, как ты пляшешь! Попляши для меня, и я дам тебе вкусный марципанчик!', },
    {'Потанцуйте для хозяйки таверны Салиенны, и за это вы получите марципан Дарнаса!', },
  },
  [8358] = {
    'Плата за леденцы',
    {'А, тебе нужны сладости? Ну-ну!$B$BЗаруби себе на носу: ты, конечно, занимаешься благородным делом, собираешь сласти для ребенка, но без платы ты ничего не получишь! У меня как раз имеется сладкий-пресладкий леденец троллей Черного Копья, девочка ему будет очень рада... но сначала изобрази-ка ты мне паровозик! То-то я посмеюсь! Посмотри, какой замечательный леденец – а всего-то и надо изобразить паровозик!$B$BНу-ка, |3-6($C)! Чух-чух-чух-чух!', },
    {'Изобразите паровозик перед Кали Ремик и получите в награду леденец троллей Черного Копья!', },
  },
  [8359] = {
    'Поиграй мускулами!',
    {'Ой, какой интересненький костюмчик! Нет, ты, конечно, $Gхороший мальчик:хорошая девочка;, раз помогаешь ребенку собрать сладости, но, боюсь, тебе все же придется выполнить для меня какое-нибудь задание, прежде чем ты получишь конфету.$B$BДай-ка подумать... У тебя такой накачанный вид в этом... костюмчике! Поиграй-ка мускулами!$B$BА я тебе за это дам вкусной оргриммарской нуги. Дети ее очень любят!', },
    {'Поиграйте мускулами перед хозяйкой таверны Гришкой и получите от нее за это нугу из Оргриммара.', },
  },
  [8360] = {
    'Танец за марципан',
    {'Хоть это и не твоя прямая обязанность, лакомство тебе придется отработать! От традиции, пожалуй, отходить не будем...$B$BДумаю, я отдам тебе марципан за... ТАНЕЦ! Да-да, покажи, как ты пляшешь! Попляши для меня, и я дам тебе вкусный марципанчик!', },
    {'Потанцуйте для хозяйки таверны Палы, и за это вы получите марципан Громового Утеса!', },
  },
  [8361] = {
    'Связь с Бездной',
    {'Мы видели, как сектанты проводят ритуалы и вызывают могущественных повелителей стихий. Мы не знаем, поклоняются ли им служители культа или используют как посредников при общении с еще более страшными силами. Ясно одно – они получают приказы от этих элементалей.$B$BОтыщи храмовника Бездны и задай ему хорошую трепку! Стоит лишить войска командира, и в их рядах начинается разброд и шатания. Принеси доказательство своего подвига.', },
    {'Убейте храмовника Бездны по поручению Бора Буйногривого из Крепости Кенария, что в Силитусе, и принесите в доказательство талисман Бездны.', },
  },
