  [8365] = {
    'Пиратские шляпы',
    {'Так это тебя Картель послал к нам на помощь? Пожалуй, ты сойдешь. Слушай внимательно, |3-6($C). Ты ведь знаешь, что я законодатель мод Азерота? Короли и королевы, вожди и... как там Орда называет своих королев? Одним словом, все носят, что я укажу. И платят за это бешеные деньги, что особенно приятно.$B$BВ общем, поступим так. Отправляйся в Бухту Сорванных Парусов и раздобудь побольше пиратских шляп. Справишься?', },
    {'Добудьте 20 шляп пиратов Южных Морей и отнесите их Отти Кутюри в Порт Картеля в Танарисе.', },
  },
  [8366] = {
    'Покажи этим пиратам',
    {'Они требуют от меня невозможного!$B$BКартель Хитрая Шестеренка хочет, чтобы я преподал пиратам Южных Морей урок. Мне что, разорваться?! Я должен поддерживать здесь порядок.$B$BВ последнее время пираты забыли, что Картель платит им за защиту. Нужно показать им, что к чему.$B$BПираты поселились в Бухте Сорванных Парусов, неподалеку отсюда, на юге. Спасибо, $N. Я твой должник!', },
    {'Начальник отдела безопасности Чепухастер из Порта Картеля в Танарисе просит убить 10 пиратов, 10 флибустьеров, 10 рабочих и 10 сорвиголов Южных морей.', },
  },
  [8367] = {
    'Великая честь',
    {'Нет выше чести, чем биться с Альянсом во имя Орды! Лучшие же наши воины – те немногие, которым довелось сразиться на ВСЕХ полях битвы. Отдельные стычки не помогут нам одолеть врага. Лишь тщательно спланировав наступление, мы сломим проклятый Альянс.$B$BСтупай же и сокруши противника на всех фронтах. За честь! За Орду!', },
    {'Принесите почетные знаки каждого поля боя ордынскому вестнику войны в любую столицу Орды или в Шаттрат.', },
  },
  [8368] = {
    'Битва за Ущелье Песни Войны',
    {'Битва со Среброкрылыми Часовыми за Ущелье Песни Войны имеет огромное значение. Под предлогом охраны леса, который им вовсе не принадлежит, бойцы Альянса пытаются лишить Орду важнейшего источника древесины.$B$BНе бывать тому! Возвращайся ко мне и приноси доказательства верной службы Орде!', },
    {'Принесите 3 почетных знака Ущелья Песни Войны ордынскому вестнику войны рядом со входом на поля боя.', },
  },
  [8369] = {
    'Вторжение в Альтеракскую долину',
    {'Слушай, $N. Что бы ни болтали про битву в Альтеракской долине, правда состоит в следующем: экспедиция Грозовой Вершины вторглась в земли клана Северного Волка. В тот день, когда мы позволим Альянсу безнаказанно нарушать наши границы, я повешу секиру на стену и примусь посыпать голову пеплом. Отправляйся и сотри в порошок наглых захватчиков!', },
    {'Принесите 3 почетных знака Альтеракской долины ордынскому вестнику войны рядом со входом на поле боя.', },
  },
  [8370] = {
    'Завоевание Низины Арати',
    {'Наш конфликт с Альянсом дошел до открытой войны. Никто уже не ищет предлогов, чтобы вступить в бой.$B$BВ Низине Арати идет простая и открытая борьба за ресурсы. Или они попадут в руки Альянса и обратятся в оружие против нас, или их захватим мы и вооружим наши войска! Помоги нам захватить источник ресурсов, $N!', },
    {'Принесите 3 почетных знака Низины Арати ордынскому вестнику войны рядом со входом на поля боя.', },
  },
  [8371] = {
    'В едином порыве',
    {'В войне против Орды решающей может оказаться любая битва, $Gсынок:дочка;.$B$BПобеда на одном фронте позволит захватить ресурсы, которые помогут победить и на другом. И с точностью до наоборот – любая проигранная битва может дать нашим врагам преимущество в другой.$B$BАльянсу не хватает бойцов, которые бы хорошенько поняли это... бойцов, закаленных в битвах! Отправляйся сражаться с Ордой, а когда побываешь на всех полях битвы, возвращайся ко мне.', },
    {'Принесите почетные знаки каждого поля боя ордынскому вестнику войны в любую столицу Орды или в Шаттрат.', },
  },
  [8372] = {
    'Битва за Ущелье Песни Войны',
    {'Среброкрылые Часовые вступили в бой со всадниками из клана Песни Войны, стараясь защитить от них лес. Впрочем, желание сохранить этот лес объсняется не только их любовью к природе.$B$BЭтот лес представляет собой естественную преграду для наступления сил Орды. Без него Астранаар не продержался бы и дня, и в наши земли давно вторглась бы огромная армия Орды.$B$BВнеси свой вклад в борьбу со всадниками из клана Песни Войны, $N! За Альянс!', },
    {'Принесите 3 почетных знака Ущелья Песни Войны бригадному генералу Альянса рядом со входом на поля боя.', },
  },
  [8373] = {
    'Сила сосен',
    {'Самое гнусное в Тыквовине – это бомбы-вонючки, которые Отрекшиеся бросают на Южнобережье. Кто бы мог подумать, что праздновение свободы требует такой вони?$B$BКогда наступит Тыквовин, убери при помощи этого очистителя любую из мерзких бомб, которые Орда может подкинуть к нам в деревню. Очиститель действует благодаря силе сосен.$B$BПрими участие в поддержании чистоты Южнобережья, и я дам тебе кое-какие тыквовинские вкусности, которым ты несомненно порадуешься. Договорились?', },
    {'Уберите любую бомбу-вонючку Отрекшихся, брошенную в Южнобережье, при помощи очистителя бомб-вонючек. По выполнении задания возвращайтесь к сержанту Дыгалу.', },
  },
  [8374] = {
    'Завоевание Низины Арати',
    {'Чтобы выиграть войну, мало быть быть умнее и хитрее противника. Зачастую победа достается тем, в чьих руках сосредоточены все ресурсы.$B$BРесурсов в Низине Арати – хоть отбавляй. Главное – захватить их первыми.$B$BЕсли победа будет за Альянсом, нашим солдатам достанется больше мечей и копий. Если Орде – те же мечи и копья обратятся против нас! Твой долг помочь Альянсу захватить Низину Арати!', },
    {'Принесите 3 почетных знака Низины Арати бригадному генералу Альянса рядом со входом на поля боя.', },
  },
  [8375] = {
    'Помни Альтеракскую долину!',
    {'Не пойми меня превратно, $N. Орда совершенно справедливо считает долину Альтерака территорией клана Северного Волка.$B$BВидишь ли, экспедиция Грозовой Вершины прибыла в эти края как мирные исследователи. Они искали руду и древние реликвии, но нашли лишь смерть. Альянс не ожидал такой бессмысленной жестокости даже от клана Северного Волка.$B$BВечная память отважным дворфам, павшим от рук вероломных орков. Ступай, |3-6($C), внеси свой вклад в битву за Альтеракскую долину. За Альянс!', },
