  [8409] = {
    'Поломанные бочки',
    {'Тухлые яйца успешно подброшены в бочку, и весь эль Южнобережья теперь безнадежно испорчен.$B$BОтправляйтесь назад, к Янке Зовущей Тьму, на фестиваль Ивового человека на Тирисфальских лесах, и доложите о своем успехе.', },
    {'Вернитесь к Янке Зовущей Тьму на фестиваль Ивового человека на Тирисфальских лесах.', },
  },
  [8410] = {
    'Покорение стихий',
    {'Бат\'ра Страж Ветра ищет шамана вроде тебя. Принеси ему по образцу каждой из стихий, чтобы доказать свое мастерство.$B$BБат\'ру можно отыскать на востоке от Мельницы Таррен. Иди вдоль реки на север до руин. Там-то Бат\'ра и разбил лагерь.', },
    {'Соберите образцы воздуха, огня, земли и воды по поручению Бат\'ры Стража Ветра.', },
  },
  [8411] = {
    'Покорение Стихий',
    {'Добро пожаловать, |3-6($C). Расклад такой: ты несешь мне стихии и только потом я с тобой говорю.$B$BМне нужны от тебя Огонь, Воздух, Вода и Земля. Только потом я тебе буду доверять, дружище. Надо удостовериться, что ты не совсем дундук.', },
    {'Принесите стихии Земли, Воздуха, Огня и Воды Бат\'ре Стражу Ветра.', },
  },
  [8412] = {
    'Тотем духов',
    {'Духи стихий здешних мест меня невзлюбили.$B$BТы знаешь о тотемах больше других, а мне без защитного тотема духов не обойтись. Стихии у нас уже есть, теперь нужно что-то более близкое к природе, первобытное.$B$BВ Западных Чумных землях можно добыть когти медведей и глаза пауков. Падальщики едят и тех, и других, поэтому у них в желудках может быть и то, и другое.', },
    {'Принесите части тотема Духа Бат\'ре Стражу Ветра.', },
  },
  [8413] = {
    'Вуду',
    {'Силы этого тотема духов не достаточно. Нам нужна магия вуду, чтобы сделать верную защиту.$B$BТролли из затонувшего храма делают перья, насыщенные магией вуду. Ступай туда и убей Гашера, Миджана, Золо, Хукку, Зул\'Лора и Лоро. Тогда нам хватит магии на настоящий тотем духов.', },
    {'Принесите вудуистские перья Бат\'ре Стражу Ветра.', },
  },
  [8414] = {
    'Борьба со злом',
    {'Привет тебе, |3-6($C). Отрадно видеть рыцаря вроде тебя в сих безотрадных землях.$B$BМы рассчитваем на твою помощь в борьбе с беконечными полчищами нежити. Собери побольше камней Плети и отнеси их верховному жрецу Тель\'данису. Он охраняет гробницу Утера неподалеку отсюда.$B$BКамни Плети находят лишь те, кто получил Жетон Серебряного Рассвета. Если у тебя нет амулета, поговори с офицером Чистосердой.', },
    {'Принесите 20 камней приспешников Плети верховному жрецу Тель\'данису в Западные Чумные земли.', },
  },
  [8415] = {
    'Лагерь Промозглого Ветра',
    {'Командиру Ашламу Неутомимому нужен паладин, не уступающий тебе честью и доблестью, $N. Командир живет в Лагере Промозглого Ветра в южной части Западных Чумных земель. Найди его и помоги делу Света!', },
    {'Обратитесь к командиру Ашламу Неутомимому в Лагере Промозглого Ветра.', },
  },
  [8416] = {
    'Бездействующие камни Плети',
    {'Камни очищены от зла, $N.$B$BЯ в курсе планов командира Аслама, и ему понадобится всего лишь один бездействующий камень. Отнеси его, и да сгорит Плеть в праведном огне.', },
    {'Отнесите бездействующий камень командиру Ашламу Неутомимому на Берег Промозглого Ветра.', },
  },
  [8417] = {
    'Неупокоенный дух',
    {'По Выжженным землям бродит неупокоенный дух одного из величайших героев Орды. Честь воина прочнее смертного тела, так что тебе не помешает поговорить с ним.', },
    {'Поговорите с павшим героем Орды у входа в Выжженные земли.', },
  },
  [8418] = {
    'Создание камня силы',
    {'Теперь мы сможем создать оружие против нежити из бездействующих камней Плети. Задание непростое. Магией, способной вынудить зло обратиться против самого себя, владеют лишь тролли из затонувшего храма.$B$BВ храме есть тролли по имени Ранокол, Миджан, Золо, Хукку, Зул\'Лор и Лоро. Не стоит их жалеть... они давно пали и их души запятнаны злом.', },
    {'Добудьте янтарные, синие и зеленые вудуистские перья у троллей из Затонувшего храма.', },
  },
  [8419] = {
    'Просьба беса',
    {'С тобой хочет встретиться один бес из Оскверненного леса по имени Бесенок. Это довольно странно, поскольку он попросил тебя принести отрез ткани Скверны в залог доброй воли. Ты весьма $Gмогущественный чернокнижник:могущественная чернокнижница; и наверняка знаешь, что ткань Скверны таскают с собой сатиры Оскверненного леса.$B$BБесенок живет на севере от водопада Ядовитой Крови. Говорят, в это время года там очень красиво.', },
    {'Отнесите Бесенку из Оскверненного леса отрез ткани Скверны.', },
  },
  [8420] = {
    'Пробирает!',
    {'Может, ты и не $Gожидал:ожидала; услышать это от беса, но мне хочется завести питомца. Правда-правда. Это же нечестно – у Ниби есть я, а у меня нет никого!$B$BМне даже не надо живого питомца. Мне нужно что-нибудь, из чего я бы мог его себе сделать – например, ткань Скверны. У сатиров тут, в Оскверненном лесу, ткань Скверны-то имеется, так что просто убивай их, пока не найдешь ткань. Звучит не так уж и ужасно, правда?', },
    {'Отнесите Бесенку из Оскверненного леса отрез ткани Скверны.', },
  },
  [8421] = {
    'Бросовый материал',
    {'Теперь, когда у меня есть ткань Скверны, я могу начать изготовление собственного питомца.$B$BНо ему нужны глазки! Может, сумеешь оторвать кусочек гнилой древесины от железного дерева, и я вырежу из нее прелестные глазки? Деревья эти можно найти в Железнолесье.$B$BА еще мне понадобится немного экстракта ядовитой крови, чтобы мой питомец приобрел демонический запах. Этот экстракт можно добыть из нечистых слизнюков, а они ошиваются прямо по соседству со мной и Ниби.', },
    {'Принесите 10 кусков гнилой древесины и 4 порции экстракта ядовитой крови Бесенку в Оскверненный лес.', },
  },
  [8422] = {
    'Тролли Пера',
    {'Мой питомец почти готов. Осталось набить его.$B$BТолько вудуистские перья с троллей затонувшего храма помогут делу. Их зовут Ранокол, Миджан, Золо, Хукку, Зул\'Лор и Лоро. Убей их. Перебей их всех! $B$BЭто непростое дело, $N, потому я заплачу тебе за услугу, дав тебе кое-что из барахла Ниби – ему это все равно, наверное, не нужно.', },
    {'Принесите 6 вудуистских перьев троллей из затонувшего храма.', },
  },
  [8423] = {
    'Воинское испытание',
    {'*Герой окидывает тебя взглядом.*$B$BНет сомнений, ты |3-6($C). У меня для тебя есть почетная задача, но сначала необходимо испытать твою силу.$B$BГоворят, что Рексар проверял остроту своих клинков, убивая зловепря одним быстрым ударом. Теперь же я проверю твою силу, отправив тебя на охоту за зловепрями.', },
    {'Убейте 7 Зловепрей в Выжженных землях и вернитесь к павшему герою Орды.', },
  },
  [8424] = {
    'Война против Приверженцев Тени',
    {'Постоянное присутствие Приверженцев Тени в Выжженных землях причиняет мне сильную боль. Их темные ритуалы и заклинания вызывают мучения, каких я не ведал при жизни.$B$BЧерез тебя я могу передать мое собственное послание боли. Иди и неси мой гнев.', },
    {'Убейте Приверженцев Тени в Выжженных землях и вернитесь к павшему герою Орды.', },
  },
  [8425] = {
    'Вудуистские перья',
