  [8446] = {
    'Под покровом кошмара',
    {'Эта вещь так и сочится скверной.$B$BМожет быть, существует связь между ней и драконами, вылетающими из Сна. Кто-то должен разобраться, что делать с этой штукой. Может, тебе обратиться к друидам Лунной поляны или Крепости Кенария?', },
    {'Найдите того, кто сможет осознать значение поглощенного кошмарами предмета.$B$BМожет быть, вам сможет помочь могущественный друид.', },
  },
  [8447] = {
    'Пробуждение легенд',
    {'Воды озера Элуне\'ара смоют кошмары, поглотившие этот предмет. Это займет совсем немного времени. Подожди, $N.', },
