  [8471] = {
    'Ритуальный тотем Зимней Спячки',
    {'Вы нашли один из ритуальных тотемов Зимней Спячки. Сам тотем представляет собой неописуемую конструкцию, которая испускает почти осязаемые волны страха и отчаяния. Если такую вещь носят все фурболги, то понятно, что довело их до такого состояния.$B$BКто-нибудь из фурболгов логова Древобрюхов наверняка разберется с этой вещью... однако фурболги будут говорить только с теми, кому достаточно доверяют.', },
    {'Отнесите ритуальный тотем Зимней Спячки в берлогу Древобрюхов и отдайте его фурболгу, который им заинтересуется. Никто из племени Древобрюхов не заговорит с вами, пока вы не завоюете себе среди них достойную репутацию.', },
  },
  [8481] = {
    'Корень всех зол',
    {'$N, спасибо тебе за то, что $Gнашел:нашла; способ вырвать корень зла из душ племени Зимней Спячки – уничтожить демона, овладевшего ими!$B$BОтнеси этот факел к входу в пещеру верховного вождя племени Зимней Спячки и поставь его там. Это заставит демона появиться. Вот тогда ты со своими союзниками нападешь на него! Только сущность погибшего демона поможет начать долгий процесс исцеления племени Зимней Спячки... и спасения Крепости Древобрюхов.', },
    {'Установите факел призыва демона у входа в пещеру верховного вождя племени Зимней Спячки в деревне фурболгов Зимней Спячки. Сразите демона и добудьте сущность Зандивиуса для Горна Одноглаза из Крепости Древобрюхов.', },
  },
  [8484] = {
    'Знак мира',
    {'Твои подвиги выше любых похвал, $N. И раз ты представляешь свой народ, то мы теперь должны заключить с ним мир.$B$BПрошу – отнеси этот дар королю дворфов, что живет в железной горе – вроде бы вы зовете этот город Стальгорном. В былые времена он пытался заключить с нами союз от имени Альянса. Так что теперь достойно будет, чтобы ты $Gзавершил:завершила; труды Альянса по установлению мира с нами.', },
    {'Отнесите мирное приношение Древобрюхов королю Магни Бронзобороду в Стальгорн.', },
  },
  [8485] = {
    'Знак мира',
    {'Твои подвиги выше любых похвал, $N. И раз ты представляешь свой народ, то мы теперь должны заключить с ним мир.$B$BПрошу – отнеси этот дар вождю орков, что живет в Дуротаре. Кажется, вы называете его город Оргриммаром. В былые времена он пытался заключить с нами союз от имени Орды. Так что теперь достойно будет, чтобы ты $Gзавершил:завершила; труды Орды по установлению мира с нами.', },
    {'Отнесите мирное приношение Древобрюхов Траллу в Оргриммар.', },
  },
  [8492] = {
    'Альянсу не хватает медных слитков!',
    {'Здорово, |3-6($C)! На вид ты вполне $Gпригоден:пригодна; к тому, чтобы принять участие в подготовке к войне! Нам пригодится любая помощь, так что наступил твой звездный час.$B$BЯ собираю медные слитки стопками по двадцать штук. Дело благое, да? Ты как, хочешь оказать помощь Альянсу?', },
    {'Принесите 20 медных слитков сержанту Каменлобу в Палаты Войны Стальгорна.', },
  },
  [8493] = {
    'Альянсу все еще не хватает медных слитков!',
    {'Ты $Gвернулся:вернулась;, |3-6($C)? Превосходно! Для подготовки к войне в Ан\'Кираже все еще нужны медные слитки. Ты поможешь нам собрать их?', },
    {'Принесите 20 медных слитков сержанту Каменлобу в Военный двор Стальгорна.', },
  },
  [8494] = {
    'Альянсу не хватает железных слитков!',
    {'Эгей, $Gмистер:мисс.; Красивые Штанишки, ты прямо отрада для моих измученных глаз. Пока ты $Gшатался:шаталась; по городам и весям в поисках приключений, у нас тут произошла прорва всего. К примеру, Альянс вот готовится к войне в Ан\'Кираже, и всем резко понадобились железные слитки... чертова прорва железных слитков. Можешь таскать их мне стопками по двадцать. Ну как, могу я на тебя рассчитывать, |3-6($C)?', },
    {'Принесите 20 железных слитков капралу Карнсу в Палаты Войны Стальгорна.', },
  },
  [8495] = {
    'Альянсу по-прежнему не хватает железных слитков!',
    {'Ты опять здесь? Не заставляй меня слишком хорошо о тебе думать, |3-6($C), а то я прямо таять начинаю. Как видишь, мы еще не собрали достаточно железных слитков, чтобы хватило на оружие, броню, паровые танки и все остальное-прочее, что нужно изготовить для большой драчки. Я думаю, ты сможешь принести еще слитков двадцать в перерыве между своими шатаниями в Зул\'Гуруб, или где там у вас, у молодежи, сейчас модно тусоваться.', },
    {'Принесите 20 железных слитков капралу Карнсу на аэродром в Дун Морог.', },
  },
  [8496] = {
    'Запас бинтов',
    {'В последнее время мы понесли серьезные потери, и запас бинтов стремительно иссякает. Добудь нам первоклассные перевязочные материалы и отнеси их призывательнице ветров Гордому Рогу в Крепость Кенария.', },
    {'Принесите 30 плотных бинтов из рунической ткани, 30 плотных шелковых бинтов и 30 бинтов из магической ткани призывательнице ветров Гордому Рогу в Крепости Кенария в Силитусе. Кроме того, сдайте тыловую задачу X.', },
  },
  [8497] = {
    'Неприкосновенный запас',
    {'В Крепость Кенария прибыли новые добровольцы, и мы должны позаботиться, чтобы они выжили в пустыне. Новичкам грозит обезвоживание и множество ядовитых тварей. Собери как можно больше составляющих неприкосновенных наборов для отправляющихся в пустыню и отнеси их Каландрате в Крепость Кенария.', },
    {'Принесите 4 сферы воды, 4 мощных противоядия и 4 копченые пустынные клецки Каландрате в таверну в Крепости Кенария в Силитусе. Кроме того, сдайте тыловую задачу I.', },
  },
  [8498] = {
    'Боевые приказы Сумеречного Молота',
    {'Разведчики донесли, что от вождей Сумеречного Молота поступили новые приказы. Их распространяют пророки под охраной солдат. Перехвати эти приказы и принеси их командиру Мар\'алиту.', },
    {'Добудьте боевые приказы Сумеречного Молота и отнесите их командиру Мар\'алиту в Крепость Кенария в Силитусе. Для завершения задания понадобится тактическая задача X.', },
  },
  [8499] = {
    'Альянсу не хватает ториевых слитков!',
    {'Крайне любезно с твоей стороны, что ты так беспокоишься о нуждах Альянса. Приготовления к войне идут полным ходом, и, помимо всего прочего, нам нужно очень много ториевых слитков. Именно за их сбор я и отвечаю. Торий необходим для изготовления различных военных машин, в основном паровых танков, которые планируется доставлять по воздуху в Силитус сразу же после сборки. За помощь в добыче ториевых слитков я была бы очень признательна, |3-6($C).', },
    {'Принесите 20 ториевых слитков госпоже Двукоссе в Палаты Войны Стальгорна.', },
  },
  [8500] = {
    'Альянсу по-прежнему не хватает ториевых слитков!',
    {'Добро пожаловать снова, $N. Надеюсь, у тебя все в порядке? Это правда, нам все еще нужны ториевые слитки. Если сможешь достать их, я буду очень признательна.', },
    {'Принесите 20 ториевых слитков госпоже Двукоссе на аэродром в Дун Морог.', },
  },
  [8501] = {
    'Цель: острожалы из улья Аши',
    {'По мере нарастания конфликта, силитиды представляют все большую опасность для наших операций. Тебя избрали для участия в нападении на улей Аши. Твоей основной целью будут летающие острожалы из этого улья.$B$BВыполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 острожалов из улья Аши и доложите о выполнении задания командиру Мар\'алиту в Крепости Кенария в Силитусе. Для завершения задания принесите боевую задачу XII.', },
  },
  [8502] = {
    'Цель: рабочие из улья Аши',
    {'По мере нарастания конфликта, силитиды представляют все большую опасность для наших операций. Тебя избрали для участия в нападении на Улей Аши. Твоей основной целью будут рабочие из этого улья.$B$BВыполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 рабочих из улья Аши и доложите о выполнении задания командиру Мар\'алиту в Крепости Кенария в Силитусе. Для завершения задания принесите боевую задачу III.', },
  },
  [8503] = {
    'Альянсу не хватает удавника!',
    {'Знаешь ли ты, |3-6($C), насколько широко и разнообразно применение удавника? Как, я тебе уже об этом рассказывал? Правда?.. Ну ладно. В любом случае, нам нужно много этой травы, и когда я говорю так, я имею в виду действительно МНОГО! Не следует ли тебе помочь нам, как ты считаешь? Я понимаю, это означает, что тебе придется остаться без всех этих чудесных штучек, которые можно приготовить из удавника... но ведь это ради благого дела!', },
    {'Принесите 20 стеблей удавника рядовому Драксельмеру в Палаты Войны Стальгорна.', },
  },
  [8504] = {
    'Альянсу по-прежнему не хватает удавника!',
    {'Эй, ты там... Ой... Что это было? Ах, да, $N! $GЗашел:Зашла; поинтересоваться, не нужно ли мне еще удавника? О, тебе крупно повезло, потому что ответ на этот жизненно важный вопрос – конечно, ДА! Ну как, соберешь еще двадцать штук?', },
    {'Принесите 20 стеблей удавника рядовому Драксельмеру на аэродром в Дун Морог.', },
  },
  [8505] = {
    'Альянсу не хватает лилового лотоса!',
    {'$C, цветы лилового лотоса где только не применяют. От имени Альянса я отвечаю за заготовку лотоса в количестве, достаточном для удовлетворения всех военных нужд.$B$BНо чтобы собирать цветы, мне нужны добровольцы. Справишься с этим заданием?', },
    {'Принесите 20 цветков лилового лотоса мастеру Ночной Песне в Палаты Войны Стальгорна.', },
  },
  [8506] = {
    'Альянсу по-прежнему не хватает лилового лотоса!',
    {'Добро пожаловать обратно, |3-6($C). Наши запасы лилового лотоса растут, но нам все равно нужно еще. Займись сбором цветов, если можешь, я тебя очень прошу.', },
    {'Принесите 20 цветков лилового лотоса мастеру Ночной Песне на аэродром в Дун Морог.', },
  },
  [8507] = {
    'Рутинное задание',
    {'Приветствую тебя, $N. Прежде чем дать тебе ответственные задания, я должен поручить тебе более простые и рутинные.$B$BДружина Стальгорна под предводительством капитана Черной Наковальни расквартирована рядом с Ульем Зора.$B$BПоговори с капитаном и его лейтенантом, Джанелой Твердомолот, а потом возвращайся. Тем временем я подготовлю для тебя новое задание.$B$BАх да, сделай одолжение, держись подальше от легиона Оргриммара. Нам нелегко удерживать эти два войска от грызни, очень уж они друг друга не любят.', },
    {'Поступите в распоряжение дружины Стальгорна возле Улья Зора. Подготовьте неподписанные бумаги о полевых обязанностях и получите подписанные бумаги о полевых обязанностях от капитана Черной Наковальни, вернитесь к призывателю ветров Калдону в Крепость Кенария в Силитусе.$B$BВнимание: исцеление или наложение благоприятных заклинаний на бойцов дружины Стальгорна переводит в режим PvP.', },
