  [8509] = {
    'Альянсу не хватает слез Артаса!',
    {'Я предпочитаю говорить быстро и по существу, |3-6($C). Альянсу нужно много слез Артаса для войны в Ан\'Кираже. Не спрашивай меня, зачем, я всего лишь выполняю приказ. Собери столько этих цветов, сколько унесешь, и сдай мне. Ну что, возьмешься за это задание?', },
    {'Принесите 20 слез Артаса старшему сержанту Гермайн в Палаты Войны Стальгорна.', },
  },
  [8510] = {
    'Альянсу снова нужны слезы Артаса',
    {'Так, похоже, слезы Артаса мне все еще нужны. Я предполагаю, что они растут там же, где и раньше. Чем быстрее мы закончим подготовку, тем быстрее начнем наступление на Ан\'Кираж.$B$BЯ могу вновь рассчитывать на твою помощь, |3-6($C)?', },
    {'Принесите 20 цветков слезы Артаса старшему сержанту Гермайн на аэродром в Дун Морог.', },
  },
  [8511] = {
    'Альянсу не хватает легкой кожи!',
    {'Наш клан Обдирщиков Камня испокон века знал толк в снятии шкур, |3-6($C). Как говорится, и с камня снимем шкуру. Потому-то Альянс и попросил нас взяться за заготовку тонкой кожи для войны в Ан\'Кираже. Я слежу, чтобы кожа была хорошо выдублена, уж не знаю, для чего она там понадобится. Но без помощи мне никак не обойтись.$B$BПока я здесь за всем надзираю, тебе придется прогуляться малость и добыть кож, а потом принести их мне. Ну что, $Gпаренек:девушка;, возьмешься за дело?', },
    {'Принесите 10 легких кож Бонни Шлифовальщице в Палаты Войны Стальгорна.', },
  },
  [8512] = {
    'Альянсу не хватает легкой кожи!',
    {'Рада снова тебя видеть, $N. Похоже, что мы еще не собрали достаточно кожи для наших воинских нужд. Если принесешь мне штук десять кож, это будет просто отлично.', },
    {'Принесите 10 кусков легкой кожи Бонни Шлифовальщице на аэродром в Дун Морог.', },
  },
  [8513] = {
    'Альянсу не хватает средней кожи!',
    {'Так точно, $Gсэр:мэм;, я отвечаю за сбор всей средней кожи, необходимой для подготовки к войне с Ан\'Кираем. Если вы можете раздобыть среднюю кожу, неважно где, сдайте ее мне, пожалуйста. Я буду благодарен за любую помощь, если у вас найдется на нее время.', },
    {'Принесите 10 средних кож рядовому Портеру в Палаты Войны Стальгорна.', },
  },
  [8514] = {
    'Альянсу по-прежнему не хватает средней кожи!',
    {'Так точно, $Gсэр:мэм;, к настоящему времени нам удалось собрать $2063w кусков средней кожи. Я знаю, что прошу слишком многого, но не могли бы вы снова оказать помощь? Если желаете, конечно. Мне необходимо выполнить установленную норму.', },
    {'Принесите 10 средних кож рядовому Портеру на аэродром в Дун Морог.', },
  },
  [8515] = {
    'Альянсу не хватает плотной кожи!',
    {'О, будет так здорово! $C, приготовления к ан\'киражской войне идут вовсю, и так получилось, что я отвечаю за заготовку плотной кожи. Из нее мы сможем наделать тысячи разных вещей, но сначала надо ее достать – тоже тысячами. В буквальном смысле. И тут на сцену выходишь ты.$B$BТы ведь принесешь мне отличную увесистую кипу плотной кожи? Правда-правда?', },
    {'Принесите 10 плотных кож Марте Остроигл в Палаты Войны Стальгорна.', },
  },
  [8516] = {
    'Альянсу по-прежнему не хватает плотной кожи!',
    {'Да-да, стопки все еще не очень высокие. $N, мы не выполняем норму – не хватает целых $2071w кусков плотной кожи. Только подумай, сколько всего полезного из нее можно сделать! Броню и оружие на любой вкус! Она годится как на отделку паровых танков, так и на оптические винтовки. Держу пари, все закончится тем, что мы соберем столько кожи, что остаток придется отдать Орде на дирижабли.$B$BТы ведь принесешь мне еще плотной кожи?', },
    {'Принесите 10 плотных кож Марте Остроигл на аэродром Стальгорна.', },
  },
  [8517] = {
    'Альянсу не хватает льняных бинтов!',
    {'Война в Ан\'Кираже будет долгой и кровавой. Прибегнуть к помощи людей, подобных тебе, было для Альянса мудрым решением. Перед тем как сломя голову ринуться в военный конфликт, надо собрать достаточно припасов.$B$BМне поручили запасать льняные бинты, и я надеюсь, что ты мне поможешь. Это не так-то легко, но по крайней мере льняные бинты проще всего изготовить. Что скажешь, |3-6($C)?', },
    {'Принесите 20 льняных бинтов часовому Серебринке Небес в Палаты Войны Стальгорна.', },
  },
  [8518] = {
    'Альянсу по-прежнему не хватает льняных бинтов!',
    {'Меня всегда изумляет самоотверженность – ведь столь многие обычно гораздо более склонны к эгоизму. $C, я все никак не могу выполнить свою норму по сбору льняных бинтов. Не поможешь ли мне еще раз?', },
    {'Принесите 20 льняных бинтов часовому Серебринке Небес на аэродром в Дун Морог.', },
  },
  [8519] = {
    'Пешка на Доске Вечности',
    {'Успокоившись и сосредоточив взгляд на кристалле, вы замечаете, что внутри что-то шевелится. Посмотрев поближе, вы осознаете, что перед вами предстало прошлое... давно забытое.', },
    {'Узнайте о прошлом все, что можно. Потом поговорите с Анахроносом в Пещерах Времени в Танарисе.', },
  },
  [8520] = {
    'Альянсу не хватает шелковых бинтов!',
    {'На горизонте маячит война в Ан\'Кираже, так что мы должны приложить все усилия, чтобы на поле боя солдаты ни в чем не нуждались. Именно этим мы и занимаемся. Я вот, например, отвечаю за заготовку шелковых бинтов.$B$BНе хочешь ли мне помочь малость?', },
    {'Принесите 20 шелковых бинтов медсестре Стоунфилд в Палаты Войны Стальгорна.', },
  },
  [8521] = {
    'Альянсу по-прежнему не хватает шелковых бинтов!',
    {'Очень мило с твой стороны снова предложить мне помощь, |3-6($C). Мы пока собрали всего лишь $2082w шелковых бинтов, так что еще работать и работать.', },
    {'Принесите 20 шелковых бинтов медсестре Стоунфилд на аэродром в Дун Морог.', },
  },
  [8522] = {
    'Альянсу не хватает бинтов из рунической ткани!',
    {'Считай ведение войны сколь угодно сомнительным занятием, |3-6($C), но военная машина уже завертелась. Оглянись на свою семью и друзей и спроси, чем ты можешь помочь им, – ведь все мы попадем в горнило битвы, и надо будет как-то выживать.$B$BИменно поэтому я запасаюсь бинтами из рунической ткани, кто бы их ни принес. Не хочешь ли внести посильный вклад в это дело?', },
    {'Принесите 20 бинтов из рунической ткани Хранителю Лунному Сумраку в Палаты Войны Стальгорна.', },
  },
  [8523] = {
    'Альянсу по-прежнему не хватает бинтов из рунической ткани!',
    {'Итак, мы снова вынуждены говорить о делах, не терпящих отлагательств, |3-6($C). Хочу еще раз поблагодарить тебя за вложенные усилия, мало кто трудится столь самоотверженно. Но работы еще непочатый край.$B$B$N, можешь принести мне еще бинтов из рунической ткани?', },
    {'Принесите 20 бинтов из рунической ткани Хранителю Лунному Сумраку на аэродром в Дун Морог.', },
  },
  [8524] = {
    'Альянсу не хватает радужного тунца!',
    {'Эй, дружище! Если бы я спросил тебя: "Нужно ли солдатам на войне хорошо кушать?" – ты бы, без сомнения, $Gответил:ответила;: "Да, конечно!". И я абсолютно с тобой согласен! Рад, что мы сошлись во мнениях. Теперь, я думаю, ты не будешь спорить, что тебе осталось только отправиться на рыбалку, наловить радужного тунца, приготовить всю порцию и принести мне. М-м-м, я уже в предвкушении.', },
    {'Принесите 20 тушек радужного тунца Гладню Гастрогному в Палаты Войны Стальгорна.', },
  },
  [8525] = {
    'Альянсу по-прежнему не хватает радужного тунца!',
    {'Что? Снова ты? Ну и дела, не будь я гном! Гладень Гастрогном, чтобы быть точным. Выходит, ты $Gпришел:пришла;, чтобы снова помочь мне? Не могу сказать, чтобы я был этим недоволен. А может, тебе просто понравилось, как пахнет жареный тунец? *облизывается*$B$BЛадно, хватит болтать. Ступай-ка и принеси мне побольше радужного тунца!', },
    {'Принесите 20 тушек радужного тунца Гладню Гастрогному на аэродром в Дун Морог.', },
  },
  [8526] = {
    'Альянсу не хватает жареного мяса ящера!',
    {'Как делишки, |3-6($C)? Я вижу, ты просто рвешься принять участие в заготовке провизии для ан\'киражской войны. Мой папочка решил, что ответственность за сбор чего-то там чему-то там меня научит. Как бы не так. Я мясо на дух не выношу.$B$BЛадно, если вдруг захочешь помочь, принеси побольше жареных ящеров. Несомненно, именно об этом мясе и будут мечтать все наши отважные солдаты.', },
    {'Принесите 20 жареных ящеров Саре Грустносвист в Палаты Войны Стальгорна.', },
  },
  [8527] = {
    'Альянсу по-прежнему не хватает жареного мяса ящера!',
    {'О, снова здорово, |3-6($C). Я вижу, ты в восторге от всей этой беготни. Наверное, ты хочешь принести мне еще двадцать жареных ящеров? Отлично. Тогда пойди и принеси. Увидимся.', },
    {'Принесите 20 жареных ящеров Саре Грустносвист на аэродром в Дун Морог.', },
  },
  [8528] = {
    'Альянсу не хватает пятнистого желтохвоста!',
    {'Возможно, заготовка вареной рыбы выглядит не особенно героически, но не стоит преуменьшать значение нашей работы, |3-6($C).Солдаты – такие же люди, как и все, и они хотят есть. Именно поэтому мы должны обеспечить их провиантом, пока не началась война в Ан\'Кираже.$B$BЯ получила приказ заготавливать пятнистый желтохвост. Это отличный пищевой ресурс, он хорошо хранится, идет на изготовление различных блюд и совсем не пахнет, в отличие от другой морской рыбы. Ну что, поможешь мне в его заготовке?', },
    {'Принесите 20 пятнистых желтохвостов охотнице Быстрой Реке в Палаты Войны Стальгорна.', },
  },
  [8529] = {
    'Альянсу по-прежнему не хватает пятнистого желтохвоста!',
    {'Если у тебя еще не пропало желание помогать нам, то нам все еще нужен желтохвост, $N. Я думаю, в прошлый раз ты $Gприобрел:приобрела; достаточно опыта, так что сейчас дело пойдет веселее. Ну что, по рукам?', },
    {'Принесите 20 пятнистых желтохвостов охотнице Быстрой Реке на аэродром в Дун Морог.', },
  },
  [8532] = {
    'Орде не хватает медных слитков!',
    {'Я так рад, что ты $Gрешил:решила; помочь нам. Не всякий бы согласился, так что я лучше еще раз уточню, правильно ли ты меня $Gпонял:поняла;. Мне нужны медные слитки, а не просто медь. У нас недостаточно ни времени, ни людей, чтобы самим ее выплавлять, так что твои усилия не останутся незамеченными. Ну как, |3-6($C), справишься?', },
    {'Принесите 20 медных слитков шахтеру Кромвелю в Аллею Духов Оргриммара.', },
  },
  [8533] = {
    'Орде по-прежнему не хватает медных слитков!',
    {'Уже $Gвернулся:вернулась;? Из тебя быстро бы получился отличный рудокоп, |3-6($C). Ну, ты уже знаешь, как управляться с киркой, так что ступай, добудь побольше медной руды, переплавь ее в слитки и возвращайся ко мне. Я знаю, что ты кое-что можешь, вопрос – хочешь ли?', },
    {'Принесите 20 медных слитков шахтеру Кромвелю в Дуротар.', },
  },
  [8534] = {
    'Донесение разведчика из Улья Зора',
    {'Мы отправили наших лучших разведчиков к ульям силитидов. Азенель уже несколько дней наблюдает за передвижениями этих насекомых. Найди ее и забери у нее письменный отчет. Нам необходимо как можно скорее получить от нее сведения.', },
    {'Свяжитесь с кенарийской разведчицей Азенель в Улье Зора и отнесите ее донесение призывательнице ветров Гордый Рог в Крепость Кенария. Кроме того, принесите тактическую задачу VI.', },
  },
  [8535] = {
    'Седой храмовник',
    {'Сумеречный Молот и повелители стихий, которым поклоняются служители культа, представляют серьезную опасность для деятельности Круга в Силитусе. Учитывая, что у нас появились новые враги – в Ан\'Кираже, мы не должны терять бдительность и давать волю внешним врагам.$B$BОтыщи способ вызвать и уничтожить седого храмовника и доложи о выполнении задания Бору Буйногривому.', },
    {'Призовите и убейте седого храмовника и доложите о выполнении задания Бору Буйногривому из Крепости Кенария. Для завершения задания принесите тактическую задачу IV.', },
  },
  [8536] = {
    'Земельник-храмовник',
    {'Сумеречный Молот и повелители стихий, которым поклоняются служители культа, представляют серьезную опасность для деятельности Круга в Силитусе. Учитывая, что у нас появились новые враги – в Ан\'Кираже, мы не должны терять бдительность и давать волю внешним врагам.$B$BОтыщи способ вызвать и уничтожить земельника-храмовника и доложи о выполнении задания Бору Буйногривому.', },
    {'Призовите и убейте земельника-храмовника и доложите о выполнении задания Бору Буйногривому из Крепости Кенария. Для завершения задания принесите тактическую задачу III.', },
  },
  [8537] = {
    'Багровый храмовник',
    {'Сумеречный Молот и повелители стихий, которым поклоняются служители культа, представляют серьезную опасность для деятельности Круга в Силитусе. Учитывая, что у нас появились новые враги – в Ан\'Кираже, мы не должны терять бдительность и давать волю внешним врагам.$B$BОтыщи способ вызвать и уничтожить багрового храмовника и доложи о выполнении задания Бору Буйногривому.', },
    {'Призовите и убейте багрового храмовника и доложите о выполнении задания Бору Буйногривому из Крепости Кенария. Для завершения задания принесите тактическую задачу II.', },
  },
  [8538] = {
    'Четыре герцога',
    {'Связь между Сумеречным Молотом и Советом Бездны оказалась куда важнее, чем нам казалось вначале.$B$BМы не сомневаемся, что повелители Стихий, именуемые герцогами Бездны, управляют служителями культа Сумеречного Молота. Найди способ выманить в наш мир четырех герцогов Бездны и убей их!$B$BМы избрали тебя за прежние заслуги и не сомневаемся, что ты справишься.', },
    {'Отыщите способ вызвать и убить герцога Огарцога, герцога Глубин, герцога Ветров и герцога Осколков и доложите о выполнении задания командиру Мар\'алиту в Крепости Кенария. Для завершения задания принесите тактическую задачу V.', },
  },
  [8539] = {
    'Цель: сестры-убийцы из Улья Зора',
    {'Мы вызвали подкрепление для сражения у Улья Зора. Твоя задача – убить как можно больше сестер-убийц. Выполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 сестер-убийц из Улья Зора и доложите о выполнении задания командиру Мар\'алиту в Крепости Кенария в Силитусе. Для завершения задания принесите боевую задачу V.', },
  },
  [8540] = {
    'Сапоги для стражей',
    {'Верховому подразделению Крепости Кенария не хватает изысканных мифриловых сапог. Принеси нам очередную партию и передай их капитану Вишу Коцусу из Крепости Кенария.', },
    {'Принесите 3 пары изысканных мифриловых сапог Вишу Коцусу, капитану стражи в Крепости Кенария в Силитусе. Кроме того сдайте тыловую задачу II.', },
  },
  [8541] = {
    'Шлифовальные камни',
    {'У стражи Крепости Кенария на исходе шлифовальные камни. Добудь их и отнеси капитану Вишу Коцусу в Крепость Кенария.', },
    {'Принесите 10 увесистых точильных каменей, 10 твердых шлифовальных камней и 10 тяжелых точильных каменей Вишу Коцусу, капитану стражи в Крепости Кенария в Силитусе. Кроме того сдайте тыловую задачу III.', },
  },
  [8542] = {
    'Орде не хватает оловянных слитков!',
    {'Лок\'тар, |3-6($R), я уверен, ты в курсе, что мы собираемся воевать с киражскими тараканами. Вождь сказал, что сначала надо как следует подготовиться. Я б охотней точил мечи или подгонял броню, но мне доверили сбор оловянных слитков. Для меня это большая честь, да! Но без помощи мне не справиться.$B$BНадо, чтобы кто-то добывал эти самые слитки и приносил сюда. Нам нужно так много, что, боюсь, я и считать-то до стольких не умею.', },
    {'Принесите 20 оловянных слитков рубаке Маугу в Аллею Духов Оргриммара.', },
  },
  [8543] = {
    'Орде все еще нужны оловянные слитки!',
    {'Отлично, я понял, к войне ты относишься серьезно. Видишь ли, в ее предвкушении я так нервничаю, что потихоньку начинаю выходить из себя. А твоя медлительность меня просто бесит!!! Не можешь притаскивать товар быстрее, что ли? Может, мне малость с тобой поразмяться, чтобы снять стресс...$B$BХар!!! Нет, не буду, пожалуй, а то кто тогда будет носить мне слитки?', },
    {'Принесите 20 оловянных слитков рубаке Маугу в Дуротар.', },
  },
  [8544] = {
    'Наплеч Завоевателя',
    {'У нас говорят, что вычурные наплечные доспехи, которые носят смертные, были изначально созданы в подражание крыльям драконов.$B$BПринеси мне наручники, принадлежащие киражским правителям, и я сделаю из них наплечье еще более устрашающее, чем крылья самого Нефариана!', },
    {'Принесите киражские наручники властности, 2 идола Ночи, 5 каменных и 5 глиняных скарабеев Андоргосу в Ан\'Кираж. Кроме того, необходимо заслужить как минимум равнодушное отношение рода Ноздорму.', },
  },
  [8545] = {
    'Орде не хватает мифриловых слитков!',
    {'Война надвигается неотвратимо, и все, что нам остается, |3-6($C), – это как следует подготовиться к ней. В такие времена мы должны сплотиться и помогать друг другу. Как видишь, меня поставили ответственным за сбор мифриловых слитков, которые потом пойдут на нужды армии. Я стараюсь как могу, но этого пока недостаточно.$B$BСумеешь добыть мифрил на рудниках и переплавить его в слитки? Это бы нам очень пригодилось.', },
    {'Принесите 20 мифриловых слитков старшему сержанту Ткелаху. Ткелах находится в Аллее Духов Оргриммара.', },
  },
  [8546] = {
    'Орде все еще не хватает мифриловых слитков!',
    nil,
    {'Принесите 20 мифриловых слитков старшему сержанту Ткелаху. Ткелах находится в Аллее Духов Оргриммара.', },
  },
  [8548] = {
    'Броня добровольца',
    {'Те знаки, которые достались тебе во время выполнения боевых, тыловых и тактических заданий в Крепости Кенария, нужны не для красоты, $N.$B$BНе теряй их. Когда ты докажешь свою верность нашей организации, я смогу предложить тебе кое-какое снаряжение, которое мы храним для ближайших сподвижников, в обмен на эти знаки.', },
    {'Принесите 5 боевых, 3 тыловых и 7 тактических знаков Кенария Варгусу из Крепости Кенария в Силитусе. Кроме того, для выполнения этого задания вам необходимо заслужить дружелюбное отношение Круга Кенария.', },
  },
  [8549] = {
    'Орде не хватает мироцвета!',
    {'Говорят, каждое поколение должно пройти испытание огнем. Боюсь, что ан\'киражская война станет для нас тем самым горном для закалки. Ничего, подготовимся как следует и покажем всем, из какого мы слеплены теста.$B$BНам нужен мироцвет, который пойдет на создание лекарственных снадобий, и я вызвалась быть ответственной за его сбор. Надеюсь, ты $Gпришел:пришла; предложить мне свою помощь. Собери побольше мироцвета и принеси его мне.', },
    {'Принесите 20 цветков мироцвета травнице Гордый Плюмаж в Аллею Духов Оргриммара.', },
  },
  [8550] = {
    'Орде не хватает мироцвета!',
    nil,
    {'Принесите 20 цветков мироцвета травнице Гордый Плюмаж в Аллею Духов Оргриммара.', },
  },
  [8551] = {
    'Сундук капитана',
    {'Когда-то у меня был корабль, на котором я ходил до самого Кабестана в Калимдоре, и репутация у меня была безупречная.$B$BПока…$B$BПока не появились проклятые морские великаны! Они разбили мой корабль, поубивали всю команду, а меня отправили на шлюпке в Пиратскую Бухту. А один из гигантов, Горлаш, украл мой капитанский сундук, мол табакерка из него выйдет неплохая. Наглец!$B$BГоворят, Горлаш бродит по берегу к востоку отсюда. Найди его и верни мой сундук! Благодарность моя не будет знать предела.', },
    {'Верните Гекельберри Смоттсу из Пиратской Бухты его сундук.', },
  },
  [8552] = {
    'Кушак с монограммой',
    {'Вдоль каймы кушака вышита монограмма "ГС".', },
    {'Верните кушак с монограммой владельцу.', },
  },
  [8553] = {
    'Капитанская сабля',
    {'После того как эти проклятые великаны уничтожили мой корабль, я купил еще один. Назвал его "Месть Смоттса", запасся продовольствием, нанял команду и отправился в поисках этих злодеев. Найти-то я их нашел, но... они снова мне надавали по шее. "Месть Смоттса" разбили на части, поубивали вторую команду, а меня снова посадили на шлюпку.$B$BУ другого великана, Неголаша, моя сабля.$B$BСразись с Неголашем и принеси мне мою саблю!$B$BМожешь поговорить со Салагой, поваром с "Мести Смоттса", который выжил в последней битве. Он поможет тебе найти великана.', },
    {'Поговорите с Салагой.', },
  },
  [8554] = {
    'Бой с Неголашем',
    {'Неголаш – великан-обжора.$B$BКогда они уничтожили наш корабль, Неголаш бросился прямиком на кухню. В отличие от его товарищей, команда его не очень интересовала. Ему хотелось вина... и жареных крылышек канюка. Это мое коронное блюдо, рецепт я привез из самих Бесплодных земель!$B$BЕсли ты хочешь приманить великана, добудь вина и кучу крылышек канюка. Сложи их в нашу старую шлюпку, которая причалена на юго-востоке отсюда. Когда Неголаш почует запах еды, он непременно явится!', },
    {'Отнесите 10 жареных крылышек канюка и 5 бутылок экзотического вина в спасательную шлюпку капитана Смоттса.$B$BУбейте Неголаша и отнесите абордажную саблю Смоттса хозяину в Тернистую долину.', },
  },
  [8555] = {
    'Создание драконов',
    nil,
    nil,
  },
  [8556] = {
    'Перстень неумолимой силы',
    {'Я слышала, что кольца, которые носят киражские лейтенанты, дают им невероятные способности. Однако считается, что если заменить пораженные скверной драгоценные камни, вставленные в оправу, на более чистые, мощь колец возрастет еще больше.$B$BКамни, украшающие киражских идолов, вполне подойдут – они отменного качества.$B$BПринеси мне этих идолов и кольцо одного из лейтенантов, и я создам для тебя могущественный талисман, |3-6($C).', },
    {'Принесите киражское кольцо власти, 2 лучистых идола, 5 бронзовых скарабеев и 5 скарабеев из слоновой кости Зовущей Йессендре в Силитус. Кроме того, необходимо заслужить как минимум уважительное отношение Круга Кенария.', },
  },
  [8557] = {
    'Пелерина неумолимой силы',
    {'Я рад слышать, что ты помогаешь нам в нападении на Ан\'Кираж, $N. В благодарность я бы хотел сделать для тебя одну вещь, которая защитит тебя от врагов.$B$BМне понадобится киражская накидка – я смогу подогнать ее под твой размер. Для застежек мне понадобятся идолы и скарабеи.$B$BУвидишь, эта вещь тебя не разочарует.', },
    {'Принесите киражскую воинскую пелерину, 2 ониксовых идола, 5 серебряных и 5 костяных скарабеев Кейлу Стремительному Когтю в Силитус. Кроме того, необходимо заслужить как минимум почтительное отношение Круга Кенария.', },
  },
  [8558] = {
    'Серп неумолимой силы',
    {'Киражи и их прислужники владеют вещами, которые созданы из редчайших и благороднейших материалов – костей гигантских древних созданий, драгоценностей из самых глубин земли, чистейших и прочных металлов...$B$BПри одном взгляде на киражские артефакты мне хочется разбить их, чтобы переплавить обломки и испробовать на них свое собственное искусство. Докажи Кругу Кенариуса свою преданность, $N, и я создам для тебя могучее оружие из лучших киражских материалов.', },
    {'Принесите киражскую шипастую рукоять, 2 алебастровых идола, 5 хрустальных и 5 каменных скарабеев Надзирателю Харо в Силитус. Кроме того, вас должны превозносить в Круге Кенария.', },
  },
  [8559] = {
    'Наголенники Завоевателя',
    {'Древняя киражская магия исполнена небывалой силы. Cамые могущественные киражи владели предметами такой мощи, что они были способны остановить даже дракона.$B$BПринеси мне киражские наручники властности и еще несколько компонентов, и я создам для тебя потрясающие сапоги.$B$BУвидишь, насколько они окажутся полезны в схватке с врагом.', },
    {'Принесите киражские наручники властности, 2 идола Войны, 5 золотых скарабеев и 5 скарабеев из слоновой кости Кандострасу в Ан\'Кираж. Кроме того, необходимо заслужить как минимум равнодушное отношение рода Ноздорму.', },
  },
  [8560] = {
    'Набедренники Завоевателя',
    {'Хочешь доказать нам, что тебе можно доверять, $Gсмертный:смертная;? Ну хорошо. Возможно, и тебе удастся извлечь из этого пользу.$B$BСлушай. Есть существо, обладающее легендарной силой. Это древний песчаный червь Оуро. Говорят, его создал сам Древний Бог, в насмешку над всеми остальными формами жизни.$B$BШкура у этого червя просто невероятная. Она такая толстая, что ее невозможно пробить, но при этом такая гибкая, что позволяет червю перемещаться с непостижимой скоростью и изяществом.$B$BПринеси мне шкуру Оуро и еще ряд компонентов, и я создам для тебя поножи, обладающие огромной силой.', },
    {'Принесите целую шкуру Оуро, 2 идола Смерти, 5 бронзовых скарабеев и 5 скарабеев из слоновой кости Кандострасу в Ан\'Кираж. Кроме того, необходимо заслужить как минимум дружественное отношение рода Ноздорму.', },
  },
  [8561] = {
    'Корона Завоевателя',
    {'Императоры-близнецы... Их непрестанный шепот в моих ушах потихоньку сводит меня с ума. Они смеются надо мной, зная, что я не сумею вступить в их владения, чтобы спасти моего брата Аригоса.$B$BЯ вижу их безобразные головы, увенчанные могущественными коронами. Но я отомщу им... эти символы власти Ан\'Кираж будут служить нам.$B$BПринеси то, что мне нужно, $N. Взамен я отдам тебе головной убор, не имеющий себе равных по силе.', },
    {'Принесите венец Век\'нилаша, 2 идола Солнца, 5 каменных и 5 хрустальных скарабеев Андоргосу в Ан\'Кираж. Кроме того, необходимо заслужить как минимум дружественное отношение рода Ноздорму.', },
  },
  [8562] = {
    'Кираса Завоевателя',
    {'Древний Бог, о котором лучше вслух не упоминать, медленно пробуждается от своего сна в Ан\'Кираже. Ужасная судьба, которая ждет Азерот в случае его пробуждения, – вопрос лишь нескольких дней... или нескольких лет. Для существа, обладающего подобной мощью, время не имеет значения.$B$BВстанут звезды подходящим образом – и ярость его обрушится на живущих, подобно плети. Тебе следует найти его и положить конец его существованию, пока он не пробудился.$B$BКогда ты сделаешь это, принеси мне кусок его панциря. Взамен я дам тебе броню, подобной которой не владел еще никто из смертных. Броню, достойную убийцы бога.', },
    {'Принесите панцирь Древнего Бога, 2 идола Войны, 5 серебряных и 5 костяных скарабеев Ветсере в Ан\'Кираж. Кроме того, необходимо заслужить как минимум уважительное отношение рода Ноздорму.', },
  },
