  [8572] = {
    'Броня ветерана',
    {'Те знаки, которые достались тебе во время выполнения боевых, тыловых и тактических заданий в Крепости Кенария, нужны не для красоты, $N.$B$BНе теряй их. Когда ты докажешь свою верность нашей организации, я смогу предложить тебе кое-какое снаряжение, которое мы храним для ближайших сподвижников, в обмен на эти знаки.', },
    {'Принесите 7 боевых, 4 тыловых и 4 тактических знаков Кенария Варгусу из Крепости Кенария в Силитусе. Кроме того, для выполнения этого задания вам необходимо заслужить уважительное отношение Круга Кенария.', },
  },
  [8573] = {
    'Броня защитника',
    {'Те знаки, которые достались тебе во время выполнения боевых, тыловых и тактических заданий в Крепости Кенария, нужны не для красоты, $N.$B$BНе теряй их. Когда ты докажешь свою верность нашей организации, я смогу предложить тебе кое-какое снаряжение, которое мы храним для ближайших сподвижников, в обмен на эти знаки.', },
    {'Принесите 15 боевых, 20 тыловых, 20 тактических знаков Кенария и 1 знак Кенария Варгусу из Крепости Кенария в Силитусе. Кроме того, вас должны превозносить в Круге Кенария.', },
  },
  [8574] = {
    'Стойкая броня',
    {'Те знаки, которые достались тебе во время выполнения боевых, тыловых и тактических заданий Зовущей из клана Гордый Рог, нужны не для красоты, $N.$B$BНе теряй их. Когда ты докажешь свою верность нашей организации, я смогу предложить тебе кое-какое снаряжение, которое мы храним для ближайших сподвижников, в обмен на эти знаки.', },
    {'Принесите 15 боевых, 20 тыловых, 17 тактических знаков Кенария и 1 знак Ремула Варгусу из Крепости Кенария в Силитусе. Кроме того, для выполнения этого задания необходимо заслужить почтительное отношение Круга Кенария.', },
  },
  [8575] = {
    'Магическая книга Азурегоса',
    {'Из надписи на книге вы ни слова не понимаете. Вы даже не знаете, на каком языке сделана эта надпись!', },
    {'Принесите магическую книгу Азурегоса Нарайну Причуденю в Танарис.', },
  },
  [8576] = {
    'Перевод книги',
    nil,
    nil,
  },
  [8577] = {
    'Тушеный Лис, БЛД',
    {'Если я хочу хоть что-то перевести, мне нужны мои гадальные очки. Увы, их давным-давно спер мой бывший лучший друг Тушеный Лис. Он разбил мое сердце на пять частей и оставил меня в горести.$B$BПри помощи моего верного хрустального шара я выяснил, где он находится – либо у Стены Седогрива в Серебряном бору или в каком-то мире за порталом Запределья, нынче разоренном Пылающим Легионом. Вы должны найти его след и отнять у него мои очки, $N!', },
    {'Нарайн Причудень хочет, чтобы вы нашли его бывшего лучшего друга (БЛД) Тушеного Лиса, и отняли у него гадальные очки, которые БЛД украл у Причуденя.', },
  },
  [8578] = {
    'Гадальные очки? Без проблем!',
    {'* Из ящика доносится вздох облегчения.*$B$BТак вам нужны только его очки? Отлично! Никаких проблем... Подождите минуточку, и... Эй! Где... Епрст!$B$BВ общем, есть новость плохая и еще хуже. Плохая – я потерял очки. Еще хуже – я потерял их, когда в прошлом месяце разнюхивал обстановку в Огненных Недрах. Да, в Огненных Недрах... Думаю, что на этом наш разговор закончен! Пока!', },
    {'Найдите гадальные очки Нарайна и отнесите их Нарайну Причуденю в Танарис.', },
  },
  [8579] = {
    'Поборники правого дела из числа смертных',
    nil,
    nil,
  },
  [8580] = {
    'Орде не хватает огнецвета!',
    {'Даров, |3-6($C). Как делишки, дружище? Орде тут для войны в Ан\'Кираже понадобилась прорва этого несчастного огнецвета. Даж интересно, они собираются запихнуть его во всякие взрывающиеся штуки или отдать Альянсу на топливо для танков? Спроси что-нибудь полегче, тут Пелекейки не в курсе, ага.$B$BНо кое-что Пелекейки знает... например, что ты сейчас пойдешь и принесешь ему этого несчастного огнецвета, ага, дружище?', },
    {'Принесите 20 огнецветов нетопыриному наезднику Пелекейки в Аллею Духов Оргриммара.', },
  },
  [8581] = {
    'Орде все еще не хватает огнецвета!',
    nil,
    {'Принесите 20 огнецветов нетопыриному наезднику Пелекейки в Аллею Духов Оргриммара.', },
  },
  [8582] = {
    'Орде не хватает лилового лотоса!',
    {'Лилового лотос, |3-6($C), является основной составляющей множества зелий, эликсиров и масел, которые необходимы для войны в Ан\'Кираже. Мы создадим бесчисленное количество самых разных снадобий. Я думаю, придется даже поэкспериментировать, чтобы исследовать все возможные способы его применения. Но для экспериментов необходимо иметь достаточно цветов в запасе.$B$BА вот это уже работа для тебя. Ступай и собери как можно больше цветков лилового лотоса. Возьмешься ради нас за это задание?', },
    {'Принесите 20 цветков лилового лотоса аптекарю Жезели в Аллею Духов Оргриммара.', },
  },
  [8583] = {
    'Орде все еще не хватает лилового лотоса!',
    nil,
    {'Принесите 20 цветков лилового лотоса аптекарю Жезели в Аллею Духов Оргриммара.', },
  },
  [8584] = {
    'Никогда не расспрашивай меня о моем бизнесе!',
    {'Обо мне вам следует знать только одно – НИКОГДА не расспрашивайте меня о моем бизнесе! Я работаю с такими материями, в которых вы просто ничего не понимаете! Например: знаете ли вы, что такое психотронный мордарий? Конечно, не знаете! Именно поэтому я – мозговой центр, а вы – добывающий придаток.$B$BКак я уже сказал, если мы хотим, чтобы у нас все получилось, нам понадобится 500-фунтовая курица.$B$BСтупайте в Прибамбасск и поговорите с Могилем Быстрорубом – он скажет, где вам искать подходящий экземпляр.', },
    {'Нарайн Причудень из Танариса хочет, чтобы вы поговорили с Могилем Быстрорубом из Прибамбасска.', },
  },
  [8585] = {
    'Остров Ужаса!',
    {'Легенды гласят, что у химер Острова Ужаса, что в Фераласе, самое сочное и нежное на свете мясо! У этих химер есть главный химер, который, если его приготовить как надо, сойдет за 500-фунтовую курицу. Или не сойдет.$B$BСтупай на остров Ужаса и убей лорда Лакмерана. Принеси мне его тушу. Гном будет доволен. А вот чтобы я был доволен, достань мне немного вырезки химеры.$B$BСделай это – и будет тебе твоя 500-фунтовая курица и кое-что сверх того.', },
    {'Принесите тушу Лакмерана и 20 кусков вырезки химер Могилю Быстрорубу в Танарис.', },
  },
  [8586] = {
    'Зажигательные химеровые отбивные Могиля',
    {'Рецепт готов! Теперь мне нужно от тебя немного ракетного топлива и глубокоскальной соли. Я займусь специями, а за счет мяса, что ты $Gдобыл:добыла; покроем остальные расходы.$B$BКак принесешь мне то, что я просил, курица будет готова, и можешь нести ее Нарайну.', },
    {'Могиль Быстроруб Прибамбасска просит, чтобы вы принесли ему 20 порций гоблинского ракетного топлива и 20 порций глубокоскальной соли.', },
  },
  [8587] = {
    'Возвращение к Нарайну',
    {'Я гоблин слова, $Gпарень:подруга;. Вот твой 500 фунтовый цыпленок.$B$BНа кой гному 500-фунтовый цыпленок – понятия не имею...$B$B*Могиль ворчит себе под нос что-то неразборчивое.*$B$BМожет, он спать в нем будет... Грязные гномы...', },
    {'Доставьте 500-фунтового цыпленка Нарайну Причуденю в Танарис.', },
  },
  [8588] = {
    'Орде не хватает толстой кожи!',
    {'Если уж мы собрались воевать, сначала надо подготовиться как следует, правда, дружище? Толстая кожа на войне – продукт первой необходимости. Она идет на все – начиная с заплаток на доспехи и заканчивая старыми добрыми ловушками.$B$BИменно поэтому ее надо собрать как можно больше. А вот это уже твоя работа, |3-6($C). Наточи поострее нож и добудь кож этак штук десять. Вот принесешь их, тогда и поговорим.', },
    {'Принесите 10 кусков толстой кожи специалисту по снятию шкур Джамани в Аллею Духов Оргриммара.', },
  },
  [8589] = {
    'Орде все еще не хватает толстой кожи!',
    nil,
    {'Принесите 10 кусков толстой кожи специалисту по снятию шкур Джамани в Аллею Духов Оргриммара.', },
  },
  [8590] = {
    'Орде не хватает плотной кожи!',
    {'Я рада, что ты $Gрешил:решила; поговорить со мной, |3-6($C). Подготовка к войне в Ан\'Кираже идет полным ходом, так что нам с тобой нельзя сбавлять темп работы.$B$BЯ отвечаю за сбор плотной кожи, и ее нужно действительно много, поэтому без твоей помощи никак не справиться. Если ты мне поможешь, в моем лице тебе будет благодарна вся Орда.', },
    {'Принесите 10 плотных кож сержанту Умале в Аллею Духов Оргриммара.', },
  },
  [8591] = {
    'Орде все еще не хватает плотной кожи!',
    nil,
    {'Принесите 10 плотных кож сержанту Умале в Аллею Духов Оргриммара.', },
  },
  [8592] = {
    'Тиара Оракула',
    {'Императоры-близнецы... Их непрестанный шепот в моих ушах потихоньку сводит меня с ума. Они смеются надо мной, зная, что я не сумею вступить в их владения, чтобы спасти моего брата Аригоса.$B$BЯ вижу их безобразные головы, увенчанные могущественными коронами. Но я отомщу им... эти символы власти Ан\'Кираж будут служить нам.$B$BПринеси то, что мне нужно, $N. Взамен я отдам тебе головной убор, не имеющий себе равных по силе.', },
    {'Принесите венец Век\'нилаша, 2 идола Мудреца, 5 серебряных и 5 костяных скарабеев Андоргосу в Ан\'Кираж. Кроме того, необходимо заслужить как минимум дружественное отношение рода Ноздорму.', },
  },
  [8593] = {
    'Брюки Оракула',
    {'Хочешь доказать нам, что тебе можно доверять, $Gсмертный:смертная;? Ну хорошо. Возможно, и тебе удастся извлечь из этого пользу.$B$BСлушай. Есть существо, обладающее легендарной силой. Это древний песчаный червь Оуро. Говорят, его создал сам Древний Бог, в насмешку над всеми остальными формами жизни.$B$BШкура у этого червя просто невероятная. Она такая толстая, что ее невозможно пробить, но при этом такая гибкая, что позволяет червю перемещаться с непостижимой скоростью и изяществом.$B$BПринеси мне шкуру Оуро и еще ряд компонентов, и я создам для тебя поножи, обладающие огромной силой.', },
    {'Принесите целую шкуру Оуро, 2 идола Жизни, 5 золотых и 5 глиняных скарабеев Кандострасу в Ан\'Кираж. Кроме того, необходимо заслужить как минимум дружественное отношение рода Ноздорму.', },
  },
  [8594] = {
    'Оплечье Оракула',
    {'У нас говорят, что вычурные наплечные доспехи, которые носят смертные, были изначально созданы в подражание крыльям драконов.$B$BПринеси мне наручники, принадлежащие киражским правителям, и я сделаю из них наплечье еще более устрашающее, чем крылья самого Нефариана!', },
    {'Принесите киражские наручники Командования, 2 идола Возрождения, 5 серебряных скарабеев и 5 скарабеев из слоновой кости Андоргосу в Ан\'Кираж. Кроме того, необходимо заслужить как минимум равнодушное отношение рода Ноздорму.', },
  },
  [8595] = {
    'Поборники правого дела из числа смертных',
    nil,
    nil,
  },
  [8596] = {
    'Обмотки Оракула',
    {'Древняя киражская магия исполнена небывалой силы. Cамые могущественные киражи владели предметами такой мощи, что они были способны остановить даже дракона.$B$BПринеси мне киражские наручники властности и еще несколько компонентов, и я создам для тебя потрясающие сапоги.$B$BУвидишь, насколько они окажутся полезны в схватке с врагом.', },
    {'Принесите киражские наручники властности, 2 идола Смерти, 5 бронзовых и 5 золотых скарабеев Кандострасу в Ан\'Кираж. Кроме того, необходимо заслужить как минимум равнодушное отношение рода Ноздорму.', },
  },
  [8597] = {
    '"Драконий язык для чайников"',
    {'Я так и знал, что однажды эта книга мне пригодится! И я в своей мудрости и и предусмотрительности приготовил для нее укромное хранилище.$B$BНе беспокойтесь, это место никто и никогда не найдет! Только для того, чтобы туда добраться, мне понадобился гирокоптер. Увы, я потерпел крушение на берегу по дороге домой. Но именно в результате этого несчастного случая я обрел сверхгномские способности!$B$BНайдите мой разбитый гирокоптер на Последнем берегу и плывите оттуда к югу! Нужное место узнаете с первого взгляда!', },
    {'Найдите книгу Нарайна Причуденя на острове в Южных морях.', },
  },
  [8598] = {
    'вЫкУП',
    {'*Текст записки оттиснут на пергаменте различными шрифтами.*$B$BТвоя драгоценная книга у нас, гном. Ежели хочешь снова ее увидеть, делай как сказано.$B$B*Дальше идет список инструкций на три страницы.*$B$BНеси деньги и приходи один!', },
    {'Отнесите письмо с требованием выкупа Нарайну Причуденю в Танарис.', },
  },
  [8599] = {
    'Любовная песнь для Нарайна',
    {'Нечасто у меня бывают гости. Говоря по чести, последний раз это было тогда, когда я спасла этого миленького, сладенького гномика из-под обломков разбитого ветролета.$B$B*Меридит смотрит вдаль, погрузившись в воспоминания.*$B$BТы его знаешь? Нарайн? Нарайн Причудень? О, как бы я была благодарна, если бы кто-нибудь доставил ему письмо от меня. Я просто должна рассказать ему о своих чувствах! Я знаю, это любовь! Я точно знаю!$B$BНе можешь ли ты доставить это письмецо Нарайну? Я век буду благодарна тебе!', },
    {'Отнесите любовное послание Меридит Нарайну Причуденю в Танарис.', },
  },
  [8600] = {
    'Орде нужна грубая кожа!',
    {'Я понял, что подгонка ножа по руке важна даже больше, чем острота лезвия. И самый тупой клинок способен порезать, если приложить нужное усилие. Правильно, |3-6($C)? Думаю, у нас не будет таких проблем, ведь наши ножи всегда хорошо заточены.$B$BНу, это не важно. У меня для тебя есть дело. Достань мне побольше грубой кожи. Она нужна, чтобы Орда как следует подготовилась к войне в Ан\'Кираже. Я не сомневаюсь, что ты отлично справишься, ага?', },
    {'Принесите 10 кусков грубой кожи Доктору Серратусу, который находится в Аллее Духов в Оргриммаре.', },
  },
  [8601] = {
    'Орде все еще не хватает грубой кожи!',
    nil,
    {'Принесите 10 кусков грубой кожи Доктору Серратусу, который находится в Аллее Духов в Оргриммаре.', },
  },
  [8602] = {
    'Наплечье Зовущего бурю',
    {'У нас говорят, что вычурные наплечные доспехи, которые носят смертные, были изначально созданы в подражание крыльям драконов.$B$BПринеси мне наручники, принадлежащие киражским правителям, и я сделаю из них наплечье еще более устрашающее, чем крылья самого Нефариана!', },
    {'Принесите киражские наручники Господства, 2 идола Жизни, 5 золотых и 5 хрустальных скарабеев Андоргосу в Ан\'Кираж. Кроме того, необходимо заслужить как минимум равнодушное отношение рода Ноздорму.', },
  },
  [8603] = {
    'Одежды оракула',
    {'Древний Бог, о котором лучше вслух не упоминать, медленно пробуждается от своего сна в Ан\'Кираже. Ужасная судьба, которая ждет Азерот в случае его пробуждения, – вопрос лишь нескольких дней... или нескольких лет. Для существа, обладающего подобной мощью, время не имеет значения.$B$BВстанут звезды подходящим образом – и ярость его обрушится на живущих, подобно плети. Тебе следует найти его и положить конец его существованию, пока он не пробудился.$B$BКогда ты сделаешь это, принеси мне кусок его панциря. Взамен я дам тебе броню, подобной которой не владел еще никто из смертных. Броню, достойную убийцы бога.', },
    {'Принесите чешую Древнего Бога, 2 идола Смерти, 5 каменных и 5 хрустальных скарабеев Ветсере в Ан\'Кираж. Кроме того, необходимо заслужить как минимум уважительное отношение рода Ноздорму.', },
  },
  [8604] = {
    'Орде не хватает шерстяных бинтов!',
    {'Приветствую, |3-6($C). Мне поручено собирать шерстяные бинты для подготовки к войне в Ан\'Кираже. К сожалению, на поле боя солдаты не могут рассчитывать, что лекари успеют добраться сразу до всех. Поэтому мы должны уделить как можно больше внимания оказанию первой помощи.$B$BЕсли у тебя есть время, помоги мне собрать шерстяные бинты. Принеси их сюда, а я позабочусь, чтобы тебе выдали положенное вознаграждение.', },
    {'Принесите 20 шерстяных бинтов целителю клана Долгий Бег в Аллею Духов Оргриммара.', },
  },
  [8605] = {
    'Орде все еще не хватает шерстяных бинтов!',
    nil,
    {'Принесите 20 шерстяных бинтов целителю клана Долгий Бег в Аллею Духов Оргриммара.', },
  },
  [8606] = {
    'Утка!',
    {'Меня посетила гениальная мысль! Вот что мы сделаем. Я насыплю в мешок камней, а вы осуществите сброс. Да, знаю, я должен доставить выкуп лично – я и доставлю, типа того.$B$B<Нарайн меряет вас взглядом.>$B$BСойдете. Берите этот тюрбан, одежду и мешок с камнями и направляйтесь к условленной точке сброса в Зимних ключах.$B$BВ этом специальном наборе все, что вам нужно. Когда они высунут нос, чтобы забрать выкуп, задайте им перцу! Будут знать, как наезжать на Нарайна!', },
    {'Нарайн Причудень из Танариса хочет, чтобы вы отправились в Зимние кючи и сбросили мешок с золотом в условленной точке, указанной книгокрадами.', },
  },
  [8607] = {
    'Орде не хватает бинтов из магической ткани!',
    {'Ты действительно хочешь помочь мне в сборе материалов, за которые я отвечаю? Отлично! Бинты из магической ткани нужны мне любой ценой, я имею в виду, в буквальном смысле "любой ценой". Хорошо бы свежие, но это совершенно не обязательно. Когда добудешь их, |3-6($C), возвращайся ко мне, я прослежу, чтобы тебя соответственным образом наградили.', },
    {'Принесите 20 бинтов из магической ткани госпоже Кэллоу в Аллею Духов Оргриммара.', },
  },
  [8608] = {
    'Орде опять не хватает бинтов из магической ткани!',
    nil,
    {'Принесите 20 бинтов из магической ткани госпоже Кэллоу в Аллею Духов Оргриммара.', },
  },
  [8609] = {
    'Орде не хватает бинтов из рунической ткани!',
    {'Альянс и Орда готовятся к войне в Ан\'Кираже, идя встречными курсами. Просто удивительно, что они собираются воевать не друг с другом, а с силитидами, какая бы сила ни стояла за ними. Не менее удивительно, что обе стороны вносят свой вклад в заготовку военных ресурсов в достаточном количестве.$B$BЯ отвечаю за сбор бинтов из рунической ткани, для оказания первой помощи. Но одна я точно не справлюсь. Помоги мне, |3-6($C), я буду очень признательна.', },
    {'Принесите 20 бинтов из рунической ткани Каменному стражу Глиняному Копыту в Аллею Духов Оргриммара.', },
  },
  [8610] = {
    'Орде все еще не хватает бинтов из рунической ткани!',
    nil,
    {'Принесите 20 бинтов из рунической ткани Каменному стражу Глиняному Копыту в Аллею Духов Оргриммара.', },
  },
  [8611] = {
    'Орде не хватает постных стейков из волчатины!',
    {'Я здесь не просто так загораю, |3-6($C). Подготовка к войне – дело нешуточное! Если мы хотим передавить киражских тараканов, надо приложить массу усилий, в том числе к заготовке провианта.$B$BМеня назначили ответственным за засолку постных стейков из волчатины. Однако не могу же я разорваться на части – бегать за волками по лесам и обеспечивать хранение и учет мяса одновременно. Так что это тебе придется добыть волчатины и приготовить ее, как надо, а потом ты принесешь ее мне. Все понятно?', },
    {'Принесите 20 постных стейков из волчатины кровостражу Сыросмолла в Аллею Духов Оргриммара.', },
