  [8733] = {
    'Эраникус, Тиран Сна',
    {'Это будет расплата. Если я сегодня погибну, то паду я за правое дело. Верни осколок зеленого жезла. Освободи Эраникуса от порчи, которой поразили его Древние Боги.$B$BОтправляйся в Дарнас. Прямо у стен города ты встретишь одного из моих агентов. Он даст ход нашим делам и оповестит Тиранду о наших планах так, чтобы не прознал Олений Шлем.$B$B*Малфурион вздыхает.*$B$BУтратить милость тысячелетий...', },
    {'Отправляйтесь на континент Тельдрассил и найдите агента Малфуриона у стен Дарнаса.', },
  },
  [8734] = {
    'Тиранда и Ремул',
    {'Тиранда будет извещена, $Gсмертный:смертная;. Шан\'до Ярость Бури подготовил меня к неожиданностям.$B$BТеперь тебе надлежит отправится в Лунную поляну и поговорить с хранителем Ремулом. Он даст тебе дальнейшие инструкции по поводу того, как вызвать Эраникуса.$B$BМы все желаем тебе великой удачи.', },
    {'Отправляйтесь в Лунную поляну и поговорите с хранителем Ремулом.', },
  },
  [8735] = {
    'Проклятие Кошмара',
    {'Тебе нужно оправиться к четырем порталам сна Азерота. Из каждого ты добудешь фрагмент проклятия Кошмара. Дракониды, населяющие те места, не будут их отдавать. Возвращайся, когда добудешь все фрагменты.', },
    {'Отправьтесь к четырем порталам Изумрудного Сна в Азероте и добудьте фрагменты проклятия Кошмара из каждого из них. Вернитесь к хранителю Ремулу в Лунную поляну по завершении задания.', },
  },
  [8736] = {
    'Явление Кошмара',
    {'Мы должны усмирить Эраникуса. Мы не можем позволить ему уничтожить себя!$B$BТиранда уже идет нам на помощь. Мы должны отбить удары дракона и спасти Ночную гавань от неминуемого уничтожения.$B$BСтой на страже! Ты и твои союзники – наша единственная надежда.', },
    {'Защитите Ночную гавань от Эраникуса. Не дайте погибнуть хранителю Ремулу. Не убивайте Эраникуса. Защищайте себя. Дождитесь Тиранды.', },
  },
  [8737] = {
    'Лазурный храмовник',
    {'Сумеречный Молот и повелители стихий, которым поклоняются служители культа, представляют серьезную опасность для деятельности Круга в Силитусе. Учитывая, что у нас появились новые враги – в Ан\'Кираже, мы не должны терять бдительность и давать волю внешним врагам.$B$BОтыщи способ вызвать и уничтожить лазурного храмовника и доложи о выполнении задания Бору Буйногривому.', },
    {'Призовите и убейте лазурного храмовника и доложите о выполнении задания Бору Буйногривому из Крепости Кенария. Для завершения задания принесите тактическую задачу I.', },
  },
  [8738] = {
    'Донесение разведчика из Улья Регал',
    {'Мы отправили наших лучших разведчиков к ульям силитидов. Разведчик Ландион уже несколько дней наблюдает за передвижениями этих насекомых. Найди его и забери у него письменный отчет. Нам необходимо как можно скорее получить собранные сведения.', },
    {'Свяжитесь с кенарийским разведчиком Ландионом в Улье Регал и отнесите донесение разведчика из Улья Регал Зовущей из клана Гордый Рог в Крепость Кенария. Кроме того, принесите тактическую задачу VII.', },
  },
  [8739] = {
    'Донесение разведчика из Улья Аши',
    {'Мы отправили наших лучших разведчиков к ульям силитидов. Разведчик Джалия уже несколько дней наблюдает за передвижениями этих насекомых. Найди ее и забери у нее письменный отчет. Нам необходимо как можно скорее получить собранные сведения.', },
    {'Свяжитесь с кенарийской разведчицей Джалией в Улье Аши и отнесите ее донесение призывательнице ветров Гордый Рог в Крепость Кенария. Кроме того, принесите тактическую задачу VIII.', },
  },
  [8740] = {
    'Сумеречные мародеры',
    {'В последнее время появились верховые отряды сторонников Сумеречного Молота. Они не суются в земли, охраняемые Крепостью Кенария, а грабят небольшие караваны и отряды.$B$BНайди и убей сумеречных мародеров, а заодно и их лидера, воительницу по имени Морна. О выполнении задания доложи Зовущей из клана Гордый Рог.', },
    {'Убейте сумеречного мародера Морну и двух сумеречных мародеров. Доложите о выполнении задания Зовущей из клана Гордый Рог. Кроме того, сдайте тактическую задачу IX.', },
  },
  [8741] = {
    'Возвращение победителя',
    {'Хранитель ждет твоего возвращения, $N. Отнеси этот осколок зеленого скипетра Анахроносу в Пещеры Времени.', },
