  [8770] = {
    'Цель: защитники из Улья Аши',
    {'По мере нарастания конфликта, силитиды представляют все большую опасность для наших операций. Тебя избрали для участия в нападении на Улей Аши. Твоей основной целью будут защитники из этого улья.$B$BВыполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 защитников из Улья Аши и доложите о выполнении задания командиру Мар\'алиту в Крепости Кенария в Силитусе. Для завершения задания принесите боевую задачу I.', },
  },
  [8771] = {
    'Цель: пескоходы из Улья Аши',
    {'По мере нарастания конфликта, силитиды представляют все большую опасность для наших операций. Тебя избрали для участия в нападении на Улей Аши. Твоей основной целью будут пескоходы из этого улья.$B$BВыполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 пескоходов из Улья Аши и доложите о выполнении задания командиру Мар\'алиту в Крепости Кенария в Силитусе. Для завершения задания принесите боевую задачу II.', },
  },
  [8772] = {
    'Цель: стражи дорог из Улья Зора',
    {'Мы вызвали подкрепление для сражения у Улья Зора. Твоя задача – убить как можно больше стражей дорог. Выполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 стражей дорог из Улья Зора и доложите о выполнении задания командиру Мар\'алиту из Крепости Кенария в Силитусе. Для завершения задания принесите боевую задачу VI.', },
  },
  [8773] = {
    'Цель: разрушители из Улья Зора',
    {'Мы вызвали подкрепление для сражения у Улья Зора. Твоя задача – убить как можно больше разрушителей. Выполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 разрушителей из Улья Зора и доложите о выполнении задания командиру Мар\'алиту в Крепости Кенария в Силитусе. Для завершения задания принесите изложение боевой задачи IV.', },
  },
  [8774] = {
    'Цель: стерегущие из Улья Регал',
    {'По мере нарастания конфликта, силитиды представляют все большую опасность для наших операций. Тебя избрали для участия в нападении на Улей Регал. Твоей основной целью будут стерегущие этот улей душители.$B$BВыполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 стерегущих из Улья Регал и доложите об этом командиру Мар\'алиту в Крепости Кенария в Силитусе. Для завершения задания принесите изложение боевой задачи VIII.', },
  },
  [8775] = {
    'Цель: злюки из Улья Регал',
    {'По мере нарастания конфликта, силитиды представляют все большую опасность для наших операций. Тебя избрали для участия в нападении на Улей Регал. Твоей основной целью будут огнеполохи из этого улья.$B$BВыполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 злюк из Улья Регал и доложите о выполнении задания командиру Мар\'алиту в Крепости Кенария в Силитусе. Для завершения задания принесите боевую задачу IX.', },
  },
  [8776] = {
    'Цель: поработители из Улья Регал',
    {'По мере нарастания конфликта, силитиды представляют все большую опасность для наших операций. Тебя избрали для участия в нападении на Улей Регал. Твоей основной целью будут поработители из этого улья.$B$BВыполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 поработителей из Улья Регал и доложите о выполнении задания командиру Мар\'алиту в Крепости Кенария в Силитусе. Для завершения задания принесите боевую задачу X.', },
  },
  [8777] = {
    'Цель: землерои из Улья Регал',
    {'По мере нарастания конфликта, силитиды представляют все большую опасность для наших операций. Тебя избрали для участия в нападении на Улей Регал. Твоей основной целью будут землерои из этого улья.$B$BВыполнив задание, отчитайся командиру Мар\'алиту.', },
    {'Убейте 30 землероев из Улья Регал и доложите о выполнении задания командиру Мар\'алиту в Крепости Кенария в Силитусе. Для завершения задания принесите боевую задачу XI.', },
  },
  [8778] = {
    'Взрывчатка для дружины Стальгорна',
    {'Дружина Стальгорна прислала необычный запрос – на весьма... э-э-э... неустойчивые вещества. Собери их и принеси господину Пружине Форсунки из дружины Стальгорна.', },
    {'Принесите 6 бутылок воспламеняющей смеси, 5 бочонков гоблинского ракетного топлива и 10 порций концентрированного взрывчатого вещества чародею Пружине Форсунки возле Улья Зора в Силитусе. Кроме того, принесите тыловое задание IV.', },
  },
  [8779] = {
    'Материалы для гадания',
    {'Исследования геолога Злая Шутка имели огромное значение для нашей деятельности в Силитусе. Недавно она запросила материалы для гадания.$B$BПожалуйста, собери все необходимое и отнеси ей. Пусть она продолжает исследования.', },
    {'Принесите 1 большой сверкающий осколок, 1 большой светозарный осколок и 1 огромный изумруд геологу Злой Шутке в Крепости Кенария в Силитусе. Также принесите тыловое задание V.', },
  },
  [8780] = {
    'Комплекты брони',
    {'У дружины Стальгорна на исходе запасы накладок на доспехи. Добудь партию таких комплектов и отнеси их Джанеле Твердомолот на заставу дружины Стальгорна, что рядом с Ульем Зора.', },
    {'Принесите 8 комплектов накладок из грубой кожи и 8 комплектов накладок из толстой кожи Джанеле Твердомолот на заставу дружины Стальгорна возле Улья Зора в Силитусе. Кроме того, сдайте тыловую задачу VII.', },
  },
  [8781] = {
    'Нехватка оружия',
    {'Дружина Стальгорна оказалась чрезвычайно полезным союзником в наших битвах в Силитусе. К сожалению, по пути они растеряли много припасов и оборудования. Добудь партию оружия и доставь ее Джанеле Твердомолот к Улью Зора.', },
    {'Принесите 2 палаша лунной стали Джанеле Твердомолот на заставу дружины Стальгорна возле Улья Зора в Силитусе. Кроме того, сдайте тыловую задачу VI.', },
  },
  [8782] = {
    'Поставки формы',
    {'У нас такой приток добровольцев, что не хватает формы. Принеси материалы для пошива формы и отдай их Зовущей из клана Гордый Рог.', },
    {'Принесите 1 кусок луноткани, 2 рулона рунической ткани и 1 железношелковую паутину Зовущей из клана Гордый Рог в Крепости Кенария в Силитусе. Кроме того сдайте тыловую задачу VIII.', },
  },
  [8783] = {
    'Необычные материалы',
    {'Варгус, кузнец Крепости Кенария, запросил партию зачарованных материалов для создания мощного оружия для наших солдат. Добудь все необходимое и отнеси их Варгусу в Крепость Кенария, чтобы он снабдил войска всем необходимым.', },
    {'Принесите Варгусу из Крепости Кенария в Силитусе 2 зачарованных ториевых слитка и 2 куска зачарованной кожи. Кроме того, принесите тыловое задание IX.', },
  },
  [8784] = {
    'Секреты Кираи',
    {'Вам удалось обнаружить древний киражский артефакт! Возможно, один из драконов, стоящих в храме недалеко от входа, сможет найти применение этой реликвии.', },
    {'Принесите древний киражский артефакт драконам, которые укрылись в храме, недалеко от входа.', },
  },
  [8785] = {
    'Амулеты для легиона Оргриммара',
    {'Легион Оргриммара послал запрос на несколько необычные вещества. Учитывая, как хорошо они проявили себя в Силитусе, мы бы хотели как можно скорее выполнить их просьбу. Собери материалы и отнеси их троллихе по имени Шаи.', },
    {'Принесите 6 мощных амулетов, 6 бутылей большого колдунства и 8 бутылок воспламеняющей смеси темной жрице Шаи к Улью Регал в Силитус. Кроме того, принесите тыловое задание IV.', },
  },
  [8786] = {
    'Нехватка оружия',
    {'Легион Оргриммара послал запрос на поставку оружия, а его нет на складе! Добудь необходимое снаряжение и отнеси его Мероку Долгому Шагу к Улью Регал.', },
    {'Принесите 3 массивных железных топора Мероку Долгому Шагу в лагерь легиона Оргриммара рядом с Ульем Регал. Кроме того, сдайте тыловую задачу VI.', },
  },
  [8787] = {
    'Комплекты брони',
    {'В Силитус, к нам на подмогу, прибыли отважные добровольцы из легиона Оргриммара. По пути они растеряли свои запасы накладок на доспехи. Добудь партию таких комплектов и отнеси их Мероку Долгому Шагу, в лагерь легиона Оргриммара.', },
    {'Принесите 8 комплектов накладок из грубой кожи и 8 комплектов накладок из толстой кожи Мероку Долгому Шагу к Улью Регал. Кроме того, сдайте тыловую задачу VII.', },
  },
  [8788] = {
    'Слегка помятый подарок',
    nil,
    nil,
  },
  [8789] = {
    'Киражское императорское оружие',
    {'Мы не то чтобы просто сидели здесь на протяжении тысячелетий, $N.$B$BМы наблюдали за торжеством этих тварей, пока они пировали и истощали свои ресурсы. Мы слушали, как они строят схемы и планы по уничтожению нашего мира. Мы узнали о предателях в их рядах.$B$BУ них есть слабости, и теперь мы знаем, как их использовать.$B$BНайди и принеси мне их церемониальное оружие. В сочетании с предметами, созданными непостоянством их собственного бога, оно станет силой, с которой придется считаться любому.', },
    {'Аригос в Храме Ан\'Киража создаст для вас усиленное элементием оружие, если вы принесете ему киражское имперское оружие и 3 куска элементиевой руды.', },
  },
  [8790] = {
    'Киражские монаршие регалии',
    {'Аригос предполагает, что элемент, рожденный в результате столкновения между Древними Богами и их прислужниками, в надлежащих условиях станет для них смертельным. Я имею в виду элементий, $Gсмертный:смертная;.$B$BПринеси мне киражские императорские регалии и это вещество-яд, я и смогу создать оружие, обладающее великой силой.', },
    {'Меритра из Сна в Храме Ан\'Киража может создать для вас усиленное элементием оружие, если вы принесете ей киражские имперские регалии и 3 куска элементиевой руды.', },
  },
  [8791] = {
    'Повергнутый Оссириан',
    {'Смертельная угроза больше не нависает над нами. Безжалостный Оссириан повержен!$B$BОб этой победе должны узнать все жители Калимдора. Отнесите голову Оссириана командиру Мар\'алиту в Крепость Кенария.', },
    {'Принесите голову Оссириана Неуязвимого командиру Мар\'алиту в Крепость Кенария.', },
  },
  [8792] = {
    'Орде нужна твоя помощь!',
    {'Приветствую! Хорошо, что ты $Gрешил:решила; поговорить со мной. Если мы хотим достойно подготовиться к ан\'киражской войне, то Орде должны помогать все, кто может. Вот и тебе не след оставаться в стороне. Пока мы тут с тобой болтаем, наши интенданты запасают всяческие ресурсы, но без твоей помощи им не справиться, $N!$B$BПоговори с тем, кто отвечает за заготовки, – с вождем Горчуком. Ну как, что скажешь, |3-6($C)? Примешь участие в этом жизненно важном деле?', },
    {'Поговорите с вождем Горчуком в Аллее Духов Оргриммара.', },
  },
  [8793] = {
