  [8798] = {
    'Твой собственный Йети',
    nil,
    nil,
  },
  [8799] = {
    'Герой дня',
    {'Слушай... Я вообще-то не должен тебе этого говорить, но раз уж ты у нас сегодня герой; и все такое...$B$BВ эту пору везде полно остролиста, но он быстро вянет. Мы получили машину для заготовки остролиста – и он не портится! Мы его используем, чтобы придать вкус празднику... и ради прибыли – летом!$B$BЯ позволю тебе попользоваться этой машинкой, но только если ты мастер-кулинар. От тебя потребуется некоторое количество глубокоскальной соли и два-три, ну, скажем, пять золотых. И получишь славную партию остролиста, которую можно хранить сколько угодно.', },
    {'Принесите 1 порцию глубокоскальной соли и 5 золотых монет к сушилке для остролиста, чтобы получить 5 сушеных остролистов.', },
  },
  [8800] = {
    'Броня Кенария',
    {'Выполнив все задания, обратись к Варгусу, кузнецу. Покажи ему все заработанные знаки, и получишь награду, в зависимости от твоей преданности нашему делу.', },
    {'Поговорите с Варгусом в Крепости Кенария в Силитусе.', },
  },
  [8801] = {
    'Наследие К\'Туна',
    {'Стены Ан\'Киража содрогаются. Сила зла, более древняя, чем сам этот мир, уничтожена.$B$BПри одном взгляде на лежащие перед вами мерзкие останки стынет сердце. Даже после смерти К\'Тун распространяет вокруг себя ауру зла.$B$BВам удалось совершить невозможное.$B$BВы извлекаете глаз и осторожно укладываете его в сумку.$B$BНужно доставить глаз К\'Туна в соседнюю залу и отдать драконам.', },
    {'Принесите Глаз К\'Туна Келестрасу в Храм Ан\'Кирая.', },
  },
  [8802] = {
    'Спаситель Калимдора',
    {'Пришла пора вернуться в Пещеры Времени, $Gгосподин:госпожа; $N. Анахронос ожидает тебя. Отдай ему глаз К\'Туна – не сомневаюсь, он непременно захочет поместить его в коллекцию своего хозяина.', },
    {'Принесите глаз К\'Туна Анахроносу в Пещеры Времени.', },
  },
  [8803] = {
    'Подарок в разноцветной упаковке',
    nil,
    nil,
  },
  [8804] = {
    'Неприкосновенный запас',
    {'В Крепость Кенария прибыли новые добровольцы, и мы должны позаботиться, чтобы они выжили в пустыне. Новичкам грозит обезвоживание и множество ядовитых тварей. Собери как можно больше составляющих неприкосновенных наборов для отправляющихся в пустыню и отнеси их Каландрате в Крепость Кенария.', },
    {'Принесите 4 сферы воды, 4 мощных противоядия и 4 копченые пустынные клецки Каландрате в таверну в Крепости Кенария в Силитусе. Кроме того, сдайте тыловую задачу I.', },
  },
  [8805] = {
    'Сапоги для стражей',
    {'Верховому подразделению Крепости Кенария не хватает изысканных мифриловых сапог. Принеси нам очередную партию и передай их капитану Вишу Коцусу из Крепости Кенария.', },
    {'Принесите 3 пары изысканных мифриловых сапог Вишу Коцусу, капитану стражи в Крепости Кенария в Силитусе. Кроме того сдайте тыловую задачу II.', },
  },
  [8806] = {
    'Шлифовальные камни',
    {'У стражи Крепости Кенария на исходе шлифовальные камни. Добудь их и отнеси капитану Вишу Коцусу в Крепость Кенария.', },
    {'Принесите 10 увесистых точильных каменей, 10 твердых шлифовальных камней и 10 тяжелых точильных каменей Вишу Коцусу, капитану стражи в Крепости Кенария в Силитусе. Кроме того сдайте тыловую задачу III.', },
  },
  [8807] = {
    'Материалы для гадания',
    {'Исследования геолога Злая Шутка имели огромное значение для нашей деятельности в Силитусе. Недавно она запросила материалы для гадания.$B$BПожалуйста, собери все необходимое и отнеси ей. Пусть она продолжает исследования.', },
    {'Принесите 1 большой сверкающий осколок, 1 большой светозарный осколок и 1 огромный изумруд геологу Злой Шутке в Крепости Кенария в Силитусе. Также принесите тыловое задание V.', },
  },
  [8808] = {
    'Поставки формы',
    {'У нас такой приток добровольцев, что не хватает формы. Принеси материалы для пошива формы и отдай их Зовущей из клана Гордый Рог.', },
    {'Принесите 1 кусок луноткани, 2 рулона рунической ткани и 1 железношелковую паутину Зовущей из клана Гордый Рог в Крепости Кенария в Силитусе. Кроме того сдайте тыловую задачу VIII.', },
  },
  [8809] = {
    'Необычные материалы',
    {'Варгус, кузнец Крепости Кенария, запросил партию зачарованных материалов для создания мощного оружия для наших солдат. Добудь все необходимое и отнеси их Варгусу в Крепость Кенария, чтобы он снабдил войска всем необходимым.', },
    {'Принесите Варгусу из Крепости Кенария в Силитусе 2 зачарованных ториевых слитка и 2 куска зачарованной кожи. Кроме того, принесите тыловое задание IX.', },
  },
  [8810] = {
    'Запас бинтов',
    {'В последнее время мы понесли серьезные потери, и запас бинтов стремительно иссякает. Добудь нам первоклассные перевязочные материалы и отнеси их призывательнице ветров Гордому Рогу в Крепость Кенария.', },
