  [8856] = {
    'Неприкосновенный запас',
    {'В Крепость Кенария прибыли новые добровольцы, и мы должны позаботиться, чтобы они выжили в пустыне. Новичкам грозит обезвоживание и множество ядовитых тварей. Собери как можно больше составляющих неприкосновенных наборов для отправляющихся в пустыню и отнеси их Каландрате в Крепость Кенария.', },
    {'Принесите 4 сферы воды, 4 мощных противоядия и 4 печеных лосося Каландрате в таверну в Крепости Кенария в Силитусе.', },
  },
  [8857] = {
    'Тайны колосса – Аши',
    {'Мне нужны закаленные в бою искатели приключений, которые согласятся принести свежие образцы панциря павшего колосса Аши Карлсу в Пиратскую Бухту. Нет-нет, я не прошу подходить к телу этой твари. Возьми один из образцов у меня и поспеши. Тухлятина ему не годится!', },
    {'Отнесите фрагмент панциря колосса Аши Карлсу Пропеллерзу в Пиратскую Бухту.', },
  },
  [8858] = {
    'Тайны колосса – Регал',
    {'Я бы с радостью сунул этот панцирь прямо в наглую рожу Скользкорука... Только вот мне не выбраться, я собираю образцы этого тараканища. И судя по его размерам, провожусь еще лет сто.$B$BОтнеси этот образец Скользкоруку, и побыстрее. Он платит МНОГО!$B$BА вот его адресок: Лагерь Братства Тория – он в Тлеющем ущелье.', },
    {'Отнесите фрагмент панциря колосса Регала надзирателю Скользоруку в Тлеющем ущелье.', },
  },
  [8859] = {
    'Тайны колосса – Зора',
    {'Моих слуг едва не убили при попытке добыть эти образцы. Не медли ни минуты! Лорд Максвелл Тиросс с нетерпением ждет свежих образцов панцирей. Он надеется, что тайны Колосса помогут нам в войне с Плетью!$B$BТы еще тут?! Вперед, в часовню Последней Надежды! Да, ты получишь неплохую награду за труды...', },
    {'Отнесите фрагмент панциря колосса Зоры лорду Максвеллу Тироссу в Часовню Последней Надежды в Восточных Чумных землях.', },
  },
  [8860] = {
    'Отмечаем Новый Год!',
    {'Штормград устраивает празднество в честь нового года! Целый день там можно пить задарма, а ночью будет фейерверк!$B$BЭто должно быть прекрасно... но в Пиратской Бухте сегодня тоже праздник, и держу пари, там будет еще лучше!$B$BНу, как бы то ни было, хозяйке таверны Аллисон в Штормграде требуются припасы Пастбищ Дымного Леса, чтобы помочь накормить всех гуляк. Окажи мне услугу, доставь эту партию!$B$BСпасибо, $N! Аллисон держит таверну "Позолоченная роза" в Торговом квартале Штормграда.', },
    {'Отнесите припасы Пастбищ Дымного Леса хозяйке таверны Аллисон в Штормград.', },
  },
  [8861] = {
    'Отмечаем Новый Год!',
    {'Громовой Утес устраивает празднество в честь нового года! Целый день там можно пить задарма, а ночью будет фейерверк!$B$BЭто должно быть прекрасно... но в Пиратской Бухте сегодня тоже праздник, и держу пари, там будет еще лучше!$B$BНу, как бы то ни было, хозяйке таверны Пале в Громовом Утесе требуются припасы Пастбищ Дымного Леса, чтобы помочь накормить всех гуляк. Окажи мне услугу, доставь эту партию!$B$BСпасибо, $N! Пала держит таверну возле моста, ведущего на вершину Охотников.', },
    {'Отнесите припасы Пастбищ Дымного Леса хозяйке таверны Пале в Громовой Утес.', },
  },
