  [8867] = {
    'Лунный фейерверк',
    {'Каждый год друиды Лунной поляны празднуют торжество своего города над древним злом.$B$BМы вспоминаем о мудрости старейшин, наслаждаемся пирами и, разумеется, пускаем фейерверки!$B$BЗа небольшое пожертвование ты можешь приобрести разноцветные ракеты у наших торговцев и запустить их с помощью пусковых установок.', },
    {'Запустите 8 ракет и 2 фейерверка. Вернитесь к предвестнице Лунного фестиваля в любую столицу.', },
  },
  [8868] = {
    'Благословение Элуны',
    {'Этот проклятый демон, Омен, был некогда героем нашего мира. Волк великой силы, баловень удачи, Омен сражался плечом к плечу с героями Войны Древних, пока не попал под власть демонический магии. Обратившись против своих союзников, он в ярости рыскал по Азероту, сея вокруг себя смерть и ужас.$B$BНаконец, побежденный здесь, в Лунной поляне, Омен ныне спит под илом озера Элуне\'ара. Но каждый год во время праздника он пробуждается... $B$BСобирай соратников, $N, вызови на бой и порази исчадие тьмы, Омена. Заслужи благословение Элуны!', },
    {'Призовите Омена, победите его и получите благословение Элуны. Вернитесь к Валадару Звездной Песне в Ночную Гавань.', },
  },
  [8869] = {
    'Секрет безмятежности',
    {'Отправляйтесь в Стратхольм и убейте Молотобойца из Багрового легиона. Возьмите его фартук и возвращайтесь к Лилит.', },
    {'Отправляйтесь в Стратхольм и убейте Молотобойца из Багрового легиона. Возьмите его фартук и возвращайтесь к Лилит.', },
  },
  [8870] = {
    'Лунный фестиваль',
    {'Время веселиться, $N! Круг Кенария приглашает всех обитателей Азерота принять участие в нашем Лунном фестивале. В городе ты найдешь предвестницу Лунного фестиваля – ее расположение отмечено кругом благословенного лунного света. Она расскажет тебе больше об этом празднике.', },
    {'Поговорите с предвестницей Лунного фестиваля в Палатах Магии Стальгорна. Вы можете также поговорить с предвестницами Лунного фестиваля в других столичных городах.', },
  },
  [8871] = {
    'Лунный фестиваль',
    {'Время веселиться, $N! Круг Кенария приглашает всех обитателей Азерота принять участие в нашем Лунном фестивале. В городе ты найдешь предвестницу Лунного фестиваля – ее расположение отмечено кругом благословенного лунного света. Она расскажет тебе больше об этом празднике.', },
    {'Поговорите с предвестницей Лунного фестиваля в парке Штормграда. Вы можете также поговорить с предвестницами Лунного фестиваля в других столичных городах.', },
  },
  [8872] = {
    'Лунный фестиваль',
    {'Время веселиться, $N! Круг Кенария приглашает всех обитателей Азерота принять участие в нашем Лунном фестивале. В городе ты найдешь предвестницу Лунного фестиваля – ее расположение отмечено кругом благословенного лунного света. Она расскажет тебе больше об этом празднике.', },
    {'Поговорите с предвестницей Лунного фестиваля в дарнасском Анклаве Кенария. Вы можете также поговорить с предвестницами Лунного фестиваля в других столичных городах.', },
  },
  [8873] = {
    'Лунный фестиваль',
    {'Время веселиться, $N! Круг Кенария приглашает всех обитателей Азерота принять участие в нашем Лунном фестивале. В городе ты найдешь предвестницу Лунного фестиваля – ее расположение отмечено кругом благословенного лунного света. Она расскажет тебе больше об этом празднике.', },
    {'Поговорите с предвестницей Лунного фестиваля в Аллее Мудрости в Оргриммаре. Вы можете также поговорить с предвестницами Лунного фестиваля в других столичных городах.', },
  },
  [8874] = {
    'Лунный фестиваль',
    {'Время веселиться, $N! Круг Кенария приглашает всех обитателей Азерота принять участие в нашем Лунном фестивале. В городе ты найдешь предвестницу Лунного фестиваля – ее расположение отмечено кругом благословенного лунного света. Она расскажет тебе больше об этом празднике.', },
    {'Поговорите с предвестницей Лунного фестиваля у входа в Подгород. Вы можете также поговорить с предвестницами Лунного фестиваля в других столичных городах.', },
