  [8946] = {
    'Доказательство жизни',
    {'Нет судьбы горше, чем видеть, как твои близкие гибнут, пытаясь спасти тебя. Ты утвержаешь, что можешь говорить с мертвыми.$B$BОтнеси Антиону этот медальон как доказательство того, что я жива и здорова. Скажи ему, что ему не стоит цепляться за этот мир, обрекая себя на скитания в обличье бесплотного духа – мое бедное сердце этого не вынесет.$B$BПрощай, $N, я всегда буду у тебя в долгу.', },
    {'Отнесите медальон Исиды Антиону Хармону в Восточные Чумные земли.', },
  },
  [8947] = {
    'Странная просьба Антиона',
    {'Мы не сразу поняли важность медальона Вальтхалака, поэтому он был поделен наряду с остальными трофеями. Первый фрагмент получил дворф по имени Тельдрен. Я сам пытался отобрать у него этот фрагмент, но дворф избил меня до полусмерти. Тебе придется забрать фрагмент силой.$B$BГоворят, Тельдрен стал гдадиатором у дворфов Черного Железа. Имей в виду, это очень сильный противник.$B$BИ сначала собери для меня некоторые материалы – они тебе понадобятся.', },
