  [9023] = {
    'Идеальный яд',
    {'Мы с братом занимаемся составлением ядов для поместья Черный Ворон.$B$BЛорд Черный Ворон поручил мне исследование нового и очень эффективного яда, который производится из ядовитых желез обителей Зул\'Гуруба и руин Ан\'Киража.$B$BВ этих проклятых руинах живут две опасных твари, которые годятся для наших целей. Принеси мне ядовитые железы Куриннакс и Веноксиса, и заслужишь мою вечную признательность.', },
    {'Дирк Громодрев из Крепости Кенария попросил принести ему ядовитую железу Веноксиса и ядовитую железу Куриннакса.', },
  },
  [9024] = {
    'Подозрения Аристана',
    {'Я понял! Ты чувствуешь новый запах в воздухе? Запах новых одеколонов и духов, только что вошедших в моду? Наши стражи не стали бы так просто пренебрегать своими обязанностями.$B$BИменно так – разве что их разум затуманен какой-то гнусной алхимией.$B$BЕсть только один способ убедиться – отнеси флакон одеколона и духов Моргану Песту и расскажи ему о моих подозрениях. Я возмещу тебе затраты, когда ты вернешься.', },
    {'Отнесите 1 флакон одеколона и 1 флакон духов Моргану Песту в Торговый квартал Штормграда.', },
  },
  [9025] = {
    'Открытие Моргана',
    {'Ну-с, что я скажу... Похоже, Аристан в конце концов все-таки напал на какой-то след. Я нашел остатки веществ, используемых в любовных зельях и хитрых средствах контроля разума.$B$BИзвестно, что такие вещества используются в некоторых духах, но некоторые из обнаруженных мною ингредиентов... Пахнет занятно.', },
    {'Вернитесь к Аристану Моттару, ожидающему около банка в Торговом квартале Штормграда.', },
  },
  [9026] = {
    'Поиск источника',
    {'Мы должны найти источник. Я видел гораздо больше народа, чем обычно, – в том числе и стражей – входящими в "Позолоченную розу" и выходящими оттуда. Поговори с Эллисон. Если она продает эти духи, она должна знать, откуда они взялись.', },
    {'Поговорите с хозяйкой таверны "Позолоченная роза" Аллисон в Торговом квартале Штормграда.', },
  },
  [9027] = {
    'Поиск источника',
    {'В этом одеколоне есть что-то интересное? Возможно. Никогда не видела, чтобы столько народа так страстно желали приобрести один и тот же товар.$B$BПослушай, если с этим что-то не так, я тоже хочу знать. Я купила всю партию у торговца по имени Эверт Соризам. Он в городе недавно.$B$BОн упоминал, что собирается доставить кое-какие товары в "Тончайшую пряжу".', },
    {'Поговорите с Эвертом Соризамом в "Тончайшей пряже" у каналов Штормграда.', },
  },
  [9028] = {
    'Источник обнаружен',
    {'Что? Предательство? Нет, нет, заверяю тебя, мои намерения были совершенно безобидными.$B$BЧто плохого в том, чтобы слегка помочь тем, кто желает облегчить мучительный ритуал ухаживания? Пойми, это не преступление.$B$BИ чтобы доказать, что я ничего плохого не желал, я даже назову источник этих благовоний. $B$BИх поставлял мне Стаффрон Лерент, какой-то аптекарь. Я никогда не говорил с ним.$B$BГоблин-посредник сказал, что этот аптекарь работает где-то в предгорьях Хилсбрада, за тем загадочным поместьем Черный Ворон. Желаю тебе удачи в поисках.', },
