  [9367] = {
    'Праздник Огня',
    {'Вновь наступает праздник Огненного солнцеворота!$B$BКаждый год, как диктует традиция, избираются Хранители праздничного костра, обязанные присматривать за этими самыми кострами, зажженными в наших столичных городах. По всему Азероту зажигаются огни, но важно, чтобы наши горели жарче и ярче всех, дабы правильно воздать дань уважения Середине Лета.$B$BНа самом деле, если ты не против, у меня для тебя есть задание. Мне пока неведомо, должным ли образом горят праздничные костры во всех столичных городах. Отправляйся туда и погрейся у этих костров, чтобы убедиться, что они достаточно горячи!', },
    {'Прикоснитесь к пламени праздничных костров в Штормграде, Стальгорне и Дарнасе, затем поговорите с хранителем мудрости Праздника в столичных городах.', },
  },
  [9368] = {
    'Праздник Огня',
    {'Вновь наступает праздник Огненного солнцеворота!$B$BКаждый год, как диктует традиция, избираются Хранители праздничного костра, обязанные присматривать за этими самыми кострами, зажженными в наших столичных городах. По всему Азероту зажигаются огни, но важно, чтобы наши горели жарче и ярче всех, дабы правильно воздать дань уважения Середине Лета.$B$BНа самом деле, если ты не против, у меня для тебя есть задание. Мне пока неведомо, должным ли образом горят праздничные костры во всех столичных городах. Отправляйся туда и погрейся у этих костров, чтобы убедиться, что они достаточно горячи!', },
    {'Прикоснитесь к пламени праздничных костров в Оргриммаре, Громовом Утесе и Подгороде, затем поговорите с Праздничным сказителем в столичных городах.', },
  },
  [9378] = {
    'DND FLAG Цитадель ужаса – Наксрамас',
    {'В чаще Проклятого леса находится вход в цитадель ужаса, Наксрамас. Все попытки войти туда разбивались о могущественные чары, наложенные на портал. До сегодняшнего дня.$B$BМы придумали, как проникнуть внутрь при помощи чародейского покрова. Это старый трюк магов Кирин-Тора, над которым я хорошенько поработала. Поскольку твоя преданность делу Серебряного Рассвета не подлежит сомнению, мы возьмем на себя все издержки.', },
    {'Верховный маг Анджела Досантос из Часовни Последней Надежды, что в Восточных Чумных землях, готова бесплатно наложить на вас заклинание чародейского покрова. Сначала добейтесь того, чтобы вас превозносили в Серебряном Рассвете.', },
  },
  [9386] = {
    'Свет в темных местах',
    {'Неужели тебе этого так хочется? Ну что ж, если снова желаешь спуститься в глубины, я с радостью тебя вознагражу. $B$BОтыщи костры, горящие в Стратхольме, Некроситете, Забытом Городе и в залах самого Пика Черной горы. Я буду ждать твоего возвращения, $N.', },
    {'Найдите костры, размещенные в Пике Черной горы, Забытом Городе, Некроситете и Стратхольме и коснитесь их; затем поговорите с хранителем праздничного пламени.', },
  },
  [9388] = {
    'Мерцающие огни Калимдора',
    {'Сегодня по всему миру горит множество огней, $N. Сложнее всего присматривать за теми кострами, которые пылают зеленым; без присмотра они не будут гореть долго. Убедись, что изумрудные огни все еще горят в Калимдоре – пусть наш Хранитель костров успокоится.$B$BТы найдешь эти костры вблизи Кабестана в Степях, неподалеку от Меча Властителя на Темных берегах, в Ясеневом лесу у моста около Аванпоста Среброкрылых, и поблизости от дороги на Утес Ветрорезов вдоль главного тракта в Когтистых горах.', },
    {'Посетите праздничные костры в Ясеневом лесу, Степях, на Темных берегах и в Когтистых горах, затем поговорите с хранителем праздничного пламени.', },
  },
  [9389] = {
    'Мерцающие огни Восточных королевств',
    {'Во время праздника Огненного солнцеворота по всей земле традиционно зажигаются мириады огней. Кое-кто считает, что зеленое пламя является самым изящным, поскольку его труднее всего поддерживать, и оно имеет столь уникальный оттенок.$B$BУбедись, что это пламя все еще горит в Восточных королевствах, чтобы успокоить нашего Хранителя огня. Эти костры размещены около Дун Модр в Болотине, поблизости от деревни Погребальных Костров в Серебряном бору, на утесах неподалеку от маяка Западного Края и возле разрушенной башни в предгорьях Хилсбрада.', },
    {'Посетите праздничные костры в предгорьях Хилсбрада, в Серебряном бору, Западном Крае и Болотине, затем поговорите с хранителем праздничного пламени.', },
  },
  [9415] = {
    'Маршал Синяя Синестен',
    {'Ты новобранец маршала Синестена? Ну, конечно.$B$BЧего ты ждешь? Наши солдаты умирают там, в пустыне. Правда, тебе не терпится к ним присоединиться?$B$BИди, и смотри, не присохни к песку по пути.', },
    {'Поговорите с маршалом Синестеном в лагере Альянса возле Улья Зора.', },
  },
  [9416] = {
    'Генерал Кирика',
    {'Должно быть, ты $Gодин:одна; из новобранцев генерала Кирики. Пора тебе браться за дело. В пустыне тела очень быстро портятся.$B$BДворфы ежедневно получают подкрепление, а мы не должны им уступать. Полезные ископаемые Силитуса наши!$B$BГенерал тебя проинструктирует. Не мое дело – учить тебя. Мне приказали только найти тебя.', },
    {'Поговорите с генералом Кирикой в лагере Орды рядом с Ульем Регал.', },
  },
  [9419] = {
