  [88] = {
    '공주를 없애라!',
    nil,
    {'공주를 해치운 후 스톤필드 농장에 있는 스톤필드 부인에게 놋쇠 목걸이를 가져가야 합니다.', },
  },
  [89] = {
    '영원고요 다리',
    {'마지막으로 오크의 침략이 있었을 때 우리는 모든 철을 녹여 총알, 검, 갑옷을 만들어야만 했네. 스톰윈드에 자재를 새로 보내 달라는 전갈을 보냈지만 붉은마루 놀 무리가 중간에 운반차를 가로채 호숫골 뒤쪽의 언덕으로 도망쳐 버렸지.$B$B그래서 이제는 다리를 재건할 수 있는 자재가 부족하네. 내게 철제 파이크 5개와 철제 리벳 5개를 가져다준다면 확실히 보상해 주도록 하지.$B$B자, 어서 출발하게나!', },
    {'호숫골에 있는 현장감독 오슬로에게 철제 파이크 5개와 철제 리벳 5개를 가져가야 합니다.', },
  },
  [90] = {
    '늑대 양념 케밥',
    {'늑대 양념 케밥은 이곳에서 가장 인기 있는 요리 중 하나지요! 손님께서 원하시면 요리해 드릴 수도 있지만 우선 재료가 있어야겠지요? 늑대 살코기 10점과 스톰윈드 향초를 구해 오셨으면 합니다. 이 마을의 숲엔 늑대가 많이 살고 있으니 살코기는 쉽게 구할 수 있을 겁니다. 아, 향초는 스톰윈드 대운하 향기로운 화원에 있는 펠리시아 검프를 찾아가 보세요.', },
    {'늑대 살코기 10개와 스톰윈드 향초를 모아 어둠골의 주방장 그루얼에게 가야 합니다.', },
  },
  [91] = {
    '솔로몬의 법',
    {'이곳 붉은마루 산맥 동쪽 언덕엔 스톰윈드의 적이 우글거리고 있습니다. 한 사악한 녀석이 비전 마술을 사용하여 마을 사람들을 불안과 공포에 떨게 하고 있지요. 이 사악한 일을 벌이는 장본인은 바로 모건스라는 자입니다.$B$B현재 모건스는 잿빛가죽 놀을 다스리며 자신의 명령을 따르게 하고 있습니다. 호숫골의 법에 따르면 왕국의 죄인을 도우면 사형에 처하도록 되어 있습니다.$B$B이 사악한 놀들에게 호숫골의 이름으로 처벌을 내려야만 합니다!$B$B그들을 처치하고 잿빛가죽 펜던트 10개를 가져다주시면 보상해 드리도록 하겠습니다.', },
    {'호숫골 마을회관에 있는 집행관 코나처에게 잿빛가죽 펜던트 10개를 가져가야 합니다.', },
  },
  [92] = {
    '붉은마루산 굴라시 스튜',
    {'손님께서 주문하신 붉은마루산 굴라시 스튜를 요리해 드릴 수 있다면 좋겠지만 문제가 좀 있어요. 최근에 오크들이 날뛰는 동안 스톰윈드 전체 부대에 식량을 공급하느라 재료 창고가 바닥이 났답니다. 혹시 재료를 구해다 주신다면 정성껏 요리해 드리죠.$B$B질긴 대머리수리 고기 5개, 큰 뿔엄니멧돼지 주둥이 5개, 파삭파삭한 거미 고기 5개를 가져다주세요.', },
    {'호숫골의 주방장 브리아나가 질긴 대머리수리 고기 5개, 큰 뿔엄니멧돼지 주둥이 5개, 파삭파삭한 거미 고기 5개를 가져다 달라고 했습니다.', },
  },
  [93] = {
    '갈색 게살 케이크',
    {'작은 비밀을 하나 알려 드릴까요? 갈색 게살 케이크는 사실 거미 다리로 만든 거랍니다! 좀 메스껍다는 건 알지만 톡 쏘는 맛이 훌륭하고 씹는 맛도 일품이거든요! 끈끈한 거미 다리를 가져다주시면 케이크를 몇 개 구워 드리도록 하죠.$B$B북쪽에 있는 언덕과 강 사이에 서식하는 그물독거미가 최고의 재료라고 합니다.', },
    {'끈끈한 거미 다리 6개를 모아 어둠골에 있는 주방장 그루얼에게 가져가야 합니다.', },
  },
  [94] = {
    '감시의 눈동자',
    {'내 경쟁자인 모건스라는 강력한 악의 마법사가 붉은마루 산맥의 일갈라의 탑에 살고 있다네. 난 아조라의 눈으로 그자를 몰래 감시할 수 있는 방법을 찾아내어 이제 멀리서도 그를 볼 수가 있게 되었네.$B$B그러나 그의 생각을 연구하려면... 도움이 좀 더 필요하다네.$B$B나를 도와 줄 생각이 있다면 내 아조라의 조각상을 일갈라의 탑으로 가져가 탑 근처의 오래된 사자상 위에 올려놓아 주게나.$B$B그게 끝나면 아조라의 힘은 모건스의 차후 계획을 예견해 줄 수 있을 걸세.', },
    {'아조라의 조각상을 붉은마루 산맥에 있는 일갈라의 탑 근처의 오래된 사자상으로 가져가야 합니다.', },
  },
  [95] = {
    '스벤의 복수',
    {'난 남동쪽에 농장을 하나 운영했었습니다. 그런데 내가 없는 사이에 저승바람 고개에서 흑기사들이 농장으로 내려와 우리 가족을 모조리 학살해 버렸습니다!$B$B집으로 돌아왔을 때 헛간 근처에서 유령 같이 창백한 얼굴을 한 자가 뭔가를 허겁지겁 묻고 있는 걸 봤습니다. 그자는 내가 다가가기 전에 도망쳐 버렸고, 나도 흑기사의 뒤를 추적해야했기에 그자가 무얼 숨긴 건지 아직까지 모르고 있습니다.$B$B그자가 뭘 묻었는지 알아봐 주면 정말 고맙겠습니다. 숨긴 곳의 위치는 내 헛간 근처의 오래된 그루터기 뒤입니다.', },
    {'요르겐 농장으로 가서 오래된 그루터기 뒤에 묻힌 물건을 찾아야 합니다.', },
  },
